    }

    // Before start of event loop allow special processing.


    int counter=0, istat=0, i=0;
    while (istat!=2) {

        istat=estructMaker->Make();

        i++; counter++;
        if (counter==200) {
            cout<<"doing event ="<<i<<endl;
            counter=0;
        }
        if ( maxNumEvents!=0 && i>=maxNumEvents ) {
            istat=2;
        }
        // Any special event loop code goes here.

    }
    // --> statistics file
    ofstream ofs(getOutFileName(outputDir,jobName,"stats"));
    estructMaker->logAllStats(ofs);
    ecuts->printCuts(ofs);
    tcuts->printCuts(ofs);

    ofs<<endl;
    ofs.close();

    // --> root cut histogram file
    TFile *tfc = new TFile(getOutFileName(outputDir,jobName,"cuts"),"RECREATE");
    ecuts->writeCutHists(tfc);
    tcuts->writeCutHists(tfc);
    StEStructCutBin* cb = StEStructCutBin::Instance();
    cb->writeCutBinHists();
    tfc->Close();

    // --> root QA histogram file
    estructMaker->writeQAHists(getOutFileName(outputDir,jobName,"QA"));

    // Any special clean up code goes here.


    // --> Write out the data in the form of root histograms.
    estructMaker->Finish();
