      DIMENSION NG21(12),NG22(12),NG23(12),NG24(12),NG25(12),NG26(12)
      DIMENSION EG21(12),EG22(12),EG23(12),EG24(12),EG25(12),EG26(12)
      DIMENSION WK1(12),WK2(12),WK3(12),WK4(12),WK5(12),WK6(12)
      DIMENSION EFL1(12),EFL2(12),EFL3(12),EFL4(12),EFL5(12),EFL6(12)
                                                                   
C  ---------------------------------------------------------------------
C                                                                       
C     SUBROUTINE FILLS ARRAYS OF COLLISION FREQUENCY              
C     CAN HAVE A MIXTURE OF UP TO 6 GASES                               
C                                                                       
C                                                                       
C  ---------------------------------------------------------------------
C                                                             
      NISO=0
      NIN1=0                                                            
      NIN2=0                                                            
      NIN3=0                                                            
      NIN4=0
      NIN5=0
      NIN6=0
      NION1=0
      NION2=0
      NION3=0
      NION4=0
      NION5=0
      NION6=0
      N4000=4000
      NONE=1                                                     
      DO 2 J=1,6  
      NAMEG(J)='-------------------------'                              
      KEL1(J)=0
      KEL2(J)=0
      KEL3(J)=0
      KEL4(J)=0
      KEL5(J)=0
      KEL6(J)=0                       
      DO 1 I=1,4000                                                    
      Q1(J,I)=0.0D0                                                     
      Q2(J,I)=0.0D0                                                     
      Q3(J,I)=0.0D0                                                     
      Q4(J,I)=0.0D0
      Q5(J,I)=0.0D0
      Q6(J,I)=0.0D0                     
    1 CONTINUE                                                          
      E1(J)=0.0D0                                                       
      E2(J)=0.0D0                                                       
      E3(J)=0.0D0                                                       
      E4(J)=0.0D0 
      E5(J)=0.0D0
    2 E6(J)=0.0D0
      DO 222 J=1,12
      EION1(J)=0.0D0
      EION2(J)=0.0D0
      EION3(J)=0.0D0
      EION4(J)=0.0D0
      EION5(J)=0.0D0
      EION6(J)=0.0D0
      EB1(J)=0.0D0
      EB2(J)=0.0D0
      EB3(J)=0.0D0
      EB4(J)=0.0D0
      EB5(J)=0.0D0
      EB6(J)=0.0D0
      EC01(J)=0.0D0
      EC02(J)=0.0D0
      EC03(J)=0.0D0
      EC04(J)=0.0D0
      EC05(J)=0.0D0
      EC06(J)=0.0D0
      EG11(J)=0.0D0
      EG12(J)=0.0D0
      EG13(J)=0.0D0
      EG14(J)=0.0D0
      EG15(J)=0.0D0
      EG16(J)=0.0D0
      EG21(J)=0.0D0
      EG22(J)=0.0D0
      EG23(J)=0.0D0
      EG24(J)=0.0D0
      EG25(J)=0.0D0
      EG26(J)=0.0D0
      WK1(J)=0.0D0
      WK2(J)=0.0D0
      WK3(J)=0.0D0
      WK4(J)=0.0D0
      WK5(J)=0.0D0
      WK6(J)=0.0D0
      EFL1(J)=0.0D0
      EFL2(J)=0.0D0
      EFL3(J)=0.0D0
      EFL4(J)=0.0D0
      EFL5(J)=0.0D0
      EFL6(J)=0.0D0
      NC01(J)=0
      NC02(J)=0
      NC03(J)=0
      NC04(J)=0
      NC05(J)=0
      NC06(J)=0
      NG11(J)=0
      NG12(J)=0
      NG13(J)=0
      NG14(J)=0
      NG15(J)=0
      NG16(J)=0
      NG21(J)=0
      NG22(J)=0
      NG23(J)=0
      NG24(J)=0
      NG25(J)=0
      NG26(J)=0
      DO 222 I=1,4000
      QION1(J,I)=0.0D0
      QION2(J,I)=0.0D0
      QION3(J,I)=0.0D0
      QION4(J,I)=0.0D0
      QION5(J,I)=0.0D0
      QION6(J,I)=0.0D0
  222 CONTINUE
      ESTEP=EFINAL/DFLOAT(NSTEP)
      EHALF=ESTEP/2.0D0
      E(1)=EHALF
      DO 3 I=2,4000
      AJ=DFLOAT(I-1)
      E(I)=EHALF+ESTEP*AJ
    3 EROOT(I)=DSQRT(E(I))
      EROOT(1)=DSQRT(EHALF)                   
      DO 4 I=1,250
      KIN1(I)=0
      KIN2(I)=0
      KIN3(I)=0
      KIN4(I)=0
      KIN5(I)=0
    4 KIN6(I)=0
      DO 6 I=1,512 
    6 INDEX(I)=0
      DO 7 I=1,8
    7 TCFMAX(I)=0.0D0                                               
C                                                                       
C   CALL GAS CROSS-SECTIONS 
      CALL GASMIX(NGASN(1),Q1,QIN1,NIN1,E1,EI1,NAME1,VIRIAL1,EB1,
     /PEQEL1,PEQIN1,PENFRA1,KEL1,KIN1,QION1,PEQION1,EION1,NION1,
     /NC01,EC01,WK1,EFL1,NG11,EG11,NG21,EG21,SCRP1) 
      IF(NGAS.EQ.1) GO TO 10 
      CALL GASMIX(NGASN(2),Q2,QIN2,NIN2,E2,EI2,NAME2,VIRIAL2,EB2,
     /PEQEL2,PEQIN2,PENFRA2,KEL2,KIN2,QION2,PEQION2,EION2,NION2,
     /NC02,EC02,WK2,EFL2,NG12,EG12,NG22,EG22,SCRP2)
      IF(NGAS.EQ.2) GO TO 10 
      CALL GASMIX(NGASN(3),Q3,QIN3,NIN3,E3,EI3,NAME3,VIRIAL3,EB3,
     /PEQEL3,PEQIN3,PENFRA3,KEL3,KIN3,QION3,PEQION3,EION3,NION3,
     /NC03,EC03,WK3,EFL3,NG13,EG13,NG23,EG23,SCRP3)
      IF(NGAS.EQ.3) GO TO 10 
      CALL GASMIX(NGASN(4),Q4,QIN4,NIN4,E4,EI4,NAME4,VIRIAL4,EB4,
     /PEQEL4,PEQIN4,PENFRA4,KEL4,KIN4,QION4,PEQION4,EION4,NION4,
     /NC04,EC04,WK4,EFL4,NG14,EG14,NG24,EG24,SCRP4)
      IF(NGAS.EQ.4) GO TO 10 
      CALL GASMIX(NGASN(5),Q5,QIN5,NIN5,E5,EI5,NAME5,VIRIAL5,EB5,
     /PEQEL5,PEQIN5,PENFRA5,KEL5,KIN5,QION5,PEQION5,EION5,NION5,
     /NC05,EC05,WK5,EFL5,NG15,EG15,NG25,EG25,SCRP5)
      IF(NGAS.EQ.5) GO TO 10 
      CALL GASMIX(NGASN(6),Q6,QIN6,NIN6,E6,EI6,NAME6,VIRIAL6,EB6,
     /PEQEL6,PEQIN6,PENFRA6,KEL6,KIN6,QION6,PEQION6,EION6,NION6,
     /NC06,EC06,WK6,EFL6,NG16,EG16,NG26,EG26,SCRP6)
   10 CONTINUE                                                          
C ---------------------------------------------------------------       
C  CORRECTION OF NUMBER DENSITY DUE TO VIRIAL COEFFICIENT               
C  CAN BE PROGRAMMED HERE NOT YET IMPLEMENTED.                          
C-----------------------------------------------------------------
C-----------------------------------------------------------------      
C     CALCULATION OF COLLISION FREQUENCIES FOR AN ARRAY OF              
C     ELECTRON ENERGIES IN THE RANGE ZERO TO EFINAL                     
C                                                                       
C     L=5*N-4    ELASTIC NTH GAS                                      
C     L=5*N-3    IONISATION NTH GAS                                     
C     L=5*N-2    ATTACHMENT NTH GAS                                     
C     L=5*N-1    INELASTIC NTH GAS    
C     L=5*N      SUPERELASTIC NTH GAS                                   
C---------------------------------------------------------------   
      DO 700 IE=1,4000  
      FCION(IE)=0.0D0
      FCATT(IE)=0.0D0
      NP=1                                                              
      CF(IE,NP)=Q1(2,IE)*VAN1
      PSCT(IE,NP)=0.5D0
      ANGCT(IE,NP)=1.0D0    
      INDEX(NP)=0 
C    ELASTIC ANG 
      IF(KEL1(2).EQ.1) THEN
       PSCT1=PEQEL1(2,IE)
       CALL ANGCUT(PSCT1,ANGC,PSCT2)
       ANGCT(IE,NP)=ANGC
       PSCT(IE,NP)=PSCT2  
       INDEX(NP)=1   
      ENDIF
      IF(KEL1(2).EQ.2) THEN
       PSCT(IE,NP)=PEQEL1(2,IE)
       INDEX(NP)=2
      ENDIF
C 
      IF(IE.GT.1) GO TO 12                                   
      RGAS1=1.0D0+E1(2)/2.0D0                                           
      RGAS(NP)=RGAS1                                                    
      EIN(NP)=0.0D0                                                     
      IPN(NP)=0 
      L=1                                                      
      IARRY(NP)=L 
      DSCRPT(NP)=SCRP1(2)  
      NAMEG(1)=NAME1  
      PENFRA(1,NP)=0.0
      PENFRA(2,NP)=0.0
      PENFRA(3,NP)=0.0 
C                                             
   12 IF(EFINAL.LT.E1(3)) GO TO 30   
      IF(NION1.GT.1) GO TO 20                                   
      NP=NP+1   
      CF(IE,NP)=Q1(3,IE)*VAN1
      FCION(IE)=FCION(IE)+CF(IE,NP) 
      PSCT(IE,NP)=0.5D0
      ANGCT(IE,NP)=1.0D0
      INDEX(NP)=0
C  
      IF(KEL1(3).EQ.1) THEN
       PSCT1=PEQEL1(3,IE) 
       CALL ANGCUT(PSCT1,ANGC,PSCT2)
