      EIN(1)=-0.032                                            
      EIN(2)=0.032 
      EIN(3)=-0.108
      EIN(4)=0.108  
      EIN(5)=-0.173                                                    
      EIN(6)=0.173
      EIN(7)=-0.363                                                    
      EIN(8)=0.363
      EIN(9)=0.519
      EIN(10)=7.2
      EIN(11)=8.6                                               
      EIN(12)=7.50                     
      EIN(13)=8.0
      EIN(14)=8.5
      EIN(15)=9.0
      EIN(16)=9.5
      EIN(17)=10.0
      EIN(18)=10.5
      EIN(19)=11.0
      EIN(20)=11.5
      EIN(21)=12.0
      EIN(22)=12.5
