      IF(QIN(14,I).LT.0.0) QIN(14,I)=0.0
      IF(EN.LE.(2.0*EIN(14))) GO TO 915
      PEQIN(14,I)=PEQEL(2,(I-IOFFN(14)))     
C EXCITATION   F=0.15700                                      
  915 QIN(15,I)=0.0              
