  260 CONTINUE
      J=NAT3
  270 A=(YAT3(J)-YAT3(J-1))/(XAT3(J)-XAT3(J-1))                         
      B=(XAT3(J-1)*YAT3(J)-XAT3(J)*YAT3(J-1))/(XAT3(J-1)-XAT3(J))       
      THREEB=FAC*(A*EN+B)*1.D-16                                        
      Q(4,I)=SINGLE+THREEB
  300 Q(5,I)=0.0                                                        
      Q(6,I)=0.0                                                        
C                                                                       
      QIN(1,I)=0.0                                                      
      IF(EN.LE.EIN(1)) GO TO 400                                        
      DO 310 J=2,NROT1                                                  
