      B=(XROT1(J-1)*YROT1(J)-XROT1(J)*YROT1(J-1))/(XROT1(J-1)-XROT1(J)) 
      QIN(1,I)=(A*EN+B)*1.D-16                                          
  400 CONTINUE                                                          
      QIN(2,I)=0.0                                                      
      IF(EN.LE.EIN(2)) GO TO 500                                        
      DO 410 J=2,NVIB1                                                  
