      QIN(5,I)=0.0                                                      
      IF(EN.LE.EIN(5)) GO TO 1401                                       
      DO 1311 J=2,NROT1                                                 
      IF(EN.LE.XROT1(J)) GO TO 1321                                     
 1311 CONTINUE                                                          
