      QIN(4,I)=(A*EN+B)*1.D-16                                          
  500 CONTINUE                                                          
      QIN(5,I)=0.0                                                      
      IF(EN.LE.EIN(5)) GO TO 600                                        
      DO 540 J=2,NVIB5                                                  
