      IF(EN.LE.XION(J)) GO TO 61                                       
   60 CONTINUE                                                          
      J=NIOND                                                   
   61 A=(YIN2(J)-YIN2(J-1))/(XION(J)-XION(J-1))                         
      B=(XION(J-1)*YIN2(J)-XION(J)*YIN2(J-1))/(XION(J-1)-XION(J))       
