      QIN(3,I)=(EN+EIN(4))*(A*(EN+EIN(4))+B)/EN
      GO TO 426
  425 QIN(3,I)=YV1V1(N1V1)*(XV1V1(N1V1)/(EN+EIN(4)))**2
  426 QIN(3,I)=QIN(3,I)*APOPV1/DEGV1*1.D-16 
      IF(KIN(3).EQ.2) PEQIN(3,I)=PEQEL(2,(I-IOFFN(3)))                 
