      DO 460 J=2,N1V1                                                
      IF(EN.LE.XV1V1(J)) GO TO 470                                      
  460 CONTINUE                                                          
      J=N1V1                                                         
  470 A=(YV1V1(J)-YV1V1(J-1))/(XV1V1(J)-XV1V1(J-1))                     
