      QIN(4,I)=A*EN+B
      GO TO 476
  475 QIN(4,I)=YV1V1(N1V1)*(XV1V1(N1V1)/EN)**2 
  476 QIN(4,I)=QIN(4,I)*APOPGS*1.D-16 
      IF(KIN(4).EQ.2) PEQIN(4,I)=PEQEL(2,(I-IOFFN(4)))
C
C V3 SUPERELASTIC  
  500 QIN(5,I)=0.0
      PEQIN(5,I)=0.5D0
      IF(KIN(5).EQ.2) PEQIN(5,I)=0.0
