      IF(KIN(6).EQ.2) PEQIN(6,I)=PEQEL(2,(I-IOFFN(6)))
c
C 2V1                                                         
  600 QIN(7,I)=0.0
      PEQIN(7,I)=0.5D0       
      IF(KIN(7).EQ.2) PEQIN(7,I)=0.0                                 
