      IF(EN.GT.XV2V1(N2V1)) GO TO 621                               
      DO 610 J=2,N2V1                                                 
      IF(EN.LE.XV2V1(J)) GO TO 620                                      
  610 CONTINUE                                                          
      J=N2V1                                                          
  620 A=(YV2V1(J)-YV2V1(J-1))/(XV2V1(J)-XV2V1(J-1))                     
      B=(XV2V1(J-1)*YV2V1(J)-XV2V1(J)*YV2V1(J-1))/(XV2V1(J-1)-XV2V1(J))
      QIN(7,I)=(A*EN+B)*1.D-16
