      QIN(7,I)=(A*EN+B)*1.D-16                                         
      GO TO 400
C SCALED BY 1/E ABOVE XVIB2(NVIB2) EV
  375 QIN(7,I)=YVIB2(NVIB2)*XVIB2(NVIB2)/EN*1.D-16                    
C INELASTIC VIB3                                                               
  400 QIN(8,I)=0.0                                                      
      IF(EN.LE.EIN(8)) GO TO 450 
      IF(EN.GT.XVIB3(NVIB3)) GO TO 425                                  
      DO 410 J=2,NVIB3                                                  
