      QIN(8,I)=(A*EN+B)*1.D-16                                    
      GO TO 450
C SCALED BY 1/E ABOVE XVIB3(NVIB3) EV
  425 QIN(8,I)=YVIB3(NVIB3)*XVIB3(NVIB3)/EN*1.D-16
C INELASTIC VIBRATION HARMONICS
  450 QIN(9,I)=0.0
      IF(EN.LE.EIN(9)) GO TO 500
      IF(EN.GT.XVHAR(NVHAR)) GO TO 475  
