      GO TO 550
C SCALED BY 1/E ABOVE XEXC1(NEXC1) EV
  525 QIN(10,I)=YEXC1(NEXC1)*XEXC1(NEXC1)/EN*1.D-16    
C EXCITATION                      
  550 QIN(11,I)=0.0
      IF(EN.LE.EIN(11)) GO TO 600
      IF(EN.GT.XEXC2(NEXC2)) GO TO 575  
      DO 560 J=2,NEXC2                                                  
