C GAS59 :  GEH4  (2005)    GERMANE BETTER TRANSPORT DATA NEEDED    3*  
C GAS60 :  SIH4  (2005)    SILANE ANISOTROPIC                      4*
C  GAS61-80 :DUMMY ROUTINES
C------------------------------------------------------------------
C
C      PROGRAM MAGBOLTZ 2                                                
      SUBROUTINE MAGBOLTZ
      IMPLICIT REAL*8 (A-H,O-Z)
      IMPLICIT INTEGER*8 (I-N)
      COMMON/BFLD/EOVB,WB,BTHETA,BMAG                                  
      COMMON/INPT/NGAS,NSTEP,NANISO,EFINAL,ESTEP,AKT,ARY,TEMPC,TORR,IPEN
      COMMON/SETP/TMAX,SMALL,API,ESTART,THETA,PHI,RSTART,EFIELD,NMAX
      COMMON/CTOWNS/ALPHA,ATT
      COMMON/THRM/AMGAS(6),VTMB(6),TCFMX,TCFMXG(6),ITHRM
C 1    CALL READIN(LAST)
C      IF(LAST.EQ.1) GO TO 99
      IF(ITHRM.EQ.0) GO TO 6 
      CALL SETUPT(LAST)                                                
      IF(LAST.EQ.1) GO TO 99
      IF(EFINAL.GT.0.0D0) GO TO 3
C CALCULATE EFINAL (START AT 0.5 EV. IF E/P GT 15 START AT 8.0 EV.)
      EFINAL=0.5D0
      EOP=EFIELD*(TEMPC+273.15D0)/(TORR*293.15D0) 
      IF(EOP.GT.15.0D0) EFINAL=8.0D0
      ESTART=EFINAL/50.0D0      
 2    CALL MIXERT
C LOOP TO CALCULATE EFINAL
      IF(BMAG.EQ.0.0D0.OR.BTHETA.EQ.0.0D0.OR.DABS(BTHETA).EQ.180.0D0) 
     /THEN 
       CALL ELIMITT(IELOW)
       GO TO 10
      ENDIF
      IF(BTHETA.EQ.90.0D0) THEN
       CALL ELIMITBT(IELOW)
       GO TO 10
      ELSE
       CALL ELIMITCT(IELOW)
      ENDIF
   10 IF(IELOW.EQ.1) THEN
       EFINAL=EFINAL*DSQRT(2.0D0)
       ESTART=EFINAL/50.0D0
       GO TO 2
      ENDIF
      GO TO 4 
    3 CALL MIXERT
    4 CONTINUE   
      CALL PRNTERT                                                      
      IF(BMAG.EQ.0.0D0) CALL MONTET
      IF(BMAG.NE.0.0D0) THEN
       IF(BTHETA.EQ.0.0D0.OR.BTHETA.EQ.180.0D0) THEN
        CALL MONTEAT
        ELSE IF(BTHETA.EQ.90.0D0) THEN
        CALL MONTEBT
        ELSE
        CALL MONTECT
       ENDIF
      ENDIF
      CALL OUTPUTT
C IF ATTACHMENT OR IONISATION RATE IS GREATER THAN SSTMIN THEN INCLUDE
C   SPATIAL GRADIENTS IN THE SOLUTION .   
      TGAS=273.15D0+TEMPC 
      ALPP=ALPHA*760.0D0*TGAS/(TORR*293.15D0)            
      ATTP=ATT*760.0D0*TGAS/(TORR*293.15D0)
C*******************      
      SSTMIN=40.0D0
C     SSTMIN=60.0D0
C*****************      
      IF(DABS(ALPP-ATTP).LT.SSTMIN) THEN
       CALL OUTPUT2T
C       GO TO 1
       RETURN
      ENDIF
C HS: If alpha == 0, skip the SST calculation.
      IF(ALPP.EQ.0.0) THEN
       CALL OUTPUT2T
       RETURN
      ENDIF
   5  IF(BMAG.EQ.0.0D0) THEN 
       CALL ALPCALCT
      ELSE IF(BTHETA.EQ.0.0D0.OR.BTHETA.EQ.180.0D0) THEN
       CALL ALPCLCAT
      ELSE IF(BTHETA.EQ.90.0D0) THEN
       CALL ALPCLCBT
      ELSE
       CALL ALPCLCCT
      ENDIF
      CALL OUTPUT2T
C      GO TO 1
  99  RETURN
C  99  STOP                 
    6 CALL SETUP(LAST)                                                
      IF(LAST.EQ.1) GO TO 999
      IF(EFINAL.GT.0.0D0) GO TO 13
C CALCULATE EFINAL (START AT 0.5 EV. IF E/P GT 15 START AT 8.0 EV.)
      EFINAL=0.5D0
      EOP=EFIELD*(TEMPC+273.15D0)/(TORR*293.15D0) 
      IF(EOP.GT.15.0D0) EFINAL=8.0D0
      ESTART=EFINAL/50.0D0      
  12  CALL MIXER 
C LOOP TO CALCULATE EFINAL
      IF(BMAG.EQ.0.0D0.OR.BTHETA.EQ.0.0D0.OR.DABS(BTHETA).EQ.180.0D0) 
     /THEN 
       CALL ELIMIT(IELOW)
       GO TO 20
      ENDIF
      IF(BTHETA.EQ.90.0D0) THEN
       CALL ELIMITB(IELOW)
       GO TO 20
      ELSE
       CALL ELIMITC(IELOW)
      ENDIF
   20 IF(IELOW.EQ.1) THEN
       EFINAL=EFINAL*DSQRT(2.0D0)
       ESTART=EFINAL/50.0D0
       GO TO 12
      ENDIF
      GO TO 14
   13 CALL MIXER
   14 CONTINUE   
      CALL PRNTER                                                       
      IF(BMAG.EQ.0.0D0) CALL MONTE
      IF(BMAG.NE.0.0D0) THEN
       IF(BTHETA.EQ.0.0D0.OR.BTHETA.EQ.180.0D0) THEN
        CALL MONTEA
        ELSE IF(BTHETA.EQ.90.0D0) THEN
        CALL MONTEB
        ELSE
        CALL MONTEC
       ENDIF
      ENDIF
      CALL OUTPUT 
C IF ATTACHMENT OR IONISATION RATE IS GREATER THAN SSTMIN THEN INCLUDE
C   SPATIAL GRADIENTS IN THE SOLUTION .   
      TGAS=273.15D0+TEMPC 
      ALPP=ALPHA*760.0D0*TGAS/(TORR*293.15D0)            
      ATTP=ATT*760.0D0*TGAS/(TORR*293.15D0)
C*******************      
      SSTMIN=40.0D0
C     SSTMIN=60.0D0
C*****************     
      IF(DABS(ALPP-ATTP).LT.SSTMIN) THEN
       CALL OUTPUT2
C       GO TO 1
       RETURN
      ENDIF
C HS: If alpha == 0, skip the SST calculation.
      IF(ALPP.EQ.0.0) THEN
       CALL OUTPUT2
       RETURN
      ENDIF
   15 IF(BMAG.EQ.0.0D0) THEN 
       CALL ALPCALC
      ELSE IF(BTHETA.EQ.0.0D0.OR.BTHETA.EQ.180.0D0) THEN
       CALL ALPCLCA
      ELSE IF(BTHETA.EQ.90.0D0) THEN
       CALL ALPCLCB
      ELSE
       CALL ALPCLCC
      ENDIF
      CALL OUTPUT2 
C      GO TO 1 
C  999 STOP                 
  999 RETURN
      END
      SUBROUTINE ANGCUT(PSCT1,ANGC,PSCT2)        
      IMPLICIT REAL*8 (A-H,O-Z)
      IMPLICIT INTEGER*8 (I-N)
C  --------------------------------------------------------------      
C  SET ANGLE CUTS ON ANGULAR DISTRIBUTION AND RENORMALISE FORWARD 
C  SCATTERING PROBABILITY  
C  ---------------------------------------------------------------
      ANGC=1.0D0
      PSCT2=PSCT1         
      IF(PSCT1.LE.1.0D0) RETURN
      API=DACOS(-1.0D0)
      RADS=2.0D0/API 
      CNS=PSCT1-0.5D0     
      THETAC=DASIN(2.0D0*DSQRT(CNS-CNS*CNS))
      FAC=(1.0D0-DCOS(THETAC))/(DSIN(THETAC)*DSIN(THETAC))
      PSCT2=(CNS*FAC)+0.5D0      
      ANGC=THETAC*RADS
      RETURN
      END                                                              
