      Q5(J,I)=0.0D0
      Q6(J,I)=0.0D0
    1 CONTINUE                                                          
      E1(J)=0.0D0                                                       
      E2(J)=0.0D0                                                       
      E3(J)=0.0D0                                                       
      E4(J)=0.0D0 
      E5(J)=0.0D0
    2 E6(J)=0.0D0
      DO 222 J=1,30
      EION1(J)=0.0D0
      EION2(J)=0.0D0
      EION3(J)=0.0D0
      EION4(J)=0.0D0
      EION5(J)=0.0D0
      EION6(J)=0.0D0
      EB1(J)=0.0D0
      EB2(J)=0.0D0
      EB3(J)=0.0D0
      EB4(J)=0.0D0
      EB5(J)=0.0D0
      EB6(J)=0.0D0
      EC01(J)=0.0D0
      EC02(J)=0.0D0
      EC03(J)=0.0D0
      EC04(J)=0.0D0
      EC05(J)=0.0D0
      EC06(J)=0.0D0
      EG11(J)=0.0D0
      EG12(J)=0.0D0
      EG13(J)=0.0D0
      EG14(J)=0.0D0
      EG15(J)=0.0D0
      EG16(J)=0.0D0
      EG21(J)=0.0D0
      EG22(J)=0.0D0
      EG23(J)=0.0D0
      EG24(J)=0.0D0
      EG25(J)=0.0D0
      EG26(J)=0.0D0
      WK1(J)=0.0D0
      WK2(J)=0.0D0
      WK3(J)=0.0D0
      WK4(J)=0.0D0
      WK5(J)=0.0D0
      WK6(J)=0.0D0
