      IF(IPLAST(KGAS).GE.32)  ISIZE(KGAS)=32
      IF(IPLAST(KGAS).GE.64)  ISIZE(KGAS)=64
      IF(IPLAST(KGAS).GE.128) ISIZE(KGAS)=128
      IF(IPLAST(KGAS).GE.256) ISIZE(KGAS)=256
C ----------------------------------------------------------------      
C     CALCULATION OF TOTAL COLLISION FREQUENCY FOR EACH GAS COMPONENT  
C --------------------------------------------------------------------- 
      TCF(KGAS,IE)=0.0D0                                              
      DO 610 IF=1,IPLAST(KGAS)                                         
      TCF(KGAS,IE)=TCF(KGAS,IE)+CF(KGAS,IE,IF)
      IF(CF(KGAS,IE,IF).LT.0.0D0) WRITE(6,776) CF(KGAS,IE,IF),KGAS,IE,IF
     /,IARRY(KGAS,IF),EIN(KGAS,IF) 
  776 FORMAT('  WARNING NEGATIVE COLLISION FEQUENCY =',D12.3,' KGAS =',
     /I2,' IE =',I6,' IF =',I3,' IARRY=',I5,' EIN=',F7.4)              
 610  CONTINUE                                                          
      DO 620 IF=1,IPLAST(KGAS)                                         
      IF(TCF(KGAS,IE).EQ.0.0D0) GO TO 615                              
      CF(KGAS,IE,IF)=CF(KGAS,IE,IF)/TCF(KGAS,IE)                       
      GO TO 620                                                         
 615  CF(KGAS,IE,IF)=0.0D0                                             
 620  CONTINUE                                                          
      DO 630 IF=2,IPLAST(KGAS)                                          
      CF(KGAS,IE,IF)=CF(KGAS,IE,IF)+CF(KGAS,IE,IF-1)                    
 630  CONTINUE                   
      FCATT(IE)=FCATT(IE)*EROOT(IE)
      FCION(IE)=FCION(IE)*EROOT(IE)                                     
      TCF(KGAS,IE)=TCF(KGAS,IE)*EROOT(IE)   
 700  CONTINUE 
C
      DO 637 IE=1,N4000
C CALCULATION OF NULL COLLISION FREQUENCIES
      NPLAST(1)=NUL1
      NPLAST(2)=NUL2
      NPLAST(3)=NUL3
      NPLAST(4)=NUL4
      NPLAST(5)=NUL5
      NPLAST(6)=NUL6
      IF((NUL1+NUL2+NUL3+NUL4+NUL5+NUL6).EQ.0) GO TO 638      
      IF(NUL1.GT.0) THEN
       DO 631 J=1,NPLAST(1)
       SCLENUL(1,J)=SCLN1(J)
       DSCRPTN(1,J)=SCRPN1(J)
  631  CFN(1,IE,J)=QNUL1(J,IE)*VAN1*SCLENUL(1,J)
      ENDIF
      IF(NUL2.GT.0) THEN
       DO 632 J=1,NPLAST(2)
       SCLENUL(2,J)=SCLN2(J)
       DSCRPTN(2,J)=SCRPN2(J)
  632  CFN(2,IE,J)=QNUL2(J,IE)*VAN2*SCLENUL(2,J)
      ENDIF
      IF(NUL3.GT.0) THEN
       DO 633 J=1,NPLAST(3)
       SCLENUL(3,J)=SCLN3(3)
       DSCRPTN(3,J)=SCRPN3(3)
  633  CFN(3,IE,J)=QNUL3(J,IE)*VAN3*SCLENUL(3,J)
      ENDIF
      IF(NUL4.GT.0) THEN
       DO 634 J=1,NPLAST(4)
       SCLENUL(4,J)=SCLN4(J)
       DSCRPTN(4,J)=SCRPN4(J)
  634  CFN(4,IE,J)=QNUL4(J,IE)*VAN4*SCLENUL(4,J)
      ENDIF
      IF(NUL5.GT.0) THEN
       DO 635 J=1,NPLAST(5)
       SCLENUL(5,J)=SCLN5(J)
       DSCRPTN(5,J)=SCRPN5(J)
  635  CFN(5,IE,J)=QNUL5(J,IE)*VAN5*SCLENUL(5,J)
      ENDIF
      IF(NUL6.GT.0) THEN
       DO 636 J=1,NPLAST(6)
       SCLENUL(6,J)=SCLN6(J)
       DSCRPTN(6,J)=SCRPN6(J)
  636  CFN(6,IE,J)=QNUL6(J,IE)*VAN6*SCLENUL(6,J)
      ENDIF
C CALCULATE NULL COLLISION FREQUENCY FOR EACH GAS COMPONENT
      DO 637 KGAS=1,NGAS
      TCFN(KGAS,IE)=0.0D0
      DO 6361 IL=1,NPLAST(KGAS)
      TCFN(KGAS,IE)=TCFN(KGAS,IE)+CFN(KGAS,IE,IL)
      IF(CFN(KGAS,IE,IL).LT.0.0D0) WRITE(6,777) CFN(KGAS,IE,IL),KGAS,IL
  777 FORMAT(' WARNING  NEGATIVE NULL COLLISION FREQUENCY=',D12.3,' KGAS
     / =',I2,' IL =',I2)
 6361 CONTINUE
      DO 6363 IL=1,NPLAST(KGAS)
      IF(TCFN(KGAS,IE).EQ.0.0D0) GO TO 6362
      CFN(KGAS,IE,IL)=CFN(KGAS,IE,IL)/TCFN(KGAS,IE)
      GO TO 6363
 6362 CFN(KGAS,IE,IL)=0.0D0
 6363 CONTINUE
      DO 6364 IL=2,NPLAST(KGAS)
      CFN(KGAS,IE,IL)=CFN(KGAS,IE,IL)+CFN(KGAS,IE,IL-1)
 6364 CONTINUE
      TCFN(KGAS,IE)=TCFN(KGAS,IE)*EROOT(IE)
  637 CONTINUE
  638 CONTINUE
C     WRITE(6,841) (INDEX(KGAS,J),J,KGAS, J=1,IPLAST(KGAS))
C 841 FORMAT(2X,' INDEX=',I3,' J=',I3,' KGAS=',I2)                                  
C  SET ANISOTROPIC FLAG IF ANISOTROPIC SCATTERING DATA IS DETECTED
      KELSUM=0
      DO 701 J=1,6
 701  KELSUM=KELSUM+KEL1(J)+KEL2(J)+KEL3(J)+KEL4(J)+KEL5(J)+KEL6(J)
      DO 702 J=1,250
 702  KELSUM=KELSUM+KIN1(J)+KIN2(J)+KIN3(J)+KIN4(J)+KIN5(J)+KIN6(J)
      IF(KELSUM.GT.0) NISO=1  
C     IF(NISO.EQ.1) WRITE(6,7765) NISO
C7765 FORMAT(3X,' ANISOTROPIC SCATTERING DETECTED NISO=',I5)            
C -------------------------------------------------------------------   
C   CALCULATE NULL COLLISION FREQUENCIES FOR EACH GAS COMPONENT                                  
C ------------------------------------------------------------------- 
      FAKEIN=DABS(FAKEI)/DFLOAT(NGAS) 
      DO 800 KGAS=1,NGAS
      TCFMAX(KGAS)=0.0D0
      DO 800 IE=1,4000
      IF((TCF(KGAS,IE)+TCFN(KGAS,IE)+FAKEIN).GE.TCFMAX(KGAS)) 
     /TCFMAX(KGAS)=TCF(KGAS,IE)+TCFN(KGAS,IE)+FAKEIN       
  800 CONTINUE                                        
C CALCULATE EACH GAS CUMULATIVE FRACTIONAL NULL COLLISION FREQUENCIES
      TCFMX=0.0D0
      DO 810 KGAS=1,NGAS
  810 TCFMX=TCFMX+TCFMAX(KGAS) 
      DO 811 KGAS=1,NGAS
  811 TCFMXG(KGAS)=TCFMAX(KGAS)/TCFMX
      DO 812 KGAS=2,NGAS
  812 TCFMXG(KGAS)=TCFMXG(KGAS)+TCFMXG(KGAS-1)
C CALCULATE MAXWELL BOLTZMAN VELOCITY FACTOR FOR EACH GAS COMPONENT
      DO 813 KGAS=1,NGAS
  813 VTMB(KGAS)=DSQRT(2.0D0*ECHARG*AKT/AMGAS(KGAS))*1.D-12            
C -------------------------------------------------------------------   
C   CROSS SECTION DATA FOR INTEGRALS IN  OUTPUT               
C --------------------------------------------------------------------- 
      DO 900 I=1,NSTEP                                               
      QTOT(I)=AN1*Q1(1,I)+AN2*Q2(1,I)+AN3*Q3(1,I)+AN4*Q4(1,I)+
     /AN5*Q5(1,I)+AN6*Q6(1,I)            
      QEL(I)=AN1*Q1(2,I)+AN2*Q2(2,I)+AN3*Q3(2,I)+AN4*Q4(2,I)+
     /AN5*Q5(2,I)+AN6*Q6(2,I)                          
      QION(1,I)=Q1(3,I)*AN1                                             
      QION(2,I)=Q2(3,I)*AN2                                             
      QION(3,I)=Q3(3,I)*AN3                                             
      QION(4,I)=Q4(3,I)*AN4
      QION(5,I)=Q5(3,I)*AN5
      QION(6,I)=Q6(3,I)*AN6                                             
      QATT(1,I)=Q1(4,I)*AN1                                             
      QATT(2,I)=Q2(4,I)*AN2                                             
      QATT(3,I)=Q3(4,I)*AN3                                             
      QATT(4,I)=Q4(4,I)*AN4
      QATT(5,I)=Q5(4,I)*AN5
      QATT(6,I)=Q6(4,I)*AN6                                             
C                                                                       
  850 QREL(I)=0.0D0                                                     
      QSATT(I)=0.0D0                                                    
      QSUM(I)=0.0D0                                                     
      DO 855 J=1,NGAS                                                   
      QSUM(I)=QSUM(I)+QION(J,I)+QATT(J,I)                               
      QSATT(I)=QSATT(I)+QATT(J,I)                                       
  855 QREL(I)=QREL(I)+QION(J,I)-QATT(J,I)                               
C                                                                       
      IF(NIN1.EQ.0) GO TO 865                                           
      DO 860 J=1,NIN1                                                   
  860 QSUM(I)=QSUM(I)+QIN1(J,I)*AN1                                     
  865 IF(NIN2.EQ.0) GO TO 875                                           
      DO 870 J=1,NIN2                                                   
  870 QSUM(I)=QSUM(I)+QIN2(J,I)*AN2                                     
  875 IF(NIN3.EQ.0) GO TO 885                                           
      DO 880 J=1,NIN3                                                   
  880 QSUM(I)=QSUM(I)+QIN3(J,I)*AN3                                     
  885 IF(NIN4.EQ.0) GO TO 895                                           
      DO 890 J=1,NIN4                                                   
  890 QSUM(I)=QSUM(I)+QIN4(J,I)*AN4                                     
  895 IF(NIN5.EQ.0) GO TO 898 
      DO 896 J=1,NIN5
  896 QSUM(I)=QSUM(I)+QIN5(J,I)*AN5
  898 IF(NIN6.EQ.0) GO TO 900
      DO 899 J=1,NIN6
  899 QSUM(I)=QSUM(I)+QIN6(J,I)*AN6                                     
C                                                                       
 900  CONTINUE                                                          
C                                                                       
      RETURN                                                            
      END 
