      CHARACTER*50 DSCRPT,DSCRPTN                                   
      CHARACTER*25 NAMEG
      WRITE(6,15)                                                       
      WRITE(6,15)                                                       
   15 FORMAT('----------------------------------------------------------
     /-------------------')
      NREAL=NMAX                                                  
      WRITE(6,109) TMAX1,NNULL,NREAL                                    
  109 FORMAT(/,2X,'CALCULATED MAX. COLLISION TIME =',F7.2,' PICOSECONDS.
     /',2(/),2X,'NUMBER OF NULL COLLISIONS =',I11,/,2X,'NUMBER OF REAL C
     /OLLISIONS =',I11)
      WMNZ=WZ*1.0D-05 
      WMNY=WY*1.0D-05
      WMNX=WX*1.0D-05                                      
      WRITE(6,940) WMNZ,DWZ,WMNY,DWY,WMNX,DWX                           
  940 FORMAT(/,2X,'Z DRIFT VELOCITY =',E11.4,' MICRONS/NANOSECOND  +-',F
     /8.2,'% ',/,2X,'Y DRIFT VELOCITY =',E11.4,' MICRONS/NANOSECOND  +-'
     /,F8.2,'%',/,2X,'X DRIFT VELOCITY =',E11.4,' MICRONS/NANOSECOND  +-
     /',F8.2,'%',/) 
      IF(BMAG.GT.0.0D0.AND.(BTHETA.GT.0.0D0.AND.BTHETA.LT.180.0D0))
     /GO TO 800 
      DTOVMB=DIFTR*EFIELD/WZ                                            
      DTMN=DSQRT(2.0D0*DIFTR/WZ)*10000.0D0 
      DFTER1=DSQRT(DFTER**2+DWZ**2)
      DFTER2=DFTER1/2.0
      WRITE(6,954)                                  
      WRITE(6,950) DIFTR,DFTER,DTOVMB,DFTER1,DTMN,DFTER2                
  950 FORMAT(/,2X,'TRANSVERSE DIFFUSION   =',D11.4,' +-',F8.2,'%',/,10X,
     /'=',F9.5,' EV. +-',F8.3,'%',/,10X,'=',F9.3,' MICRONS/CENTIMETER**0
     /.5  +-',F8.2,'%',/)                     
      DLOVMB=DIFLN*EFIELD/WZ                                            
      DLMN=DSQRT(2.0D0*DIFLN/WZ)*10000.0D0 
      DFLER1=DSQRT(DFLER**2+DWZ**2)     
      DFLER2=DFLER1/2.0                               
      WRITE(6,992) DIFLN,DFLER,DLOVMB,DFLER1,DLMN,DFLER2                
  992 FORMAT(1/,2X,'LONGITUDINAL DIFFUSION =',D11.4,' +-',F8.1,'%',/,10X
     /,'=',F9.4,' EV. +-',F8.2,'%',/,10X,'=',F9.3,' MICRONS/CENTIMETER**
     /0.5  +-',F8.2,'%',/)                     
      GO TO 900
  800 WRITE(6,954) 
  954 FORMAT(/,10X,' DIFFUSION IN CM**2/SEC.',/)     
      WRITE(6,955) DIFXX,DIFYY,DIFZZ,DIFYZ,DIFXY,DIFXZ
  955 FORMAT(/,2X,'DIFFUSION TENSOR :',/,6X,' DIFXX =',D11.4,' DIFYY =',
     /D11.4,' DIFZZ =',D11.4,/,6X,' DIFYZ =',D11.4,' DIFXY =',D11.4,' DI
     /FXZ =',D11.4,/)
      WRITE(6,956) DXXER,DYYER,DZZER,DYZER,DXYER,DXZER
  956 FORMAT(/,2X,'ERROR ON DIFFUSION TENSOR :',/,6X,' DIFXX =',F8.2,'% 
     / DIFYY =',F8.2,'%  DIFZZ =',F8.2,'%',/,6X,' DIFYZ =',F8.2,'%  DIFX
     /Y =',F8.2,'%  DIFXZ =',F8.2,'%',/)
      IF(BTHETA.EQ.90.) WRITE(6,957) DIFLN,DFLER,DIFTR,DFTER,DIFXX,DXXER
  957 FORMAT(/,8X,' LONGITUDINAL DIFFUSION =',D11.4,' +-',F8.2,'%',/,10X
     /,' TRANSVERSE DIFFUSION =',D11.4,' +-',F8.2,'%',/,2X,'TRANSVERSE D
     /IFFUSION (PARALLEL TO B-FIELD) DIFXX=',D11.4,' +-',F8.2,'%',/)
  900 WRITE(6,333) ALPHA,ALPER,ATT,ATTER                             
  333 FORMAT(2(/),'  IONISATION RATE /CM.=',E11.4,' +/-',F6.2,' PERCENT.
     /',/,'  ATTACHMENT RATE /CM.=',E11.4,' +/-',F6.2,' PERCENT.',2(/)) 
      WRITE(6,960) AVE,DEN             
  960 FORMAT(/,2X,'MEAN ELECTRON ENERGY =',F9.4,' EV. ERROR =  +-',F8.2,
     /'%',/)              
      RETURN                                                            
      END
      SUBROUTINE MONTEAT                                                
      IMPLICIT REAL*8 (A-H,O-Z)                                         
      IMPLICIT INTEGER*8 (I-N)
      COMMON/INPT/NGAS,NSTEP,NANISO,EFINAL,ESTEP,AKT,ARY,TEMPC,TORR,IPEN
      COMMON/CNSTS/ECHARG,EMASS,AMU,PIR2
      COMMON/CNSTS1/CONST1,CONST2,CONST3,CONST4,CONST5                  
      COMMON/SETP/TMAX,SMALL,API,ESTART,THETA,PHI,RSTART,EFIELD,NMAX
      COMMON/BFLD/EOVB,WB,BTHETA,BMAG                                   
      COMMON/LARGET/CF(6,4000,290),EIN(6,290),TCF(6,4000),IARRY(6,290),
     /RGAS(6,290),IPN(6,290),WPL(6,290),LAST(6),ISIZE(6),PENFRA(6,3,290)
     /,TCFMAX(6)
      COMMON/LARGENT/CFN(6,4000,10),TCFN(6,4000),SCLENUL(6,10),NPLAST(6)
      COMMON/DECOR/NCOLM,NCORLN,NCORST
      COMMON/ANIST/PSCT(6,4000,290),ANGCT(6,4000,290),INDEX(6,290),NISO 
      COMMON/IONFL/NC0(6,290),EC0(6,290),NG1(6,290),EG1(6,290),
     /NG2(6,290),EG2(6,290),WKLM(6,290),EFL(6,290)
      COMMON/DIFVEL/DIFLN,DIFTR
      COMMON/DIFERL/DFLER,DFTER
      COMMON/DIFLAB/DIFXX,DIFYY,DIFZZ,DIFYZ,DIFXY,DIFXZ
      COMMON/DIFERB/DXXER,DYYER,DZZER,DYZER,DXYER,DXZER
      COMMON/VEL/WX,WY,WZ                    
      COMMON/VELERR/DWX,DWY,DWZ
      COMMON/CTOWNS/ALPHA,ATT
      COMMON/CTWNER/ALPER,ATTER
      COMMON/THRM/AMGAS(6),VTMB(6),TCFMX,TCFMXG(6),ITHRM
      COMMON/OUTPTT/TIME(300),ICOLL(6,5),SPEC(4000),TMAX1,
     /AVE,DEN,XID,X,Y,Z,ST,NNULL,ICOLN(6,290),ICOLNN(6,10)     
      COMMON/RANM/RNMX(6)                   
      COMMON/FAKE/FAKEI,IFAKE,IFAKET(8),IFAKED(9)  
      DIMENSION XST(2000000),YST(2000000),ZST(2000000),STO(2000000) 
      DIMENSION WZST(10),AVEST(10)                          
      DIMENSION DFZZST(10),DFYYST(10),DFXXST(10),TEMP(6,4000)
C -------------------------------------------------------------------   
C   CALCULATES COLLISION EVENTS AND UPDATES DIFFUSION AND VELOCITY.  
C   USED WITH MAGNETIC FIELD , B , PARALLEL TO ELECTRIC FIELD IN THE
C   Z DIRECTION.
C   BACKGROUND GAS MOTION INCLUDED AT TEMP = AKT
C -------------------------------------------------------------------
      WX=0.0D0
      WY=0.0D0
      DWX=0.0D0
      DWY=0.0D0
      DIFYZ=0.0D0
      DIFXY=0.0D0
      DIFXZ=0.0D0
      DYZER=0.0D0
      DXYER=0.0D0
      DXZER=0.0D0
      X=0.0D0
      Y=0.0D0
      Z=0.0D0
      ST=0.0D0 
      ST1=0.0D0
      ST2=0.0D0
      SUME2=0.0D0
      SUMXX=0.0D0
      SUMYY=0.0D0
      SUMZZ=0.0D0
      SUMVX=0.0D0
      SUMVY=0.0D0
      ZOLD=0.0D0
      STOLD=0.0D0
      ST1OLD=0.0D0
      ST2OLD=0.0D0
      SZZOLD=0.0D0
      SXXOLD=0.0D0
      SYYOLD=0.0D0
      SVXOLD=0.0D0
      SVYOLD=0.0D0
      SME2OLD=0.0D0
      SMALL=1.0D-20                                                     
      TMAX1=0.0D0                                                       
      RDUM=RSTART                                                       
      E1=ESTART                                                         
      CONST9=CONST3*0.01D0 
      CONST10=CONST9*CONST9
      N4000=4000
      N300=300                                             
      ITMAX=10                                                          
      ID=0                                                              
      NCOL=0                                                            
      NNULL=0
      IEXTRA=0
C GENERATE INITIAL RANDOM NUMBERS FOR MAXWELL BOLTZMAN 
      CALL GERJAN
      IMBPT=0
      TDASH=0.0D0 
C 
      DO 111 K=1,6
      DO 111 J=1,N4000
  111 TEMP(K,J)=TCF(K,J)+TCFN(K,J)  
      ABSFAKEI=FAKEI
      IFAKE=0                                  
C                                                                       
C     INITIAL DIRECTION COSINES                                         
C                                                                       
      DCZ1=DCOS(THETA)                                                  
      DCX1=DSIN(THETA)*DCOS(PHI)                                        
      DCY1=DSIN(THETA)*DSIN(PHI) 
C INITIAL VELOCITY
      VTOT=CONST9*DSQRT(E1)
      CX1=DCX1*VTOT
      CY1=DCY1*VTOT
      CZ1=DCZ1*VTOT                                             
      BP=EFIELD*EFIELD*CONST1                                           
      F1=EFIELD*CONST2                                                  
      F2=EFIELD*CONST3
      F4=2.0D0*DACOS(-1.0D0)                                           
      J2M=NMAX/ITMAX                                                    
C MAIN LOOP                                                             
      DO 210 J1=1,ITMAX                                                 
      DO 133 J2=1,J2M                                                   
    1 R1=drand48(RDUM)  
      T=-DLOG(R1)/TCFMX+TDASH                                          
      TDASH=T  
      AP=DCZ1*F2*DSQRT(E1)                                              
      E=E1+(AP+BP*T)*T 
      WBT=WB*T
      COSWT=DCOS(WBT) 
      SINWT=DSIN(WBT)
      CONST6=DSQRT(E1/E)
C  FIND IDENTITY OF GAS FOR COLLISION
      KGAS=0
      IF(NGAS.EQ.1) THEN
       R2=drand48(RDUM)
       KGAS=1
       GO TO 4
      ENDIF
      R2=drand48(RDUM)
    3 KGAS=KGAS+1
      IF(TCFMXG(KGAS).LT.R2) GO TO 3
C CALCULATE ELECTRON VELOCITY VECTORS BEFORE COLLISION
    4 CX2=CX1*COSWT-CY1*SINWT
      CY2=CY1*COSWT+CX1*SINWT
      VTOT=CONST9*DSQRT(E) 
      CZ2=VTOT*(DCZ1*CONST6+EFIELD*T*CONST5/DSQRT(E))  
C CALCULATE GAS VELOCITY VECTORS VGX,VGY,VGZ
      IMBPT=IMBPT+1
      IF(IMBPT.GT.6) THEN
       CALL GERJAN
       IMBPT=1
      ENDIF   
      VGX=VTMB(KGAS)*RNMX(IMBPT)
      IMBPT=IMBPT+1
      VGY=VTMB(KGAS)*RNMX(IMBPT)
      IMBPT=IMBPT+1
      VGZ=VTMB(KGAS)*RNMX(IMBPT)
C CALCULATE ENERGY WITH STATIONARY GAS TARGET
      EOK=((CX2-VGX)**2+(CY2-VGY)**2+(CZ2-VGZ)**2)/CONST10              
      IE=DINT(EOK/ESTEP)+1                                            
      IE=DMIN0(IE,N4000)   
C                                           
C     TEST FOR REAL OR NULL COLLISION
C                                   
      R5=drand48(RDUM)                                                
      TEST1=TCF(KGAS,IE)/TCFMAX(KGAS)                            
      IF(R5.LE.TEST1) GO TO 137                                   
      NNULL=NNULL+1 
      TEST2=TEMP(KGAS,IE)/TCFMAX(KGAS)
      IF(R5.LT.TEST2) THEN
C TEST FOR NULL LEVELS
       IF(NPLAST(KGAS).EQ.0) GO TO 1
       R2=drand48(RDUM)
       I=0
  888  I=I+1
       IF(CFN(KGAS,IE,I).LT.R2) GO TO 888
C INCREMENT NULL SCATTER SUM
       ICOLNN(KGAS,I)=ICOLNN(KGAS,I)+1
       GO TO 1
      ENDIF                                
      TEST3=(TEMP(KGAS,IE)+ABSFAKEI)/TCFMAX(KGAS)
      IF(R5.LT.TEST3) THEN
C FAKE IONISATION INCREMENT COUNTER
       IFAKE=IFAKE+1
       GO TO 1
      ENDIF 
      GO TO 1
  137 NCOL=NCOL+1                                                      
C  CALCULATE DIRECTION COSINES OF ELECTRON IN O KELVIN FRAME
      CONST11=1.0D0/(CONST9*DSQRT(EOK))
      DXCOM=(CX2-VGX)*CONST11
      DYCOM=(CY2-VGY)*CONST11
      DZCOM=(CZ2-VGZ)*CONST11
C  CALCULATE POSITIONS AT INSTANT BEFORE COLLISION IN LAB FRAME
C    ALSO UPDATE DIFFUSION  AND ENERGY CALCULATIONS.                    
      T2=T*T                                                            
      IF(T.GE.TMAX1) TMAX1=T                                            
      TDASH=0.0D0
      A=AP*T                                                            
      B=BP*T2                                                           
      SUME2=SUME2+T*(E1+A/2.0D0+B/3.0D0)                                
      CONST7=CONST9*DSQRT(E1)                                           
      A=T*CONST7                                                        
      DX=(CX1*SINWT-CY1*(1.0D0-COSWT))/WB
      X=X+DX                             
      DY=(CY1*SINWT+CX1*(1.0D0-COSWT))/WB                               
      Y=Y+DY 
C     IF(DCZ1.GT.1.0D0) WRITE(6,9999) DCZ1
C9999 FORMAT(' DCZ1=',D12.3)                                          
      Z=Z+DCZ1*A+T2*F1                                                  
      ST=ST+T                                                           
      IT=DINT(T+1.0D0)                                              
      IT=DMIN0(IT,N300)                                                
      TIME(IT)=TIME(IT)+1.0D0
C ENERGY SPECTRUM IN O KELVIN FRAME
      SPEC(IE)=SPEC(IE)+1.0D0                                           
      WZ=Z/ST 
      SUMVX=SUMVX+DX*DX
      SUMVY=SUMVY+DY*DY  
      IF(ID.EQ.0) GO TO 121                                             
      KDUM=0
      DO 120 JDUM=1,NCORST
      ST2=ST2+T
      NCOLDM=NCOL+KDUM
      IF(NCOLDM.GT.NCOLM) NCOLDM=NCOLDM-NCOLM
      SDIF=ST-STO(NCOLDM)
      SUMXX=SUMXX+((X-XST(NCOLDM))**2)*T/SDIF
      SUMYY=SUMYY+((Y-YST(NCOLDM))**2)*T/SDIF
      IF(J1.LT.3) GO TO 120                                             
      ST1=ST1+T                                                       
      SUMZZ=SUMZZ+((Z-ZST(NCOLDM)-WZ*SDIF)**2)*T/SDIF
  120 KDUM=KDUM+NCORLN                      
  121 XST(NCOL)=X
      YST(NCOL)=Y
      ZST(NCOL)=Z
      STO(NCOL)=ST  
      IF(NCOL.GE.NCOLM) THEN                                            
       ID=ID+1                                                          
       XID=DFLOAT(ID)                                                   
       NCOL=0
      ENDIF                                                             
C --------------------------------------------------------------------- 
C     DETERMINATION OF REAL COLLISION TYPE                              
C --------------------------------------------------------------------- 
      R2=drand48(RDUM) 
C FIND LOCATION WITHIN 4 UNITS IN COLLISION ARRAY                       
      CALL SORTT(KGAS,I,R2,IE)                                         
  140 I=I+1                                                             
      IF(CF(KGAS,IE,I).LT.R2) GO TO 140                                
      S1=RGAS(KGAS,I)                                                   
      EI=EIN(KGAS,I)                                                    
      IF(IPN(KGAS,I).LE.0) GO TO 666                                    
C  USE FLAT DISTRIBUTION OF  ELECTRON ENERGY BETWEEN E-EION AND 0.0 EV  
C  SAME AS IN BOLTZMANN                                                 
      R9=drand48(RDUM)
      EXTRA=R9*(EOK-EI)                                              
      EI=EXTRA+EI    
C IF FLUORESCENCE OR AUGER ADD EXTRA ELECTRONS
      IEXTRA=IEXTRA+NC0(KGAS,I)                                      
C                                                                       
C  GENERATE SCATTERING ANGLES AND UPDATE  LABORATORY COSINES AFTER      
C   COLLISION ALSO UPDATE ENERGY OF ELECTRON.                           
C                                                                       
  666 IPT=IARRY(KGAS,I)                                                 
      ICOLL(KGAS,IPT)=ICOLL(KGAS,IPT)+1 
      ICOLN(KGAS,I)=ICOLN(KGAS,I)+1
      IF(EOK.LT.EI) THEN
       EI=EOK-0.0001D0
      ENDIF
C IF EXCITATION THEN ADD PROBABLITY,PENFRAC(1,I),OF TRANSFER TO GIVE
C IONISATION OF THE OTHER GASES IN THE MIXTURE.
      IF(IPEN.EQ.0) GO TO 5
      IF(PENFRA(KGAS,1,I).NE.0.0) THEN
       RAN=drand48(RDUM)
       IF(RAN.GT.PENFRA(KGAS,1,I)) GO TO 5
C ADD EXTRA IONISATION COLLISION
       IEXTRA=IEXTRA+1
      ENDIF
    5 S2=(S1*S1)/(S1-1.0D0) 
C ANISOTROPIC SCATTERING 
      R3=drand48(RDUM)
      IF(INDEX(KGAS,I).EQ.1) THEN
       R31=drand48(RDUM)
       F3=1.0D0-R3*ANGCT(KGAS,IE,I)
       IF(R31.GT.PSCT(KGAS,IE,I)) F3=-F3
      ELSE IF(INDEX(KGAS,I).EQ.2) THEN
       EPSI=PSCT(KGAS,IE,I)
       F3=1.0D0-(2.0D0*R3*(1.0D0-EPSI)/(1.0D0+EPSI*(1.0D0-2.0D0*R3)))
      ELSE
C ISOTROPIC SCATTERING 
       F3=1.0D0-2.0D0*R3
      ENDIF
      THETA0=DACOS(F3)                                                  
      R4=drand48(RDUM) 
      PHI0=F4*R4
      F8=DSIN(PHI0)                                                    
      F9=DCOS(PHI0)                                                     
      ARG1=1.0D0-S1*EI/EOK                                            
      ARG1=DMAX1(ARG1,SMALL)                                            
      D=1.0D0-F3*DSQRT(ARG1)                                            
      E1=EOK*(1.0D0-EI/(S1*EOK)-2.0D0*D/S2)                            
      E1=DMAX1(E1,SMALL)                                                
      Q=DSQRT((EOK/E1)*ARG1)/S1                                        
      Q=DMIN1(Q,1.0D0)                                                  
      THETA=DASIN(Q*DSIN(THETA0))                                       
      F6=DCOS(THETA)                                                    
      U=(S1-1.0D0)*(S1-1.0D0)/ARG1                                      
      CSQD=F3*F3                                                        
      IF(F3.LT.0.0D0.AND.CSQD.GT.U) F6=-1.0D0*F6                        
      F5=DSIN(THETA)                                                    
      DZCOM=DMIN1(DZCOM,1.0D0)
      ARGZ=DSQRT(DXCOM*DXCOM+DYCOM*DYCOM) 
      IF(ARGZ.EQ.0.0D0) THEN
       DCZ1=F6
       DCX1=F9*F5
       DCY1=F8*F5
       GO TO 130
      ENDIF                                          
      DCZ1=DZCOM*F6+ARGZ*F5*F8                                         
      DCY1=DYCOM*F6+(F5/ARGZ)*(DXCOM*F9-DYCOM*DZCOM*F8)               
      DCX1=DXCOM*F6-(F5/ARGZ)*(DYCOM*F9+DXCOM*DZCOM*F8)  
  130 CONTINUE            
C TRANSFORM VELOCITY VECTORS TO LAB FRAME
      VTOT=CONST9*DSQRT(E1)
      CX1=DCX1*VTOT+VGX
      CY1=DCY1*VTOT+VGY
      CZ1=DCZ1*VTOT+VGZ
C CALCULATE ENERGY AND DIRECTION IN LAB FRAME
      E1=(CX1*CX1+CY1*CY1+CZ1*CZ1)/CONST10
      CONST11=1.0D0/(CONST9*DSQRT(E1))
      DCX1=CX1*CONST11
      DCY1=CY1*CONST11
      DCZ1=CZ1*CONST11 
  133 CONTINUE                                                          
C   ------------------------------------------  
      IF(J1.EQ.1) WRITE(6,201)                                         
  201 FORMAT(/,'    VEL      POS        TIME      ENERGY   COUNT   DIFXX
     /     DIFYY     DIFZZ  ',/)
      WZ=WZ*1.0D+09
      AVE=SUME2/ST
      IF(NISO.EQ.0) THEN
       DIFXX=5.0D+15*SUMVX/ST
       DIFYY=5.0D+15*SUMVY/ST
       DFXXST(J1)=5.0D+15*(SUMVX-SVXOLD)/(ST-STOLD)
       DFYYST(J1)=5.0D+15*(SUMVY-SVYOLD)/(ST-STOLD)
      ELSE 
       IF(ST2.NE.0.0D0) THEN
        DIFYY=5.0D+15*SUMYY/ST2
        DIFXX=5.0D+15*SUMXX/ST2  
        DFXXST(J1)=5.0D+15*(SUMXX-SXXOLD)/(ST2-ST2OLD)
        DFYYST(J1)=5.0D+15*(SUMYY-SYYOLD)/(ST2-ST2OLD)
       ELSE
        DFXXST(J1)=0.0D0
        DFYYST(J1)=0.0D0 
       ENDIF                                                  
      ENDIF 
      IF(ST1.NE.0.0D0) THEN
       DIFZZ=5.0D+15*SUMZZ/ST1
       DFZZST(J1)=5.0D+15*(SUMZZ-SZZOLD)/(ST1-ST1OLD)
      ELSE
       DFZZST(J1)=0.0D0
      ENDIF
      WZST(J1)=(Z-ZOLD)/(ST-STOLD)*1.0D+09
      AVEST(J1)=(SUME2-SME2OLD)/(ST-STOLD)
      ZOLD=Z
      STOLD=ST
      ST1OLD=ST1
      ST2OLD=ST2
      SVXOLD=SUMVX
      SVYOLD=SUMVY
      SZZOLD=SUMZZ
      SXXOLD=SUMXX
      SYYOLD=SUMYY
      SME2OLD=SUME2                                              
      WRITE(6,202) WZ,Z,ST,AVE,ID,DIFXX,DIFYY,DIFZZ                
  202 FORMAT(1X,F8.2,2(1X,D10.3),F9.4,1X,I5,1X,3(2X,F8.1))              
C LOOP                                                                      
  210 CONTINUE  
C CALCULATE ERRORS AND CHECK AVERAGES   
      TWZST=0.0D0 
      TAVE=0.0D0   
      T2WZST=0.0D0    
      T2AVE=0.0D0     
      TZZST=0.0D0     
      TYYST=0.0D0     
      TXXST=0.0D0    
      T2ZZST=0.0D0
      T2YYST=0.0D0
      T2XXST=0.0D0
      DO 790 K=1,10
      TWZST=TWZST+WZST(K)
      TAVE=TAVE+AVEST(K)
      T2WZST=T2WZST+WZST(K)*WZST(K)
      T2AVE=T2AVE+AVEST(K)*AVEST(K)
      TXXST=TXXST+DFXXST(K)  
      TYYST=TYYST+DFYYST(K)  
      T2XXST=T2XXST+DFXXST(K)*DFXXST(K)
      T2YYST=T2YYST+DFYYST(K)*DFYYST(K)
      IF(K.LT.3) GO TO 790
      TZZST=TZZST+DFZZST(K)
      T2ZZST=T2ZZST+DFZZST(K)*DFZZST(K)    
  790 CONTINUE 
      DWZ=100.0D0*DSQRT((T2WZST-TWZST*TWZST/10.0D0)/9.0D0)/WZ
      DEN=100.0D0*DSQRT((T2AVE-TAVE*TAVE/10.0D0)/9.0D0)/AVE
      DXXER=100.0D0*DSQRT((T2XXST-TXXST*TXXST/10.0D0)/9.0D0)/DIFXX
      DYYER=100.0D0*DSQRT((T2YYST-TYYST*TYYST/10.0D0)/9.0D0)/DIFYY
      DZZER=100.0D0*DSQRT((T2ZZST-TZZST*TZZST/8.0D0)/7.0D0)/DIFZZ 
      DWZ=DWZ/DSQRT(10.0D0)
      DEN=DEN/DSQRT(10.0D0)
      DXXER=DXXER/DSQRT(10.0D0)
      DYYER=DYYER/DSQRT(10.0D0)
      DZZER=DZZER/DSQRT(8.0D0)
      DIFLN=DIFZZ
      DIFTR=(DIFXX+DIFYY)/2.0D0   
C CONVERT TO CM/SEC
      WZ=WZ*1.0D05
      DFLER=DZZER 
      DFTER=(DXXER+DYYER)/2.0D0 
C CALCULATE TOWNSEND COEFICIENTS AND ERRORS
      ANCATT=0.0D0
      ANCION=0.0D0
      DO 800 I=1,NGAS
      ANCATT=ANCATT+ICOLL(I,3)
  800 ANCION=ANCION+ICOLL(I,2)
      ANCION=ANCION+IEXTRA
      ATTER=0.0
      IF(ANCATT.EQ.0.0D0) GO TO 810
      ATTER=100.0*DSQRT(ANCATT)/ANCATT
  810 ATT=ANCATT/(ST*WZ)*1.0D12
      ALPER=0.0
      IF(ANCION.EQ.0.0D0) GO TO 820
      ALPER=100.0*DSQRT(ANCION)/ANCION
  820 ALPHA=ANCION/(ST*WZ)*1.0D12 
      RETURN                                                            
      END                                                               
      SUBROUTINE MONTEBT                                                
      IMPLICIT REAL*8 (A-H,O-Z)                                         
      IMPLICIT INTEGER*8 (I-N)
      COMMON/INPT/NGAS,NSTEP,NANISO,EFINAL,ESTEP,AKT,ARY,TEMPC,TORR,IPEN
      COMMON/CNSTS/ECHARG,EMASS,AMU,PIR2
      COMMON/CNSTS1/CONST1,CONST2,CONST3,CONST4,CONST5                  
      COMMON/SETP/TMAX,SMALL,API,ESTART,THETA,PHI,RSTART,EFIELD,NMAX
      COMMON/BFLD/EOVB,WB,BTHETA,BMAG                                   
      COMMON/LARGET/CF(6,4000,290),EIN(6,290),TCF(6,4000),IARRY(6,290),
     /RGAS(6,290),IPN(6,290),WPL(6,290),LAST(6),ISIZE(6),PENFRA(6,3,290)
     /,TCFMAX(6)
      COMMON/LARGENT/CFN(6,4000,10),TCFN(6,4000),SCLENUL(6,10),NPLAST(6)
      COMMON/ANIST/PSCT(6,4000,290),ANGCT(6,4000,290),INDEX(6,290),NISO
      COMMON/IONFL/NC0(6,290),EC0(6,290),NG1(6,290),EG1(6,290),
     /NG2(6,290),EG2(6,290),WKLM(6,290),EFL(6,290)
      COMMON/MIX2/ES(4000),EROOT(4000),QTOT(4000),QREL(4000),QINEL(4000)
     /,QEL(4000)      
      COMMON/DECOR/NCOLM,NCORLN,NCORST            
      COMMON/DIFLAB/DIFXX,DIFYY,DIFZZ,DIFYZ,DIFXY,DIFXZ
      COMMON/DIFERB/DXXER,DYYER,DZZER,DYZER,DXYER,DXZER 
      COMMON/DIFVEL/DIFLN,DIFTR
      COMMON/DIFERL/DFLER,DFTER
      COMMON/VEL/WX,WY,WZ
      COMMON/VELERR/DWX,DWY,DWZ
      COMMON/CTOWNS/ALPHA,ATT
      COMMON/CTWNER/ALPER,ATTER
      COMMON/THRM/AMGAS(6),VTMB(6),TCFMX,TCFMXG(6),ITHRM
      COMMON/OUTPTT/TIME(300),ICOLL(6,5),SPEC(4000),TMAX1,
     /AVE,DEN,XID,X,Y,Z,ST,NNULL,ICOLN(6,290),ICOLNN(6,10)
      COMMON/RANM/RNMX(6)
      COMMON/FAKE/FAKEI,IFAKE,IFAKET(8),IFAKED(9)            
      DIMENSION XST(1000000),YST(1000000),ZST(1000000),STO(1000000)
      DIMENSION WZST(10),WYST(10),AVEST(10)
      DIMENSION DFZZST(10),DFYYST(10),DFXXST(10)
      DIMENSION DFYZST(10),DFLNST(10),DFTRST(10),TEMP(6,4000)           
C -------------------------------------------------------------------   
C   CALCULATES COLLISION EVENTS AND UPDATES DIFFUSION AND VELOCITY. 
C   SUBROUTINE HANDLES MAGNETIC FIELD AND ELECTRIC FIELD
C   BFIELD ALONG X-AXIS EFIELD ALONG Z-AXIS (90 DEGREES). 
C   BACKGROUND GAS MOTION INCLUDED AT TEMP = TEMPC  
C ------------------------------------------------------------------- 
      WX=0.0D0
      DWX=0.0D0
      DIFXZ=0.0D0
      DIFXY=0.0D0
      DXZER=0.0D0
      DXYER=0.0D0
      X=0.0D0
      Y=0.0D0
      Z=0.0D0 
      ST=0.0D0
      ST1=0.0D0
      ST2=0.0D0                                                         
      SUMXX=0.0D0 
      SUMYY=0.0D0                                                       
      SUMZZ=0.0D0
      SUMYZ=0.0D0
      SUMLS=0.0D0                                                       
      SUMTS=0.0D0
      SUMVX=0.0D0
      ZOLD=0.0D0
      YOLD=0.0D0
      STOLD=0.0D0
      ST1OLD=0.0D0
      ST2OLD=0.0D0
      SZZOLD=0.0D0
      SXXOLD=0.0D0
      SYYOLD=0.0D0
      SYZOLD=0.0D0
      SVXOLD=0.0D0
      SLNOLD=0.0D0
      STROLD=0.0D0
      EBAROLD=0.0D0
      SMALL=1.0D-20                                                     
      TMAX1=0.0D0  
      EF100=EFIELD*100.0D0
      RDUM=RSTART                                                       
      E1=ESTART    
      N4000=4000
      N300=300                                                     
      ITMAX=10 
      ID=0                                                              
      NCOL=0                                                            
      NNULL=0  
      IEXTRA=0
C
      DO 111 K=1,6
      DO 111 J=1,N4000
  111 TEMP(K,J)=TCF(K,J)+TCFN(K,J)
      ABSFAKEI=FAKEI
      IFAKE=0
C
C GENERATE INITIAL RANDOM NUMBER FOR MAXWELL BOLTZMAN
      CALL GERJAN
      IMBTP=0
      TDASH=0.0D0 
      F4=2.0D0*DACOS(-1.0D0)
      CONST9=CONST3*0.01D0     
      CONST10=CONST9*CONST9                                         
C     INITIAL DIRECTION COSINES                                         
      DCZ1=DCOS(THETA)                                                  
      DCX1=DSIN(THETA)*DCOS(PHI)                                        
      DCY1=DSIN(THETA)*DSIN(PHI)                                        
C     INITIAL VELOCITY
      VTOT=CONST9*DSQRT(E1)
      CX1=DCX1*VTOT                               
      CY1=DCY1*VTOT             
      CZ1=DCZ1*VTOT                                           
      J2M=NMAX/ITMAX                                                    
C MAIN LOOP 
      DO 210 J1=1,ITMAX                                                 
      DO 133 J2=1,J2M
    1 R1=drand48(RDUM) 
      T=-DLOG(R1)/TCFMX+TDASH
      TDASH=T
      WBT=WB*T
      COSWT=DCOS(WBT)
      SINWT=DSIN(WBT)
      DZ=(CZ1*SINWT+(EOVB-CY1)*(1.0D0-COSWT))/WB
      E=E1+DZ*EF100 
C CALCULATE ELECTRON VELOCITY IN LAB FRAME
      CX2=CX1
      CY2=(CY1-EOVB)*COSWT+CZ1*SINWT+EOVB
      CZ2=CZ1*COSWT-(CY1-EOVB)*SINWT  
C FIND IDENTITY OF GAS FOR COLLISION
      KGAS=0
      IF(NGAS.EQ.1) THEN
       R2=drand48(RDUM)
       KGAS=1
       GO TO 4
      ENDIF
      R2=drand48(RDUM)
    3 KGAS=KGAS+1
      IF(TCFMXG(KGAS).LT.R2) GO TO 3 
C CALCULATE GAS VELOCITY VECTORS VGX,VGY,VGZ
    4 IMBPT=IMBPT+1
      IF(IMBPT.GT.6) THEN
       CALL GERJAN
       IMBPT=1
      ENDIF
      VGX=VTMB(KGAS)*RNMX(IMBPT)
      IMBPT=IMBPT+1
      VGY=VTMB(KGAS)*RNMX(IMBPT)
      IMBPT=IMBPT+1
      VGZ=VTMB(KGAS)*RNMX(IMBPT)
C CALCULATE ENERGY WITH STATIONARY GAS TARGET,EOK
      EOK=((CX2-VGX)**2+(CY2-VGY)**2+(CZ2-VGZ)**2)/CONST10
      IE=DINT(EOK/ESTEP)+1                                             
      IE=DMIN0(IE,N4000)   
C                                                                       
C     TEST FOR REAL OR NULL COLLISION  
C                                  
      R5=drand48(RDUM)                                                
      TEST1=TCF(KGAS,IE)/TCFMAX(KGAS)
      IF(R5.LE.TEST1) GO TO 137                                         
      NNULL=NNULL+1   
      TEST2=TEMP(KGAS,IE)/TCFMAX(KGAS)
      IF(R5.LT.TEST2) THEN
C TEST FOR NULL LEVELS
       IF(NPLAST(KGAS).EQ.0) GO TO 1  
       R2=drand48(RDUM)
       I=0
  888  I=I+1
       IF(CFN(KGAS,IE,I).LT.R2) GO TO 888
C INCREMENT NULL SCATTER SUM
       ICOLNN(KGAS,I)=ICOLNN(KGAS,I)+1
       GO TO 1
      ENDIF                                 
      TEST3=(TEMP(KGAS,IE)+ABSFAKEI)/TCFMAX(KGAS)
      IF(R5.LT.TEST3) THEN
C FAKE IONISATION INCREMENT COUNTER
       IFAKE=IFAKE+1
       GO TO 1
      ENDIF   
      GO TO 1     
  137 NCOL=NCOL+1
C CALCULATE DIRECTION COSINES OF ELECTRON IN 0 KELVIN FRAME
      CONST11=1.0D0/(CONST9*DSQRT(EOK))
      DXCOM=(CX2-VGX)*CONST11
      DYCOM=(CY2-VGY)*CONST11
      DZCOM=(CZ2-VGZ)*CONST11                                           
C                                                                       
C  CALCULATE POSITIONS AT INSTANT BEFORE COLLISION
C    ALSO UPDATE DIFFUSION  AND ENERGY CALCULATIONS.                    
      T2=T*T
      IF(T.GE.TMAX1) TMAX1=T  
      TDASH=0.0D0                                          
C CALC NEW POSITION
      X=X+CX1*T
      Y=Y+EOVB*T+((CY1-EOVB)*SINWT+CZ1*(1.0D0-COSWT))/WB
      Z=Z+DZ                                   
      ST=ST+T                                                           
      IT=DINT(T+1.0D0)           
      IT=DMIN0(IT,N300)                                               
      TIME(IT)=TIME(IT)+1.0D0
C ENERGY SPECTRUM FOR O KELVIN FRAME  
      SPEC(IE)=SPEC(IE)+1.0D0                                           
      WZ=Z/ST
      WY=Y/ST
      SUMVX=SUMVX+CX1*CX1*T2                                  
      IF(ID.EQ.0) GO TO 121
      KDUM=0
      DO 120 J=1,NCORST
      ST2=ST2+T
      NCOLDM=NCOL+KDUM
      IF(NCOLDM.GT.NCOLM) NCOLDM=NCOLDM-NCOLM
      SDIF=ST-STO(NCOLDM)
      SUMXX=SUMXX+((X-XST(NCOLDM))**2)*T/SDIF 
      IF(J1.LT.3) GO TO 120  
      ST1=ST1+T     
      SUMZZ=SUMZZ+((Z-ZST(NCOLDM)-WZ*SDIF)**2)*T/SDIF
      SUMYY=SUMYY+((Y-YST(NCOLDM)-WY*SDIF)**2)*T/SDIF               
      SUMYZ=SUMYZ+(Z-ZST(NCOLDM)-WZ*SDIF)*(Y-YST(NCOLDM)-WY*SDIF)*T/SDIF
      A2=(WZ*SDIF)**2+(WY*SDIF)**2
      B2=(Z-WZ*SDIF-ZST(NCOLDM))**2+(Y-WY*SDIF-YST(NCOLDM))**2 
      C2=(Z-ZST(NCOLDM))**2+(Y-YST(NCOLDM))**2
      DL2=(A2+B2-C2)**2/(4.0D0*A2)
      DT2=B2-DL2
      SUMLS=SUMLS+DL2*T/SDIF
      SUMTS=SUMTS+DT2*T/SDIF
 120  KDUM=KDUM+NCORLN   
 121  XST(NCOL)=X                                                       
      YST(NCOL)=Y                                                       
      ZST(NCOL)=Z                                                       
      STO(NCOL)=ST                                                      
      IF(NCOL.GE.NCOLM) THEN     
       ID=ID+1                                                          
       XID=DFLOAT(ID)                                                   
       NCOL=0 
      ENDIF                                                            
C --------------------------------------------------------------------- 
C     DETERMINATION OF REAL COLLISION TYPE                              
C --------------------------------------------------------------------- 
      R2=drand48(RDUM)
C FIND LOCATION WITHIN 4 UNITS IN COLLISION ARRAY                       
      CALL SORTT(KGAS,I,R2,IE)                                          
  140 I=I+1                                                             
      IF(CF(KGAS,IE,I).LT.R2) GO TO 140                                
      S1=RGAS(KGAS,I)                                                  
      EI=EIN(KGAS,I)
      IF(IPN(KGAS,I).LE.0) GO TO 666                                   
C  USE FLAT DISTRIBUTION OF  ELECTRON ENERGY BETWEEN E-EION AND 0.0 EV  
C  SAME AS IN BOLTZMANN                                                 
      R9=drand48(RDUM)
      EXTRA=R9*(EOK-EI)                                                 
      EI=EXTRA+EI   
C IF FLOUORESCENCE OR AUGER ADD EXTRA ELECTRONS
      IEXTRA=IEXTRA+NC0(KGAS,I)                                      
C                                                                       
C  GENERATE SCATTERING ANGLES AND UPDATE  LABORATORY COSINES AFTER      
C   COLLISION ALSO UPDATE ENERGY OF ELECTRON.                           
C                                                                       
  666 IPT=IARRY(KGAS,I)
      ICOLL(KGAS,IPT)=ICOLL(KGAS,IPT)+1
      ICOLN(KGAS,I)=ICOLN(KGAS,I)+1
      IF(EOK.LT.EI) THEN
C  FIX ENERGY LOSS SMALLER THAN INCIDENT ENERGY IF ERROR OCCURS
       EI=EOK-0.0001D0
      ENDIF
C IF EXCITATION THEN ADD PROBABILITY,PENFRAC(1,I), OF TRANSFER TO GIVE
C IONISATION OF THE OTHER GASES IN THE MIXTURE
      IF(IPEN.EQ.0) GO TO 5
      IF(PENFRA(KGAS,1,I).NE.0.0) THEN
       RAN=drand48(RDUM)
       IF(RAN.GT.PENFRA(KGAS,1,I)) GO TO 5
C ADD EXTRA IONISATION COLLISION
       IEXTRA=IEXTRA+1
      ENDIF
    5 S2=(S1*S1)/(S1-1.0D0) 
C  ANISOTROPIC SCATTERING
      R3=drand48(RDUM)
      IF(INDEX(KGAS,I).EQ.1) THEN
        R31=drand48(RDUM)
        F3=1.0D0-R3*ANGCT(KGAS,IE,I)
        IF(R31.GT.PSCT(KGAS,IE,I)) F3=-F3
      ELSE IF(INDEX(KGAS,I).EQ.2) THEN
       EPSI=PSCT(KGAS,IE,I)
       F3=1.0D0-(2.0D0*R3*(1.0D0-EPSI)/(1.0D0+EPSI*(1.0D0-2.0D0*R3)))
      ELSE
C ISOTROPIC SCATTERING
        F3=1.0D0-2.0D0*R3
      ENDIF
      THETA0=DACOS(F3)                                                  
      R4=drand48(RDUM)
      PHI0=F4*R4                                                        
      F8=DSIN(PHI0)                                                     
      F9=DCOS(PHI0)                                                     
      ARG1=1.0D0-S1*EI/EOK                                             
      ARG1=DMAX1(ARG1,SMALL)                                            
      D=1.0D0-F3*DSQRT(ARG1)                                            
      E1=EOK*(1.0D0-EI/(S1*EOK)-2.0D0*D/S2)                             
      E1=DMAX1(E1,SMALL)                                                
      Q=DSQRT((EOK/E1)*ARG1)/S1                                        
      Q=DMIN1(Q,1.0D0)                                                  
      THETA=DASIN(Q*DSIN(THETA0))                                       
      F6=DCOS(THETA)                                                    
      U=(S1-1.0D0)*(S1-1.0D0)/ARG1
      CSQD=F3*F3                                                        
      IF(F3.LT.0.0D0.AND.CSQD.GT.U) F6=-1.0D0*F6                        
      F5=DSIN(THETA)                                                    
      DZCOM=DMIN1(DZCOM,1.0D0)
      ARGZ=DSQRT(DXCOM*DXCOM+DYCOM*DYCOM)
      IF(ARGZ.EQ.0.0D0) THEN
       DCZ1=F6
       DCX1=F9*F5
       DCY1=F8*F5
       GO TO 130
      ENDIF                                          
      DCZ1=DZCOM*F6+ARGZ*F5*F8                                         
      DCY1=DYCOM*F6+(F5/ARGZ)*(DXCOM*F9-DYCOM*DZCOM*F8)             
      DCX1=DXCOM*F6-(F5/ARGZ)*(DYCOM*F9+DXCOM*DZCOM*F8)
  130 CONTINUE
C TRANSFORM VELOCITY VECTORS TO LAB FRAME
      VTOT=CONST9*DSQRT(E1)
      CX1=DCX1*VTOT+VGX
      CY1=DCY1*VTOT+VGY
      CZ1=DCZ1*VTOT+VGZ                     
C CALCULATE ENERGY AND DIRECTION COSINES IN LAB FRAME
      E1=(CX1*CX1+CY1*CY1+CZ1*CZ1)/CONST10
      CONST11=1.0D0/(CONST9*DSQRT(E1))
      DCX1=CX1*CONST11
      DCY1=CY1*CONST11
      DCZ1=CZ1*CONST11                                                
  133 CONTINUE                                                          
C   ------------------------------------------  
      IF(J1.EQ.1) WRITE(6,201)                                         
  201 FORMAT(/,'   VELZ    VELY  ENERGY   ID  DIFXX   DIFYY   DIFZZ   DI
     /FYZ  DIFLNG  DIFTRN',/)       
      WZ=WZ*1.0D+09
      WY=WY*1.0D+09
      IF(ST2.NE.0.0D0) DIFXX=5.0D+15*SUMXX/ST2
      IF(ST1.NE.0.0D0) DIFYY=5.0D+15*SUMYY/ST1
      IF(ST1.NE.0.0D0) DIFZZ=5.0D+15*SUMZZ/ST1
      IF(ST1.NE.0.0D0) DIFYZ=-5.0D+15*SUMYZ/ST1
      IF(ST1.NE.0.0D0) DIFLN=5.0D+15*SUMLS/ST1
      IF(ST1.NE.0.0D0) DIFTR=5.0D+15*SUMTS/ST1
      IF(NISO.EQ.0) DIFXX=5.0D+15*SUMVX/ST
      EBAR=0.0D0
      DO 300 IK=1,4000
      TCFSUM=0.0D0
      DO 299 KI=1,NGAS
  299 TCFSUM=TCFSUM+TCF(KI,IK)
  300 EBAR=EBAR+ES(IK)*SPEC(IK)/TCFSUM       
      AVE=EBAR/ST
      WZST(J1)=(Z-ZOLD)/(ST-STOLD)*1.0D+09
      WYST(J1)=(Y-YOLD)/(ST-STOLD)*1.0D+09
      AVEST(J1)=(EBAR-EBAROLD)/(ST-STOLD)
      EBAROLD=EBAR
      DFZZST(J1)=0.0D0
      DFYYST(J1)=0.0D0
      DFYZST(J1)=0.0D0
      DFLNST(J1)=0.0D0
      DFTRST(J1)=0.0D0
      IF(J1.GT.2) THEN 
       DFZZST(J1)=5.0D+15*(SUMZZ-SZZOLD)/(ST1-ST1OLD)
       DFYYST(J1)=5.0D+15*(SUMYY-SYYOLD)/(ST1-ST1OLD)
       DFYZST(J1)=5.0D+15*(SUMYZ-SYZOLD)/(ST1-ST1OLD)
       DFLNST(J1)=5.0D+15*(SUMLS-SLNOLD)/(ST1-ST1OLD)
       DFTRST(J1)=5.0D+15*(SUMTS-STROLD)/(ST1-ST1OLD)
      ENDIF
      DFXXST(J1)=5.0D+15*(SUMXX-SXXOLD)/(ST2-ST2OLD)
      IF(NISO.EQ.0) DFXXST(J1)=5.0D+15*(SUMVX-SVXOLD)/(ST-STOLD)
      ZOLD=Z
      YOLD=Y
      STOLD=ST
      ST1OLD=ST1
      ST2OLD=ST2
      SVXOLD=SUMVX
      SZZOLD=SUMZZ
      SXXOLD=SUMXX
      SYYOLD=SUMYY
      SYZOLD=SUMYZ
      SLNOLD=SUMLS
      STROLD=SUMTS
      WRITE(6,202) WZ,WY,AVE,ID,DIFXX,DIFYY,DIFZZ,
     /DIFYZ,DIFLN,DIFTR                                                 
  202 FORMAT(2(F7.2,1X),F7.4,1X,I4,6(F7.1,1X))   
C  LOOP                                                                       
  210 CONTINUE
C CALCULATE ERRORS AND CHECK AVERAGES
      TWZST=0.0D0
      TWYST=0.0D0
      TAVE=0.0D0
      T2WZST=0.0D0
      T2WYST=0.0D0
      T2AVE=0.0D0
      TZZST=0.0D0
      TYYST=0.0D0
      TXXST=0.0D0
      TYZST=0.0D0
      TLNST=0.0D0
      TTRST=0.0D0
      T2ZZST=0.0D0
      T2YYST=0.0D0
      T2XXST=0.0D0
      T2YZST=0.0D0
      T2LNST=0.0D0
      T2TRST=0.0D0
      DO 790 K=1,10
      TWZST=TWZST+WZST(K)
      TWYST=TWYST+WYST(K)
      TAVE=TAVE+AVEST(K)
      T2WZST=T2WZST+WZST(K)*WZST(K)
      T2WYST=T2WYST+WYST(K)*WYST(K)
      T2AVE=T2AVE+AVEST(K)*AVEST(K)
      TXXST=TXXST+DFXXST(K)
      T2XXST=T2XXST+DFXXST(K)*DFXXST(K)
      IF(K.LT.3) GO TO 790
      TZZST=TZZST+DFZZST(K)
      TYYST=TYYST+DFYYST(K)
      TYZST=TYZST+DFYZST(K)
      TLNST=TLNST+DFLNST(K)
      TTRST=TTRST+DFTRST(K)
      T2ZZST=T2ZZST+DFZZST(K)*DFZZST(K)    
      T2YYST=T2YYST+DFYYST(K)*DFYYST(K)     
      T2YZST=T2YZST+DFYZST(K)*DFYZST(K)     
      T2LNST=T2LNST+DFLNST(K)*DFLNST(K)    
      T2TRST=T2TRST+DFTRST(K)*DFTRST(K)       
  790 CONTINUE 
      DWZ=100.0D0*DSQRT((T2WZST-TWZST*TWZST/10.0D0)/9.0D0)/WZ
      DWY=100.0D0*DSQRT((T2WYST-TWYST*TWYST/10.0D0)/9.0D0)/DABS(WY)
      DEN=100.0D0*DSQRT((T2AVE-TAVE*TAVE/10.0D0)/9.0D0)/AVE
      DXXER=100.0D0*DSQRT((T2XXST-TXXST*TXXST/10.0D0)/9.0D0)/DIFXX  
      DYYER=100.0D0*DSQRT((T2YYST-TYYST*TYYST/8.0D0)/7.0D0)/DIFYY       
      DZZER=100.0D0*DSQRT((T2ZZST-TZZST*TZZST/8.0D0)/7.0D0)/DIFZZ    
      DYZER=100.0D0*DSQRT((T2YZST-TYZST*TYZST/8.0D0)/7.0D0)/DABS(DIFYZ)
      DFLER=100.0D0*DSQRT((T2LNST-TLNST*TLNST/8.0D0)/7.0D0)/DIFLN
      DFTER=100.0D0*DSQRT((T2TRST-TTRST*TTRST/8.0D0)/7.0D0)/DIFTR 
      DWZ=DWZ/DSQRT(10.0D0)
      DWY=DWY/DSQRT(10.0D0)
      DEN=DEN/DSQRT(10.0D0)
      DXXER=DXXER/DSQRT(10.0D0)
      DYYER=DYYER/DSQRT(8.0D0)
      DZZER=DZZER/DSQRT(8.0D0)
      DYZER=DYZER/DSQRT(8.0D0)
      DFLER=DFLER/DSQRT(8.0D0)
      DFTER=DFTER/DSQRT(8.0D0)
C CONVERT TO CM/SEC      
      WZ=WZ*1.0D05
      WY=WY*1.0D05
C CALCULATE TOWNSEND COEFICIENTS AND ERRORS
      ANCATT=0.0D0
      ANCION=0.0D0
      DO 800 I=1,NGAS
      ANCATT=ANCATT+ICOLL(I,3)
  800 ANCION=ANCION+ICOLL(I,2)
      ANCION=ANCION+IEXTRA
      ATTER=0.0D0
      IF(ANCATT.EQ.0.0D0) GO TO 810
      ATTER=100.0D0*DSQRT(ANCATT)/ANCATT
  810 ATT=ANCATT/(ST*WZ)*1.0D12
      ALPER=0.0D0
      IF(ANCION.EQ.0.0D0) GO TO 820
      ALPER=100.0D0*DSQRT(ANCION)/ANCION
  820 ALPHA=ANCION/(ST*WZ)*1.0D12 
      RETURN                                                            
      END 
      SUBROUTINE MONTECT                                                
      IMPLICIT REAL*8 (A-H,O-Z)                                         
      IMPLICIT INTEGER*8 (I-N)
      COMMON/INPT/NGAS,NSTEP,NANISO,EFINAL,ESTEP,AKT,ARY,TEMPC,TORR,IPEN
      COMMON/CNSTS/ECHARG,EMASS,AMU,PIR2
      COMMON/CNSTS1/CONST1,CONST2,CONST3,CONST4,CONST5                  
      COMMON/SETP/TMAX,SMALL,API,ESTART,THETA,PHI,RSTART,EFIELD,NMAX
      COMMON/BFLD/EOVB,WB,BTHETA,BMAG                                   
      COMMON/LARGET/CF(6,4000,290),EIN(6,290),TCF(6,4000),IARRY(6,290),
     /RGAS(6,290),IPN(6,290),WPL(6,290),LAST(6),ISIZE(6),PENFRA(6,3,290)
     /,TCFMAX(6)
      COMMON/LARGENT/CFN(6,4000,10),TCFN(6,4000),SCLENUL(6,10),NPLAST(6)
      COMMON/ANIST/PSCT(6,4000,290),ANGCT(6,4000,290),INDEX(6,290),NISO
      COMMON/IONFL/NC0(6,290),EC0(6,290),NG1(6,290),EG1(6,290),
     /NG2(6,290),EG2(6,290),WKLM(6,290),EFL(6,290)
      COMMON/MIX2/ES(4000),EROOT(4000),QTOT(4000),QREL(4000),QINEL(4000)
     /,QEL(4000)
      COMMON/DECOR/NCOLM,NCORLN,NCORST                        
      COMMON/DIFLAB/DIFXX,DIFYY,DIFZZ,DIFYZ,DIFXY,DIFXZ 
      COMMON/DIFERB/DXXER,DYYER,DZZER,DYZER,DXYER,DXZER
      COMMON/DIFVEL/DIFLN,DIFTR
      COMMON/DIFERL/DFLER,DFTER
      COMMON/VEL/WX,WY,WZ            
      COMMON/VELERR/DWX,DWY,DWZ
      COMMON/CTOWNS/ALPHA,ATT 
      COMMON/CTWNER/ALPER,ATTER   
      COMMON/THRM/AMGAS(6),VTMB(6),TCFMX,TCFMXG(6),ITHRM
      COMMON/OUTPTT/TIME(300),ICOLL(6,5),SPEC(4000),TMAX1,
     /AVE,DEN,XID,X,Y,Z,ST,NNULL,ICOLN(6,290),ICOLNN(6,10)
      COMMON/RANM/RNMX(6)  
      COMMON/FAKE/FAKEI,IFAKE,IFAKET(8),IFAKED(9)             
      DIMENSION XST(2000000),YST(2000000),ZST(2000000),STO(2000000)
      DIMENSION WZST(10),WYST(10),WXST(10),AVEST(10)
      DIMENSION DFZZST(10),DFYYST(10),DFXXST(10) 
      DIMENSION DFYZST(10),DFXYST(10),DFXZST(10),TEMP(6,4000)
C -------------------------------------------------------------------   
C   CALCULATES COLLISION EVENTS AND UPDATES DIFFUSION AND VELOCITY. 
C   SUBROUTINE SOLVES MOTION IN COORDINATE SYSTEM WITH BFIELD
C   ALIGNED ALONG X AXIS AND ELECTRIC FIELD AT AN ANGLE BTHETA IN
C   THE X-Z PLANE.  THE VELOCITY VECTORS AND DIFFUSION ARE THEN
C   ROTATED INTO THE STANDARD COORDINATE FRAME WITH THE ELECTRIC-
C   FIELD ALONG Z-AXIS AND THE  BFIELD AT AN ANGLE BTHETA TO THE 
C   ELECTRIC FIELD IN THE X-Z PLANE.
C   BACKGROUND GAS MOTION INCLUDED AT TEMP = TEMPC
C -------------------------------------------------------------------
      X=0.0D0
      Y=0.0D0
      Z=0.0D0 
C CALC LONG AND TRANS ONLY FOR 90 DEGREES BETWEEN E AND B      
      DIFLN=0.0D0
      DIFTR=0.0D0
      DFLER=0.0D0
      DFTER=0.0D0
      DIFXXR=0.0D0
      DIFYYR=0.0D0
      DIFZZR=0.0D0
      DIFYZR=0.0D0
      DIFXZR=0.0D0
      DIFXYR=0.0D0
      ST=0.0D0
      ST1=0.0D0
      SUMXX=0.0D0 
      SUMYY=0.0D0                                                       
      SUMZZ=0.0D0
      SUMYZ=0.0D0
      SUMXY=0.0D0
      SUMXZ=0.0D0
      ZROLD=0.0D0
      YROLD=0.0D0
      XROLD=0.0D0
      SZZR=0.0D0
      SYYR=0.0D0
      SXXR=0.0D0
      SXYR=0.0D0
      SYZR=0.0D0
      SXZR=0.0D0
      STOLD=0.0D0
      ST1OLD=0.0D0
      ST2OLD=0.0D0
      SZZOLD=0.0D0
      SYYOLD=0.0D0
      SXXOLD=0.0D0
      SYZOLD=0.0D0
      SXYOLD=0.0D0
      SXZOLD=0.0D0
      EBAROLD=0.0D0
      SMALL=1.0D-20                                                     
      TMAX1=0.0D0
      API=DACOS(-1.0D0)
C CALC ROTATION MATRIX ANGLES
      RCS=DCOS((BTHETA-90.0D0)*API/180.0D0)  
      RSN=DSIN((BTHETA-90.0D0)*API/180.0D0)
C            
      RTHETA=BTHETA*API/180.0D0  
      EFZ100=EFIELD*100.0D0*DSIN(RTHETA)
      EFX100=EFIELD*100.0D0*DCOS(RTHETA)
      F1=EFIELD*CONST2*DCOS(RTHETA)
      F4=2.0D0*API
      CONST9=CONST3*0.01D0                        
      CONST10=CONST9*CONST9                      
      EOVBR=EOVB*DSIN(RTHETA)
      RDUM=RSTART                                                       
      E1=ESTART    
      N4000=4000
      N300=300                                                     
      ITMAX=10 
      ID=0                                                              
      NCOL=0                                                            
      NNULL=0    
      IEXTRA=0             
C GENERATE INITIAL RANDOM NUMBER FOR MAXWELL BOLTZMAN 
      CALL GERJAN
      IMBPT=0
      TDASH=0.0D0 
C
      DO 111 K=1,6
      DO 111 J=1,N4000
  111 TEMP(K,J)=TCF(K,J)+TCFN(K,J)
      ABSFAKEI=FAKEI
      IFAKE=0
C
C                                                                       
C     INITIAL DIRECTION COSINES                                         
C                                                                       
      DCZ1=DCOS(THETA)                                                  
      DCX1=DSIN(THETA)*DCOS(PHI)                                        
      DCY1=DSIN(THETA)*DSIN(PHI)                                        
C     INITIAL VELOCITY
      VTOT=CONST9*DSQRT(E1)
      CX1=DCX1*VTOT                               
      CY1=DCY1*VTOT             
      CZ1=DCZ1*VTOT                                           
      J2M=NMAX/ITMAX                                                    
C MAIN LOOP 
      DO 210 J1=1,ITMAX                                                 
      DO 133 J2=1,J2M
    1 R1=drand48(RDUM) 
      T=-DLOG(R1)/TCFMX+TDASH                                           
      TDASH=T                                                           
      WBT=WB*T
      COSWT=DCOS(WBT)
      SINWT=DSIN(WBT)
      DZ=(CZ1*SINWT+(EOVBR-CY1)*(1.0D0-COSWT))/WB
      DX=CX1*T+F1*T*T
      E=E1+DZ*EFZ100+DX*EFX100 
C CALCULATE ELECTRON VELOCITY IN LAB FRAME
      CX2=CX1+2.0D0*F1*T
      CY2=(CY1-EOVBR)*COSWT+CZ1*SINWT+EOVBR
      CZ2=CZ1*COSWT-(CY1-EOVBR)*SINWT
C FIND IDENTITY OF GAS FOR COLLISION
      KGAS=0
      IF(NGAS.EQ.1) THEN
       R2=drand48(RDUM)
       KGAS=1
       GO TO 4
      ENDIF
      R2=drand48(RDUM)
    3 KGAS=KGAS+1
      IF(TCFMXG(KGAS).LT.R2) GO TO 3
C  CALCULATE GAS VELOCITY VECTORS VGX,VGY,VGZ
    4 IMBPT=IMBPT+1
      IF(IMBPT.GT.6) THEN
       CALL GERJAN
       IMBPT=1
      ENDIF  
      VGX=VTMB(KGAS)*RNMX(IMBPT)
      IMBPT=IMBPT+1
      VGY=VTMB(KGAS)*RNMX(IMBPT)
      IMBPT=IMBPT+1
      VGZ=VTMB(KGAS)*RNMX(IMBPT)
C CALCULATE ENERGY WITH STATIONARY GAS TARGET,EOK
      EOK=((CX2-VGX)**2+(CY2-VGY)**2+(CZ2-VGZ)**2)/CONST10
      IE=DINT(EOK/ESTEP)+1                                             
      IE=DMIN0(IE,N4000)                                               
C                                                                       
C     TEST FOR REAL OR NULL COLLISION     
C      
      R5=drand48(RDUM)                                                  
      TEST1=TCF(KGAS,IE)/TCFMAX(KGAS)            
      IF(R5.LE.TEST1) GO TO 137                                        
      NNULL=NNULL+1 
      TEST2=TEMP(KGAS,IE)/TCFMAX(KGAS)
      IF(R5.LT.TEST2) THEN
C TEST FOR NULL LEVELS
       IF(NPLAST(KGAS).EQ.0) GO TO 1
       R2=drand48(RDUM)
       I=0
  888  I=I+1
       IF(CFN(KGAS,IE,I).LT.R2) GO TO 888
C INCREMENT NULL SCATTER SUM
       ICOLNN(KGAS,I)=ICOLNN(KGAS,I)+1
       GO TO 1
      ENDIF                              
      TEST3=(TEMP(KGAS,IE)+ABSFAKEI)/TCFMAX(KGAS)
      IF(R5.LT.TEST3) THEN
C FAKE IONISATION INCREMENT COUNTER
       IFAKE=IFAKE+1
       GO TO 1
      ENDIF
      GO TO 1                      
  137 NCOL=NCOL+1                                                     
C CALCULATE DIRECTION COSINES OF ELECTRON IN 0 KELVIN FRAME
      CONST11=1.0D0/(CONST9*DSQRT(EOK))
C     VTOT=1.0D0/CONST11
      DXCOM=(CX2-VGX)*CONST11
      DYCOM=(CY2-VGY)*CONST11
      DZCOM=(CZ2-VGZ)*CONST11                                         
C  CALCULATE POSITIONS AT INSTANT BEFORE COLLISION
C    ALSO UPDATE DIFFUSION  AND ENERGY CALCULATIONS.                    
      T2=T*T
      IF(T.GE.TMAX1) TMAX1=T                                            
      TDASH=0.0D0                                                       
C CALC NEW POSITION
      X=X+DX    
      Y=Y+EOVBR*T+((CY1-EOVBR)*SINWT+CZ1*(1.0D0-COSWT))/WB
      Z=Z+DZ                                   
      ST=ST+T                                                           
      IT=DINT(T+1.0D0)                                               
      IT=DMIN0(IT,N300)                                               
      TIME(IT)=TIME(IT)+1.0D0  
C ENERGY SPECTRUM FOR  0 KELVIN FRAME
      SPEC(IE)=SPEC(IE)+1.0D0                                           
      WZ=Z/ST
      WY=Y/ST
      WX=X/ST
      IF(J1.LT.3) GO TO 121
      KDUM=0
      DO 120 J=1,NCORST
      NCOLDM=NCOL+KDUM
      IF(NCOLDM.GT.NCOLM) NCOLDM=NCOLDM-NCOLM
      ST1=ST1+T     
      SDIF=ST-STO(NCOLDM)                                               
      SUMZZ=SUMZZ+((Z-ZST(NCOLDM)-WZ*SDIF)**2)*T/SDIF
      SUMYY=SUMYY+((Y-YST(NCOLDM)-WY*SDIF)**2)*T/SDIF
      SUMXX=SUMXX+((X-XST(NCOLDM)-WX*SDIF)**2)*T/SDIF               
      SUMYZ=SUMYZ+(Z-ZST(NCOLDM)-WZ*SDIF)*(Y-YST(NCOLDM)-WY*SDIF)*T/SDIF
      SUMXY=SUMXY+(X-XST(NCOLDM)-WX*SDIF)*(Y-YST(NCOLDM)-WY*SDIF)*T/SDIF
      SUMXZ=SUMXZ+(X-XST(NCOLDM)-WX*SDIF)*(Z-ZST(NCOLDM)-WZ*SDIF)*T/SDIF
 120  KDUM=KDUM+NCORLN   
 121  XST(NCOL)=X                                                       
      YST(NCOL)=Y                                                       
      ZST(NCOL)=Z                                                       
      STO(NCOL)=ST                                                      
      IF(NCOL.GE.NCOLM) THEN      
       ID=ID+1                                                          
       XID=DFLOAT(ID)                                                   
       NCOL=0  
      ENDIF                                                           
C --------------------------------------------------------------------- 
C     DETERMINATION OF REAL COLLISION TYPE                              
C --------------------------------------------------------------------- 
      R2=drand48(RDUM) 
C FIND LOCATION WITHIN 4 UNITS IN COLLISION ARRAY       
      CALL SORTT(KGAS,I,R2,IE)                                          
  140 I=I+1                                                             
      IF(CF(KGAS,IE,I).LT.R2) GO TO 140                               
      S1=RGAS(KGAS,I)                                                  
      EI=EIN(KGAS,I)
      IF(IPN(KGAS,I).LE.0) GO TO 666                                    
C  USE FLAT DISTRIBUTION OF  ELECTRON ENERGY BETWEEN E-EION AND 0.0 EV  
C  SAME AS IN BOLTZMANN                                                 
      R9=drand48(RDUM)  
      EXTRA=R9*(EOK-EI)                                               
      EI=EXTRA+EI                                 
C IF FLUORESCENCE OR AUGER ADD EXTRA ELECTRONS
      IEXTRA=IEXTRA+NC0(KGAS,I)
C                                                                       
C  GENERATE SCATTERING ANGLES AND UPDATE  LABORATORY COSINES AFTER      
C   COLLISION ALSO UPDATE ENERGY OF ELECTRON.                           
C                                                                       
  666 IPT=IARRY(KGAS,I)
      ICOLL(KGAS,IPT)=ICOLL(KGAS,IPT)+1
      ICOLN(KGAS,I)=ICOLN(KGAS,I)+1
      IF(EOK.LT.EI) THEN
       EI=EOK-0.0001D0
      ENDIF
C IF EXCITATION THEN ADD PROBABILITY, PENFRA(1,I), OF TRANSFER TO GIVE
C IONISATION OF THE OTHER GASES IN THE MIXTURE
      IF(IPEN.EQ.0) GO TO 5
      IF(PENFRA(KGAS,1,I).NE.0.0) THEN
       RAN=drand48(RDUM)
       IF(RAN.GT.PENFRA(KGAS,1,I)) GO TO 5
C ADD EXTRA IONISATION COLLISION
       IEXTRA=IEXTRA+1
      ENDIF
    5 S2=(S1*S1)/(S1-1.0D0)  
C ANISOTROPIC SCATTERING 
      R3=drand48(RDUM)
      IF(INDEX(KGAS,I).EQ.1) THEN
        R31=drand48(RDUM)
        F3=1.0D0-R3*ANGCT(KGAS,IE,I)
        IF(R31.GT.PSCT(KGAS,IE,I)) F3=-F3
      ELSE IF(INDEX(KGAS,I).EQ.2) THEN
       EPSI=PSCT(KGAS,IE,I)
       F3=1.0D0-(2.0D0*R3*(1.0D0-EPSI)/(1.0D0+EPSI*(1.0D0-2.0D0*R3)))
      ELSE
C ISOTROPIC SCATTERING
        F3=1.0D0-2.0D0*R3
      ENDIF
      THETA0=DACOS(F3)                                                  
      R4=drand48(RDUM)
      PHI0=F4*R4                                                        
      F8=DSIN(PHI0)                                                     
      F9=DCOS(PHI0)                                                     
      ARG1=1.0D0-S1*EI/EOK                                              
      ARG1=DMAX1(ARG1,SMALL)                                            
      D=1.0D0-F3*DSQRT(ARG1)                                            
      E1=EOK*(1.0D0-EI/(S1*EOK)-2.0D0*D/S2)                            
      E1=DMAX1(E1,SMALL)                                                
      Q=DSQRT((EOK/E1)*ARG1)/S1                                       
      Q=DMIN1(Q,1.0D0)                                                  
      THETA=DASIN(Q*DSIN(THETA0))                                       
      F6=DCOS(THETA)                                                    
      U=(S1-1.0D0)*(S1-1.0D0)/ARG1
      CSQD=F3*F3                                                        
      IF(F3.LT.0.0D0.AND.CSQD.GT.U) F6=-1.0D0*F6
      F5=DSIN(THETA)
      DZCOM=DMIN1(DZCOM,1.0D0)
      ARGZ=DSQRT(DXCOM*DXCOM+DYCOM*DYCOM)
      IF(ARGZ.EQ.0.0D0) THEN
       DCZ1=F6
       DCX1=F9*F5
       DCY1=F8*F5
       GO TO 130
      ENDIF                                          
      DCZ1=DZCOM*F6+ARGZ*F5*F8                                          
      DCY1=DYCOM*F6+(F5/ARGZ)*(DXCOM*F9-DYCOM*DZCOM*F8)                 
      DCX1=DXCOM*F6-(F5/ARGZ)*(DYCOM*F9+DXCOM*DZCOM*F8)    
  130 CONTINUE            
C TRANSFORM VELOCITY VECTORS TO LAB FRAME  
      VTOT=CONST9*DSQRT(E1)          
      CX1=DCX1*VTOT+VGX
      CY1=DCY1*VTOT+VGY
      CZ1=DCZ1*VTOT+VGZ
C CALCULATE ENERGY AND DIRECTION COSINES IN LAB FRAME
      E1=(CX1*CX1+CY1*CY1+CZ1*CZ1)/CONST10
      CONST11=1.0D0/(CONST9*DSQRT(E1))
      DCX1=CX1*CONST11
      DCY1=CY1*CONST11
      DCZ1=CZ1*CONST11
  133 CONTINUE                                                          
C   ------------------------------------------  
      IF(J1.EQ.1) WRITE(6,201)                                         
  201 FORMAT(/,'   VELZ    VELY    VELX   ENERGY  ID  DIFXX  DIFYY  DIFZ
     /Z  DIFYZ  DIFXZ  DIFXY',/)
      WZ=WZ*1.0D+09
      WY=WY*1.0D+09
      WX=WX*1.0D+09
C CALCULATE ROTATED VECTORS AND POSITIONS
      WZR=WZ*RCS-WX*RSN
      WYR=WY
      WXR=WZ*RSN+WX*RCS
      ZR=Z*RCS-X*RSN
      YR=Y
      XR=Z*RSN+X*RCS
      EBAR=0.0D0
      DO 300 IK=1,4000
      TCFSUM=0.0
      DO 299 KI=1,NGAS
  299 TCFSUM=TCFSUM+TCF(KI,IK)
 300  EBAR=EBAR+ES(IK)*SPEC(IK)/TCFSUM      
      AVE=EBAR/ST
      WZST(J1)=(ZR-ZROLD)/(ST-STOLD)*1.0D+09
      WYST(J1)=(YR-YROLD)/(ST-STOLD)*1.0D+09
      WXST(J1)=(XR-XROLD)/(ST-STOLD)*1.0D+09
      AVEST(J1)=(EBAR-EBAROLD)/(ST-STOLD)
      EBAROLD=EBAR
      IF(J1.LT.3) GO TO 100 
      DIFXX=5.0D+15*SUMXX/ST1
      DIFYY=5.0D+15*SUMYY/ST1
      DIFZZ=5.0D+15*SUMZZ/ST1
      DIFYZ=5.0D+15*SUMYZ/ST1
      DIFXZ=5.0D+15*SUMXZ/ST1
      DIFXY=5.0D+15*SUMXY/ST1
C CALCULATE  ROTATED TENSOR .
      DIFXXR=DIFXX*RCS*RCS+DIFZZ*RSN*RSN+2.0D0*RCS*RSN*DIFXZ
      DIFYYR=DIFYY
      DIFZZR=DIFXX*RSN*RSN+DIFZZ*RCS*RCS-2.0D0*RCS*RSN*DIFXZ
      DIFXYR=RCS*DIFXY+RSN*DIFYZ   
      DIFYZR=RSN*DIFXY-RCS*DIFYZ
      DIFXZR=(RCS*RCS-RSN*RSN)*DIFXZ-RSN*RCS*(DIFXX-DIFZZ)
      SXXR=SUMXX*RCS*RCS+SUMZZ*RSN*RSN+2.0D0*RCS*RSN*SUMXZ
      SYYR=SUMYY
      SZZR=SUMXX*RSN*RSN+SUMZZ*RCS*RCS-2.0D0*RCS*RSN*SUMXZ
      SXYR=RCS*SUMXY+RSN*SUMYZ
      SYZR=RSN*SUMXY-RCS*SUMYZ
      SXZR=(RCS*RCS-RSN*RSN)*SUMXZ-RSN*RCS*(SUMXX-SUMZZ)
C 
  100 DFZZST(J1)=0.0D0   
      DFYYST(J1)=0.0D0
      DFXXST(J1)=0.0D0
      DFXYST(J1)=0.0D0
      DFYZST(J1)=0.0D0
      DFXZST(J1)=0.0D0
      IF(J1.GT.2) THEN 
       DFZZST(J1)=5.0D+15*(SZZR-SZZOLD)/(ST1-ST1OLD)
       DFYYST(J1)=5.0D+15*(SYYR-SYYOLD)/(ST1-ST1OLD)
       DFXXST(J1)=5.0D+15*(SXXR-SXXOLD)/(ST1-ST1OLD)
       DFXYST(J1)=5.0D+15*(SXYR-SXYOLD)/(ST1-ST1OLD)
       DFYZST(J1)=5.0D+15*(SYZR-SYZOLD)/(ST1-ST1OLD)
       DFXZST(J1)=5.0D+15*(SXZR-SXZOLD)/(ST1-ST1OLD)
      ENDIF               
      ZROLD=ZR
      YROLD=YR
      XROLD=XR
      STOLD=ST
      ST1OLD=ST1
      SZZOLD=SZZR
      SYYOLD=SYYR
      SXXOLD=SXXR
      SXYOLD=SXYR
      SYZOLD=SYZR
      SXZOLD=SXZR
C OUTPUT ROTATED VECTORS AND TENSOR       
      WRITE(6,202) WZR,WYR,WXR,AVE,ID,DIFXXR,DIFYYR,DIFZZR,
     /DIFYZR,DIFXZR,DIFXYR                                             
  202 FORMAT(3(F7.2,1X),1X,F7.4,I4,6(F7.1))   
C LOOP                                                                      
  210 CONTINUE
C CALCULATE ERRORS AND CHECK AVERAGES
      TWZST=0.0D0
      TWYST=0.0D0
      TWXST=0.0D0
      TAVE=0.0D0
      T2WZST=0.0D0
      T2WYST=0.0D0
      T2WXST=0.0D0
      T2AVE=0.0D0
      TZZST=0.0D0
      TYYST=0.0D0
      TXXST=0.0D0
      TXYST=0.0D0
      TYZST=0.0D0
      TXZST=0.0D0
      T2ZZST=0.0D0
      T2YYST=0.0D0
      T2XXST=0.0D0
      T2XYST=0.0D0
      T2YZST=0.0D0
      T2XZST=0.0D0
      DO 790 K=1,10
      TWZST=TWZST+WZST(K)
      TWYST=TWYST+WYST(K)
      TWXST=TWXST+WXST(K)
      TAVE=TAVE+AVEST(K)
      T2WZST=T2WZST+WZST(K)*WZST(K)
      T2WYST=T2WYST+WYST(K)*WYST(K)
      T2WXST=T2WXST+WXST(K)*WXST(K)
      T2AVE=T2AVE+AVEST(K)*AVEST(K)
      IF(K.LT.3) GO TO 790
      TZZST=TZZST+DFZZST(K)
      TYYST=TYYST+DFYYST(K)
      TXXST=TXXST+DFXXST(K)
      TXYST=TXYST+DFXYST(K)
      TYZST=TYZST+DFYZST(K)
      TXZST=TXZST+DFXZST(K)  
      T2ZZST=T2ZZST+DFZZST(K)*DFZZST(K)
      T2YYST=T2YYST+DFYYST(K)*DFYYST(K)
      T2XXST=T2XXST+DFXXST(K)*DFXXST(K)    
      T2XYST=T2XYST+DFXYST(K)*DFXYST(K)
      T2YZST=T2YZST+DFYZST(K)*DFYZST(K)
      T2XZST=T2XZST+DFXZST(K)*DFXZST(K)
  790 CONTINUE
      DWZ=100.0D0*DSQRT((T2WZST-TWZST*TWZST/10.0D0)/9.0D0)/WZR
      DWY=100.0D0*DSQRT((T2WYST-TWYST*TWYST/10.0D0)/9.0D0)/DABS(WYR)
      DWX=100.0D0*DSQRT((T2WXST-TWXST*TWXST/10.0D0)/9.0D0)/DABS(WXR) 
      DEN=100.0D0*DSQRT((T2AVE-TAVE*TAVE/10.0D0)/9.0D0)/AVE
      DZZER=100.0D0*DSQRT((T2ZZST-TZZST*TZZST/8.0D0)/7.0D0)/DIFZZR
      DYYER=100.0D0*DSQRT((T2YYST-TYYST*TYYST/8.0D0)/7.0D0)/DIFYYR
      DXXER=100.0D0*DSQRT((T2XXST-TXXST*TXXST/8.0D0)/7.0D0)/DIFXXR
      DXYER=100.0D0*DSQRT((T2XYST-TXYST*TXYST/8.0D0)/7.0D0)/DABS(DIFXYR)
      DYZER=100.0D0*DSQRT((T2YZST-TYZST*TYZST/8.0D0)/7.0D0)/DABS(DIFYZR)
      DXZER=100.0D0*DSQRT((T2XZST-TXZST*TXZST/8.0D0)/7.0D0)/DABS(DIFXZR)
      DWZ=DWZ/DSQRT(10.0D0)
      DWY=DWY/DSQRT(10.0D0)
      DWX=DWX/DSQRT(10.0D0)
      DEN=DEN/DSQRT(10.0D0)
      DZZER=DZZER/DSQRT(8.0D0)
      DYYER=DYYER/DSQRT(8.0D0)
      DXXER=DXXER/DSQRT(8.0D0)
      DXYER=DXYER/DSQRT(8.0D0)
      DYZER=DYZER/DSQRT(8.0D0)
      DXZER=DXZER/DSQRT(8.0D0)
C LOAD ROTATED VALUES INTO ARRAYS
      WZ=WZR
      WX=WXR
      WY=WYR
      DIFXX=DIFXXR
      DIFYY=DIFYYR
      DIFZZ=DIFZZR
      DIFYZ=DIFYZR
      DIFXZ=DIFXZR
      DIFXY=DIFXYR          
C CONVERT TO CM/SEC.
      WZ=WZ*1.0D05
      WY=WY*1.0D05
      WX=WX*1.0D05
C CALCULATE TOWNSEND COEFICIENTS AND ERRORS
      ANCATT=0.0D0
      ANCION=0.0D0
      DO 800 I=1,NGAS
      ANCATT=ANCATT+ICOLL(I,3)
  800 ANCION=ANCION+ICOLL(I,2)
      ANCION=ANCION+IEXTRA
      ATTER=0.0D0
      IF(ANCATT.EQ.0.0D0) GO TO 810
      ATTER=100.0D0*DSQRT(ANCATT)/ANCATT
  810 ATT=ANCATT/(ST*WZ)*1.0D12
      ALPER=0.0D0
      IF(ANCION.EQ.0.0D0) GO TO 820
      ALPER=100.0D0*DSQRT(ANCION)/ANCION
  820 ALPHA=ANCION/(ST*WZ)*1.0D12                
      RETURN                                                            
      END 
      SUBROUTINE ELIMITT(IELOW)                                         
      IMPLICIT REAL*8 (A-H,O-Z)                                         
      IMPLICIT INTEGER*8 (I-N)
      COMMON/INPT/NGAS,NSTEP,NANISO,EFINAL,ESTEP,AKT,ARY,TEMPC,TORR,IPEN
      COMMON/CNSTS/ECHARG,EMASS,AMU,PIR2             
      COMMON/CNSTS1/CONST1,CONST2,CONST3,CONST4,CONST5                  
      COMMON/SETP/TMAX,SMALL,API,ESTART,THETA,PHI,RSTART,EFIELD,NMAX  
      COMMON/THRM/AMGAS(6),VTMB(6),TCFMX,TCFMXG(6),ITHRM            
      COMMON/RANM/RNMX(6)                 
      COMMON/LARGET/CF(6,4000,290),EIN(6,290),TCF(6,4000),IARRY(6,290),
     /RGAS(6,290),IPN(6,290),WPL(6,290),LAST(6),ISIZE(6),PENFRA(6,3,290)
     /,TCFMAX(6)
      COMMON/ANIST/PSCT(6,4000,290),ANGCT(6,4000,290),INDEX(6,290),NISO 
C -------------------------------------------------------------------   
C   CALCULATES COLLISION EVENTS AND TESTS TO FIND IF THE UPPER ENERGY  
C   LIMIT FOR THE ELECTRON ENERGY IS EXCEEDED. 
C    IF ENERGY LIMIT IS OK       IELOW = 0
C    IF ENERGY LIMIT IS EXCEEDED IELOW = 1          
C   THE TEST IS CARRIED OUT FOR A SAMPLE OF COLLISIONS THAT ARE
C   SMALLER THAN THE FULL SAMPLE BY A FACTOR OF 1/ISAMP
C   
C   USED WITH MAGNETIC FIELD B =0.0   ELECTRIC FIELD IN Z DIRECTION.
C -------------------------------------------------------------------
      ISAMP=10
      SMALL=1.0D-20                                                     
      RDUM=RSTART                                                       
      E1=ESTART    
      N4000=4000                                                     
      TDASH=0.0D0    
      CONST9=CONST3*0.01D0
      CONST10=CONST9*CONST9    
C GENERATE RANDOM NUMBER FOR MAXWELL BOLTZMAN 
      CALL GERJAN
      IMBPT=0
C                                                                       
C     INITIAL DIRECTION COSINES                                         
C                                                                       
      DCZ1=DCOS(THETA)                                                  
      DCX1=DSIN(THETA)*DCOS(PHI)                                        
      DCY1=DSIN(THETA)*DSIN(PHI)                                        
C                                                                       
      BP=EFIELD*EFIELD*CONST1                                           
      F1=EFIELD*CONST2                                                  
      F2=EFIELD*CONST3                                                  
      F4=2.0D0*DACOS(-1.0D0)                                            
      J2M=NMAX/ISAMP 
C MAIN LOOP                                                             
      DO 210 J1=1,J2M  
    1 R1=drand48(RDUM)
      T=-DLOG(R1)/TCFMX+TDASH                                          
      TDASH=T                                                           
      AP=DCZ1*F2*DSQRT(E1)
      E=E1+(AP+BP*T)*T              
C CALCULATE DIRECTION COSINES BEFORE COLLISION
      CONST6=DSQRT(E1/E)
      DCX2=DCX1*CONST6
      DCY2=DCY1*CONST6
      DCZ2=DCZ1*CONST6+EFIELD*T*CONST5/DSQRT(E)       
C FIND IDENTITY OF GAS FOR COLLISION
      KGAS=0
      R2=drand48(RDUM)
    3 KGAS=KGAS+1
      IF(TCFMXG(KGAS).LT.R2) GO TO 3
C CALCULATE GAS VELOCITY VECTORS VGX,VGY,VGZ
      IMBPT=IMBPT+1
      IF(IMBPT.GT.6) THEN
       CALL GERJAN
       IMBPT=1
      ENDIF
      VGX=VTMB(KGAS)*RNMX(IMBPT)
      IMBPT=IMBPT+1
      VGY=VTMB(KGAS)*RNMX(IMBPT)
      IMBPT=IMBPT+1
      VGZ=VTMB(KGAS)*RNMX(IMBPT)
C CALCULATE ELECTRON VELOCITY VECTORS VEX,VEY,VEZ
      VEX=DCX2*CONST9*DSQRT(E)
      VEY=DCY2*CONST9*DSQRT(E)
      VEZ=DCZ2*CONST9*DSQRT(E)
C CALCULATE ENERGY WITH STATIONARY GAS TARGET ,EOK
      EOK=((VEX-VGX)**2+(VEY-VGY)**2+(VEZ-VGZ)**2)/CONST10
      IE=DINT(EOK/ESTEP)+1
      IE=DMIN0(IE,N4000) 
C                                                                       
C     TEST FOR REAL OR NULL COLLISION                                   
C                                                                       
      R5=drand48(RDUM)
      TEST1=TCF(KGAS,IE)/TCFMAX(KGAS)                           
      IF(R5.GT.TEST1) GO TO 1                                           
C                                                                       
      IF(IE.EQ.4000) THEN
C ELECTRON ENERGY OUT OF RANGE
       IELOW=1
       RETURN
      ENDIF
      TDASH=0.0D0
C                                                                       
C  CALCULATE DIRECTION COSINES OF ELECTRON IN 0 KELVIN FRAME
C                       
      CONST11=1.0D0/(CONST9*DSQRT(EOK))                                 
      DXCOM=(VEX-VGX)*CONST11                                           
      DYCOM=(VEY-VGY)*CONST11
      DZCOM=(VEZ-VGZ)*CONST11              
C --------------------------------------------------------------------- 
C     DETERMINATION OF REAL COLLISION TYPE                              
C --------------------------------------------------------------------- 
      R2=drand48(RDUM)
C FIND LOCATION WITHIN 4 UNITS IN COLLISION ARRAY       
      CALL SORTT(KGAS,I,R2,IE)                                          
  140 I=I+1                                                             
      IF(CF(KGAS,IE,I).LT.R2) GO TO 140                               
      S1=RGAS(KGAS,I)                                                   
      EI=EIN(KGAS,I)                                                    
      IF(IPN(KGAS,I).LE.0) GO TO 666                                    
      R9=drand48(RDUM)
      EXTRA=R9*(EOK-EI)                                                
      EI=EXTRA+EI                                                       
C                                                                       
C  GENERATE SCATTERING ANGLES AND UPDATE  LABORATORY COSINES AFTER      
C   COLLISION ALSO UPDATE ENERGY OF ELECTRON.                           
C                        
  666 IPT=IARRY(KGAS,I)                                               
      IF(EOK.LT.EI) THEN
       EI=EOK-0.0001D0
      ENDIF
      S2=(S1*S1)/(S1-1.0D0)
      R3=drand48(RDUM)
      IF(INDEX(KGAS,I).EQ.1) THEN
       R31=drand48(RDUM)
       F3=1.0D0-R3*ANGCT(KGAS,IE,I)
       IF(R31.GT.PSCT(KGAS,IE,I))  F3=-F3
      ELSE IF(INDEX(KGAS,I).EQ.2) THEN
       EPSI=PSCT(KGAS,IE,I)
       F3=1.0D0-(2.0D0*R3*(1.0D0-EPSI)/(1.0D0+EPSI*(1.0D0-2.0D0*R3)))
      ELSE
       F3=1.0D0-2.0D0*R3
      ENDIF
      THETA0=DACOS(F3)                                                  
      R4=drand48(RDUM) 
      PHI0=F4*R4                                                        
      F8=DSIN(PHI0)                                                     
      F9=DCOS(PHI0)                                                     
      ARG1=1.0D0-S1*EI/EOK                                              
      ARG1=DMAX1(ARG1,SMALL)                                            
      D=1.0D0-F3*DSQRT(ARG1)                                            
      E1=EOK*(1.0D0-EI/(S1*EOK)-2.0D0*D/S2)                             
      E1=DMAX1(E1,SMALL)                                                
      Q=DSQRT((EOK/E1)*ARG1)/S1                                         
      Q=DMIN1(Q,1.0D0)                                                  
      THETA=DASIN(Q*DSIN(THETA0))                                       
      F6=DCOS(THETA)                                                    
      U=(S1-1.0D0)*(S1-1.0D0)/ARG1                                      
      CSQD=F3*F3                                                        
      IF(F3.LT.0.0D0.AND.CSQD.GT.U) F6=-1.0D0*F6                        
      F5=DSIN(THETA) 
      DZCOM=DMIN1(DZCOM,1.0D0)                                         
      ARGZ=DSQRT(DXCOM*DXCOM+DYCOM*DYCOM) 
      IF(ARGZ.EQ.0.0D0) THEN
       DCZ1=F6
       DCX1=F9*F5
       DCY1=F8*F5
       GO TO 130
      ENDIF                                          
      DCZ1=DZCOM*F6+ARGZ*F5*F8                                         
      DCY1=DYCOM*F6+(F5/ARGZ)*(DXCOM*F9-DYCOM*DZCOM*F8)                 
      DCX1=DXCOM*F6-(F5/ARGZ)*(DYCOM*F9+DXCOM*DZCOM*F8)
  130 CONTINUE
C TRANSFORM VELOCITY VECTORS TO LAB FRAME
      CONST12=CONST9*DSQRT(E1)
      VXLAB=DCX1*CONST12+VGX
      VYLAB=DCY1*CONST12+VGY
      VZLAB=DCZ1*CONST12+VGZ
C CALCULATE ENERGY AND DIRECTION COSINES IN LAB FRAME
      E1=(VXLAB*VXLAB+VYLAB*VYLAB+VZLAB*VZLAB)/CONST10
      CONST11=1.0D0/(CONST9*DSQRT(E1))
      DCX1=VXLAB*CONST11
      DCY1=VYLAB*CONST11
      DCZ1=VZLAB*CONST11
C LOOP                                                                 
  210 CONTINUE
      IELOW=0
      RETURN                                                            
      END 
      SUBROUTINE ELIMITBT(IELOW)                                        
      IMPLICIT REAL*8 (A-H,O-Z)                                         
      IMPLICIT INTEGER*8 (I-N)
      COMMON/INPT/NGAS,NSTEP,NANISO,EFINAL,ESTEP,AKT,ARY,TEMPC,TORR,IPEN
      COMMON/CNSTS/ECHARG,EMASS,AMU,PIR2             
      COMMON/CNSTS1/CONST1,CONST2,CONST3,CONST4,CONST5                  
      COMMON/SETP/TMAX,SMALL,API,ESTART,THETA,PHI,RSTART,EFIELD,NMAX 
      COMMON/THRM/AMGAS(6),VTMB(6),TCFMX,TCFMXG(6),ITHRM
      COMMON/RANM/RNMX(6) 
      COMMON/BFLD/EOVB,WB,BTHETA,BMAG                                   
      COMMON/LARGET/CF(6,4000,290),EIN(6,290),TCF(6,4000),IARRY(6,290),
     /RGAS(6,290),IPN(6,290),WPL(6,290),LAST(6),ISIZE(6),PENFRA(6,3,290)
     /,TCFMAX(6)
      COMMON/ANIST/PSCT(6,4000,290),ANGCT(6,4000,290),INDEX(6,290),NISO 
C -------------------------------------------------------------------   
C   CALCULATES COLLISION EVENTS AND TESTS TO FIND IF THE UPPER ENERGY  
C   LIMIT FOR THE ELECTRON ENERGY IS EXCEEDED. 
C    IF ENERGY LIMIT IS OK       IELOW = 0
C    IF ENERGY LIMIT IS EXCEEDED IELOW = 1          
C   THE TEST IS CARRIED OUT FOR A SAMPLE OF COLLISIONS THAT ARE
C   SMALLER THAN THE FULL SAMPLE BY A FACTOR OF 1/ISAMP
C   
C   USED WITH MAGNETIC FIELD B AT 90 DEGREES TO ELECTRIC FIELD  
C -------------------------------------------------------------------
      ISAMP=20      
      SMALL=1.0D-20 
      EF100=EFIELD*100.0D0                                              
      RDUM=RSTART                                                       
      E1=ESTART    
      N4000=4000                                                     
      TDASH=0.0D0 
C GENERATE RANDOM NUMBER FOR MAXWELL BOLTZMAN 
      CALL GERJAN
      IMBPT=0
C
      CONST9=CONST3*0.01D0 
      CONST10=CONST9*CONST9                                             
C                                                                       
C     INITIAL DIRECTION COSINES                                         
C                                                                       
      DCZ1=DCOS(THETA)                                                  
      DCX1=DSIN(THETA)*DCOS(PHI)                                        
      DCY1=DSIN(THETA)*DSIN(PHI)                                        
C                                                                       
      VTOT=CONST9*DSQRT(E1)                                             
      CX1=DCX1*VTOT                                                     
      CY1=DCY1*VTOT
      CZ1=DCZ1*VTOT                                                     
      F4=2.0D0*DACOS(-1.0D0)                                            
      J2M=NMAX/ISAMP 
C MAIN LOOP                                                             
      DO 210 J1=1,J2M  
    1 R1=drand48(RDUM) 
      T=-DLOG(R1)/TCFMX+TDASH                                          
      TDASH=T                                                           
      WBT=WB*T
      COSWT=DCOS(WBT)
      SINWT=DSIN(WBT)
      DZ=(CZ1*SINWT+(EOVB-CY1)*(1.0D0-COSWT))/WB                        
      E=E1+DZ*EF100              
C CALC ELECTRON VELOCITY IN LAB FRAME
      CX2=CX1
      CY2=(CY1-EOVB)*COSWT+CZ1*SINWT+EOVB      
      CZ2=CZ1*COSWT-(CY1-EOVB)*SINWT
C FIND IDENTITY OF GAS FOR COLLISION
      KGAS=0
      R2=drand48(RDUM)
    3 KGAS=KGAS+1
      IF(TCFMXG(KGAS).LT.R2) GO TO 3
C CALCULATE GAS VELOCITY VECTORS VGX,VGY,VGZ
      IMBPT=IMBPT+1
      IF(IMBPT.GT.6) THEN
       CALL GERJAN
       IMBPT=1
      ENDIF
      VGX=VTMB(KGAS)*RNMX(IMBPT)
      IMBPT=IMBPT+1
      VGY=VTMB(KGAS)*RNMX(IMBPT)
      IMBPT=IMBPT+1
      VGZ=VTMB(KGAS)*RNMX(IMBPT)
C CALCULATE ENERGY WITH STATIONARY GAS TARGET , EOK
      EOK=((CX2-VGX)**2+(CY2-VGY)**2+(CZ2-VGZ)**2)/CONST10         
      IE=DINT(EOK/ESTEP)+1 
      IE=DMIN0(IE,N4000) 
C                                                                       
C     TEST FOR REAL OR NULL COLLISION                                   
C                                                                       
      R5=drand48(RDUM) 
      TLIM=TCF(KGAS,IE)/TCFMAX(KGAS)                               
      IF(R5.GT.TLIM) GO TO 1                                          
C                       
      IF(IE.EQ.4000) THEN 
C ELECTRON ENERGY OUT OF RANGE
       IELOW=1
       RETURN
      ENDIF                                                           
      TDASH=0.0D0                       
C CALCULATE DIRECTION COSINES OF ELECTRON IN 0 KELVIN FRAME           
      CONST11=1.0D0/(CONST9*DSQRT(EOK))
      DXCOM=(CX2-VGX)*CONST11
      DYCOM=(CY2-VGY)*CONST11
      DZCOM=(CZ2-VGZ)*CONST11                     
C --------------------------------------------------------------------- 
C     DETERMINATION OF REAL COLLISION TYPE                              
C --------------------------------------------------------------------- 
      R2=drand48(RDUM)
C FIND LOCATION WITHIN 4 UNITS IN COLLISION ARRAY         
      CALL SORTT(KGAS,I,R2,IE)                                          
  140 I=I+1                                                             
      IF(CF(KGAS,IE,I).LT.R2) GO TO 140                                 
      S1=RGAS(KGAS,I)                                                  
      EI=EIN(KGAS,I)                                                   
      IF(IPN(KGAS,I).LE.0) GO TO 666                                   
      R9=drand48(RDUM)
      EXTRA=R9*(EOK-EI)                                                
      EI=EXTRA+EI                                                       
C                                                                       
C  GENERATE SCATTERING ANGLES AND UPDATE  LABORATORY COSINES AFTER      
C   COLLISION ALSO UPDATE ENERGY OF ELECTRON.                           
C                        
  666 IPT=IARRY(KGAS,I)                                               
      IF(EOK.LT.EI) THEN
       EI=EOK-0.0001D0
      ENDIF
      S2=(S1*S1)/(S1-1.0D0)
      R3=drand48(RDUM)
      IF(INDEX(KGAS,I).EQ.1) THEN
       R31=drand48(RDUM)
       F3=1.0D0-R3*ANGCT(KGAS,IE,I)
       IF(R31.GT.PSCT(KGAS,IE,I)) F3=-F3
      ELSE IF(INDEX(KGAS,I).EQ.2) THEN
       EPSI=PSCT(KGAS,IE,I)
       F3=1.0D0-(2.0D0*R3*(1.0D0-EPSI)/(1.0D0+EPSI*(1.0D0-2.0D0*R3)))
      ELSE
       F3=1.0D0-2.0D0*R3
      ENDIF
      THETA0=DACOS(F3)                                                  
      R4=drand48(RDUM)
      PHI0=F4*R4                                                        
      F8=DSIN(PHI0)                                                     
      F9=DCOS(PHI0)                                                     
      ARG1=1.0D0-S1*EI/EOK                                              
      ARG1=DMAX1(ARG1,SMALL)                                            
      D=1.0D0-F3*DSQRT(ARG1)                                            
      E1=EOK*(1.0D0-EI/(S1*EOK)-2.0D0*D/S2)                             
      E1=DMAX1(E1,SMALL)                                                
      Q=DSQRT((EOK/E1)*ARG1)/S1                                         
      Q=DMIN1(Q,1.0D0)                                                  
      THETA=DASIN(Q*DSIN(THETA0))                                       
      F6=DCOS(THETA)                                                    
      U=(S1-1.0D0)*(S1-1.0D0)/ARG1                                      
      CSQD=F3*F3                                                        
      IF(F3.LT.0.0D0.AND.CSQD.GT.U) F6=-1.0D0*F6                        
      F5=DSIN(THETA) 
      DCZ2=DMIN1(DZCOM,1.0D0)
      ARGZ=DSQRT(DXCOM*DXCOM+DYCOM*DYCOM) 
      IF(ARGZ.EQ.0.0D0) THEN
       DCZ1=F6
       DCX1=F9*F5
       DCY1=F8*F5
       GO TO 130
      ENDIF                                          
      DCZ1=DZCOM*F6+ARGZ*F5*F8                                         
      DCY1=DYCOM*F6+(F5/ARGZ)*(DXCOM*F9-DYCOM*DZCOM*F8)                 
      DCX1=DXCOM*F6-(F5/ARGZ)*(DYCOM*F9+DXCOM*DZCOM*F8)
  130 CONTINUE
C  TRANSFORM VELOCITY VECTORS TO LAB FRAME
      VTOT=CONST9*DSQRT(E1)
      CX1=DCX1*VTOT+VGX
      CY1=DCY1*VTOT+VGY
      CZ1=DCZ1*VTOT+VGZ
C CALCULATE ENERGY AND DIRECTION COSINES IN LAB FRAME
      E1=(CX1*CX1+CY1*CY1+CZ1*CZ1)/CONST10
      CONST11=1.0D0/(CONST9*DSQRT(E1))
      DCX1=CX1*CONST11
      DCY1=CY1*CONST11
      DCZ1=CZ1*CONST11    
C LOOP                                                                 
  210 CONTINUE
      IELOW=0
      RETURN                                                            
      END 
      SUBROUTINE ELIMITCT(IELOW)                                       
      IMPLICIT REAL*8 (A-H,O-Z)                                         
      IMPLICIT INTEGER*8 (I-N)
      COMMON/INPT/NGAS,NSTEP,NANISO,EFINAL,ESTEP,AKT,ARY,TEMPC,TORR,IPEN
      COMMON/CNSTS/ECHARG,EMASS,AMU,PIR2             
      COMMON/CNSTS1/CONST1,CONST2,CONST3,CONST4,CONST5                  
      COMMON/SETP/TMAX,SMALL,API,ESTART,THETA,PHI,RSTART,EFIELD,NMAX
      COMMON/THRM/AMGAS(6),VTMB(6),TCFMX,TCFMXG(6),ITHRM
      COMMON/RANM/RNMX(6)  
      COMMON/BFLD/EOVB,WB,BTHETA,BMAG                                   
      COMMON/LARGET/CF(6,4000,290),EIN(6,290),TCF(6,4000),IARRY(6,290),
     /RGAS(6,290),IPN(6,290),WPL(6,290),LAST(6),ISIZE(6),PENFRA(6,3,290)
     /,TCFMAX(6)
      COMMON/ANIST/PSCT(6,4000,290),ANGCT(6,4000,290),INDEX(6,290),NISO
C -------------------------------------------------------------------   
C   CALCULATES COLLISION EVENTS AND TESTS TO FIND IF THE UPPER ENERGY  
C   LIMIT FOR THE ELECTRON ENERGY IS EXCEEDED. 
C    IF ENERGY LIMIT IS OK       IELOW = 0
C    IF ENERGY LIMIT IS EXCEEDED IELOW = 1          
C   THE TEST IS CARRIED OUT FOR A SAMPLE OF COLLISIONS THAT ARE
C   SMALLER THAN THE FULL SAMPLE BY A FACTOR OF 1/ISAMP
C   
C   USED WITH MAGNETIC FIELD B AT ANGLES BETWEEN 0 AND 90 DEGREES TO
C   THE ELECTRIC FIELD.
C -------------------------------------------------------------------
      ISAMP=20
      SMALL=1.0D-20
      API=DACOS(-1.0D0) 
      RTHETA=BTHETA*API/180.0D0
      EFZ100=EFIELD*100.0D0*DSIN(RTHETA)
      EFX100=EFIELD*100.0D0*DCOS(RTHETA)
      F1=EFIELD*CONST2*DCOS(RTHETA)
      F4=2.0D0*API
      EOVBR=EOVB*DSIN(RTHETA)
      RDUM=RSTART                                                       
      E1=ESTART    
      N4000=4000                                                     
      TDASH=0.0D0 
      CONST9=CONST3*0.01D0
      CONST10=CONST9*CONST9            
C GENERATE RANDOM NUMBER FOR MAXWELL BOLTZMAN
      CALL GERJAN
      IMBPT=0
C                                                                       
C     INITIAL DIRECTION COSINES                                         
C                                                                       
      DCZ1=DCOS(THETA)                                                  
      DCX1=DSIN(THETA)*DCOS(PHI)                                        
      DCY1=DSIN(THETA)*DSIN(PHI)                                        
C                                                                       
      VTOT=CONST9*DSQRT(E1)                                             
      CX1=DCX1*VTOT                                                     
      CY1=DCY1*VTOT
      CZ1=DCZ1*VTOT                                                     
      J2M=NMAX/ISAMP 
C MAIN LOOP                                                             
      DO 210 J1=1,J2M  
    1 R1=drand48(RDUM)
      T=-DLOG(R1)/TCFMX+TDASH                                         
      TDASH=T                                                           
      WBT=WB*T
      COSWT=DCOS(WBT)
      SINWT=DSIN(WBT)
      DZ=(CZ1*SINWT+(EOVBR-CY1)*(1.0D0-COSWT))/WB 
      DX=CX1*T+F1*T*T                                                   
      E=E1+DZ*EFZ100+DX*EFX100           
      CX2=CX1+2.0D0*F1*T
      CY2=(CY1-EOVBR)*COSWT+CZ1*SINWT+EOVBR
      CZ2=CZ1*COSWT-(CY1-EOVBR)*SINWT
C FIND IDENTITY OF GAS FOR COLLISION
      KGAS=0
      R2=drand48(RDUM)
    3 KGAS=KGAS+1
      IF(TCFMXG(KGAS).LT.R2) GO TO 3
C CALCULATE GAS VELOCITY VECTORS VGX,VGY,VGZ
      IMBPT=IMBPT+1
      IF(IMBPT.GT.6) THEN
       CALL GERJAN
       IMBPT=1
      ENDIF
      VGX=VTMB(KGAS)*RNMX(IMBPT)
      IMBPT=IMBPT+1
      VGY=VTMB(KGAS)*RNMX(IMBPT)
      IMBPT=IMBPT+1
      VGZ=VTMB(KGAS)*RNMX(IMBPT)
C CALCULATE ENERGY WITH STATIONARY GAS TARGET
      EOK=((CX2-VGX)**2+(CY2-VGY)**2+(CZ2-VGZ)**2)/CONST10
      IE=DINT(EOK/ESTEP)+1 
      IE=DMIN0(IE,N4000) 
C                                                                       
C     TEST FOR REAL OR NULL COLLISION                                   
C                                                                       
      R5=drand48(RDUM)
      TLIM=TCF(KGAS,IE)/TCFMAX(KGAS)                                   
      IF(R5.GT.TLIM) GO TO 1 
C                                                                       
C  CALCULATE DIRECTION COSINES AT INSTANT BEFORE COLLISION
C                       
      IF(IE.EQ.4000) THEN 
C ELECTRON ENERGY OUT OF RANGE
       IELOW=1
       RETURN
      ENDIF 
      TDASH=0.0D0 
C CALCULATE DIRECTION COSINES OF ELECTRON IN 0 KELVIN FRAME
      CONST11=1.0D0/(CONST9*DSQRT(EOK))
      DXCOM=(CX2-VGX)*CONST11
      DYCOM=(CY2-VGY)*CONST11
      DZCOM=(CZ2-VGZ)*CONST11                                          
C --------------------------------------------------------------------- 
C     DETERMINATION OF REAL COLLISION TYPE                              
C --------------------------------------------------------------------- 
      R2=drand48(RDUM)  
C FIND LOCATION WITHIN 4 UNITS IN COLLISION ARRAY       
      CALL SORTT(KGAS,I,R2,IE)                                          
  140 I=I+1                                                             
      IF(CF(KGAS,IE,I).LT.R2) GO TO 140                                 
      S1=RGAS(KGAS,I)                                                   
      EI=EIN(KGAS,I)                                                    
      IF(IPN(KGAS,I).LE.0) GO TO 666                                   
      R9=drand48(RDUM) 
      EXTRA=R9*(EOK-EI)                                               
      EI=EXTRA+EI                                                       
C                                                                       
C  GENERATE SCATTERING ANGLES AND UPDATE  LABORATORY COSINES AFTER      
C   COLLISION ALSO UPDATE ENERGY OF ELECTRON.                           
C                        
  666 IPT=IARRY(KGAS,I)                                               
      IF(EOK.LT.EI) THEN
       EI=EOK-0.0001D0
      ENDIF
      S2=(S1*S1)/(S1-1.0D0)
      R3=drand48(RDUM)
      IF(INDEX(KGAS,I).EQ.1) THEN
       R31=drand48(RDUM)
       F3=1.0D0-R3*ANGCT(KGAS,IE,I)
       IF(R31.GT.PSCT(KGAS,IE,I)) F3=-F3
      ELSE IF(INDEX(KGAS,I).EQ.2) THEN
       EPSI=PSCT(KGAS,IE,I)
       F3=1.0D0-(2.0D0*R3*(1.0D0-EPSI)/(1.0D0+EPSI*(1.0D0-2.0D0*R3)))
      ELSE
       F3=1.0D0-2.0D0*R3
      ENDIF
      THETA0=DACOS(F3)                                                  
      R4=drand48(RDUM)
      PHI0=F4*R4                                                        
      F8=DSIN(PHI0)                                                     
      F9=DCOS(PHI0)                                                     
      ARG1=1.0D0-S1*EI/EOK                                              
      ARG1=DMAX1(ARG1,SMALL)                                            
      D=1.0D0-F3*DSQRT(ARG1)                                            
      E1=EOK*(1.0D0-EI/(S1*EOK)-2.0D0*D/S2)                            
      E1=DMAX1(E1,SMALL)                                                
      Q=DSQRT((EOK/E1)*ARG1)/S1                                        
      Q=DMIN1(Q,1.0D0)                                                  
      THETA=DASIN(Q*DSIN(THETA0))                                       
      F6=DCOS(THETA)                                                    
      U=(S1-1.0D0)*(S1-1.0D0)/ARG1                                      
      CSQD=F3*F3                                                        
      IF(F3.LT.0.0D0.AND.CSQD.GT.U) F6=-1.0D0*F6                        
      F5=DSIN(THETA) 
      DZCOM=DMIN1(DZCOM,1.0D0)
      ARGZ=DSQRT(DXCOM*DXCOM+DYCOM*DYCOM) 
      IF(ARGZ.EQ.0.0D0) THEN
       DCZ1=F6
       DCX1=F9*F5
       DCY1=F8*F5
       GO TO 130
      ENDIF                                          
      DCZ1=DZCOM*F6+ARGZ*F5*F8                                         
      DCY1=DYCOM*F6+(F5/ARGZ)*(DXCOM*F9-DYCOM*DZCOM*F8)                 
      DCX1=DXCOM*F6-(F5/ARGZ)*(DYCOM*F9+DXCOM*DZCOM*F8)
  130 CONTINUE
C TRANSFORM VELOCITY VECTORS TO LAB FRAME
      VTOT=CONST9*DSQRT(E1)
      CX1=DCX1*VTOT+VGX
      CY1=DCY1*VTOT+VGY
      CZ1=DCZ1*VTOT+VGZ
C CALCULATE ENERGY AND DIRECTION COSINES IN LAB FRAME
      E1=(CX1*CX1+CY1*CY1+CZ1*CZ1)/CONST10
      CONST11=1.0D0/(CONST9*DSQRT(E1))
      DCX1=CX1*CONST11
      DCY1=CY1*CONST11
      DCZ1=CZ1*CONST11    
C LOOP                                                                 
  210 CONTINUE
      IELOW=0
      RETURN                                                            
      END 
      SUBROUTINE ALPCALCT 
      IMPLICIT REAL*8 (A-H,O-Z)
      IMPLICIT INTEGER*8 (I-N)
      COMMON/INPT/NGAS,NSTEP,NANISO,EFINAL,ESTEP,AKT,ARY,TEMPC,TORR,IPEN
      COMMON/SETP/TMAX,SMALL,API,ESTART,THETA,PHI,RSTART,EFIELD,NMAX 
      COMMON/SSTOUT/VDOUT,VDERR,WSOUT,WSERR,DLOUT,DLERR,DTOUT,DTERR,
     /ALPHSST,ALPHERR,ATTSST,ATTERR         
      COMMON/TOFOUT/RALPHA,RALPER,TOFENE,TOFENER,TOFWV,TOFWVER,
     /TOFDL,TOFDLER,TOFDT,TOFDTER,TOFWR,TOFWRER,RATTOF,RATOFER 
      COMMON/VEL/WX,WY,WZ
      COMMON/CTOWNS/ALPHA,ATT
      COMMON/CTWNER/ALPER,ATTER
      COMMON/CTCALC/ZPLANE1,ZPLANE2,ZPLANE3,ZPLANE4,ZPLANE5,ZPLANE6,
     /ZPLANE7,ZPLANE8,IZFINAL
      COMMON/CION/ALPHAST,VDST,TSTEP,ZSTEP,TFINAL,ZFINAL,ITFINAL,IPRIM
      COMMON/FAKE/FAKEI,IFAKE,IFAKET(8),IFAKED(9) 
      COMMON/LARGET/CF(6,4000,290),EIN(6,290),TCF(6,4000),IARRY(6,290),
     /RGAS(6,290),IPN(6,290),WPL(6,290),LAST(6),ISIZE(6),PENFRA(6,3,290)
     /,TCFMAX(6)
C ---------------------------------------------------------------------      
C  ESTIMATE TIME STEP AND SPACE STEPS FOR AVALANCHE SIMULATION IN 
C  TIME OF FLIGHT AND STEADY STATE TOWNSEND SIMULATIONS.
C  USES ESTIMATED GAIN OF 3.0 BETWEEN PLANES. 
C  CALLS SST AND TOF SUBROUTINES AND UPDATES ALPHA AND ATT
C ------------------------------------------------------------------
      IMAX=NMAX/10000000 
      IF(IMAX.LT.5) IMAX=5 
      NMAX=IMAX*10000000
      CORR=760.0*(273.15+TEMPC)/(293.15*TORR)  
      ALPP=ALPHA/CORR
      ATTP=ATT/CORR
      ANETP=ALPP-ATTP
      ANET=ALPHA-ATT
      TCUTH=1.2D-10*CORR
      TCUTL=1.D-13*CORR
      ZCUTH=1.2D-2*CORR
      ZCUTL=1.D-5*CORR
C
      IF(ANETP.GT.30.0) THEN
       ALPHAD=0.0
       ALP1=ALPHA
       ATT1=ATT
C WEAK ATTACHMENT
       GO TO 6
      ENDIF
C SOME ATTACHMENT
      IF(DABS(ANETP).LT.100.) THEN
C SMALL NET ATTACHMENT
       ALPHAD=DABS(ATT)*0.8
      ELSE IF(DABS(ANETP).GE.100..AND.DABS(ANETP).LT.1000.) THEN
C LARGER NET ATTACHMENT
       ALPHAD=DABS(ANET)*0.65
      ELSE IF(DABS(ANETP).GE.1000..AND.DABS(ANETP).LT.10000.) THEN
       ALPHAD=DABS(ANET)*0.6
      ELSE IF(DABS(ANETP).GE.10000..AND.DABS(ANETP).LT.100000.) THEN
       ALPHAD=DABS(ANET)*0.5
      ELSE IF(DABS(ANETP).GT.100000..AND.DABS(ANETP).LT.2000000.) THEN
       ALPHAD=DABS(ANET)*0.2
      ELSE
       WRITE(6,91)
   91  FORMAT(' ATTACHMENT TOO LARGE PROGRAM STOPPED')
      ENDIF
    3 VDST=WZ*1.D-5
      FAKEI=ALPHAD*WZ*1.D-12
      ALPHAST=0.85*DABS(ALPHAD+ANET)
      TSTEP=DLOG(3.0D0)/(ALPHAST*VDST*1.D5)
      IF(TSTEP.GT.TCUTH) TSTEP=TCUTH
      IF(TSTEP.LT.TCUTL) TSTEP=TCUTL
C UPDATE NULL COLLISION FREQUENCY LIMIT
      DO 4 J=1,NGAS
    4 TCFMAX(J)=TCFMAX(J)+DABS(FAKEI)/DFLOAT(NGAS) 
      WRITE(6,871) ALPHAST,ANET,ALPHAD,TSTEP
  871 FORMAT(' ALPHAST1=',D10.3,' ANET1=',D10.3,' ALPHAD1=',D10.3,/,
     /' TSTEP1=',D10.3) 
C CONVERT TO PICOSECONDS 
      TSTEP=TSTEP*1.0D12
      TFINAL=7.0D0*TSTEP
      ITFINAL=7
C CALCULATE GOOD STARTING VALUES FOR ALPHA AND NETA
      JPRT=0
      CALL MONTEFTT(JPRT)
      CALL PT(JPRT)
      CALL TOF(JPRT)
      ALP1=RALPHA/TOFWR*1.D7
      ALP1ER=RALPER*ALP1/100.
      ATT1=RATTOF/TOFWR*1.D7
      ATT1ER=RATOFER*ATT1/100.     
      WRITE(6,944) ALP1,ALP1ER,ATT1,ATT1ER
  944 FORMAT(' GOOD STARTING VALUES FOR CALC:',/,' ALPHA=',D12.3,' ERR='
     /,D12.3,/,' NETA =',D12.3,' ERR=',D12.3)
C 
C CALCULATE USING GO0D STARTING VALUES
C RESET TCFMAX
      DO 44 J=1,NGAS
   44 TCFMAX(J)=TCFMAX(J)-DABS(FAKEI)/DFLOAT(NGAS) 
C CALCULATE FAKE IONISATION RATE SCALING BY 1.2
      ALPHAD=ATT1*1.2
      IF((ALP1-ATT1).GT.30.*CORR) ALPHAD=DABS(ATT1)*0.4
      IF(DABS(ALP1-ATT1).LT.(ALP1/10.0).OR.DABS(ALP1-ATT1).LT.
     /(ATT1/10.0)) ALPHAD=DABS(ATT1)*0.3
      IF((ALP1-ATT1).GT.100.*CORR) ALPHAD=0.0
      WZ=TOFWR*1.D5
C 
    6 VDST=WZ*1.D-5
      FAKEI=ALPHAD*WZ*1.D-12
      ALPHAST=0.85*DABS(ALPHAD+ALP1-ATT1)    
      IF((ALP1+ALPHAD).GT.(10.0*ALPHAST).OR.ATT1.GT.(10.0*ALPHAST)) THEN
C LARGE CANCELLATION BETWEEN ALPHA AND NETA  REDUCE STEP SIZE BY 15.0
       IF((ALP1+ALPHAD).GT.100.*CORR) THEN
        ALPHAST=ALPHAST*15.0
       ELSE IF((ALP1+ALPHAD).GT.50.0*CORR) THEN
        ALPHAST=ALPHAST*12.0
       ELSE 
        ALPHAST=ALPHAST*8.0
       ENDIF
      ENDIF
      TSTEP=DLOG(3.0D0)/(ALPHAST*VDST*1.0D5)
      ZSTEP=DLOG(3.0D0)/ALPHAST
      IF(TSTEP.GT.TCUTH.AND.ALPHAD.NE.0.0) TSTEP=TCUTH
C     IF(TSTEP.LT.TCUTL) TSTEP=TCUTL
      IF(ZSTEP.GT.ZCUTH.AND.ALPHAD.NE.0.0) ZSTEP=ZCUTH
C     IF(ZSTEP.LT.ZCUTL) ZSTEP=ZCUTL
C UPDATE NULL CLLISION FREQUENCY LIMIT
      DO 10 J=1,NGAS
   10 TCFMAX(J)=TCFMAX(J)+DABS(FAKEI)/DFLOAT(NGAS)
      ANET2=ALP1-ATT1
      WRITE(6,878) ALPHAST,ANET2,ALPHAD,TSTEP,ZSTEP
  878 FORMAT(' ALPHAST=',D10.3,' ANET=',D10.3,' ALPHAD=',D10.3,/,
     /' TSTEP=',D10.3,' ZSTEP=',D10.3)
C  CONVERT TO METRES AND PICOSECONDS     
      TSTEP=TSTEP*1.0D12
      ZSTEP=ZSTEP*0.01D0
      TFINAL=7.0D0*TSTEP
      ITFINAL=7
      ZFINAL=8.0D0*ZSTEP
      IZFINAL=8    
      ZPLANE1=ZSTEP
      ZPLANE2=2.0D0*ZSTEP
      ZPLANE3=3.0D0*ZSTEP
      ZPLANE4=4.0D0*ZSTEP
      ZPLANE5=5.0D0*ZSTEP
      ZPLANE6=6.0D0*ZSTEP
      ZPLANE7=7.0D0*ZSTEP
      ZPLANE8=8.0D0*ZSTEP
C CALC SST
      WRITE(6,11)
   11 FORMAT(/,2X,'SOLUTION FOR STEADY STATE TOWNSEND PARAMETERS',/,'  -
     /------------------------------------------------')        
      ZSTEPM=ZSTEP*1.0D6
      WRITE(6,12) ZSTEPM
   12 FORMAT(1(/),' SPACE STEP BETWEEN SAMPLING PLANES =',D12.5,' MICRON
     /S.',/)   
      JPRT=1
      CALL MONTEFDT
      CALL SST
C--------------------------------------------------------      
C LOAD NEW ALPHA AND ATTACHMENT INTO COMMON BLOCKS
C-----------------------------------------------
      ALPHA=ALPHSST
      ALPER=ALPHERR
      ATT=ATTSST 
      ATTER=ATTERR        
C-----------------------------------------------      
      WRITE(6,18) 
   18 FORMAT(/,' SST DRIFT VELOCITIES')   
      WRITE(6,19) VDOUT,VDERR,WSOUT,WSERR
   19 FORMAT(/,' VD=',F9.1,' +- ',F6.2,' %   WS=',F9.1,' +- ',F6.2,' %')
      WRITE(6,20)      
   20 FORMAT(/,' SST DIFFUSION')  
      WRITE(6,21) DLOUT,DLERR,DTOUT,DTERR   
   21 FORMAT(/,' DL=',F9.1,' +- ',F6.1,' %   DT=',F9.1,' +- ',F6.2,' %')
      WRITE(6,22) 
   22 FORMAT(/,' SST TOWNSEND COEFICIENTS') 
      WRITE(6,23) ALPHSST,ALPHERR,ATTSST,ATTERR 
   23 FORMAT(/,' ALPHA =',F9.1,' +- ',F6.2,' %    ATT=',F9.1,' +- ',F6.2
     /,' %')   
      CALL OUTPUT1T                                        
C CALC TIME OF FLIGHT AND PT 

  777 WRITE(6,25) 
   25 FORMAT(/,2X,'SOLUTION FOR PULSED TOWNSEND AND TIME OF FLIGHT PARAM
     /ETERS',/,'  ------------------------------------------------------
     /--------') 
      WRITE(6,26) TSTEP 
   26 FORMAT(1(/),'  TIME STEP BETWEEN SAMPLING PLANES =',D12.5,' PICOSE
     /CS.',/)     
      JPRT=1
      CALL MONTEFTT(JPRT)                             
      CALL FRIEDLANDT 
      CALL PT(JPRT) 
      CALL TOF(JPRT) 
      WRITE(6,27) RALPHA,RALPER,RATTOF,RATOFER 
   27 FORMAT(/,' PT IONISATION AND ATTACHMENT RATES *10**12/SEC',/,' ALP
     /HA=',D10.3,' +- ',F6.2,' %      ATT=',D10.3,' +- ',F6.2,' %')    
      WRITE(6,28) 
   28 FORMAT(/,' TOF DIFFUSION') 
      WRITE(6,29) TOFDL,TOFDLER,TOFDT,TOFDTER 
   29 FORMAT(/,'  DL=',F8.1,' +- ',F6.1,' %       DT=',F8.1,' +- ',F6.1,
     /' %')     
      WRITE(6,30) 
   30 FORMAT(/,' TOF DRIFT VELOCITY') 
      WRITE(6,31) TOFWR,TOFWRER   
   31 FORMAT(/,'    WR=',F8.2,' +-',F6.1,' %') 
C CALCULATE TOWNSEND SST COEFICIENTS DIRECTLY FROM TOF RESULTS
      WRZN=TOFWR*1.0D05 
      FC1=WRZN/(2.0D0*TOFDL) 
      FC2=((RALPHA-RATTOF)*1.0D12)/TOFDL 
      ALPTEST=FC1-DSQRT(FC1**2-FC2) 
      WRITE(6,888) ALPTEST 
  888 FORMAT(/,'TOWNSEND COEFICIENT (ALPHA-ATT) CALCULATED FROM TOF RESU
     /LTS:',/,' ALPHA-ATT /CM.=',D11.4,' N.B. APPROXIMATE FORMULA NOT AC
     /CURATE',/)                             
C
C CALCULATE DIRECTLY FROM TOF FREQUENCY
C     ALP1=RALPHA/TOFWR*1.D7
C     ALP1ER=RALPER*ALP1/100.
C     ATT1=RATTOF/TOFWR*1.D7
C     ATT1ER=RATOFER*ATT1/100.
C
C     WRITE(6,33) ALP1,ALP1ER,ATT1,ATT1ER
C  33 FORMAT(' TOF TOWNSEND IN UNITS OF 1/CM:',/,'   GAIN =',D12.4,' +-'
C    /,D12.4,/,' ATTACH =',D12.4,' +-',D12.4,/)  
      RETURN                                                     
      END                                                               
      SUBROUTINE MONTEFTT(JPRT)                                        
      IMPLICIT REAL*8 (A-H,O-Z)                                         
      IMPLICIT INTEGER*8 (I-N)
      COMMON/INPT/NGAS,NSTEP,NANISO,EFINAL,ESTEP,AKT,ARY,TEMPC,TORR,IPEN
      COMMON/CNSTS/ECHARG,EMASS,AMU,PIR2
      COMMON/CNSTS1/CONST1,CONST2,CONST3,CONST4,CONST5                  
      COMMON/SETP/TMAX,SMALL,API,ESTART,THETA,PHI,RSTART,EFIELD,NMAXOLD
      COMMON/LARGET/CF(6,4000,290),EIN(6,290),TCF(6,4000),IARRY(6,290),
     /RGAS(6,290),IPN(6,290),WPL(6,290),IPLAST(6),ISIZE(6),
     /PENFRA(6,3,290),TCFMAX(6)
      COMMON/LARGENT/CFN(6,4000,10),TCFN(6,4000),SCLENUL(6,10),NPLAST(6)
      COMMON/CION/ALPHAST,VDST,TSTEP,ZSTEP,TFINAL,ZFINAL,ITFINAL,IPRIM
      COMMON/OUTPTT/TIME(300),ICOLL(6,5),SPEC(4000),TMAX1,
     /AVE,DEN,XID,X,Y,Z,ST,NNULL,ICOLN(6,290),ICOLNN(6,10)
      COMMON/THRM/AMGAS(6),VTMB(6),TCFMX,TCFMXG(6),ITHRM
      COMMON/RANM/RNMX(6)
      COMMON/TTRM/ZTOT,TTOT,ZTOTS,TTOTS
      COMMON/ISPT/XS(2000),YS(2000),ZS(2000),TS(2000),ES(2000),   
     /DCX(2000),DCY(2000),DCZ(2000),IPL(2000)
      COMMON/TPLOUT/ETPL(8),XTPL(8),YTPL(8),ZTPL(8),TTPL(8),XXTPL(8),
     /YYTPL(8),ZZTPL(8),VZTPL(8),NETPL(8),ATTOINT,ATTERT,AIOERT 
      COMMON/ANIST/PSCT(6,4000,290),ANGCT(6,4000,290),INDEX(6,290),NISO 
      COMMON/IONFL/NC0(6,290),EC0(6,290),NG1(6,290),EG1(6,290),
     /NG2(6,290),EG2(6,290),WKLM(6,290),EFL(6,290)
      COMMON/FAKE/FAKEI,IFAKE,IFAKET(8),IFAKED(9) 
      DIMENSION EPRM(10000000),IESPECP(100),TEMP(6,4000)            
C -------------------------------------------------------------------   
C   CALCULATES COLLISION EVENTS AND UPDATES DIFFUSION AND VELOCITY.
C   THIS ROUTINE HANDLES TERMINATIONS AT FIXED DRIFT TIMES.   
C ------------------------------------------------------------------- 
      IF(JPRT.EQ.0) THEN
C CALCULATION OF APPROXIMATE STARTING PARAMETERS
       NMAX=NMAXOLD
       IF(NMAXOLD.GT.80000000) NMAX=80000000
      ELSE
C ENTRY FOR NORMAL CALCULATION
       NMAX=NMAXOLD
      ENDIF 
      S=0.0D0 
      ST=0.0D0 
      X=0.0D0
      Y=0.0D0
      Z=0.0D0 
      ZTOT=0.0D0
      ZTOTS=0.0D0
      TTOT=0.0D0
      TTOTS=0.0D0
      SMALL=1.0D-20                                                     
      TMAX1=0.0D0                                                       
      RDUM=RSTART                                                       
      E1=ESTART                                                         
      CONST9=CONST3*0.01D0
      CONST10=CONST9*CONST9
      API=DACOS(-1.0D0)
      DO 25 I=1,300
   25 TIME(I)=0.0D0
      DO 26 I=1,5
      DO 26 K=1,6
   26 ICOLL(K,I)=0
      DO 27 I=1,290
      DO 27 K=1,6
   27 ICOLN(K,I)=0
      DO 271 I=1,10
      DO 271 K=1,6
  271 ICOLNN(K,I)=0
      DO 28 I=1,4000
   28 SPEC(I)=0.0D0            
      DO 33 I=1,100
   33 IESPECP(I)=0 
      DO 34 I=1,8
      ETPL(I)=0.0D0
      XTPL(I)=0.0D0
      YTPL(I)=0.0D0
      ZTPL(I)=0.0D0
      TTPL(I)=0.0D0
      XXTPL(I)=0.0D0
      YYTPL(I)=0.0D0
      ZZTPL(I)=0.0D0
      VZTPL(I)=0.0D0
   34 NETPL(I)=0                                   
      ID=0
      N4000=4000
      N300=300
      N100=100
      I100=0                                                            
      NCOL=0                                                            
      NNULL=0
      NELEC=0
      NEION=0
      NMXADD=0
      NTMPFLG=0
      NPONT=0 
      NCLUS=0
      J1=1 
      ZSTRT=0.0D0
      TSSTRT=0.0D0 
C 
      DO 111 K=1,6
      DO 111 J=1,N4000
  111 TEMP(K,J)=TCF(K,J)+TCFN(K,J)
      ABSFAKEI=DABS(FAKEI) 
      IFAKE=0
      DO 112 J=1,8
  112 IFAKET(J)=0
C                                  
C     INITIAL DIRECTION COSINES                                         
      DCZ1=DCOS(THETA)                                                  
      DCX1=DSIN(THETA)*DCOS(PHI)                                        
      DCY1=DSIN(THETA)*DSIN(PHI) 
      E100=E1
      DCZ100=DCZ1
      DCX100=DCX1
      DCY100=DCY1                                       
      BP=EFIELD*EFIELD*CONST1                                           
      F1=EFIELD*CONST2                                                  
      F2=EFIELD*CONST3                                                  
      F4=2.0D0*API
C GENERATE INITIAL RANDOM NUMBERS FOR MAXWELL BOLTZMAN 
      CALL GERJAN
      IMBPT=0
C
      JPRINT=NMAX/10
      IPRINT=0
      ITER=0
      IPLANE=0
      IPRIM=0
C  LOOP FOR NEW STARTING ELECTRONS 
  544 IPRIM=IPRIM+1 
      IF(IPRIM.GT.1) THEN
C CHECK IF PROGRAM WILL EXCEED MAXIMUM NUMBER OF ITERATIONS 
C IN THIS CYCLE IF SO OUTPUT CURRENT RESULTS. 
       IF(ITER.GT.NMAX) THEN
        IPRIM=IPRIM-1
        GO TO 700      
       ENDIF 
       X=0.0D0
       Y=0.0D0
       Z=0.0D0
       DCZ1=DCZ100
       DCX1=DCX100
       DCY1=DCY100
       E1=E100
       NCLUS=NCLUS+1
       ST=0.0D0
       TSSTRT=0.0D0
       ZSTRT=0.0D0
       IPLANE=0
      ENDIF
      IF(IPRIM.GT.10000000) THEN
      WRITE(6,944) IPRIM
 944  FORMAT(/,2X,'PROGRAM STOPPED TOO MANY PRIMARIES IPRIM =',I7)
      GO TO 700
      ENDIF
      EPRM(IPRIM)=E1
      IDUM=DINT(E1)+1
      IDUM=DMIN0(IDUM,N100)
      IESPECP(IDUM)=IESPECP(IDUM)+1      
C START OF LOOP FOR NEWLY CREATED ELECTRONS
  555 TDASH=0.0D0
      NELEC=NELEC+1
      TSTOP=TSTEP+IPLANE*TSTEP       
C MAIN LOOP                                                             
    1 CONTINUE                                                          
C     IF(ITER.GT.NMAX)  GO TO 315
      R1=drand48(RDUM)
      T=-DLOG(R1)/TCFMX+TDASH
      TDASH=T
      AP=DCZ1*F2*DSQRT(E1)
  15  IF((T+ST).GE.TSTOP) THEN                     
       IPLANE=IPLANE+1
       TSTOP=TSTOP+TSTEP
C STORE POSITION AND ENERGY AT TIME PLANE =IPLANE. 
       CALL TPLANET(T,E1,DCX1,DCY1,DCZ1,AP,BP,EFIELD,IPLANE)
C CHECK IF PASSED THROUGH MORE THAN ONE PLANE IN THIS STEP       
       IF((T+ST).GE.TSTOP.AND.TSTOP.LE.TFINAL) GO TO 15
       IF((T+ST).GE.TFINAL) THEN
        ZTOT=ZTOT+Z     
        TTOT=TTOT+ST
        ZTOTS=ZTOTS+Z-ZSTRT      
        TTOTS=TTOTS+ST-TSSTRT    
        TSTOP=TSTEP
C NO MORE ELECTRONS IN CASCADE TRY NEW PRIMARY ELECTRON
        IF(NELEC.EQ.(NCLUS+1)) GO TO 544  
C TAKE ELECTRONS FROM STORE 
   20   X=XS(NPONT) 
        Y=YS(NPONT) 
        Z=ZS(NPONT) 
        ST=TS(NPONT) 
        E1=ES(NPONT) 
        DCX1=DCX(NPONT) 
        DCY1=DCY(NPONT) 
        DCZ1=DCZ(NPONT) 
        IPLANE=IPL(NPONT)   
        NPONT=NPONT-1 
        ZSTRT=Z 
        TSSTRT=ST
        GO TO 555
       ENDIF              
      ENDIF
 913  FORMAT(3X,' AFTER STORE ITER=',I10,' E1=',D12.3,' T=',D12.3,' AP='
     /,D12.3,' BP=',D12.3,' DCZ1=',D12.3)       
      E=E1+(AP+BP*T)*T
      IF(E.LT.0.0D0) THEN
       WRITE(6,913)ITER,E,E1,AP,BP,DCZ1 
       E=0.001D0
      ENDIF  
C FIND IDENTITY OF GAS FOR COLLISION
      KGAS=0
      IF(NGAS.EQ.1) THEN
       R2=drand48(RDUM)
       KGAS=1
       GO TO 4
      ENDIF
      R2=drand48(RDUM)
   21 KGAS=KGAS+1
      IF(TCFMXG(KGAS).LT.R2) GO TO 21 
C CALC GAS VELOCITY VECTORS VGX,VGY,VGZ
    4 IMBPT=IMBPT+1
      IF(IMBPT.GT.6) THEN
       CALL GERJAN
       IMBPT=1
      ENDIF
      VGX=VTMB(KGAS)*RNMX(IMBPT)
      IMBPT=IMBPT+1
      VGY=VTMB(KGAS)*RNMX(IMBPT)
      IMBPT=IMBPT+1
      VGZ=VTMB(KGAS)*RNMX(IMBPT)
C CALCULATE ELECTRON VELOCITY VECTORS VEX,VEY,VEZ BEFORE COLLISION
      CONST6=DSQRT(E1/E)
      DCX2=DCX1*CONST6
      DCY2=DCY1*CONST6
      DCZ2=DCZ1*CONST6+EFIELD*T*CONST5/DSQRT(E)
      VEX=DCX2*CONST9*DSQRT(E)
      VEY=DCY2*CONST9*DSQRT(E)
      VEZ=DCZ2*CONST9*DSQRT(E)
C CALCULATE ENERGY WITH STATIONARY GAS TARGET
      EOK=((VEX-VGX)**2+(VEY-VGY)**2+(VEZ-VGZ)**2)/CONST10
      IE=DINT(EOK/ESTEP)+1                                             
      IE=DMIN0(IE,N4000)                                              
C                                                    I                   
C     TEST FOR REAL OR NULL COLLISION  
C                                 
      R5=drand48(RDUM)                                                  
      TEST1=TCF(KGAS,IE)/TCFMAX(KGAS)                                  
      IF(R5.LE.TEST1) GO TO 137                                         
      NNULL=NNULL+1
      TEST2=TEMP(KGAS,IE)/TCFMAX(KGAS)
      IF(R5.LT.TEST2) THEN
C TEST FOR NULL LEVELS
       IF(NPLAST(KGAS).EQ.0) GO TO 1
       R2=drand48(RDUM)
       I=0
  888  I=I+1
       IF(CFN(KGAS,IE,I).LT.R2) GO TO 888
C INCREMENT NULL SCATTER SUM
       ICOLNN(KGAS,I)=ICOLNN(KGAS,I)+1
       GO TO 1
      ENDIF                               
      TEST3=(TEMP(KGAS,IE)+ABSFAKEI)/TCFMAX(KGAS)
      IF(R5.LT.TEST3) THEN
C FAKE COLLISION INCREMENT COUNTERS
       IFAKE=IFAKE+1
       IFAKET(IPLANE+1)=IFAKET(IPLANE+1)+1 
       IF(FAKEI.LT.0.0) THEN
        NEION=NEION+1
C FAKE ATTACHMENT START NEW ELECTRON
        IF(NELEC.EQ.(NCLUS+1)) GO TO 544
C ELECTRON CAPTURED TAKE NEXT ELECTRON FROM STORE
        GO TO 20
       ENDIF
C FAKE IONISATION ADD ELECTRON TO STORE
       NCLUS=NCLUS+1
       NPONT=NPONT+1
       NMXADD=MAX(NPONT,NMXADD)
       IF(NPONT.GT.2000) THEN 
        WRITE(6,546) NPONT,ITER
        STOP
       ENDIF     
       A=T*CONST9*DSQRT(E1)
       XS(NPONT)=X+DCX1*A       
       YS(NPONT)=Y+DCY1*A
       ZS(NPONT)=Z+DCZ1*A+T*T*F1
       TS(NPONT)=ST+T
       ES(NPONT)=E
       IPL(NPONT)=IPLANE
       DCX(NPONT)=DCX2
       DCY(NPONT)=DCY2
       DCZ(NPONT)=DCZ2
       GO TO 1
      ENDIF                      
      GO TO 1 
  137 NCOL=NCOL+1 
C CALCULATE DIRECTION COSINES OF ELECTRON IN 0 KELVIN FRAME
      CONST11=1.0D0/(CONST9*DSQRT(EOK)) 
      DXCOM=(VEX-VGX)*CONST11
      DYCOM=(VEY-VGY)*CONST11
      DZCOM=(VEZ-VGZ)*CONST11       
C                                                                       
C  CALCULATE POSITIONS AT INSTANT BEFORE COLLISION
C    ALSO UPDATE DIFFUSION  AND ENERGY CALCULATIONS.                    
      T2=T*T
      IF(T.GE.TMAX1) TMAX1=T
      TDASH=0.0D0                                                       
      CONST7=CONST9*DSQRT(E1)                                           
      A=T*CONST7                                                        
      X=X+DCX1*A                                                        
      Y=Y+DCY1*A
      Z=Z+DCZ1*A+T2*F1
      ST=ST+T
      IT=DINT(T+1.0D0)                                               
      IT=DMIN0(IT,N300)                                                
      TIME(IT)=TIME(IT)+1.0D0
C ENERGY SPECTRUM FOR 0 KELVIN FRAME                                       
      SPEC(IE)=SPEC(IE)+1.0D0
C --------------------------------------------------------------------- 
C     DETERMINATION OF REAL COLLISION TYPE                              
C --------------------------------------------------------------------- 
      R2=drand48(RDUM)
C FIND LOCATION WITHIN 4 UNITS IN COLLISION ARRAY       
      CALL SORTT(KGAS,I,R2,IE)                                          
  140 I=I+1                                                             
      IF(CF(KGAS,IE,I).LT.R2) GO TO 140                                
      S1=RGAS(KGAS,I)                                                 
      EI=EIN(KGAS,I)
      IF(EOK.LT.EI) THEN
       EI=EOK-0.0001D0
      ENDIF                                                          
      IF(IPN(KGAS,I).EQ.0) GO TO 666
C ATTACHMENT       
      IF(IPN(KGAS,I).EQ.-1) THEN
       NEION=NEION+1
       IPT=IARRY(KGAS,I)
       ID=ID+1
       ITER=ITER+1
       IPRINT=IPRINT+1
       ICOLL(KGAS,IPT)=ICOLL(KGAS,IPT)+1
       ICOLN(KGAS,I)=ICOLN(KGAS,I)+1 
       IT=DINT(T+1.0D0)
       IT=DMIN0(IT,N300)
       TIME(IT)=TIME(IT)+1.0D0
       ZTOT=ZTOT+Z
       TTOT=TTOT+ST
       ZTOTS=ZTOTS+Z-ZSTRT
       TTOTS=TTOTS+ST-TSSTRT
C  ELECTRON CAPTURED START NEW PRIMARY          
       IF(NELEC.EQ.(NCLUS+1)) GO TO 544
C ELECTRON CAPTURED TAKE NEXT ELECTRON FROM STORE       
       GO TO 20
      ENDIF    
      EISTR=EI                                             
      R9=drand48(RDUM)
C     ESEC=R9*(E-EI)      
C    USE OPAL PETERSON AND BEATY SPLITTING FACTOR.
      ESEC=WPL(KGAS,I)*TAN(R9*ATAN((EOK-EI)/(2.0D0*WPL(KGAS,I)))) 
      ESEC=WPL(KGAS,I)*(ESEC/WPL(KGAS,I))**0.9524     
      EI=ESEC+EI 
C STORE POSITION ,ENERGY, DIRECTION COSINES AND TIME OF GENERATION
C OF IONISATION ELECTRON      
      NCLUS=NCLUS+1
      NPONT=NPONT+1
      NMXADD=MAX(NPONT,NMXADD)
      IF(NPONT.GT.2000) THEN 
      WRITE(6,546) NPONT,ITER
 546  FORMAT(2X,' PROGRAM STOPPED . NPONT=',I5,' ITER=',I10)
      STOP
      ENDIF     
      XS(NPONT)=X       
      YS(NPONT)=Y
      ZS(NPONT)=Z
      TS(NPONT)=ST
      ES(NPONT)=ESEC
      NTMPFLG=1
      NCLTMP=NPONT
      IPL(NPONT)=IPLANE
C RANDOMISE SECONDARY ELECTRON DIRECTION
C      R3=drand48(RDUM)
C      F3=1.0D0-2.0D0*R3
C      THETA0=DACOS(F3)
C      F6=DCOS(THETA0)
C      F5=DSIN(THETA0)
C      R4=drand48(rdum)
C      PHI0=F4*R4
C      F8=DSIN(PHI0)
C      F9=DCOS(PHI0)               
C      DCX(NPONT)=F9*F5
C      DCY(NPONT)=F8*F5
C      DCZ(NPONT)=F6     
C      IPL(NPONT)=IPLANE 
C                                                                       
C  GENERATE SCATTERING ANGLES AND UPDATE  LABORATORY COSINES AFTER      
C   COLLISION ALSO UPDATE ENERGY OF ELECTRON.                           
C
C STORE POSIBLE SHELL EMISSIONS AUGER OR FLUORESCENCE
      IF(EISTR.GT.30.0) THEN
C AUGER EMISSION WITHOUT FLUORESCENCE
       NAUG=NC0(KGAS,I)
       EAVAUG=EC0(KGAS,I)/DFLOAT(NAUG)
       DO 701 JFL=1,NAUG
       NCLUS=NCLUS+1
       NPONT=NPONT+1
       XS(NPONT)=X
       YS(NPONT)=Y
       ZS(NPONT)=Z
       TS(NPONT)=ST
       ES(NPONT)=EAVAUG
       R3=drand48(RDUM)
       F3=1.0D0-2.0D0*R3
       THETA0=DACOS(F3)
       F6=DCOS(THETA0)
       F5=DSIN(THETA0)
       R4=drand48(RDUM)
       PHI0=F4*R4
       F8=DSIN(PHI0)
       F9=DCOS(PHI0)
       DCX(NPONT)=F9*F5
       DCY(NPONT)=F8*F5
       DCZ(NPONT)=F6
       IPL(NPONT)=IPLANE
 701   CONTINUE
      ENDIF
  666 IPT=IARRY(KGAS,I)
      ID=ID+1    
      ITER=ITER+1
      IPRINT=IPRINT+1                                                   
      ICOLL(KGAS,IPT)=ICOLL(KGAS,IPT)+1 
      ICOLN(KGAS,I)=ICOLN(KGAS,I)+1 
C IF EXCITATION THEN ADD PROBABILITY ,PENFRA(KGAS,1,I), OF TRANSFER TO
C  GIVE IONISATION OF THE OTHER GASES IN THE MIXTURE.
      IF(IPEN.EQ.0) GO TO 5
      IF(PENFRA(KGAS,1,I).NE.0.0) THEN
       RAN=drand48(RDUM)
       IF(RAN.GT.PENFRA(KGAS,1,I)) GO TO 5
       NCLUS=NCLUS+1
       NPONT=NPONT+1
       IF(NPONT.GT.2000) THEN 
        WRITE(6,546) NPONT,ITER
        STOP
       ENDIF
C ENTER HERE POSSIBLE DELOCALISATION LENGTH FOR PENNING TRANSFER
       IF(PENFRA(KGAS,2,I).EQ.0.0) THEN
        XS(NPONT)=X
        YS(NPONT)=Y
        ZS(NPONT)=Z
        GO TO 667
       ENDIF
       ASIGN=1.0
       RAN=drand48(RDUM)
       RAN1=drand48(RDUM)
       IF(RAN1.LT.0.5) ASIGN=-ASIGN
       XS(NPONT)=X-DLOG(RAN)*PENFRA(KGAS,2,I)*ASIGN
       RAN=drand48(RDUM)
       RAN1=drand48(RDUM)
       IF(RAN1.LT.0.5) ASIGN=-ASIGN
       YS(NPONT)=Y-DLOG(RAN)*PENFRA(KGAS,2,I)*ASIGN
       RAN=drand48(RDUM)
       RAN1=drand48(RDUM)
       IF(RAN1.LT.0.5) ASIGN=-ASIGN
       ZS(NPONT)=Z-DLOG(RAN)*PENFRA(KGAS,2,I)*ASIGN
C POSSIBLE PENNING TRANSFER TIME
  667  TPEN=ST
       IF(PENFRA(KGAS,3,I).EQ.0.0) GO TO 668
       RAN=drand48(RDUM)
       TPEN=ST-DLOG(RAN)*PENFRA(KGAS,3,I)
  668  TS(NPONT)=TPEN                 
       ES(NPONT)=1.0
       DCX(NPONT)=DCX1
       DCY(NPONT)=DCY1
       DCZ(NPONT)=DCZ1
C FIND LAST TIME PLANE BEFORE TPEN 
       TSTOP1=0.0
       IPLANE1=0  
       DO 669 KDUM=1,ITFINAL
       TSTOP1=TSTEP+TSTOP1
       IF(TPEN.LT.TSTOP1) GO TO 670
       IPLANE1=IPLANE1+1  
  669  CONTINUE
C PENNING TRANSFER OCCURS AFTER FINAL TIME PLANE CLEAR ENTRY
       NPONT=NPONT-1
       NCLUS=NCLUS-1
       GO TO 5
  670  IPL(NPONT)=IPLANE1
      ENDIF                                            
    5 S2=(S1*S1)/(S1-1.0D0) 
C ANISOTROPIC SCATTERING
      R3=drand48(RDUM)
      IF(INDEX(KGAS,I).EQ.1) THEN
       R31=drand48(RDUM)
       F3=1.0D0-R3*ANGCT(KGAS,IE,I)
       IF(R31.GT.PSCT(KGAS,IE,I)) F3=-F3
      ELSE IF(INDEX(KGAS,I).EQ.2) THEN
       EPSI=PSCT(KGAS,IE,I)
       F3=1.0D0-(2.0D0*R3*(1.0D0-EPSI)/(1.0D0+EPSI*(1.0D0-2.0D0*R3)))
      ELSE
C ISOTROPIC SCATTERING                                              
       F3=1.0D0-2.0D0*R3  
      ENDIF
      THETA0=DACOS(F3)                                                  
      R4=drand48(RDUM)
      PHI0=F4*R4                                                        
      F8=DSIN(PHI0)                                                     
      F9=DCOS(PHI0)                                                     
      IF(EOK.LT.EI) EI=EOK-0.0001D0                              
CC    IF(EOK.LT.EI) EI=0.0D0                                     
      ARG1=1.0D0-S1*EI/EOK                                            
      ARG1=DMAX1(ARG1,SMALL)                                            
      D=1.0D0-F3*DSQRT(ARG1)                                            
      E1=EOK*(1.0D0-EI/(S1*EOK)-2.0D0*D/S2) 
      E1=DMAX1(E1,SMALL)                                                
      Q=DSQRT((EOK/E1)*ARG1)/S1                                        
      Q=DMIN1(Q,1.0D0)                                                  
      THETA=DASIN(Q*DSIN(THETA0))                                       
      F6=DCOS(THETA)                                                    
      U=(S1-1.0D0)*(S1-1.0D0)/ARG1                                      
      CSQD=F3*F3                                                        
      IF(F3.LT.0.0D0.AND.CSQD.GT.U) F6=-1.0D0*F6                        
      F5=DSIN(THETA)                                                    
      DZCOM=DMIN1(DZCOM,1.0D0)                                         
      ARGZ=DSQRT(DXCOM*DXCOM+DYCOM*DYCOM)
      IF(ARGZ.EQ.0.0D0) THEN
       DCZ1=F6         
       DCX1=F9*F5                             
       DCY1=F8*F5 
C USE FREE KINEMATICS FOR IONISATION SECONDARY ANGLE 
       IF(NTMPFLG.EQ.1) THEN
        F5S=F5*DSQRT(E1/ES(NCLTMP))
        IF(F5S.GT.1.0) F5S=1.0
        THSEC=DASIN(F5S)
        F5S=DSIN(THSEC)
        F6S=DCOS(THSEC)
        IF(F6.LT.0.0) F6S=-F6S
        PHIS=PHI0+API
        IF(PHIS.GT.F4) PHIS=PHI0-F4
        F8S=DSIN(PHIS)
        F9S=DCOS(PHIS)
        DCZ(NCLTMP)=F6S
        DCX(NCLTMP)=F9S*F5S
        DCY(NCLTMP)=F8S*F5S
        NTMPFLG=0
       ENDIF
       GO TO 190
      ENDIF                                            
      DCZ1=DZCOM*F6+ARGZ*F5*F8                                         
      DCY1=DYCOM*F6+(F5/ARGZ)*(DXCOM*F9-DYCOM*DZCOM*F8)                 
      DCX1=DXCOM*F6-(F5/ARGZ)*(DYCOM*F9+DXCOM*DZCOM*F8) 
      IF(NTMPFLG.EQ.1) THEN
C USE FREE KINEMATICS FOR IONISATION SECONDARY ANGLE
       F5S=F5*DSQRT(E1/ES(NCLTMP))
       IF(F5S.GT.1.0) F5S=1.0
       THSEC=DASIN(F5S)
       F5S=DSIN(THSEC)
       F6S=DCOS(THSEC)
       IF(F6.LT.0.0) F6S=-F6S
       PHIS=PHI0+API
       IF(PHIS.GT.F4) PHIS=PHI0-F4
       F8S=DSIN(PHIS)
       F9S=DCOS(PHIS)
       DCZ(NCLTMP)=DZCOM*F6S+ARGZ*F5S*F8S
       DCY(NCLTMP)=DYCOM*F6S+(F5S/ARGZ)*(DXCOM*F9S-DYCOM*DZCOM*F8S)
       DCX(NCLTMP)=DXCOM*F6S-(F5S/ARGZ)*(DYCOM*F9S+DXCOM*DZCOM*F8S)
       NTMPFLG=0
      ENDIF 
  190 CONTINUE  
C TRANSFORM VELOCITY VECTORS TO LAB FRAME
      CONST12=CONST9*DSQRT(E1)
      VXLAB=DCX1*CONST12+VGX
      VYLAB=DCY1*CONST12+VGY
      VZLAB=DCZ1*CONST12+VGZ
C CALCULATE ENERGY AND DIRECTION COSINES IN LAB FRAME
      E1=(VXLAB*VXLAB+VYLAB*VYLAB+VZLAB*VZLAB)/CONST10
      CONST11=1.0D0/(CONST9*DSQRT(E1))
      DCX1=VXLAB*CONST11
      DCY1=VYLAB*CONST11
      DCZ1=VZLAB*CONST11 
C STORE DIRECTION COSINES AND ENERGY AFTER N COLLISIONS
C FOR LATER REUSE IN PRIMARY GENERATION
      I100=I100+1
      IF(I100.EQ.200) THEN
       DCZ100=DCZ1
       DCX100=DCX1        
       DCY100=DCY1 
       E100=E1
       I100=0
      ENDIF  
      IF(IPRINT.GT.JPRINT) GO TO 200
      GO TO 1                                    
C    INTERMEDIATE PRINTOUT                       
 200  IPRINT=0 
      W=ZTOTS/TTOTS                          
      W=W*1.0D+09                                                       
      JCT=ID/100000
C     IF(J1.EQ.1) WRITE(6,201)                                         
C 201 FORMAT(/,7X,'INTERMEDIATE OUTPUT',/,'    VEL      POS        TIME 
C    /       COUNT    ')                  
C     WRITE(6,202) W,ZTOTS,TTOTS,JCT   
C 202 FORMAT(1X,F8.3,2(1X,D10.3),4X,I6)                 
      J1=J1+1            
      GO TO 1
C  MAIN LOOP END                                                  
  700 XID=DFLOAT(ID)
      IF(NELEC.GT.IPRIM) THEN
       ANEION=DFLOAT(NEION)
       ANBT=DFLOAT(NELEC-IPRIM)
       ATTOINT=ANEION/ANBT
       ATTERT=DSQRT(ANEION)/ANEION
       AIOERT=DSQRT(ANBT)/ANBT
      ELSE
       ANEION=DFLOAT(NEION) 
       ATTOINT=-1.0
       ATTERT=DSQRT(ANEION)/ANEION
      ENDIF
      JCT=ID/100000  
      IF(J1.EQ.1) THEN
      WRITE(6,940) NCLUS,ITER
  940 FORMAT(2(/),' PROGRAM STOPPED  (TOO FEW COLLISIONS), DECREASE THE 
     /ESTIMATED ALPHA.  NCLUS = ',I7,'  ITER =',I9)      
      STOP
      ENDIF
      IF(JPRT.EQ.1) WRITE(6,878) NELEC,NEION,IPRIM
 878  FORMAT(/,' TOTAL NO OF ELECTRONS=',I10,/,' TOTAL NO OF NEG. IONS='
     /,I10,/,' TOTAL NO OF PRIMARIES=',I10)  
      EPRMBAR=0.0D0
      E2PRM=0.0D0
      IF(IPRIM.EQ.1) RETURN      
      DO 310 I=1,IPRIM
      E2PRM=E2PRM+EPRM(I)*EPRM(I)
 310  EPRMBAR=EPRMBAR+EPRM(I)
      EBAR=EPRMBAR/IPRIM
      EERR=DSQRT(E2PRM/IPRIM-EBAR**2)
      IF(JPRT.EQ.1) WRITE(6,836) EBAR,EERR
 836  FORMAT(/,2X,'AVERAGE ENERGY OF PRIMARY ELECTRON =',F10.3,' EV.',/,
     /'   ENERGY SPREAD OF PRIMARY ELECTRON =',F10.3,' EV.')
C     WRITE(6,835) (IESPECP(J),J=1,100)
C835  FORMAT(/,2X,'ENERGY SPECTRUM OF PRIMARY ELECTRONS IN 1 EV. BINS',/
C    /,10(2X,10I5,/))
      RETURN               
  315 IF(ITER.GT.NMAX) THEN
       WRITE(6,991) ITER,NMAX,NPONT,NELEC,IPRIM,NMXADD
 991   FORMAT(2(/),' PROGRAM STOPPED.  ITER =',I10,'    NMAX =',I10,/,
     /' NPONT=',I4,' NELEC=',I8,' IPRIM=',I4,' NMXADD=',I3)
       STOP
      ENDIF                                                             
      RETURN                                                            
      END
      SUBROUTINE TPLANET(T,E1,DCX1,DCY1,DCZ1,AP,BP,EFLD,IPLANE)
      IMPLICIT REAL*8 (A-H,O-Z)  
      IMPLICIT INTEGER*8 (I-N)
      COMMON/CNSTS1/CONST1,CONST2,CONST3,CONST4,CONST5
      COMMON/CION/ALPHAST,VDST,TSTEP,ZSTEP,TFINAL,ZFINAL,ITFINAL,IPRIM
      COMMON/OUTPTT/TIME(300),ICOLL(6,5),SPEC(4000),TMAX1,
     /AVE,DEN,XID,X,Y,Z,ST,NNULL,ICOLN(6,290),ICOLNN(6,10)
      COMMON/TPLOUT/ETPL(8),XTPL(8),YTPL(8),ZTPL(8),TTPL(8),XXTPL(8),
     /YYTPL(8),ZZTPL(8),VZTPL(8),NETPL(8),ATTOINT,ATTERT,AIOERT 
C-----------------------------------------------------------------------
C STORES POSITION, TIME AND ENERGY AND SUMS REQUIRED
C TO CALCULATE DEVIATIONS AND MEANS AT PLANE =IPLANE 
C-----------------------------------------------------------------------
      TIMESP=IPLANE*TSTEP
C CALC TIME LEFT TO ARRIVE AT PLANE
      TIMLFT=TIMESP-ST
      T2LFT=TIMLFT*TIMLFT
      A=AP*TIMLFT
      B=BP*T2LFT 
      EPLANE=E1+A+B                      
      CONST6=DSQRT(E1/EPLANE)         
C     DCX2=DCX1*CONST6
C     DCY2=DCY1*CONST6
      DCZ2=DCZ1*CONST6+EFLD*TIMLFT*CONST5/DSQRT(EPLANE)
      XPLANE=X+DCX1*TIMLFT*DSQRT(E1)*CONST3*0.01D0
      YPLANE=Y+DCY1*TIMLFT*DSQRT(E1)*CONST3*0.01D0
      ZPLANE=Z+DCZ1*TIMLFT*DSQRT(E1)*CONST3*0.01D0+T2LFT*EFLD*CONST2
      VZPLANE=DCZ2*DSQRT(EPLANE)*CONST3*0.01D0
      XTPL(IPLANE)=XTPL(IPLANE)+XPLANE
      YTPL(IPLANE)=YTPL(IPLANE)+YPLANE
      ZTPL(IPLANE)=ZTPL(IPLANE)+ZPLANE
      XXTPL(IPLANE)=XXTPL(IPLANE)+XPLANE*XPLANE
      YYTPL(IPLANE)=YYTPL(IPLANE)+YPLANE*YPLANE
      ZZTPL(IPLANE)=ZZTPL(IPLANE)+ZPLANE*ZPLANE
      ETPL(IPLANE)=ETPL(IPLANE)+EPLANE
      TTPL(IPLANE)=TTPL(IPLANE)+ST+TIMLFT
      VZTPL(IPLANE)=VZTPL(IPLANE)+VZPLANE
      NETPL(IPLANE)=NETPL(IPLANE)+1 
      RETURN
      END
      SUBROUTINE FRIEDLANDT
      IMPLICIT REAL*8 (A-H,O-Z)
      IMPLICIT INTEGER*8 (I-N)
      COMMON/INPT/NGAS,NSTEP,NANISO,EFINAL,ESTEP,AKT,ARY,TEMPC,TORR,IPEN
      COMMON/FRED/FCION(4000),FCATT(4000)                               
      COMMON/OUTPTT/TIME(300),ICOLL(6,5),SPEC(4000),TMAX1,
     /AVE,DEN,XID,X,Y,Z,ST,NNULL,ICOLN(6,290),ICOLNN(6,10)
      COMMON/CNSTS1/CONST1,CONST2,CONST3,CONST4,CONST5
      COMMON/TTRM/ZTOT,TTOT,ZTOTS,TTOTS 
      COMMON/LARGET/CF(6,4000,290),EIN(6,290),TCF(6,4000),IARRY(6,290),
     /RGAS(6,290),IPN(6,290),WPL(6,290),LAST(6),ISIZE(6),PENFRA(6,3,290)
     /,TCFMAX(6)
      COMMON/MIX2/E(4000),EROOT(4000),QTOT(4000),QREL(4000),QINEL(4000),
     /QEL(4000) 
      DIMENSION FR(4000)                                              
C -------------------------------------------------------
C CALCULATE DISTRIBUTION FUNCTION USING FRIEDLAND TECHNIQUE
C CF: J.FRIEDLAND  PHYSICS OF FLUIDS 20(1461)1977
C  USE DITRIBUTION FUNCTION TO CALCULATE AVERAGE ENERGY
C  IONISATION RATE AND ATTACHMENT RATE.  
C-----------------------------------------------------  
      ALFBAR=0.0D0
      ATTBAR=0.0D0
      EBAR=0.0D0
      FSUM=0.0D0
      DO 100 I=1,4000
      TCFSUM=0.0D0
      DO 10 KGAS=1,NGAS
   10 TCFSUM=TCFSUM+TCF(KGAS,I)
      FR(I)=SPEC(I)/TCFSUM
      EBAR=EBAR+E(I)*SPEC(I)/TCFSUM
      ALFBAR=ALFBAR+FCION(I)*SPEC(I)/TCFSUM
      ATTBAR=ATTBAR+FCATT(I)*SPEC(I)/TCFSUM
  100 FSUM=FSUM+FR(I)
      DO 200 I=1,4000
 200  FR(I)=FR(I)/FSUM
      EBAR=EBAR/TTOTS
      ALFBAR=ALFBAR/TTOTS           
      ATTBAR=ATTBAR/TTOTS
      WRITE(6,900) EBAR,ALFBAR,ATTBAR    
 900  FORMAT(2(/),' ESTIMATE USING FRIEDLAND :',/,' AVERAGE ENERGY =',F8
     /.3,'EV.',/,' AVERAGE IONISATION =',E11.4,' *10**12/SEC',/,' AVERAG
     /E ATTACHMENT =',E11.4,' *10**12/SEC') 
      RETURN   
      END
      SUBROUTINE PT(JPRT)
      IMPLICIT REAL*8 (A-H,O-Z)                                         
      IMPLICIT INTEGER*8 (I-N)
      COMMON/CION/ALPHAST,VDST,TSTEP,ZSTEP,TFINAL,ZFINAL,ITFINAL,IPRIM
      COMMON/TPLOUT/ETPL(8),XTPL(8),YTPL(8),ZTPL(8),TTPL(8),XXTPL(8),
     /YYTPL(8),ZZTPL(8),VZTPL(8),NETPL(8),ATTOINT,ATTERT,AIOERT
      COMMON/PTTOF/RI(8),EPT(8),VZPT(8),TTEST(8)
      COMMON/FAKE/FAKEI,IFAKE,IFAKET(8),IFAKED(9) 
      DIMENSION ANTPL(8)
C ------------------------------------------------
C CALCULATES PULSED TOWNSEND COEFFICIENTS
C-------------------------------------------------
      ANTPL(1)=DFLOAT(NETPL(1))
      RI(1)=(DLOG(ANTPL(1))-DLOG(DFLOAT(IPRIM)))/TSTEP
      RI(1)=RI(1)-FAKEI
      EPT(1)=ETPL(1)/ANTPL(1)
      TTEST(1)=TTPL(1)/ANTPL(1)
      VZPT(1)=1.0D+09*VZTPL(1)/ANTPL(1)
      DO 10 I=2,ITFINAL
      IF(NETPL(I).EQ.0) THEN
       ITFINAL=I-1
       GO TO 11
      ENDIF      
      ANTPL(I)=DFLOAT(NETPL(I))
      RI(I)=(DLOG(ANTPL(I))-DLOG(ANTPL(I-1)))/TSTEP
      RI(I)=RI(I)-FAKEI
      EPT(I)=ETPL(I)/ANTPL(I)
      TTEST(I)=TTPL(I)/ANTPL(I)
      VZPT(I)=1.0D+09*VZTPL(I)/ANTPL(I)
  10  CONTINUE
  11  IF(JPRT.EQ.1) WRITE(6,900) ITFINAL
 900  FORMAT(2(/),' PULSED TOWNSEND RESULTS AT',I2,' SEQUENTIAL TIME PLA
     /NES',/,' PLANE NO.   (ION-ATT) FREQ.   ENERGY      WV      NO.OF E
     /LECTRONS',/)   
      DO 20 IPL=1,ITFINAL
      IF(JPRT.EQ.1) WRITE(6,910) IPL,RI(IPL),EPT(IPL),VZPT(IPL),
     /NETPL(IPL)
 910  FORMAT(2X,I2,4X,E15.4,7X,F7.2,4X,F7.2,3X,I8)
  20  CONTINUE 
      RETURN
      END
      SUBROUTINE TOF(JPRT)
      IMPLICIT REAL*8 (A-H,O-Z)                                         
      IMPLICIT INTEGER*8 (I-N)
      COMMON/CION/ALPHAST,VDST,TSTEP,ZSTEP,TFINAL,ZFINAL,ITFINAL,IPRIM
      COMMON/TPLOUT/ETPL(8),XTPL(8),YTPL(8),ZTPL(8),TTPL(8),XXTPL(8),
     /YYTPL(8),ZZTPL(8),VZTPL(8),NETPL(8),ATTOINT,ATTERT,AIOERT
      COMMON/TOFOUT/RALPHA,RALPER,TOFENE,TOFENER,TOFWV,TOFWVER,
     /TOFDL,TOFDLER,TOFDT,TOFDTER,TOFWR,TOFWRER,RATTOF,RATOFER 
      COMMON/PTTOF/RI(8),EPT(8),VZPT(8),TTEST(8)
      COMMON/THRM/AMGAS(6),VTMB(6),TCFMX,TCFMXG(6),ITHRM
      DIMENSION DLTF(8),DXTF(8),DYTF(8),WR(8),ANTPL(8)
C----------------------------------------------------------
C CALCULATES TIME OF FLIGHT COEFFICIENTS
C---------------------------------------------
      IF(ITHRM.EQ.1) CALL COLFT(DUM1,DUM2,FRION,FRATT,DUM3,IDUM4)
      IF(ITHRM.EQ.0) CALL COLF(DUM1,DUM2,FRION,FRATT,DUM3,IDUM4)
      ATTOINT=FRATT/FRION
      CORERR=DABS((FAKEI+FRION-FRATT)/(FRION-FRATT))
      ANTPL(1)=DFLOAT(NETPL(1))
      WR(1)=ZTPL(1)/(ANTPL(1)*TSTEP)
      DLTF(1)=((ZZTPL(1)/ANTPL(1))-(ZTPL(1)/ANTPL(1))**2)/(2.0D0*TSTEP)
      DXTF(1)=((XXTPL(1)/ANTPL(1))-(XTPL(1)/ANTPL(1))**2)/(2.0D0*TSTEP)
      DYTF(1)=((YYTPL(1)/ANTPL(1))-(YTPL(1)/ANTPL(1))**2)/(2.0D0*TSTEP)
      DO 10 I=2,ITFINAL
      ANTPL(I)=DFLOAT(NETPL(I))
      WR(I)=((ZTPL(I)/ANTPL(I))-(ZTPL(I-1)/ANTPL(I-1)))/TSTEP
      DLTF(I)=((ZZTPL(I)/ANTPL(I))-(ZTPL(I)/ANTPL(I))**2-(ZZTPL(I-1)/ANT
     /PL(I-1))+(ZTPL(I-1)/ANTPL(I-1))**2)/(2.0D0*TSTEP)
      DXTF(I)=((XXTPL(I)/ANTPL(I))-(XTPL(I)/ANTPL(I))**2-(XXTPL(I-1)/ANT
     /PL(I-1))+(XTPL(I-1)/ANTPL(I-1))**2)/(2.0D0*TSTEP)
      DYTF(I)=((YYTPL(I)/ANTPL(I))-(YTPL(I)/ANTPL(I))**2-(YYTPL(I-1)/ANT
     /PL(I-1))+(YTPL(I-1)/ANTPL(I-1))**2)/(2.0D0*TSTEP)
  10  CONTINUE  
      DO 15 I=1,ITFINAL
      WR(I)=WR(I)*1.0D+09
      DLTF(I)=DLTF(I)*1.0D+16
      DXTF(I)=DXTF(I)*1.0D+16
      DYTF(I)=DYTF(I)*1.0D+16
  15  CONTINUE
      IF(JPRT.EQ.1) WRITE(6,900) ITFINAL
 900  FORMAT(2(/),' TIME OF FLIGHT RESULTS AT',I2,' SEQUENTIAL TIME PLAN
     /ES',/,' PLANE NO.        DL         DX          DY            WR',
     //)
      DO 20 IPL=1,ITFINAL   
      IF(JPRT.EQ.1) WRITE(6,910) IPL,DLTF(IPL),DXTF(IPL),DYTF(IPL),
     /WR(IPL)
 910  FORMAT(3X,I3,4X,3F12.1,4X,F8.2)
  20  CONTINUE    
      IF(NETPL(1).GT.NETPL(ITFINAL)) THEN
C        NET ATTACHMENT TAKE RESULTS FROM PLANE 2
       TOFENE=EPT(2)
       TOFENER=100.0D0*DABS((EPT(2)-EPT(3))/(2.0D0*EPT(2)))
       TOFWV=VZPT(2)
       TOFWVER=100.0D0*DABS((VZPT(2)-VZPT(3))/(2.0D0*VZPT(2))) 
       TOFDL=DLTF(2)
       TOFDLER=100.0D0*DABS((DLTF(2)-DLTF(3))/(2.0D0*DLTF(2)))
       TDT2=(DXTF(2)+DYTF(2))/2.0D0
       TDT3=(DXTF(3)+DYTF(3))/2.0D0
       TOFDT=TDT2
       TOFDTER=100.0D0*DABS((TDT2-TDT3)/(2.0D0*TDT2))
       TOFWR=WR(2)
       TOFWRER=100.0D0*DABS((WR(2)-WR(3))/(2.0D0*WR(2))) 
        ANST2=DFLOAT(NETPL(2))
        ANST3=DFLOAT(NETPL(3))
        ANST4=ANST3-DSQRT(ANST3)
        ANST5=DLOG(ANST2/ANST3)
        ANST6=DLOG(ANST2/ANST4)
        ANST7=ANST6/ANST5
        ANST8=ANST7-1.0D0
       IF(ATTOINT.EQ.-1.0) THEN
C        NO IONISATION
        RALPHA=0.0D0
        RALPER=0.0D0
        RATTOF=-RI(2)
        RATOFER=100.0D0*DSQRT(ANST8**2+ATTERT**2)
       ELSE
        RALPHA=RI(2)/(1.0D0-ATTOINT)
        RALPER=100.0D0*DSQRT(ANST8**2+AIOERT**2)
        RATTOF=ATTOINT*RI(2)/(1.0D0-ATTOINT)
        RATOFER=100.0D0*DSQRT(ANST8**2+ATTERT**2)
       ENDIF 
      ELSE
C NET IONISATION TAKE RESULTS FROM PLANE ITFINAL
       I1=ITFINAL
       I2=ITFINAL-1  
       TOFENE=EPT(I1)
       TOFENER=100.0D0*DABS((EPT(I1)-EPT(I2))/EPT(I1))
       TOFWV=VZPT(I1)
       TOFWVER=100.0D0*DABS((VZPT(I1)-VZPT(I2))/VZPT(I1))
       TOFDL=DLTF(I1)
       TOFDLER=100.0D0*DABS((DLTF(I1)-DLTF(I2))/DLTF(I1))
       TDT1=(DXTF(I1)+DYTF(I1))/2.0D0
       TDT2=(DXTF(I2)+DYTF(I2))/2.0D0
       TOFDT=TDT1
       TOFDTER=100.0D0*DABS((TDT1-TDT2)/TDT1)
       TOFWR=WR(I1)
       TOFWRER=100.0D0*DABS((WR(I1)-WR(I2))/WR(I1))
       ATER=DABS((RI(I1)-RI(I2))/RI(I1))
       RALPHA=RI(I1)/(1.0D0-ATTOINT)
       RALPER=100.0D0*DSQRT(ATER**2+AIOERT**2)
       RATTOF=ATTOINT*RI(I1)/(1.0D0-ATTOINT)
       IF(ATTOINT.NE.0.0D0) THEN
        RATOFER=100.0D0*DSQRT(ATER**2+ATTERT**2)
       ELSE
        RATOFER=0.0D0
       ENDIF  
      ENDIF
      RETURN
      END               
      SUBROUTINE MONTEFDT                                      
      IMPLICIT REAL*8 (A-H,O-Z)                                         
      IMPLICIT INTEGER*8 (I-N)
      COMMON/INPT/NGAS,NSTEP,NANISO,EFINAL,ESTEP,AKT,ARY,TEMPC,TORR,IPEN
      COMMON/CNSTS1/CONST1,CONST2,CONST3,CONST4,CONST5                  
      COMMON/SETP/TMAX,SMALL,API,ESTART,THETA,PHI,RSTART,EFIELD,NMAX   
      COMMON/LARGET/CF(6,4000,290),EIN(6,290),TCF(6,4000),IARRY(6,290), 
     /RGAS(6,290),IPN(6,290),WPL(6,290),LAST(6),ISIZE(6),PENFRA(6,3,290)
     /,TCFMAX(6)
      COMMON/LARGENT/CFN(6,4000,10),TCFN(6,4000),SCLENUL(6,10),NPLAST(6)
      COMMON/CION/ALPHAST,VDST,TSTEP,ZSTEP,TFINAL,ZFINAL,ITFINAL,IPRIM
      COMMON/OUTPTT/TIME(300),ICOLL(6,5),SPEC(4000),TMAX1,
     /AVE,DEN,XID,X,Y,Z,ST,NNULL,ICOLN(6,290),ICOLNN(6,10)
      COMMON/TTRM/ZTOT,TTOT,ZTOTS,TTOTS 
