      WRITE(6,335)
  335 FORMAT(/,2X,'NULL COLLISION FREQUENCIES FOR GAS MIXTURE',/,'  NB. 
     /OUTPUT CORRECTED FOR SCALING OF X-SECTIONS.',/,'------------------
     /------------------------------')
