      ENDIF
C RESCALE NULL COLLISIONS
      FRELV=FRELV/(SCLENUL(JK,JJ)**2)
      WRITE(6,1070) DSCRPTN(JK,JJ),FRELV,ERRFE
 1080 CONTINUE
 1091 CONTINUE
 1092 CONTINUE
      WRITE(6,15)
      WRITE(6,301)                                                      
  301 FORMAT(2(/),10X,' NORMALISED ENERGY DISTRIBUTION')                
