      SUBROUTINE ALPCLCBT  
      IMPLICIT REAL*8 (A-H,O-Z)
      IMPLICIT INTEGER*8 (I-N)
      COMMON/INPT/NGAS,NSTEP,NANISO,EFINAL,ESTEP,AKT,ARY,TEMPC,TORR,IPEN
      COMMON/SETP/TMAX,SMALL,API,ESTART,THETA,PHI,RSTART,EFIELD,NMAX 
      COMMON/TOFGOUT/RALPHA,RALPER,TOFENE,TOFENER,TOFWVZ,TOFWVZER,
     /TOFWVY,TOFWVYER,TOFDZZ,TOFDZZER,TOFDXX,TOFDXXER,TOFDYY,TOFDYYER,
     /TOFDYZ,TOFDYZER,TOFWRZ,TOFWRZER,TOFWRY,TOFWRYER,RATTOF,RATOFER 
      COMMON/VEL/WX,WY,WZ
      COMMON/CTOWNS/ALPHA,ATT
      COMMON/CTWNER/ALPER,ATTER
      COMMON/CION/ALPHAST,VDST,TSTEP,ZSTEP,TFINAL,ZFINAL,ITFINAL,IPRIM
      COMMON/FAKE/FAKEI,IFAKE,IFAKET(8),IFAKED(9) 
      COMMON/LARGET/CF(6,4000,290),EIN(6,290),TCF(6,4000),IARRY(6,290),
     /RGAS(6,290),IPN(6,290),WPL(6,290),LAST(6),ISIZE(6),PENFRA(6,3,290)
     /,TCFMAX(6)
C ----------------------------------------------------------------------     
C  ESTIMATE TIME STEP  FOR AVALANCHE SIMULATION IN TIME OF FLIGHT SIM. 
C  USES ESTIMATED GAIN OF 3.0 BETWEEN PLANES. 
C  CALLS TOF AND PT SUBROUTINES AND UPDATES ALPHA AND ATT
C  VERSION WITH BFIELD ALONG X-AXIS AND EFIELD ALONG Z-AXIS (90 DEGREES)
C ----------------------------------------------------------------------
      IMAX=NMAX/10000000 
      IF(IMAX.LT.5) IMAX=5 
      NMAX=IMAX*10000000
      CORR=760.0*(273.15+TEMPC)/(TORR*293.15)
      ALPP=ALPHA/CORR
      ATTP=ATT/CORR
      ANETP=ALPP-ATTP
      ANET=ALPHA-ATT
      TCUTH=1.2D-10*CORR
      TCUTL=1.D-13*CORR
C   
      IF (ANETP.GT.30.0) THEN
       ALPHAD=0.0
       ALP1=ALPHA
       ATT1=ATT
C WEAK ATTACHMENT
       GO TO 6
      ENDIF 
C  SOME ATTACHMENT
      IF(DABS(ANETP).LT.100.) THEN
C SMALL NET ATTACHMENT
       ALPHAD=DABS(ATT)*0.8
      ELSE IF(DABS(ANETP).GE.100..AND.DABS(ANETP).LT.1000.) THEN
C LARGER NET ATTACHMENT
       ALPHAD=DABS(ANET)*0.65
      ELSE IF(DABS(ANETP).GT.1000..AND.DABS(ANETP).LT.10000.) THEN
       ALPHAD=DABS(ANET)*0.6
      ELSE IF(DABS(ANETP).GT.10000..AND.DABS(ANETP).LT.100000.) THEN
       ALPHAD=DABS(ANET)*0.5
      ELSE IF(DABS(ANETP).GT.100000..AND.DABS(ANETP).LT.2000000.) THEN
       ALPHAD=DABS(ANET)*0.4
      ELSE 
       WRITE(6,91) 
   91 FORMAT(' ATTACHMENT TOO LARGE PROGRAM STOPPED')
       STOP
      ENDIF
   3  VDST=DSQRT(WZ*WZ+WY*WY)*1.D-5
      FAKEI=ALPHAD*WZ*1.D-12
      ALPHAST=0.85*DABS(ALPHAD+ANET)                        
      TSTEP=DLOG(3.0D0)/(ALPHAST*VDST*1.0D5)
      IF(TSTEP.GT.TCUTH) TSTEP=TCUTH
      IF(TSTEP.LT.TCUTL) TSTEP=TCUTL
C UPDATE NULL COLLISION FREQUENCY LIMIT
      DO 4 J=1,NGAS 
    4 TCFMAX(J)=TCFMAX(J)+DABS(FAKEI)/DFLOAT(NGAS)
      WRITE(6,871) ALPHAST,ANET,ALPHAD,TSTEP
  871 FORMAT(' ALPHAST1=',D10.3,' ANET1=',D10.3,' ALPHAD1=',D10.3,/,
     /' TSTEP1=',D10.3)
C  CONVERT TO  PICOSECONDS     
      TSTEP=TSTEP*1.0D12
      TFINAL=7.0D0*TSTEP
      ITFINAL=7
C CALCULATE GOOD STARTING VALUES FOR ALPHA AND NETA
      JPRT=0
      CALL MONTEFTGT(JPRT)
      CALL PTG(JPRT)
      CALL TOFG(JPRT)
      TOFWR=DSQRT(TOFWRZ*TOFWRZ+TOFWRY*TOFWRY)
      ALP1=RALPHA/TOFWR*1.D7
      ALP1ER=RALPER*ALP1/100. 
      ATT1=RATTOF/TOFWR*1.D7
      ATT1ER=RATOFER*ATT1/100.
      WRITE(6,944) ALP1,ALP1ER,ATT1,ATT1ER
  944 FORMAT(' GOOD STARTING VALUES FOR CALC:',/,' ALPHA=',D12.3,' ERR='
     /,D12.3,/,' NETA =',D12.3,' ERR=',D12.3)
C
C CALCULATE USING GOOD STARTING VALUES
C  RESET TCFMAX
      DO 44 J=1,NGAS
   44 TCFMAX(J)=TCFMAX(J)-DABS(FAKEI)/DFLOAT(NGAS)
C CALCULATE FAKE IONISATION RATE SCALING BY 1.2 
      ALPHAD=DABS(ATT1)*1.2
      IF((ALP1-ATT1).GT.30.*CORR) ALPHAD=DABS(ATT1)*0.4
      IF(DABS(ALP1-ATT1).LT.(ALP1/10.0).OR.DABS(ALP1-ATT1).LT.
     /(ATT1/10.0)) ALPHAD=DABS(ATT1)*0.3
      IF((ALP1-ATT1).GT.100.*CORR) ALPHAD=0.0
      WZ=TOFWRZ*1.D5 
      WY=TOFWRY*1.D5 
C
C
C NET IONISATION
C   5 IF(ANET.GT.(11000./CORR)) THEN
C LARGE IONISATION
C      ALPHAD=7000.-(ALPHA-ATT)
C     ELSE IF (DABS(ALPP).GT.(5.0*DABS(ANET)).OR.DABS(ATTP).GT.
C    /(5.0*DABS(ANET))) THEN
C LARGE CANCELLATION BETWEEN ATTACHMENT AND IONISATION
C      ALPHAD=-6.0*(ALPHA-ATT)
C     ELSE
C      ALPHAD=0.0
C     ENDIF
    6 VTOT=DSQRT(WZ*WZ+WY*WY)
      VDST=VTOT*1.D-5
      FAKEI=ALPHAD*VTOT*1.D-12
      ALPHAST=0.85D0*DABS(ALPHAD+ALP1-ATT1)
      IF((ALP1+ALPHAD).GT.(10.0*ALPHAST).OR.ATT1.GT.(10.0*ALPHAST)) THEN
C LARGE CANCELLATION BETWEEN ALPHA AND NETA. REDUCE STEP SIZE BY 15.0
       IF((ALP1+ALPHAD).GT.100.*CORR) THEN
        ALPHAST=ALPHAST*15.0
       ELSE IF((ALP1+ALPHAD).GT.50.0*CORR) THEN
        ALPHAST=ALPHAST*12.0
       ELSE
        ALPHAST=ALPHAST*8.0
       ENDIF
      ENDIF
      TSTEP=DLOG(3.0D0)/(ALPHAST*VDST*1.0D5)
      IF(TSTEP.GT.TCUTH.AND.ALPHAD.NE.0.0) TSTEP=TCUTH
C UPDATE NULL COLLISION FREQUENCY
      DO 10 J=1,NGAS
   10 TCFMAX(J)=TCFMAX(J)+DABS(FAKEI)/DFLOAT(NGAS)
      ANET1=ALP1-ATT1
      WRITE(6,878) ALPHAST,ANET1,ALPHAD,TSTEP
  878 FORMAT(' ALPHAST=',D10.3,' ANET=',D10.3,' ALPHAD=',D10.3,/,
     /' TSTEP=',D10.3)
      TSTEP=TSTEP*1.0D12
      TFINAL=7.0D0*TSTEP
      ITFINAL=7
C CALC TIME OF FLIGHT AND PT 
      WRITE(6,25)
   25 FORMAT(/,2X,'SOLUTION FOR PULSED TOWNSEND AND TIME OF FLIGHT PARAM
     /ETERS',/,'  ------------------------------------------------------
     /--------')
      WRITE(6,26) TSTEP
   26 FORMAT(1(/),'  TIME STEP BETWEEN SAMPLING PLANES =',D12.5,' PICOSE
     /CS.',/)        
      JPRT=1             
      CALL MONTEFTGT(JPRT)                            
      CALL FRIEDLANDT
      CALL PTG(JPRT)
      CALL TOFG(JPRT)
      WRITE(6,27) RALPHA,RALPER,RATTOF,RATOFER
   27 FORMAT(/,'PT IONISATION AND ATTACHMENT RATES *10**12/SEC',/,'ALPHA
     /=',D10.3,' +- ',F6.2,' %      ATT=',D10.3,' +- ',F6.2,' %')    
      WRITE(6,28) 
   28 FORMAT(/,'TOF DIFFUSION')
      WRITE(6,29) TOFDZZ,TOFDZZER,TOFDXX,TOFDXXER,TOFDYY,TOFDYYER,  
     /TOFDYZ,TOFDYZER  
   29 FORMAT(/,'DZZ=',F8.1,' +- ',F5.1,' %',/,'DXX=',F8.1,' +- ',F5.1,' 
     /%',/,'DYY=',F8.1,' +- ',F5.1,' %',/,'DYZ=',F8.1,' +- ',F5.1,' %')
      WRITE(6,30)
   30 FORMAT(/,'TOF DRIFT VELOCITY')
      WRITE(6,31) TOFWRZ,TOFWRZER,TOFWRY,TOFWRYER   
   31 FORMAT(/,'WRZ=',F8.2,' +-',F6.1,' %    WRY=',F8.2,' +-',F6.1,' %')
C CALCULATE TOWNSEND SST COEFICIENTS FROM TOF RESULTS      
      TOFWR=DSQRT(TOFWRZ*TOFWRZ+TOFWRY*TOFWRY)
      WRZN=TOFWR*1.0D05
      FC1=WRZN/(2.0D0*TOFDZZ)
      FC2=((RALPHA-RATTOF)*1.0D12)/TOFDZZ
      ALPZZ=FC1-DSQRT(FC1**2-FC2)
      WRITE(6,888) ALPZZ
  888 FORMAT(/,'TOWNSEND COEFICIENT (ALPHA-ATT) CALCULATED FROM TOF RESU
     /LTS:',/,' ALPHA-ATT /CM.=',D11.4,' N.B. APPROXIMATE FORMULA NOT AC
     /CURATE',/) 
C---- -------------------------------------------------    
C      LOAD NEW ALPHA AND ATTACHMENT INTO COMMON BLOCKS
C---- -------------------------------------------
      ALPHA=RALPHA/TOFWR*1.D7
      ALPER=RALPER*ALPHA/100.
      ATT=RATTOF/TOFWR*1.D7
      ATTER=RATOFER*ATT/100.
C     WRITE(6,32) ALPHA,ALPER,ATT,ATTER
C  32 FORMAT(/,'TOWNSEND COEFICIENTS CALCULATED FROM TOF RESULTS:',2(/),
C    /'IONISATION RATE /CM.=',D11.4,' +-',F6.2,/,'ATTACHMENT RATE /CM.='
C    /,D11.4,' +-',F6.2,/)        
C --- -------------------------------------------      
      RETURN                                                     
      END                                                               
      SUBROUTINE MONTEFTGT(JPRT)                                     
      IMPLICIT REAL*8 (A-H,O-Z)                                         
      IMPLICIT INTEGER*8 (I-N)
      COMMON/INPT/NGAS,NSTEP,NANISO,EFINAL,ESTEP,AKT,ARY,TEMPC,TORR,IPEN
      COMMON/CNSTS1/CONST1,CONST2,CONST3,CONST4,CONST5                  
      COMMON/SETP/TMAX,SMALL,API,ESTART,THETA,PHI,RSTART,EFIELD,NMAXOLD
      COMMON/BFLD/EOVB,WB,BTHETA,BMAG                                   
      COMMON/LARGET/CF(6,4000,290),EIN(6,290),TCF(6,4000),IARRY(6,290),
     /RGAS(6,290),IPN(6,290),WPL(6,290),IPLAST(6),ISIZE(6),
     /PENFRA(6,3,290),TCFMAX(6)
      COMMON/LARGENT/CFN(6,4000,10),TCFN(6,4000),SCLENUL(6,10),NPLAST(6)
      COMMON/CION/ALPHAST,VDST,TSTEP,ZSTEP,TFINAL,ZFINAL,ITFINAL,IPRIM
      COMMON/OUTPTT/TIME(300),ICOLL(6,5),SPEC(4000),TMAX1,
     /AVE,DEN,XID,X,Y,Z,ST,NNULL,ICOLN(6,290),ICOLNN(6,10)
      COMMON/TTRM/ZTOT,TTOT,ZTOTS,TTOTS
      COMMON/THRM/AMGAS(6),VTMB(6),TCFMX,TCFMXG(6),ITHRM
      COMMON/RANM/RNMX(6)
      COMMON/ISPT/XS(2000),YS(2000),ZS(2000),TS(2000),ES(2000),
     /DCX(2000),DCY(2000),DCZ(2000),IPL(2000)
      COMMON/TPLOUTG/ETPL(8),XTPL(8),YTPL(8),ZTPL(8),TTPL(8),XXTPL(8),
     /YYTPL(8),ZZTPL(8),YZTPL(8),VZTPL(8),VYTPL(8),NETPL(8),ATTOINT,
     /ATTERT,AIOERT 
      COMMON/ANIST/PSCT(6,4000,290),ANGCT(6,4000,290),INDEX(6,290),NISO 
      COMMON/IONFL/NC0(6,290),EC0(6,290),NG1(6,290),EG1(6,290),
     /NG2(6,290),EG2(6,290),WKLM(6,290),EFL(6,290)
      COMMON/FAKE/FAKEI,IFAKE,IFAKET(8),IFAKED(9) 
      DIMENSION EPRM(10000000),IESPECP(100),TEMP(6,4000)           
C -------------------------------------------------------------------   
C   CALCULATES COLLISION EVENTS AND UPDATES DIFFUSION AND VELOCITY.
C   THIS ROUTINE HANDLES TERMINATIONS AT FIXED DRIFT TIMES. 
C   B FIELD AT 90 DEGREES TO EFIELD  
C ------------------------------------------------------------------- 
      IF(JPRT.EQ.0) THEN
C CALCULATION OF APPROXIMATE STARTING PARAMETERS
       NMAX=NMAXOLD
       IF(NMAXOLD.GT.80000000) NMAX=80000000
      ELSE
C ENTRY FOR NORMAL CALCULATION
       NMAX=NMAXOLD 
      ENDIF 
      S=0.0D0 
      ST=0.0D0 
      X=0.0D0
      Y=0.0D0
      Z=0.0D0 
      ZTOT=0.0D0
      ZTOTS=0.0D0
      YTOT=0.0D0
      YTOTS=0.0D0
      TTOT=0.0D0
      TTOTS=0.0D0
      SMALL=1.0D-20                                                     
      TMAX1=0.0D0                                                       
      RDUM=RSTART                                                       
      E1=ESTART                                                         
      CONST9=CONST3*0.01D0
      CONST10=CONST9*CONST9
      API=DACOS(-1.0D0)
      F4=2.0D0*API
      DO 25 I=1,300
   25 TIME(I)=0.0D0
      DO 26 K=1,6
      DO 26 I=1,5
   26 ICOLL(K,I)=0
      DO 27 K=1,6
      DO 27 I=1,290
   27 ICOLN(K,I)=0
      DO 271 K=1,6
      DO 271 I=1,10
  271 ICOLNN(K,I)=0
      DO 28 I=1,4000
   28 SPEC(I)=0.0D0            
      DO 33 I=1,100
   33 IESPECP(I)=0 
      DO 34 I=1,8
      ETPL(I)=0.0D0
      XTPL(I)=0.0D0
      YTPL(I)=0.0D0
      ZTPL(I)=0.0D0
      TTPL(I)=0.0D0
      XXTPL(I)=0.0D0
      YYTPL(I)=0.0D0
      ZZTPL(I)=0.0D0
      YZTPL(I)=0.0D0
      VZTPL(I)=0.0D0
      VYTPL(I)=0.0D0
   34 NETPL(I)=0                                   
      ID=0
      N4000=4000
      N300=300
      N100=100
      I100=0                                                            
      NCOL=0                                                            
      NNULL=0
      NELEC=0
      NEION=0
      NMXADD=0
      NTMPFLG=0
      NPONT=0 
      NCLUS=0
      J1=1 
C
      DO 111 K=1,6
      DO 111 J=1,N4000
  111 TEMP(K,J)=TCF(K,J)+TCFN(K,J)
      IFAKE=0
      ABSFAKEI=DABS(FAKEI)
      DO 112 J=1,8
  112 IFAKET(J)=0
C
C GENERATE INITIAL RANDOM NUMBERS FOR MAXWELL BOLTZMAN 
      CALL GERJAN
      IMBPT=0
C
      ZSTRT=0.0D0
      YSTRT=0.0D0
      TSSTRT=0.0D0                                                    
C     INITIAL DIRECTION COSINES                                         
      DCZ1=DCOS(THETA)                                                  
      DCX1=DSIN(THETA)*DCOS(PHI)                                        
      DCY1=DSIN(THETA)*DSIN(PHI) 
C INITIAL VELOCITY
      VTOT=CONST9*DSQRT(E1)
      CX1=DCX1*VTOT
      CY1=DCY1*VTOT
      CZ1=DCZ1*VTOT      
      E100=E1
      DCZ100=DCZ1
      DCX100=DCX1
      DCY100=DCY1                                       
      JPRINT=NMAX/10
      IPRINT=0
      ITER=0
      IPLANE=0
      IPRIM=0
C  LOOP FOR NEW STARTING ELECTRONS 
  544 IPRIM=IPRIM+1 
      IF(IPRIM.GT.1) THEN
C CHECK IF PROGRAM WILL EXCEED MAXIMUM NUMBER OF ITERATIONS 
C IN THIS CYCLE IF SO OUTPUT CURRENT RESULTS. 
       IF(ITER.GT.NMAX) THEN
        IPRIM=IPRIM-1
        GO TO 700      
       ENDIF 
       X=0.0D0
       Y=0.0D0
       Z=0.0D0
       DCZ1=DCZ100
       DCX1=DCX100
       DCY1=DCY100
       E1=E100
       VTOT=CONST9*DSQRT(E1)
       CX1=DCX1*VTOT
       CY1=DCY1*VTOT
       CZ1=DCZ1*VTOT
       NCLUS=NCLUS+1
       ST=0.0D0
       TSSTRT=0.0D0
       ZSTRT=0.0D0
       YSTRT=0.0D0
       IPLANE=0
      ENDIF
      IF(IPRIM.GT.10000000) THEN
      WRITE(6,944) IPRIM
 944  FORMAT(/,2X,'PROGRAM STOPPED TOO MANY PRIMARIES IPRIM =',I7)
      GO TO 700
      ENDIF
      EPRM(IPRIM)=E1
      IDUM=DINT(E1)+1
      IDUM=DMIN0(IDUM,N100)
      IESPECP(IDUM)=IESPECP(IDUM)+1      
C START OF LOOP FOR NEWLY CREATED ELECTRONS
  555 TDASH=0.0D0
      NELEC=NELEC+1
      TSTOP=TSTEP+IPLANE*TSTEP       
C MAIN LOOP                                                             
    1 CONTINUE                                                          
C     IF(ITER.GT.NMAX)  GO TO 315
      R1=drand48(RDUM)
      T=-DLOG(R1)/TCFMX+TDASH
      TDASH=T
      WBT=WB*T
      COSWT=DCOS(WBT)
      SINWT=DSIN(WBT)
  15  IF((T+ST).GE.TSTOP) THEN                     
       IPLANE=IPLANE+1
       TSTOP=TSTOP+TSTEP
C STORE POSITION AND ENERGY AT TIME PLANE =IPLANE. 
       CALL TPLANEGT(T,E1,CX1,CY1,CZ1,EFIELD,IPLANE)
C CHECK IF PASSED THROUGH MORE THAN ONE PLANE IN THIS STEP       
       IF((T+ST).GE.TSTOP.AND.TSTOP.LE.TFINAL) GO TO 15
       IF((T+ST).GE.TFINAL) THEN
        ZTOT=ZTOT+Z
        YTOT=YTOT+Y     
        TTOT=TTOT+ST
        ZTOTS=ZTOTS+Z-ZSTRT 
        YTOTS=YTOTS+Y-YSTRT     
        TTOTS=TTOTS+ST-TSSTRT    
        TSTOP=TSTEP
C NO MORE ELECTRONS IN CASCADE TRY NEW PRIMARY ELECTRON
        IF(NELEC.EQ.(NCLUS+1)) GO TO 544  
C TAKE ELECTRONS FROM STORE 
   20   X=XS(NPONT) 
        Y=YS(NPONT) 
        Z=ZS(NPONT) 
        ST=TS(NPONT) 
        E1=ES(NPONT) 
        DCX1=DCX(NPONT) 
        DCY1=DCY(NPONT) 
        DCZ1=DCZ(NPONT)
        VTOT=CONST9*DSQRT(E1)
        CX1=DCX1*VTOT
        CY1=DCY1*VTOT
        CZ1=DCZ1*VTOT 
        IPLANE=IPL(NPONT)   
        NPONT=NPONT-1 
        ZSTRT=Z 
        YSTRT=Y
        TSSTRT=ST
        GO TO 555
       ENDIF              
      ENDIF
      DZ=(CZ1*SINWT+(EOVB-CY1)*(1.0D0-COSWT))/WB
      E=E1+DZ*EFIELD*100.0D0
 913  FORMAT(3X,' AFTER STORE ITER=',I10,' DZ=',D12.3,'E1=',D12.3,' COSW
     /T=',D12.3,' SINWT=',D12.3,' WBT=',D12.3,' CY1=',D12.3)   
      IF(E.LT.0.0D0) THEN
       WRITE(6,913)ITER,DZ,E,COSWT,SINWT,WBT,CY1  
       E=0.001D0
      ENDIF     
C CALCULATE ELECTRON VELOCITY IN LAB FRAME
      CX2=CX1
      CY2=(CY1-EOVB)*COSWT+CZ1*SINWT+EOVB
      CZ2=CZ1*COSWT-(CY1-EOVB)*SINWT
C FIND IDENTITY OF GAS FOR COLLISION
      KGAS=0
      IF(NGAS.EQ.1) THEN
       R2=drand48(RDUM)
       KGAS=1
       GO TO 4
      ENDIF
      R2=drand48(RDUM)
    3 KGAS=KGAS+1
      IF(TCFMXG(KGAS).LT.R2) GO TO 3
C CALCULATE GAS VELOCITY VECTORS VGX,VGY,VGZ
    4 IMBPT=IMBPT+1
      IF(IMBPT.GT.6) THEN
       CALL GERJAN
       IMBPT=1
      ENDIF
      VGX=VTMB(KGAS)*RNMX(IMBPT)
      IMBPT=IMBPT+1
      VGY=VTMB(KGAS)*RNMX(IMBPT)
      IMBPT=IMBPT+1
      VGZ=VTMB(KGAS)*RNMX(IMBPT)
C CALCULATE ENERGY WITH STATIONARY GAS TARGET EOK
      EOK=((CX2-VGX)**2+(CY2-VGY)**2+(CZ2-VGZ)**2)/CONST10
      IE=DINT(EOK/ESTEP)+1                                            
      IE=DMIN0(IE,N4000)                                               
C                                                                       
C     TEST FOR REAL OR NULL COLLISION   
C                                
      R5=drand48(RDUM)                                         
      TEST1=TCF(KGAS,IE)/TCFMAX(KGAS)                                  
      IF(R5.LE.TEST1) GO TO 137                                         
      NNULL=NNULL+1 
      TEST2=TEMP(KGAS,IE)/TCFMAX(KGAS)    
      IF(R5.LT.TEST2) THEN
C TEST FOR NULL LEVELS
       IF(NPLAST(KGAS).EQ.0) GO TO 1
       R2=drand48(RDUM)
       I=0
  888  I=I+1
       IF(CFN(KGAS,IE,I).LT.R2) GO TO 888
C INCREMENT NULL SCATTER SUM
       ICOLNN(KGAS,I)=ICOLNN(KGAS,I)+1
       GO TO 1
      ENDIF                      
      TEST3=(TEMP(KGAS,IE)+ABSFAKEI)/TCFMAX(KGAS)
      IF(R5.LT.TEST3) THEN
C FAKE COLLISION INCREMENT COUNTER
       IFAKE=IFAKE+1
       IFAKET(IPLANE+1)=IFAKET(IPLANE+1)+1
       IF(FAKEI.LT.0.0) THEN
        NION=NION+1
C FAKE ATTACHMENT START NEW ELECTRON
        IF(NELEC.GT.(NCLUS+1)) GO TO 544
C ELECTRON CAPTURED TAKE ELECTRON FROM STORE
        GO TO 20
       ENDIF
C
       NCLUS=NCLUS+1
       NPONT=NPONT+1
       NMXADD=MAX(NPONT,NMXADD)
       IF(NPONT.GT.2000) THEN 
        WRITE(6,546) NPONT,ITER
        STOP
       ENDIF     
       XS(NPONT)=X+CX1*T       
       YS(NPONT)=Y+EOVB*T+((CY1-EOVB)*SINWT+CZ1*(1.0D0-COSWT))/WB
       ZS(NPONT)=Z+DZ
       TS(NPONT)=ST+T
       ES(NPONT)=E
       IPL(NPONT)=IPLANE
       BOT=1.0D0/DSQRT(CX2*CX2+CY2*CY2+CZ2*CZ2)
       DCX(NPONT)=CX2*BOT
       DCY(NPONT)=CY2*BOT
       DCZ(NPONT)=CZ2*BOT
       GO TO 1
      ENDIF                  
      GO TO 1                             
  137 NCOL=NCOL+1
C CALCULATE DIRECTION COSINES OF ELECTRON IN 0 KELVIN FRAME
      CONST11=1.0D0/(CONST9*DSQRT(EOK))
      DXCOM=(CX2-VGX)*CONST11
      DYCOM=(CY2-VGY)*CONST11
      DZCOM=(CZ2-VGZ)*CONST11                              
C                                                                       
C  CALCULATE DIRECTION COSINES AND POSITIONS AT INSTANT BEFORE COLLISION
C    ALSO UPDATE DIFFUSION  AND ENERGY CALCULATIONS.                    
      T2=T*T
      IF(T.GE.TMAX1) TMAX1=T
      TDASH=0.0D0  
C CALC NEW POSITION                                                
      X=X+CX1*T                                                         
      Y=Y+EOVB*T+((CY1-EOVB)*SINWT+CZ1*(1.0D0-COSWT))/WB
      Z=Z+DZ          
      ST=ST+T
      IT=DINT(T+1.0D0)                                              
      IT=DMIN0(IT,N300)                                               
      TIME(IT)=TIME(IT)+1.0D0
C ENERGY SPECTRUM IN 0 KELVIN FRAME                               
      SPEC(IE)=SPEC(IE)+1.0D0
C --------------------------------------------------------------------- 
C     DETERMINATION OF REAL COLLISION TYPE                              
C --------------------------------------------------------------------- 
      R2=drand48(RDUM)
C FIND LOCATION WITHIN 4 UNITS IN COLLISION ARRAY
      CALL SORTT(KGAS,I,R2,IE)                                          
  140 I=I+1                                                             
      IF(CF(KGAS,IE,I).LT.R2) GO TO 140                              
      S1=RGAS(KGAS,I)                                                   
      EI=EIN(KGAS,I)
      IF(EOK.LT.EI) THEN
C FIX ENERGY LOSS SMALLER THAN INCIDENT ENERGY IF A BINING ERROR OCCURS.
      EI=EOK-0.0001D0
      ENDIF                                                          
      IF(IPN(KGAS,I).EQ.0) GO TO 666
C ATTACHMENT       
      IF(IPN(KGAS,I).EQ.-1) THEN
       NEION=NEION+1
       IPT=IARRY(KGAS,I)
       ID=ID+1
       ITER=ITER+1
       IPRINT=IPRINT+1
       ICOLL(KGAS,IPT)=ICOLL(KGAS,IPT)+1
       ICOLN(KGAS,I)=ICOLN(KGAS,I)+1 
       IT=DINT(T+1.0D0)
       IT=DMIN0(IT,N300)
       TIME(IT)=TIME(IT)+1.0D0
       ZTOT=ZTOT+Z
       YTOT=YTOT+Y
       TTOT=TTOT+ST
       ZTOTS=ZTOTS+Z-ZSTRT
       YTOTS=YTOTS+Y-YSTRT
       TTOTS=TTOTS+ST-TSSTRT
C  ELECTRON CAPTURED START NEW PRIMARY          
       IF(NELEC.EQ.(NCLUS+1)) GO TO 544
C ELECTRON CAPTURED TAKE NEXT ELECTRON FROM STORE       
       GO TO 20
      ENDIF    
      EISTR=EI                               
      R9=drand48(RDUM)
C     ESEC=R9*(EOK-EI)      
C    USE OPAL PETERSON AND BEATY SPLITTING FACTOR.
      ESEC=WPL(KGAS,I)*TAN(R9*ATAN((EOK-EI)/(2.0D0*WPL(KGAS,I))))  
      ESEC=WPL(KGAS,I)*(ESEC/WPL(KGAS,I))**0.9524     
      EI=ESEC+EI 
C STORE POSITION ,ENERGY, DIRECTION COSINES AND TIME OF GENERATION
C OF IONISATION ELECTRON      
      NCLUS=NCLUS+1
      NPONT=NPONT+1
      NMXADD=MAX(NPONT,NMXADD)
      IF(NPONT.GT.2000) THEN 
      WRITE(6,546) NPONT,ITER
 546  FORMAT(2X,' PROGRAM STOPPED . NPONT=',I5,' ITER=',I10)
      STOP
      ENDIF     
      XS(NPONT)=X       
      YS(NPONT)=Y
      ZS(NPONT)=Z
      TS(NPONT)=ST
      ES(NPONT)=ESEC
      IPL(NPONT)=IPLANE
      NCLTMP=NPONT
      NTMPFLG=1
C RANDOMISE SECONDARY ELECTRON DIRECTION
C     R3=drand48(RDUM)
C     F3=1.0D0-2.0D0*R3
C     THETA0=DACOS(F3)
C     F6=DCOS(THETA0)
C     F5=DSIN(THETA0)
C     R4=drand48(rdum)
C     PHI0=F4*R4
C     F8=DSIN(PHI0)
C     F9=DCOS(PHI0)               
C     DCX(NPONT)=F9*F5
C     DCY(NPONT)=F8*F5
C     DCZ(NPONT)=F6     
C STORE POSSIBLE SHELL EMISSIONS AUGER OR FLUORESCENCE
      IF(EISTR.GT.30.0) THEN
C AUGER EMISSION WITHOUT FLUORESCENCE
       NAUG=NC0(KGAS,I)
       EAVAUG=EC0(KGAS,I)/DFLOAT(NAUG)
       DO 701 JFL=1,NAUG
       NCLUS=NCLUS+1
       NPONT=NPONT+1
       XS(NPONT)=X
       YS(NPONT)=Y
       ZS(NPONT)=Z
       TS(NPONT)=ST
       ES(NPONT)=EAVAUG
       IPL(NPONT)=IPLANE
       R3=drand48(RDUM)
       F3=1.0D0-2.0D0*R3
       THETA0=DACOS(F3)
       F6=DCOS(THETA0)
       F5=DSIN(THETA0)
       R4=drand48(RDUM)
       PHI0=F4*R4
       F8=DSIN(PHI0)
       F9=DCOS(PHI0)
       DCX(NPONT)=F9*F5
       DCY(NPONT)=F8*F5
       DCZ(NPONT)=F6
  701  CONTINUE  
      ENDIF
C                                                                       
C  GENERATE SCATTERING ANGLES AND UPDATE  LABORATORY COSINES AFTER      
C   COLLISION ALSO UPDATE ENERGY OF ELECTRON.                           
C
  666 IPT=IARRY(KGAS,I)
      ID=ID+1    
      ITER=ITER+1
      IPRINT=IPRINT+1                                                   
      ICOLL(KGAS,IPT)=ICOLL(KGAS,IPT)+1 
      ICOLN(KGAS,I)=ICOLN(KGAS,I)+1  
C IF EXCITATION THEN ADD PROBABILITY ,PENFRA(KGAS,1,I),OF TRANSFER TO 
C GIVE IONISATION OF THE OTHER GASES IN THE MIXTURE
      IF(IPEN.EQ.0) GO TO 5  
      IF(PENFRA(KGAS,1,I).EQ.0.0) THEN
       RAN=drand48(RDUM)
       IF(RAN.GT.PENFRA(KGAS,1,I)) GO TO 5
       NCLUS=NCLUS+1
       NPONT=NPONT+1
       IF(NPONT.GT.2000) THEN
        WRITE(6,546) NPONT,ITER
        STOP
       ENDIF
C ENTER HERE POSSIBLE DELOCALISATION LENGTH FOR PENNING TRANSFER
       IF(PENFRA(KGAS,2,I).EQ.0.0) THEN
        XS(NPONT)=X
        YS(NPONT)=Y
        ZS(NPONT)=Z
        GO TO 667
       ENDIF
       ASIGN=1.0
       RAN=drand48(RDUM)
       RAN1=drand48(RDUM)
       IF(RAN1.LT.0.5) ASIGN=-ASIGN
       XS(NPONT)=X-DLOG(RAN)*PENFRA(KGAS,2,I)*ASIGN
       RAN=drand48(RDUM)
       RAN1=drand48(RDUM)
       IF(RAN1.LT.0.5) ASIGN=-ASIGN
       YS(NPONT)=Y-DLOG(RAN)*PENFRA(KGAS,2,I)*ASIGN
       RAN=drand48(RDUM)
       RAN1=drand48(RDUM)
       IF(RAN1.LT.0.5) ASIGN=-ASIGN
       ZS(NPONT)=Z-DLOG(RAN)*PENFRA(KGAS,2,I)*ASIGN
C POSSIBLE PENNING TRANSFER TIME
  667  TPEN=ST
       IF(PENFRA(KGAS,3,I).EQ.0.0) GO TO 668
       RAN=drand48(RDUM)
       TPEN=ST-DLOG(RAN)*PENFRA(KGAS,3,I)
  668  TS(NPONT)=TPEN
       ES(NPONT)=1.0
       DCX(NPONT)=DCX1
       DCY(NPONT)=DCY1
       DCZ(NPONT)=DCZ1
C FIND LAST TIME PLANE BEFORE TPEN
       TSTOP1=0.0
       IPLANE1=0
       DO 669 KDUM=1,ITFINAL
       TSTOP1=TSTEP+TSTOP1
       IF(TPEN.LT.TSTOP1) GO TO 670
       IPLANE1=IPLANE1+1
  669  CONTINUE
C PENNING TRANSFER OCCURS AFTER FINAL TIME PLANE CLEAR ENTRY
       NPONT=NPONT-1
       NCLUS=NCLUS-1
       GO TO 5
  670  IPL(NPONT)=IPLANE1                
      ENDIF
    5 S2=(S1*S1)/(S1-1.0D0) 
C ANISOTROPIC SCATTERING 
      R3=drand48(RDUM)
      IF(INDEX(KGAS,I).EQ.1) THEN
       R31=drand48(RDUM)
       F3=1.0D0-R3*ANGCT(KGAS,IE,I)
       IF(R31.GT.PSCT(KGAS,IE,I)) F3=-F3
      ELSE IF(INDEX(KGAS,I).EQ.2) THEN
       EPSI=PSCT(KGAS,IE,I)
       F3=1.0D0-(2.0D0*R3*(1.0D0-EPSI)/(1.0D0+EPSI*(1.0D0-2.0D0*R3)))
      ELSE
C ISOTROPIC SCATTERING                                             
       F3=1.0D0-2.0D0*R3  
      ENDIF
      THETA0=DACOS(F3)                                                  
      R4=drand48(RDUM)
      PHI0=F4*R4                                                        
      F8=DSIN(PHI0)                                                     
      F9=DCOS(PHI0)                                                     
C     IF(EOK.LT.EI) EI=0.0D0                                           
      ARG1=1.0D0-S1*EI/EOK                                              
      ARG1=DMAX1(ARG1,SMALL)                                            
      D=1.0D0-F3*DSQRT(ARG1)                                            
      E1=EOK*(1.0D0-EI/(S1*EOK)-2.0D0*D/S2) 
      E1=DMAX1(E1,SMALL)                                                
      Q=DSQRT((EOK/E1)*ARG1)/S1                                        
      Q=DMIN1(Q,1.0D0)                                                  
      THETA=DASIN(Q*DSIN(THETA0))                                       
      F6=DCOS(THETA)                                                    
      U=(S1-1.0D0)*(S1-1.0D0)/ARG1                                      
      CSQD=F3*F3                                                        
      IF(F3.LT.0.0D0.AND.CSQD.GT.U) F6=-1.0D0*F6                        
      F5=DSIN(THETA)                                                    
      DZCOM=DMIN1(DZCOM,1.0D0)                                         
      ARGZ=DSQRT(DXCOM*DXCOM+DYCOM*DYCOM)
      IF(ARGZ.EQ.0.0D0) THEN
       DCZ1=F6         
       DCX1=F9*F5                             
       DCY1=F8*F5 
       IF(NTMPFLG.EQ.1) THEN
C USE FREE KINEMATICS FOR IONISATION SECONDARY ANGLE
        F5S=F5*DSQRT(E1/ES(NCLTMP))
        IF(F5S.GT.1.0) F5S=1.0
        THSEC=DASIN(F5S)
        F5S=DSIN(THSEC)
        F6S=DCOS(THSEC)
        IF(F6.LT.0.0) F6S=-F6S
        PHIS=PHI0+API
        IF(PHIS.GT.F4) PHIS=PHI0-F4
        F8S=DSIN(PHIS)
        F9S=DCOS(PHIS)
        DCZ(NCLTMP)=F6S
        DCY(NCLTMP)=F8S*F5S
        DCX(NCLTMP)=F9S*F5S
        NTMPFLG=0
       ENDIF
       GO TO 190
      ENDIF                                            
      DCZ1=DZCOM*F6+ARGZ*F5*F8                                         
      DCY1=DYCOM*F6+(F5/ARGZ)*(DXCOM*F9-DYCOM*DZCOM*F8)                 
      DCX1=DXCOM*F6-(F5/ARGZ)*(DYCOM*F9+DXCOM*DZCOM*F8)
      IF(NTMPFLG.EQ.1) THEN
C USE FREE KINEMATICS FOR IONISATION SECONDARY ANGLE
       F5S=F5*DSQRT(E1/ES(NCLTMP))
       IF(F5S.GT.1.0) F5S=1.0
       THSEC=DASIN(F5S)
       F5S=DSIN(THSEC)
       F6S=DCOS(THSEC)
       IF(F6.LT.0.0) F6S=-F6S
       PHIS=PHI0+API
       IF(PHIS.GT.F4) PHIS=PHI0-F4
       F8S=DSIN(PHIS)
       F9S=DCOS(PHIS)
       DCZ(NCLTMP)=DZCOM*F6S+ARGZ*F5S*F8S
       DCY(NCLTMP)=DYCOM*F6S+(F5S/ARGZ)*(DXCOM*F9S-DYCOM*DZCOM*F8S)
       DCX(NCLTMP)=DXCOM*F6S-(F5S/ARGZ)*(DYCOM*F9S+DXCOM*DZCOM*F8S)
       NTMPFLG=0
      ENDIF 
  190 CONTINUE  
C TRANSFORM VELOCITY VECTORS TO LAB FRAME
      VTOT=CONST9*DSQRT(E1)
      CX1=DCX1*VTOT+VGX
      CY1=DCY1*VTOT+VGY
      CZ1=DCZ1*VTOT+VGZ
C CALCULATE ENERGY AND DIRECTION COSINES IN LAB FRAME
      E1=(CX1*CX1+CY1*CY1+CZ1*CZ1)/CONST10
      CONST11=1.0D0/(CONST9*DSQRT(E1))
      DCX1=CX1*CONST11
      DCY1=CY1*CONST11
      DCZ1=CZ1*CONST11
C STORE DIRECTION COSINES AND ENERGY AFTER N COLLISIONS
C FOR LATER REUSE IN PRIMARY GENERATION
      I100=I100+1
      IF(I100.EQ.200) THEN
       DCZ100=DCZ1
       DCX100=DCX1        
       DCY100=DCY1 
       E100=E1
       I100=0
      ENDIF  
      IF(IPRINT.GT.JPRINT) GO TO 200
      GO TO 1                                    
C    INTERMEDIATE PRINTOUT                       
 200  IPRINT=0 
      W=ZTOTS/TTOTS                          
      W=W*1.0D+09  
      WY=YTOTS/TTOTS   
      WY=WY*1.0D+09
      JCT=ID/100000
C     IF(J1.EQ.1) WRITE(6,201)                                         
C 201 FORMAT(/,7X,'INTERMEDIATE OUTPUT',/,'    VELZ     POSZ       TIME 
C    /      VELY        COUNT    ')                  
C     WRITE(6,202) W,ZTOTS,TTOTS,WY,JCT   
C 202 FORMAT(1X,F8.3,3(1X,D10.3),4X,I6)                 
      J1=J1+1            
      GO TO 1
C  MAIN LOOP END                                                  
  700 XID=DFLOAT(ID)
      IF(NELEC.GT.IPRIM) THEN
       ANEION=DFLOAT(NEION)
       ANBT=DFLOAT(NELEC-IPRIM)
       ATTOINT=ANEION/ANBT
       ATTERT=DSQRT(ANEION)/ANEION
       AIOERT=DSQRT(ANBT)/ANBT
      ELSE
       ANEION=DFLOAT(NEION) 
       ATTOINT=-1.0D0
       ATTERT=DSQRT(ANEION)/ANEION
      ENDIF
      JCT=ID/100000  
      IF(J1.EQ.1) THEN
      WRITE(6,940) NCLUS,ITER
  940 FORMAT(2(/),' PROGRAM STOPPED  (TOO FEW COLLISIONS), DECREASE THE 
     /ESTIMATED ALPHA.  NCLUS = ',I7,'  ITER =',I9)      
      STOP
      ENDIF
      IF(JPRT.EQ.1) WRITE(6,878) NELEC,NEION,IPRIM
 878  FORMAT(/,' TOTAL NO OF ELECTRONS=',I8,/,' TOTAL NO OF NEG. IONS=',
     /I8,/,' TOTAL NO OF PRIMARIES=',I8)  
      EPRMBAR=0.0D0
      E2PRM=0.0D0
      IF(IPRIM.EQ.1) RETURN      
      DO 310 I=1,IPRIM
      E2PRM=E2PRM+EPRM(I)*EPRM(I)
 310  EPRMBAR=EPRMBAR+EPRM(I)
      EBAR=EPRMBAR/IPRIM
      EERR=DSQRT(E2PRM/IPRIM-EBAR**2)
      IF(JPRT.EQ.1) WRITE(6,836) EBAR,EERR
 836  FORMAT(/,2X,'AVERAGE ENERGY OF PRIMARY ELECTRON =',F10.3,' EV.',/,
     /'   ENERGY SPREAD OF PRIMARY ELECTRON =',F10.3,' EV.')
C     WRITE(6,835) (IESPECP(J),J=1,100)
C835  FORMAT(/,2X,'ENERGY SPECTRUM OF PRIMARY ELECTRONS IN 1 EV. BINS',/
C    /,10(2X,10I5,/))
      RETURN               
  315 IF(ITER.GT.NMAX) THEN
       WRITE(6,991) ITER,NMAX,NPONT,NELEC,IPRIM,NMXADD
 991   FORMAT(2(/),' PROGRAM STOPPED.  ITER =',I10,'    NMAX =',I10,/,
     /' NPONT=',I4,' NELEC=',I8,' IPRIM=',I4,' NMXADD=',I3)
       STOP
      ENDIF                                                             
      RETURN                                                            
      END
      SUBROUTINE TPLANEGT(T,E1,CX1,CY1,CZ1,EFLD,IPLANE)
      IMPLICIT REAL*8 (A-H,O-Z)  
      IMPLICIT INTEGER*8 (I-N)
      COMMON/CNSTS1/CONST1,CONST2,CONST3,CONST4,CONST5
      COMMON/BFLD/EOVB,WB,BTHETA,BMAG
      COMMON/CION/ALPHAST,VDST,TSTEP,ZSTEP,TFINAL,ZFINAL,ITFINAL,IPRIM
      COMMON/OUTPTT/TIME(300),ICOLL(6,5),SPEC(4000),TMAX1,
     /AVE,DEN,XID,X,Y,Z,ST,NNULL,ICOLN(6,290),ICOLNN(6,10)
      COMMON/TPLOUTG/ETPL(8),XTPL(8),YTPL(8),ZTPL(8),TTPL(8),XXTPL(8),
     /YYTPL(8),ZZTPL(8),YZTPL(8),VZTPL(8),VYTPL(8),NETPL(8),ATTOINT,
     /ATTERT,AIOERT 
C-----------------------------------------------------------------------
C STORES POSITION, TIME AND ENERGY AND SUMS REQUIRED
C TO CALCULATE DEVIATIONS AND MEANS AT PLANE =IPLANE 
C BFIELD AT 90 DEGREES TO EFIELD
C-----------------------------------------------------------------------
      TIMESP=IPLANE*TSTEP
C CALC TIME LEFT TO ARRIVE AT PLANE
      TIMLFT=TIMESP-ST
      T2LFT=TIMLFT*TIMLFT
      WBT=WB*TIMLFT
      COSWT=DCOS(WBT)
      SINWT=DSIN(WBT)
      DZ=(CZ1*SINWT+(EOVB-CY1)*(1.0D0-COSWT))/WB
      CX2=CX1
      CY2=(CY1-EOVB)*COSWT+CZ1*SINWT+EOVB
      CZ2=CZ1*COSWT-(CY1-EOVB)*SINWT
      VTOT=DSQRT(CX2*CX2+CY2*CY2+CZ2*CZ2)
      DCZ2=CZ2/VTOT
      DCY2=CY2/VTOT
      XPLANE=X+CX1*TIMLFT
      YPLANE=Y+EOVB*TIMLFT+((CY1-EOVB)*SINWT+CZ1*(1.0D0-COSWT))/WB
      DZ=(CZ1*SINWT+(EOVB-CY1)*(1.0D0-COSWT))/WB
      ZPLANE=Z+DZ         
      EPLANE=E1+DZ*EFLD*100.0D0
      VZPLANE=DCZ2*DSQRT(EPLANE)*CONST3*0.01D0
      VYPLANE=DCY2*DSQRT(EPLANE)*CONST3*0.01D0
      XTPL(IPLANE)=XTPL(IPLANE)+XPLANE
      YTPL(IPLANE)=YTPL(IPLANE)+YPLANE
      ZTPL(IPLANE)=ZTPL(IPLANE)+ZPLANE
      XXTPL(IPLANE)=XXTPL(IPLANE)+XPLANE*XPLANE
      YYTPL(IPLANE)=YYTPL(IPLANE)+YPLANE*YPLANE
      ZZTPL(IPLANE)=ZZTPL(IPLANE)+ZPLANE*ZPLANE
      YZTPL(IPLANE)=YZTPL(IPLANE)+YPLANE*ZPLANE
      ETPL(IPLANE)=ETPL(IPLANE)+EPLANE
      TTPL(IPLANE)=TTPL(IPLANE)+ST+TIMLFT
      VZTPL(IPLANE)=VZTPL(IPLANE)+VZPLANE
      VYTPL(IPLANE)=VYTPL(IPLANE)+VYPLANE
      NETPL(IPLANE)=NETPL(IPLANE)+1 
      RETURN
      END
      SUBROUTINE PTG(JPRT)
      IMPLICIT REAL*8 (A-H,O-Z)                                         
      IMPLICIT INTEGER*8 (I-N)
      COMMON/CION/ALPHAST,VDST,TSTEP,ZSTEP,TFINAL,ZFINAL,ITFINAL,IPRIM
      COMMON/TPLOUTG/ETPL(8),XTPL(8),YTPL(8),ZTPL(8),TTPL(8),XXTPL(8),
     /YYTPL(8),ZZTPL(8),YZTPL(8),VZTPL(8),VYTPL(8),NETPL(8),ATTOINT,
     /ATTERT,AIOERT
      COMMON/PTTOFG/RI(8),EPT(8),VZPT(8),VYPT(8),TTEST(8)
      COMMON/FAKE/FAKEI,IFAKE,IFAKET(8),IFAKED(9) 
      DIMENSION ANTPL(8)
C ------------------------------------------------
C CALCULATES PULSED TOWNSEND COEFFICIENTS
C-------------------------------------------------
      ANTPL(1)=DFLOAT(NETPL(1))
      RI(1)=(DLOG(ANTPL(1))-DLOG(DFLOAT(IPRIM)))/TSTEP
      RI(1)=RI(1)-FAKEI
      EPT(1)=ETPL(1)/ANTPL(1)
      TTEST(1)=TTPL(1)/ANTPL(1)
      VZPT(1)=1.0D+09*VZTPL(1)/ANTPL(1)
      VYPT(1)=1.0D+09*VYTPL(1)/ANTPL(1)
      DO 10 I=2,ITFINAL
      IF(NETPL(I).EQ.0) THEN
       ITFINAL=I-1
       GO TO 11
      ENDIF
      ANTPL(I)=DFLOAT(NETPL(I))      
      RI(I)=(DLOG(ANTPL(I))-DLOG(ANTPL(I-1)))/TSTEP
      RI(I)=RI(I)-FAKEI
      EPT(I)=ETPL(I)/ANTPL(I)
      TTEST(I)=TTPL(I)/ANTPL(I)
      VZPT(I)=1.0D+09*VZTPL(I)/ANTPL(I)
      VYPT(I)=1.0D+09*VYTPL(I)/ANTPL(I)
  10  CONTINUE
  11  IF(JPRT.EQ.1) WRITE(6,900) ITFINAL
 900  FORMAT(2(/),' PULSED TOWNSEND RESULTS AT',I2,' SEQUENTIAL TIME PLA
     /NES',/,'PLANE   (ION-ATT)FRQ.    ENERGY      WVZ       WVY   NO.OF
     / ELECTRONS',/)   
      DO 20 IPL=1,ITFINAL
      IF(JPRT.EQ.1) WRITE(6,910) IPL,RI(IPL),EPT(IPL),VZPT(IPL),
     /VYPT(IPL),NETPL(IPL)
 910  FORMAT(1X,I2,4X,D12.4,4X,F7.2,4X,F6.1,4X,F6.1,4X,I8)
  20  CONTINUE 
      RETURN
      END
      SUBROUTINE TOFG(JPRT)
      IMPLICIT REAL*8 (A-H,O-Z)                                         
      IMPLICIT INTEGER*8 (I-N)
      COMMON/CION/ALPHAST,VDST,TSTEP,ZSTEP,TFINAL,ZFINAL,ITFINAL,IPRIM
      COMMON/TPLOUTG/ETPL(8),XTPL(8),YTPL(8),ZTPL(8),TTPL(8),XXTPL(8),
     /YYTPL(8),ZZTPL(8),YZTPL(8),VZTPL(8),VYTPL(8),NETPL(8),ATTOINT,
     /ATTERT,AIOERT
      COMMON/TOFGOUT/RALPHA,RALPER,TOFENE,TOFENER,TOFWVZ,TOFWVZER,
     /TOFWVY,TOFWVYER,TOFDZZ,TOFDZZER,TOFDXX,TOFDXXER,TOFDYY,TOFDYYER,
     /TOFDYZ,TOFDYZER,TOFWRZ,TOFWRZER,TOFWRY,TOFWRYER,RATTOF,RATOFER 
      COMMON/PTTOFG/RI(8),EPT(8),VZPT(8),VYPT(8),TTEST(8)
      COMMON/THRM/AMGAS(6),VTMB(6),TCFMX,TCFMXG(6),ITHRM
      DIMENSION DZTF(8),DXTF(8),DYTF(8),DYZTF(8),WRZ(8),WRY(8),ANTPL(8)
C----------------------------------------------------------
C CALCULATES TIME OF FLIGHT COEFFICIENTS
C---------------------------------------------
      IF(ITHRM.EQ.1) CALL COLFT(DUM1,DUM2,FRION,FRATT,DUM3,IDUM4)
      IF(ITHRM.EQ.0) CALL COLF(DUM1,DUM2,FRION,FRATT,DUM3,IDUM4)
      CORERR=DABS((FAKEI+FRION-FRATT)/(FRION-FRATT))
      ATTOINT=FRATT/FRION
      ANTPL(1)=DFLOAT(NETPL(1))
      WRZ(1)=ZTPL(1)/(ANTPL(1)*TSTEP)
      WRY(1)=YTPL(1)/(ANTPL(1)*TSTEP)
      DZTF(1)=((ZZTPL(1)/ANTPL(1))-(ZTPL(1)/ANTPL(1))**2)/(2.0D0*TSTEP)
      DXTF(1)=((XXTPL(1)/ANTPL(1))-(XTPL(1)/ANTPL(1))**2)/(2.0D0*TSTEP)
      DYTF(1)=((YYTPL(1)/ANTPL(1))-(YTPL(1)/ANTPL(1))**2)/(2.0D0*TSTEP)
      DYZTF(1)=((YZTPL(1)/ANTPL(1))-(YTPL(1)*ZTPL(1)/(ANTPL(1)**2)))/(2
     /.0D0*TSTEP) 
      DO 10 I=2,ITFINAL
      ANTPL(I)=DFLOAT(NETPL(I)) 
      WRZ(I)=((ZTPL(I)/ANTPL(I))-(ZTPL(I-1)/ANTPL(I-1)))/TSTEP
      WRY(I)=((YTPL(I)/ANTPL(I))-(YTPL(I-1)/ANTPL(I-1)))/TSTEP
      DZTF(I)=((ZZTPL(I)/ANTPL(I))-(ZTPL(I)/ANTPL(I))**2-(ZZTPL(I-1)/ANT
     /PL(I-1))+(ZTPL(I-1)/ANTPL(I-1))**2)/(2.0D0*TSTEP)
      DXTF(I)=((XXTPL(I)/ANTPL(I))-(XTPL(I)/ANTPL(I))**2-(XXTPL(I-1)/ANT
     /PL(I-1))+(XTPL(I-1)/ANTPL(I-1))**2)/(2.0D0*TSTEP)
      DYTF(I)=((YYTPL(I)/ANTPL(I))-(YTPL(I)/ANTPL(I))**2-(YYTPL(I-1)/ANT
     /PL(I-1))+(YTPL(I-1)/ANTPL(I-1))**2)/(2.0D0*TSTEP)
      DYZTF(I)=((YZTPL(I)/ANTPL(I))-(YTPL(I)*ZTPL(I)/(ANTPL(I)**2))-(YZT
     /PL(I-1)/ANTPL(I-1))+(YTPL(I-1)*ZTPL(I-1)/(ANTPL(I-1)**2)))/(2.0D0*
     /TSTEP)
  10  CONTINUE  
      DO 15 I=1,ITFINAL
      WRZ(I)=WRZ(I)*1.0D+09
      WRY(I)=WRY(I)*1.0D+09
      DZTF(I)=DZTF(I)*1.0D+16
      DXTF(I)=DXTF(I)*1.0D+16
      DYTF(I)=DYTF(I)*1.0D+16
      DYZTF(I)=DYZTF(I)*1.0D+16
  15  CONTINUE
      IF(JPRT.EQ.1) WRITE(6,900) ITFINAL
 900  FORMAT(2(/),' TIME OF FLIGHT RESULTS AT',I2,' SEQUENTIAL TIME PLAN
     /ES',/,'PLANE    DZZ      DXX      DYY      DYZ        WRZ         
     /WRY',/)
      DO 20 IPL=1,ITFINAL   
      IF(JPRT.EQ.1) WRITE(6,910) IPL,DZTF(IPL),DXTF(IPL),DYTF(IPL),
     /DYZTF(IPL),WRZ(IPL),WRY(IPL)  
 910  FORMAT(1X,I2,2X,4F9.1,4X,F8.2,4X,F8.2)
  20  CONTINUE    
      IF(NETPL(1).GT.NETPL(ITFINAL)) THEN
C        NET ATTACHMENT TAKE RESULTS FROM PLANE 2
       TOFENE=EPT(2)
       TOFENER=100.0D0*DABS((EPT(2)-EPT(3))/(2.0D0*EPT(2)))
       TOFWVZ=VZPT(2)
       TOFWVZER=100.0D0*DABS((VZPT(2)-VZPT(3))/(2.0D0*VZPT(2))) 
       TOFWVY=VYPT(2)
       TOFWVYER=100.0D0*DABS((VYPT(2)-VYPT(3))/(2.0D0*VYPT(2))) 
       TOFDZZ=DZTF(2)
       TOFDZZER=100.0D0*DABS((DZTF(2)-DZTF(3))/(2.0D0*DZTF(2)))
       TOFDXX=DXTF(2)
       TOFDXXER=100.0D0*DABS((DXTF(2)-DXTF(3))/(2.0D0*DXTF(2)))
       TOFDYY=DYTF(2)
       TOFDYYER=100.0D0*DABS((DYTF(2)-DYTF(3))/(2.0D0*DYTF(2)))
       TOFDYZ=DYZTF(2)
       TOFDYZER=100.0D0*DABS((DYZTF(2)-DYZTF(3))/(2.0D0*DYZTF(2)))
       TOFWRZ=WRZ(2)
       TOFWRY=WRY(2)
       TOFWRZER=100.0D0*DABS((WRZ(2)-WRZ(3))/(2.0D0*WRZ(2)))
       TOFWRYER=100.0D0*DABS((WRY(2)-WRY(3))/(2.0D0*WRY(2))) 
        ANST2=DFLOAT(NETPL(2))
        ANST3=DFLOAT(NETPL(3))
        ANST4=ANST3-DSQRT(ANST3)
        ANST5=DLOG(ANST2/ANST3)
        ANST6=DLOG(ANST2/ANST4)
        ANST7=ANST6/ANST5
        ANST8=ANST7-1.0D0
       IF(ATTOINT.EQ.-1.0D0) THEN
C        NO IONISATION
        RALPHA=0.0D0
        RALPER=0.0D0
        RATTOF=-RI(2)
        RATOFER=100.0D0*DSQRT(ANST8**2+ATTERT**2)
       ELSE
        RALPHA=RI(2)/(1.0D0-ATTOINT)
        RALPER=100.0D0*DSQRT(ANST8**2+AIOERT**2)
        RATTOF=ATTOINT*RI(2)/(1.0D0-ATTOINT)
        RATOFER=100.0D0*DSQRT(ANST8**2+ATTERT**2)
       ENDIF 
      ELSE
C NET IONISATION TAKE RESULTS FROM PLANE ITFINAL
       I1=ITFINAL
       I2=ITFINAL-1  
       TOFENE=EPT(I1)
       TOFENER=100.0D0*DABS((EPT(I1)-EPT(I2))/EPT(I1))
       TOFWVZ=VZPT(I1)
       TOFWVZER=100.0D0*DABS((VZPT(I1)-VZPT(I2))/VZPT(I1))
       TOFWVY=VYPT(I1)
       TOFWVYER=100.0D0*DABS((VYPT(I1)-VYPT(I2))/VYPT(I1))
       TOFDZZ=DZTF(I1)
       TOFDZZER=100.0D0*DABS((DZTF(I1)-DZTF(I2))/DZTF(I1))
       TOFDXX=DXTF(I1)
       TOFDXXER=100.0D0*DABS((DXTF(I1)-DXTF(I2))/DXTF(I1))
       TOFDYY=DYTF(I1)
       TOFDYYER=100.0D0*DABS((DYTF(I1)-DYTF(I2))/DYTF(I1))
       TOFDYZ=DYZTF(I1)
       TOFDYZER=100.0D0*DABS((DYZTF(I1)-DYZTF(I2))/DYZTF(I1))
       TOFWRZ=WRZ(I1)
       TOFWRY=WRY(I1)
       TOFWRZER=100.0D0*DABS((WRZ(I1)-WRZ(I2))/WRZ(I1))
       TOFWRYER=100.0D0*DABS((WRY(I1)-WRY(I2))/WRY(I1))
       ATER=DABS((RI(I1)-RI(I2))/RI(I1))
       RALPHA=RI(I1)/(1.0D0-ATTOINT)
       RALPER=100.0D0*DSQRT(ATER**2+AIOERT**2)
       RATTOF=ATTOINT*RI(I1)/(1.0D0-ATTOINT)
       IF(ATTOINT.NE.0.0D0) THEN
        RATOFER=100.0D0*DSQRT(ATER**2+ATTERT**2)
       ELSE
        RATOFER=0.0D0
       ENDIF  
      ENDIF
      RETURN
      END                
      SUBROUTINE ALPCLCCT   
      IMPLICIT REAL*8 (A-H,O-Z)
      IMPLICIT INTEGER*8 (I-N)
      COMMON/INPT/NGAS,NSTEP,NANISO,EFINAL,ESTEP,AKT,ARY,TEMPC,TORR,IPEN
      COMMON/SETP/TMAX,SMALL,API,ESTART,THETA,PHI,RSTART,EFIELD,NMAX 
      COMMON/TOFHOUT/RALPHA,RALPER,TOFENE,TOFENER,TOFWVZ,TOFWVZER,
     /TOFWVY,TOFWVYER,TOFWVX,TOFWVXER,TOFDZZ,TOFDZZER,TOFDXX,TOFDXXER,
     /TOFDYY,TOFDYYER,TOFDYZ,TOFDYZER,TOFDXY,TOFDXYER,TOFDXZ,TOFDXZER,
     /TOFWRZ,TOFWRZER,TOFWRY,TOFWRYER,TOFWRX,TOFWRXER,RATTOF,RATOFER 
      COMMON/VEL/WX,WY,WZ
      COMMON/CTOWNS/ALPHA,ATT
      COMMON/CTWNER/ALPER,ATTER
      COMMON/CION/ALPHAST,VDST,TSTEP,ZSTEP,TFINAL,ZFINAL,ITFINAL,IPRIM
      COMMON/FAKE/FAKEI,IFAKE,IFAKET(8),IFAKED(9) 
      COMMON/LARGET/CF(6,4000,290),EIN(6,290),TCF(6,4000),IARRY(6,290),
     /RGAS(6,290),IPN(6,290),WPL(6,290),IPLAST(6),ISIZE(6),
     /PENFRA(6,3,290),TCFMAX(6)
C ----------------------------------------------------------------------     
C  ESTIMATE TIME STEP  FOR AVALANCHE SIMULATION IN TIME OF FLIGHT SIM. 
C  USES ESTIMATED GAIN OF 3.0 BETWEEN PLANES. 
C  CALLS TOF AND PT SUBROUTINES AND UPDATES ALPHA AND ATT
C  BFIELD AT ANY ANGLE BTHETA TO EFIELD
C ----------------------------------------------------------------------
      IMAX=NMAX/10000000 
      IF(IMAX.LT.5) IMAX=5 
      NMAX=IMAX*10000000
      CORR=760.0*(TEMPC+273.15)/(TORR*293.15)
      ALPP=ALPHA/CORR
      ATTP=ATT/CORR
      ANETP=ALPP-ATTP
      ANET=ALPHA-ATT
      TCUTH=1.2D-10*CORR
      TCUTL=1.D-13*CORR
C   
      IF (ANETP.GT.30.0) THEN
       ALPHAD=0.0
       ALP1=ALPHA
       ATT1=ATT
C WEAK ATTACHMENT
       GO TO 6
      ENDIF 
C  SOME ATTACHMENT
      IF(DABS(ANETP).LT.100.) THEN
C SMALL NET ATTACHMENT
       ALPHAD=DABS(ATT)*0.8
      ELSE IF(DABS(ANETP).GE.100..AND.DABS(ANETP).LT.1000.) THEN
C LARGER NET ATTACHMENT
       ALPHAD=DABS(ANET)*0.65
      ELSE IF(DABS(ANETP).GT.1000..AND.DABS(ANETP).LT.10000.) THEN
       ALPHAD=DABS(ANET)*0.6
      ELSE IF(DABS(ANETP).GT.10000..AND.DABS(ANETP).LT.100000.) THEN
       ALPHAD=DABS(ANET)*0.5
      ELSE IF(DABS(ANETP).GT.100000..AND.DABS(ANETP).LT.2000000.) THEN
       ALPHAD=DABS(ANET)*0.4
      ELSE 
       WRITE(6,91) 
   91 FORMAT(' ATTACHMENT TOO LARGE PROGRAM STOPPED')
       STOP
      ENDIF
   3  VDST=DSQRT(WZ*WZ+WY*WY+WX*WX)*1.D-5
      FAKEI=ALPHAD*DSQRT(WZ*WZ+WY*WY+WZ*WZ)*1.D-12
      ALPHAST=0.85*DABS(ALPHAD+ANET)                        
      TSTEP=DLOG(3.0D0)/(ALPHAST*VDST*1.0D5)
      IF(TSTEP.GT.TCUTH) TSTEP=TCUTH
      IF(TSTEP.LT.TCUTL) TSTEP=TCUTL
C UPDATE NULL COLLISION FREQUENCY LIMIT
      DO 4 J=1,NGAS 
    4 TCFMAX(J)=TCFMAX(J)+DABS(FAKEI)/DFLOAT(NGAS)
      WRITE(6,871) ALPHAST,ANET,ALPHAD,TSTEP
  871 FORMAT(' ALPHAST1=',D10.3,' ANET1=',D10.3,' ALPHAD1=',D10.3,/,
     /' TSTEP1=',D10.3)
C  CONVERT TO  PICOSECONDS     
      TSTEP=TSTEP*1.0D12
      TFINAL=7.0D0*TSTEP
      ITFINAL=7
C CALCULATE GOOD STARTING VALUES FOR ALPHA AND NETA
      JPRT=0
      CALL MONTEFTHT(JPRT)
      CALL PTH(JPRT)
      CALL TOFH(JPRT)
      TOFWR=DSQRT(TOFWRZ*TOFWRZ+TOFWRY*TOFWRY+TOFWRX*TOFWRX)
      ALP1=RALPHA/TOFWR*1.D7
      ALP1ER=RALPER*ALP1/100. 
      ATT1=RATTOF/TOFWR*1.D7
      ATT1ER=RATOFER*ATT1/100.
      WRITE(6,944) ALP1,ALP1ER,ATT1,ATT1ER
  944 FORMAT(' GOOD STARTING VALUES FOR CALC:',/,' ALPHA=',D12.3,' ERR='
     /,D12.3,/,' NETA =',D12.3,' ERR=',D12.3)
C
C CALCULATE USING GOOD STARTING VALUES
C  RESET TCFMAX
      DO 44 J=1,NGAS
   44 TCFMAX(J)=TCFMAX(J)-DABS(FAKEI)/DFLOAT(NGAS)
C CALCULATE FAKE IONISATION RATE SCALING BY 1.2 
      ALPHAD=DABS(ATT1)*1.2
      IF((ALP1-ATT1).GT.30.*CORR) ALPHAD=DABS(ATT1)*0.4
      IF(DABS(ALP1-ATT1).LT.(ALP1/10.0).OR.DABS(ALP1-ATT1).LT.
     /(ATT1/10.0)) ALPHAD=DABS(ATT1)*0.3
      IF((ALP1-ATT1).GT.100.*CORR) ALPHAD=0.0
      WZ=TOFWRZ*1.D5
      WY=TOFWRY*1.D5
      WX=TOFWRX*1.D5
C
C
C NET IONISATION
C   5 IF(ANET.GT.(11000./CORR)) THEN
C HIGH IONISATION
C      ALPHAD=7000.-(ALPHA-ATT)
C     ELSE IF (DABS(ALPP).GT.(5.0*DABS(ANET)).OR.DABS(ATTP).GT.
C    /(5.0*DABS(ANET))) THEN
C LARGE CANCELLATION BETWEEN ATTACHMENT AND IONISATION
C      ALPHAD=-6.0*DABS(ALPHA-ATT)
C     ELSE
C      ALPHAD=0.0
C     ENDIF
    6 VTTT=DSQRT(WZ*WZ+WY*WY+WX*WX) 
      VDST=VTTT*1.D-5
      FAKEI=ALPHAD*VTTT*1.D-12
      ALPHAST=0.85D0*DABS(ALPHAD+ALP1-ATT1)
      IF((ALP1+ALPHAD).GT.(10.0*ALPHAST).OR.ATT1.GT.(10.0*ALPHAST)) THEN
C LARGE CANCELLATION BETWEEN ALPHA AND NETA. REDUCE STEP SIZE BY 15.0
       IF((ALP1+ALPHAD).GT.100.*CORR) THEN
        ALPHAST=ALPHAST*15.0
       ELSE IF((ALP1+ALPHAD).GT.50.0*CORR) THEN
        ALPHAST=ALPHAST*12.0
       ELSE
        ALPHAST=ALPHAST*8.0
       ENDIF
      ENDIF
      TSTEP=DLOG(3.0D0)/(ALPHAST*VDST*1.0D5)
      IF(TSTEP.GT.TCUTH.AND.ALPHAD.NE.0.0) TSTEP=TCUTH
C UPDATE NULL COLLISION FREQUENCY
      DO 10 J=1,NGAS
   10 TCFMAX(J)=TCFMAX(J)+DABS(FAKEI)/NGAS
      ANET1=ALP1-ATT1
      WRITE(6,878) ALPHAST,ANET1,ALPHAD,TSTEP
  878 FORMAT(' ALPHAST=',D10.3,' ANET=',D10.3,' ALPHAD=',D10.3,/,
     /' TSTEP=',D10.3)
      TSTEP=TSTEP*1.0D12
      TFINAL=7.0D0*TSTEP
      ITFINAL=7
C CALC TIME OF FLIGHT AND PT 
      WRITE(6,25)
   25 FORMAT(/,2X,'SOLUTION FOR PULSED TOWNSEND AND TIME OF FLIGHT PARAM
     /ETERS',/,'  ------------------------------------------------------
     /--------') 
      WRITE(6,26) TSTEP
   26 FORMAT(1(/),'  TIME STEP BETWEEN SAMPLING PLANES =',D12.5,' PICOSE
     /CS.',/)       
      JPRT=1              
      CALL MONTEFTHT(JPRT)                             
      CALL FRIEDLANDT 
      CALL PTH(JPRT)
      CALL TOFH(JPRT)
      WRITE(6,27) RALPHA,RALPER,RATTOF,RATOFER
   27 FORMAT(/,'PT IONISATION AND ATTACHMENT RATES *10**12/SEC',/,'ALPHA
     /=',D10.3,' +- ',F6.2,' %      ATT=',D10.3,' +- ',F6.2,' %')    
      WRITE(6,28) 
   28 FORMAT(/,'TOF DIFFUSION')
      WRITE(6,29) TOFDZZ,TOFDZZER,TOFDXX,TOFDXXER,TOFDYY,TOFDYYER,  
     /TOFDYZ,TOFDYZER,TOFDXZ,TOFDXZER,TOFDXY,TOFDXYER  
   29 FORMAT(/,'DZZ=',F8.1,' +- ',F5.1,' %',/,'DXX=',F8.1,' +- ',F5.1,' 
     /%',/,'DYY=',F8.1,' +- ',F5.1,' %',/,'DYZ=',F8.1,' +- ',F5.1,' %',/
     /,'DXZ=',F8.1,' +- ',F5.1,' %',/,'DXY=',F8.1,' +- ',F5.1,' %')
      WRITE(6,30)
   30 FORMAT(/,'TOF DRIFT VELOCITY')
      WRITE(6,31) TOFWRZ,TOFWRZER,TOFWRY,TOFWRYER,TOFWRX,TOFWRXER   
   31 FORMAT(/,'WRZ=',F8.2,' +-',F6.1,' %    WRY=',F8.2,' +-',F6.1,' %  
     / WRX=',F8.2,' +-',F6.1,' %')  
C CALCULATE TOWNSEND SST COEFICIENTS FROM TOF RESULTS   
      TOFWR=DSQRT(TOFWRZ*TOFWRZ+TOFWRY*TOFWRY+TOFWRX*TOFWRX)   
      WRZN=TOFWR*1.0D05
      FC1=WRZN/(2.0D0*TOFDZZ)
      FC2=((RALPHA-RATTOF)*1.0D12)/TOFDZZ
      ALPZZ=FC1-DSQRT(FC1**2-FC2)
      WRITE(6,888) ALPZZ
  888 FORMAT(/,'TOWNSEND COEFICIENT (ALPHA-ATT) CALCULATED FROM TOF RESU
     /LTS:',/,' ALPHA-ATT /CM.=',D11.4,' N.B. APPROXIMATE FORMULA NOT AC
     /CURATE',/)  
C      -----------------------------------------------------    
C LOAD NEW ALPHA AND ATTACHMENT INTO COMMON BLOCKS
C      -----------------------------------------------
      ALPHA=RALPHA/TOFWR*1.D7
      ALPER=RALPER*ALPHA/100.
      ATT=RATTOF/TOFWR*1.D7
      ATTER=RATOFER*ATT/100.
C     WRITE(6,32) ALPHA,ALPER,ATT,ATTER 
C  32 FORMAT(/,'TOWNSEND COEFICIENTS CALCULATED FROM TOF RESULTS:',2(/),
C    /'IONISATION RATE /CM.=',D11.4,' +-',F6.2,/,'ATTACHMENT RATE /CM.='
C    /,D11.4,' +-',F6.2,/)           
C      ------------------------------------------      
      RETURN                                                     
      END                                                               
      SUBROUTINE MONTEFTHT(JPRT)                                       
      IMPLICIT REAL*8 (A-H,O-Z)                                         
      IMPLICIT INTEGER*8 (I-N)
      COMMON/INPT/NGAS,NSTEP,NANISO,EFINAL,ESTEP,AKT,ARY,TEMPC,TORR,IPEN
      COMMON/CNSTS1/CONST1,CONST2,CONST3,CONST4,CONST5                  
      COMMON/SETP/TMAX,SMALL,API,ESTART,THETA,PHI,RSTART,EFIELD,NMAXOLD
      COMMON/BFLD/EOVB,WB,BTHETA,BMAG
      COMMON/ROTS/RCS,RSN,EFZ100,EFX100,F1,EOVBR                        
      COMMON/LARGET/CF(6,4000,290),EIN(6,290),TCF(6,4000),IARRY(6,290),
     /RGAS(6,290),IPN(6,290),WPL(6,290),IPLAST(6),ISIZE(6),
     /PENFRA(6,3,290),TCFMAX(6)
      COMMON/LARGENT/CFN(6,4000,10),TCFN(6,4000),SCLENUL(6,10),NPLAST(6)
      COMMON/CION/ALPHAST,VDST,TSTEP,ZSTEP,TFINAL,ZFINAL,ITFINAL,IPRIM
      COMMON/OUTPTT/TIME(300),ICOLL(6,5),SPEC(4000),TMAX1,
     /AVE,DEN,XID,X,Y,Z,ST,NNULL,ICOLN(6,290),ICOLNN(6,10)
      COMMON/TTRM/ZTOT,TTOT,ZTOTS,TTOTS
      COMMON/THRM/AMGAS(6),VTMB(6),TCFMX,TCFMXG(6),ITHRM
      COMMON/RANM/RNMX(6)
      COMMON/ISPT/XS(2000),YS(2000),ZS(2000),TS(2000),ES(2000),
     /DCX(2000),DCY(2000),DCZ(2000),IPL(2000)
      COMMON/TPLOUTH/ETPL(8),XTPL(8),YTPL(8),ZTPL(8),TTPL(8),XXTPL(8),
     /YYTPL(8),ZZTPL(8),YZTPL(8),XZTPL(8),XYTPL(8),VZTPL(8),VYTPL(8),
     /VXTPL(8),NETPL(8),ATTOINT,ATTERT,AIOERT
      COMMON/ANIST/PSCT(6,4000,290),ANGCT(6,4000,290),INDEX(6,290),NISO 
      COMMON/IONFL/NC0(6,290),EC0(6,290),NG1(6,290),EG1(6,290),
     /NG2(6,290),EG2(6,290),WKLM(6,290),EFL(6,290)
      COMMON/FAKE/FAKEI,IFAKE,IFAKET(8),IFAKED(9) 
      DIMENSION EPRM(10000000),IESPECP(100),TEMP(6,4000)
C -------------------------------------------------------------------   
C   CALCULATES COLLISION EVENTS AND UPDATES DIFFUSION AND VELOCITY.
C   THIS ROUTINE HANDLES TERMINATIONS AT FIXED DRIFT TIMES. 
C   SOLVES MOTION IN COORDINATE SYSTEM WITH BFIELD ALIGNED TO X-AXIS
C   ELECTRIC FIELD AT AN ANGLE BTHETA IN THE X-Z PLANE.
C   THE RESULTS FOR THE VELOCITY VECTORS  ARE THEN 
C   ROTATED INTO THE STANDARD COORDINATE FRAME WITH THE ELECTRIC FIELD 
C   ALONG THE Z-AXIS AND THE BFIELD AT AN ANGLE BTHETA TO THE ELECTRIC
C   FIELD IN THE X-Z PLANE  
C ------------------------------------------------------------------- 
      IF(JPRT.EQ.0) THEN
C CALCULATION OF APPROXIMATE STARTING PARAMETERS
       NMAX=NMAXOLD
       IF(NMAXOLD.GT.80000000) NMAX=80000000
      ELSE
C ENTRY FOR NORMAL CALCULATION
       NMAX=NMAXOLD
      ENDIF 
      S=0.0D0 
      ST=0.0D0 
      X=0.0D0
      Y=0.0D0
      Z=0.0D0 
      ZTOT=0.0D0
      ZTOTS=0.0D0
      YTOT=0.0D0
      YTOTS=0.0D0
      XTOT=0.0D0
      XTOTS=0.0D0
      TTOT=0.0D0
      TTOTS=0.0D0
      SMALL=1.0D-20                                                     
      TMAX1=0.0D0                                                       
      RDUM=RSTART                                                       
      E1=ESTART                                                         
      DO 25 I=1,300
   25 TIME(I)=0.0D0
      DO 26 I=1,5
      DO 26 K=1,6
   26 ICOLL(K,I)=0
      DO 27 I=1,290
      DO 27 K=1,6
   27 ICOLN(K,I)=0
      DO 271 I=1,10
      DO 271 K=1,6
  271 ICOLNN(K,I)=0
      DO 28 I=1,4000
   28 SPEC(I)=0.0D0            
      DO 33 I=1,100
   33 IESPECP(I)=0 
      DO 34 I=1,8
      ETPL(I)=0.0D0
      XTPL(I)=0.0D0
      YTPL(I)=0.0D0
      ZTPL(I)=0.0D0
      TTPL(I)=0.0D0
      XXTPL(I)=0.0D0
      YYTPL(I)=0.0D0
      ZZTPL(I)=0.0D0
      YZTPL(I)=0.0D0
      XZTPL(I)=0.0D0
      XYTPL(I)=0.0D0
      VZTPL(I)=0.0D0
      VYTPL(I)=0.0D0
      VXTPL(I)=0.0D0
   34 NETPL(I)=0                                   
      ID=0
      N4000=4000
      N300=300
      N100=100
      I100=0                                                            
      NCOL=0                                                            
      NNULL=0
      NELEC=0
      NEION=0
      NMXADD=0
      NTMPFLG=0
      NPONT=0 
      NCLUS=0
      J1=1 
      ZSTRT=0.0D0
      YSTRT=0.0D0
      XSTRT=0.0D0
      TSSTRT=0.0D0 
      API=DACOS(-1.0D0)  
C
      DO 111 K=1,6
      DO 111 J=1,N4000
  111 TEMP(K,J)=TCF(K,J)+TCFN(K,J)
      ABSFAKEI=DABS(FAKEI)
      IFAKE=0
      DO 112 J=1,8
  112 IFAKET(J)=0
C
C CALC ROTATION MATRIX ANGLES
      RCS=DCOS((BTHETA-90.0D0)*API/180.0D0)
      RSN=DSIN((BTHETA-90.0D0)*API/180.0D0)
C 
      RTHETA=BTHETA*API/180.0D0
      EFZ100=EFIELD*100.0D0*DSIN(RTHETA)
      EFX100=EFIELD*100.0D0*DCOS(RTHETA)
      F1=EFIELD*CONST2*DCOS(RTHETA)
      F4=2.0D0*API
      CONST9=CONST3*0.01D0
      CONST10=CONST9*CONST9
      EOVBR=EOVB*DSIN(RTHETA)
C GENERATE INITIAL RANDOM NUMBERS FOR MAXWELL BOLTZMAN 
      CALL GERJAN
      IMBPT=0
C     INITIAL DIRECTION COSINES                                         
      DCZ1=DCOS(THETA)                                                  
      DCX1=DSIN(THETA)*DCOS(PHI)                                        
      DCY1=DSIN(THETA)*DSIN(PHI) 
C INITIAL VELOCITY
      VTOT=CONST9*DSQRT(E1)
      CX1=DCX1*VTOT
      CY1=DCY1*VTOT
      CZ1=DCZ1*VTOT      
      E100=E1
      DCZ100=DCZ1
      DCX100=DCX1
      DCY100=DCY1                                       
      JPRINT=NMAX/10
      IPRINT=0
      ITER=0
      IPLANE=0
      IPRIM=0
C  LOOP FOR NEW STARTING ELECTRONS 
  544 IPRIM=IPRIM+1 
      IF(IPRIM.GT.1) THEN
C CHECK IF PROGRAM WILL EXCEED MAXIMUM NUMBER OF ITERATIONS 
C IN THIS CYCLE IF SO OUTPUT CURRENT RESULTS. 
       IF(ITER.GT.NMAX) THEN
        IPRIM=IPRIM-1
        GO TO 700      
       ENDIF 
       X=0.0D0
       Y=0.0D0
       Z=0.0D0
       DCZ1=DCZ100
       DCX1=DCX100
       DCY1=DCY100
       E1=E100
       VTOT=CONST9*DSQRT(E1)
       CX1=DCX1*VTOT
       CY1=DCY1*VTOT
       CZ1=DCZ1*VTOT
       NCLUS=NCLUS+1
       ST=0.0D0
       TSSTRT=0.0D0
       ZSTRT=0.0D0
       YSTRT=0.0D0
       XSTRT=0.0D0
       IPLANE=0
      ENDIF
      IF(IPRIM.GT.10000000) THEN
      WRITE(6,944) IPRIM
 944  FORMAT(/,2X,'PROGRAM STOPPED TOO MANY PRIMARIES IPRIM =',I7)
      GO TO 700
      ENDIF
      EPRM(IPRIM)=E1
      IDUM=DINT(E1)+1
      IDUM=DMIN0(IDUM,N100)
      IESPECP(IDUM)=IESPECP(IDUM)+1      
C START OF LOOP FOR NEWLY CREATED ELECTRONS
  555 TDASH=0.0D0
      NELEC=NELEC+1
      TSTOP=TSTEP+IPLANE*TSTEP       
C MAIN LOOP                                                             
    1 CONTINUE                                                          
C     IF(ITER.GT.NMAX)  GO TO 315
      R1=drand48(RDUM)
      T=-DLOG(R1)/TCFMX+TDASH
      TDASH=T
      WBT=WB*T
      COSWT=DCOS(WBT)
      SINWT=DSIN(WBT)
  15  IF((T+ST).GE.TSTOP) THEN                     
       IPLANE=IPLANE+1
       TSTOP=TSTOP+TSTEP
C STORE POSITION AND ENERGY AT TIME PLANE =IPLANE. 
       CALL TPLANEHT(T,E1,CX1,CY1,CZ1,EFIELD,IPLANE)
C CHECK IF PASSED THROUGH MORE THAN ONE PLANE IN THIS STEP       
       IF((T+ST).GE.TSTOP.AND.TSTOP.LE.TFINAL) GO TO 15
       IF((T+ST).GE.TFINAL) THEN
        ZTOT=ZTOT+Z
        YTOT=YTOT+Y
        XTOT=XTOT+X     
        TTOT=TTOT+ST
        ZTOTS=ZTOTS+Z-ZSTRT 
        YTOTS=YTOTS+Y-YSTRT
        XTOTS=XTOTS+X-XSTRT     
        TTOTS=TTOTS+ST-TSSTRT    
        TSTOP=TSTEP
C NO MORE ELECTRONS IN CASCADE TRY NEW PRIMARY ELECTRON
        IF(NELEC.EQ.(NCLUS+1)) GO TO 544  
C TAKE ELECTRONS FROM STORE 
   20   X=XS(NPONT) 
        Y=YS(NPONT) 
        Z=ZS(NPONT) 
        ST=TS(NPONT) 
        E1=ES(NPONT) 
        DCX1=DCX(NPONT) 
        DCY1=DCY(NPONT) 
        DCZ1=DCZ(NPONT)
        VTOT=CONST9*DSQRT(E1)
        CX1=DCX1*VTOT
        CY1=DCY1*VTOT
        CZ1=DCZ1*VTOT 
        IPLANE=IPL(NPONT)   
        NPONT=NPONT-1 
        ZSTRT=Z 
        YSTRT=Y
        XSTRT=X
        TSSTRT=ST
        GO TO 555
       ENDIF              
      ENDIF
      DZ=(CZ1*SINWT+(EOVBR-CY1)*(1.0D0-COSWT))/WB
      DX=CX1*T+F1*T*T
      E=E1+DZ*EFZ100+DX*EFX100
 913  FORMAT(3X,' AFTER STORE ITER=',I10,' DZ=',D12.3,'E1=',D12.3,/,' CO
     /SWT=',D12.6,' SINWT=',D12.6,' WBT=',D12.3,' CY1=',D12.3)   
      IF(E.LT.0.0D0) THEN
C      WRITE(6,913)ITER,DZ,E,COSWT,SINWT,WBT,CY1  
       E=0.001D0
      ENDIF            
C CALCULATE ELECTRON VELOCITY IN LAB FRAME
      CX2=CX1+2.0D0*F1*T
      CY2=(CY1-EOVBR)*COSWT+CZ1*SINWT+EOVBR
      CZ2=CZ1*COSWT-(CY1-EOVBR)*SINWT
C FIND IDENTITY OF GAS FOR COLLISION
      KGAS=0
      IF(NGAS.EQ.1) THEN
      R2=drand48(RDUM)
       KGAS=1
       GO TO 4
      ENDIF
      R2=drand48(RDUM)
    3 KGAS=KGAS+1
      IF(TCFMXG(KGAS).LT.R2) GO TO 3
C CALCULATE GAS VELOCITY VECTORS VGX,VGY,VGZ
    4 IMBPT=IMBPT+1
      IF(IMBPT.GT.6) THEN
       CALL GERJAN
       IMBPT=1
      ENDIF
      VGX=VTMB(KGAS)*RNMX(IMBPT)
      IMBPT=IMBPT+1
      VGY=VTMB(KGAS)*RNMX(IMBPT)
      IMBPT=IMBPT+1
      VGZ=VTMB(KGAS)*RNMX(IMBPT)
C CALCULATE ENERGY WITH STATIONARY GAS TARGET, EOK.
      EOK=((CX2-VGX)**2+(CY2-VGY)**2+(CZ2-VGZ)**2)/CONST10
      IE=DINT(EOK/ESTEP)+1                                             
      IE=DMIN0(IE,N4000)                                               
C                                                                       
C     TEST FOR REAL OR NULL COLLISION   
C                                
      R5=drand48(RDUM)                                           
      TEST1=TCF(KGAS,IE)/TCFMAX(KGAS)                                   
      IF(R5.LE.TEST1) GO TO 137
      NNULL=NNULL+1
      TEST2=TEMP(KGAS,IE)/TCFMAX(KGAS)
      IF(R5.LT.TEST2) THEN
C TEST FOR NULL LEVELS
       IF(NPLAST(KGAS).EQ.0) GO TO 1 
       R2=drand48(RDUM)
       I=0
  888  I=I+1
       IF(CFN(KGAS,IE,I).LT.R2) GO TO 888
C INCREMENT NULL SCATTER SUM
       ICOLNN(KGAS,I)=ICOLNN(KGAS,I)+1
       GO TO 1
      ENDIF  
      TEST3=(TEMP(KGAS,IE)+ABSFAKEI)/TCFMAX(KGAS) 
      IF(R5.LT.TEST3) THEN                                             
C FAKE IONISATION INCREMENT COUNTER AND ADD ELECTRON TO STORE
       IFAKE=IFAKE+1
       IFAKET(IPLANE+1)=IFAKET(IPLANE+1)+1
       IF(FAKEI.LT.0.0) THEN
        NEION=NEION+1
C FAKE ATTACHMENT START NEW ELECTRON
        IF(NELEC.EQ.(NCLUS+1)) GO TO 544
C ELECTRON CAPTURED TAKE NEW ELECTRON FROM STORE
        GO TO 20
       ENDIF
C FAKE IONISATION ADD ELECTRON TO STORE
       NCLUS=NCLUS+1
       NPONT=NPONT+1
       NMXADD=MAX(NPONT,NMXADD)
       IF(NPONT.GT.2000) THEN 
        WRITE(6,546) NPONT,ITER
        STOP
       ENDIF     
       XS(NPONT)=X+DX       
       YS(NPONT)=Y+EOVBR*T+((CY1-EOVBR)*SINWT+CZ1*(1.0D0-COSWT))/WB
       ZS(NPONT)=Z+DZ
       TS(NPONT)=ST+T
       ES(NPONT)=E   
       IPL(NPONT)=IPLANE
       BOT=1.0D0/DSQRT(CX2*CX2+CY2*CY2+CZ2*CZ2)
       DCX(NPONT)=CX2*BOT      
       DCY(NPONT)=CY2*BOT    
       DCZ(NPONT)=CZ2*BOT       
       GO TO 1
      ENDIF
      GO TO 1 
  137 NCOL=NCOL+1   
C CALCULATE DIRECTION COSINES OF ELECTRON IN 0 KELVIN FRAME
      CONST11=1.0D0/(CONST9*DSQRT(EOK))
      DXCOM=(CX2-VGX)*CONST11
      DYCOM=(CY2-VGY)*CONST11
      DZCOM=(CZ2-VGZ)*CONST11
C  CALCULATE  POSITIONS AT INSTANT BEFORE COLLISION
C    ALSO UPDATE DIFFUSION  AND ENERGY CALCULATIONS.                    
      T2=T*T
      IF(T.GE.TMAX1) TMAX1=T
      TDASH=0.0D0  
C CALC NEW POSITION                                                
      X=X+DX                                                            
      Y=Y+EOVBR*T+((CY1-EOVBR)*SINWT+CZ1*(1.0D0-COSWT))/WB
      Z=Z+DZ          
      ST=ST+T
      IT=DINT(T+1.0D0)                                                
      IT=DMIN0(IT,N300)                                                
      TIME(IT)=TIME(IT)+1.0D0    
C ENERGY SPECTRUM IN 0 KELVIN FRAME                                    
      SPEC(IE)=SPEC(IE)+1.0D0
C --------------------------------------------------------------------- 
C     DETERMINATION OF REAL COLLISION TYPE                              
C --------------------------------------------------------------------- 
      R2=drand48(RDUM)
C FIND LOCATION WITHIN 4 UNITS IN COLLISION ARRAY
      CALL SORTT(KGAS,I,R2,IE)                                         
  140 I=I+1                                                             
      IF(CF(KGAS,IE,I).LT.R2) GO TO 140                                 
      S1=RGAS(KGAS,I)                                                   
      EI=EIN(KGAS,I)
      IF(EOK.LT.EI) THEN
C FIX ENERGY LOSS SMALLER THAN INCIDENT ENERGY IF A BINING ERROR OCCURS.
      EI=EOK-0.0001D0
      ENDIF                                                          
      IF(IPN(KGAS,I).EQ.0) GO TO 666
C ATTACHMENT       
      IF(IPN(KGAS,I).EQ.-1) THEN
       NEION=NEION+1
       IPT=IARRY(KGAS,I)
       ID=ID+1
       ITER=ITER+1
       IPRINT=IPRINT+1
       ICOLL(KGAS,IPT)=ICOLL(KGAS,IPT)+1
       ICOLN(KGAS,I)=ICOLN(KGAS,I)+1 
       IT=DINT(T+1.0D0)
       IT=DMIN0(IT,N300)
       TIME(IT)=TIME(IT)+1.0D0
       ZTOT=ZTOT+Z
       YTOT=YTOT+Y
       XTOT=XTOT+X
       TTOT=TTOT+ST
       ZTOTS=ZTOTS+Z-ZSTRT
       YTOTS=YTOTS+Y-YSTRT
       XTOTS=XTOTS+X-XSTRT
       TTOTS=TTOTS+ST-TSSTRT
C  ELECTRON CAPTURED START NEW PRIMARY          
       IF(NELEC.EQ.(NCLUS+1)) GO TO 544
C ELECTRON CAPTURED TAKE NEXT ELECTRON FROM STORE       
       GO TO 20
      ENDIF    
      EISTR=EI                               
      R9=drand48(RDUM)
C     ESEC=R9*(EOK-EI)      
C    USE OPAL PETERSON AND BEATY SPLITTING FACTOR.
      ESEC=WPL(KGAS,I)*TAN(R9*ATAN((EOK-EI)/(2.0D0*WPL(KGAS,I))))
      ESEC=WPL(KGAS,I)*(ESEC/WPL(KGAS,I))**0.9524    
      EI=ESEC+EI 
C STORE POSITION ,ENERGY, DIRECTION COSINES AND TIME OF GENERATION
C OF IONISATION ELECTRON      
      NCLUS=NCLUS+1
      NPONT=NPONT+1
      NMXADD=MAX(NPONT,NMXADD)
      IF(NPONT.GT.2000) THEN 
      WRITE(6,546) NPONT,ITER
 546  FORMAT(2X,' PROGRAM STOPPED . NPONT=',I5,' ITER=',I10)
      STOP
      ENDIF     
      XS(NPONT)=X       
      YS(NPONT)=Y
      ZS(NPONT)=Z
      TS(NPONT)=ST
      ES(NPONT)=ESEC
      IPL(NPONT)=IPLANE
      NCLTMP=NPONT
      NTMPFLG=1
C RANDOMISE SECONDARY ELECTRON DIRECTION
C      R3=drand48(RDUM)
C      F3=1.0D0-2.0D0*R3
C      THETA0=DACOS(F3)
C      F6=DCOS(THETA0)
C      F5=DSIN(THETA0)
C      R4=drand48(rdum)
C      PHI0=F4*R4
C      F8=DSIN(PHI0)
C      F9=DCOS(PHI0)               
C      DCX(NPONT)=F9*F5
C      DCY(NPONT)=F8*F5
C      DCZ(NPONT)=F6  
C STORE POSSIBLE SHELL EMISSIONS AUGER OR FLUORESCENCE
      IF(EISTR.GT.30.0) THEN
C AUGER EMISSIONS WITHOUT FLUORESCENCE
       NAUG=NC0(KGAS,I)
       EAVAUG=EC0(KGAS,I)/DFLOAT(NAUG)
       DO 701 JFL=1,NAUG
       NCLUS=NCLUS+1
       NPONT=NPONT+1
       XS(NPONT)=X
       YS(NPONT)=Y
       ZS(NPONT)=Z
       TS(NPONT)=ST
       ES(NPONT)=EAVAUG
       IPL(NPONT)=IPLANE
       R3=drand48(RDUM)
       F3=1.0D0-2.0D0*R3
       THETA0=DACOS(F3)
       F6=DCOS(THETA0)
       F5=DSIN(THETA0)
       R4=drand48(RDUM)
       PHI0=F4*R4
       F8=DSIN(PHI0)
       F9=DCOS(PHI0)
       DCX(NPONT)=F9*F5
       DCY(NPONT)=F8*F5
       DCZ(NPONT)=F6
  701  CONTINUE
      ENDIF   
C                                                                       
C  GENERATE SCATTERING ANGLES AND UPDATE  LABORATORY COSINES AFTER      
C   COLLISION ALSO UPDATE ENERGY OF ELECTRON.                           
C
  666 IPT=IARRY(KGAS,I)
      ID=ID+1    
      ITER=ITER+1
      IPRINT=IPRINT+1                                                   
      ICOLL(KGAS,IPT)=ICOLL(KGAS,IPT)+1 
      ICOLN(KGAS,I)=ICOLN(KGAS,I)+1   
C IF EXCITATION THEN ADD PROBABILITY ,PENFRA(KGAS,1,I),OF TRANSFER TO 
C GIVE IONISATION OF THE OTHER GASES IN THE MIXTURE
      IF(IPEN.EQ.0) GO TO 5
      IF(PENFRA(KGAS,1,I).NE.0.0) THEN
       RAN=drand48(RDUM)
       IF(RAN.GT.PENFRA(KGAS,1,I)) GO TO 5
       NCLUS=NCLUS+1
       NPONT=NPONT+1
       IF(NPONT.GT.2000) THEN
        WRITE(6,546) NPONT,ITER
        STOP
       ENDIF
C ENTER HERE POSSIBLE DELOCALISATION LENGTH FOR PENNING TRANSFER
       IF(PENFRA(KGAS,2,I).EQ.0.0) THEN
        XS(NPONT)=X
        YS(NPONT)=Y
        ZS(NPONT)=Z
        GO TO 667
       ENDIF    
       ASIGN=1.0
       RAN=drand48(RDUM)
       RAN1=drand48(RDUM)
       IF(RAN1.LT.0.5) ASIGN=-ASIGN
       XS(NPONT)=X-DLOG(RAN)*PENFRA(KGAS,2,I)*ASIGN
       RAN=drand48(RDUM)
       RAN1=drand48(RDUM)
       IF(RAN1.LT.0.5) ASIGN=-ASIGN
       YS(NPONT)=Y-DLOG(RAN)*PENFRA(KGAS,2,I)*ASIGN
       RAN=drand48(RDUM)
       RAN1=drand48(RDUM)
       IF(RAN1.LT.0.5) ASIGN=-ASIGN
       ZS(NPONT)=Z-DLOG(RAN)*PENFRA(KGAS,2,I)*ASIGN
C ENTER POSSIBLE PENNING TRANSFER TIME
  667  TPEN=ST
       IF(PENFRA(KGAS,3,I).EQ.0.0) GO TO 668
       RAN=drand48(RDUM)
       TPEN=ST-DLOG(RAN)*PENFRA(KGAS,3,I)
  668  TS(NPONT)=TPEN
       ES(NPONT)=1.0
       DCX(NPONT)=DCX1
       DCY(NPONT)=DCY1
       DCZ(NPONT)=DCZ1
C FIND LAST TIME PLANE BEFORE TPEN
       TSTOP1=0.0
       IPLANE1=0
       DO 669 KDUM=1,ITFINAL
       TSTOP1=TSTEP+TSTOP1
       IF(TPEN.LT.TSTOP1) GO TO 670
       IPLANE1=IPLANE1+1
  669  CONTINUE
C PENNING TRANSFER OCCURS AFTER FINAL TIME PLANE. CLEAR ENTRY
       NPONT=NPONT-1
       NCLUS=NCLUS-1
       GO TO 5
  670  IPL(NPONT)=IPLANE1         
      ENDIF                                   
    5 S2=(S1*S1)/(S1-1.0D0) 
C ANISOTROPIC SCATTERING
      R3=drand48(RDUM)
      IF(INDEX(KGAS,I).EQ.1) THEN
       R31=drand48(RDUM)
       F3=1.0D0-R3*ANGCT(KGAS,IE,I)        
       IF(R31.GT.PSCT(KGAS,IE,I)) F3=-F3
      ELSE IF(INDEX(KGAS,I).EQ.2) THEN
       EPSI=PSCT(KGAS,IE,I)
       F3=1.0D0-(2.0D0*R3*(1.0D0-EPSI)/(1.0D0+EPSI*(1.0D0-2.0D0*R3)))
      ELSE
C ISOTROPIC SCATTERING                                            
       F3=1.0D0-2.0D0*R3  
      ENDIF
      THETA0=DACOS(F3)                                                  
      R4=drand48(RDUM)
      PHI0=F4*R4                                                        
      F8=DSIN(PHI0)                                                     
      F9=DCOS(PHI0)                                                     
C     IF(EOK.LT.EI) EI=0.0D0                                           
      ARG1=1.0D0-S1*EI/EOK                                              
      ARG1=DMAX1(ARG1,SMALL)                                            
      D=1.0D0-F3*DSQRT(ARG1)                                            
      E1=EOK*(1.0D0-EI/(S1*EOK)-2.0D0*D/S2) 
      E1=DMAX1(E1,SMALL)                                                
      Q=DSQRT((EOK/E1)*ARG1)/S1                                        
      Q=DMIN1(Q,1.0D0)                                                  
      THETA=DASIN(Q*DSIN(THETA0))                                       
      F6=DCOS(THETA)                                                    
      U=(S1-1.0D0)*(S1-1.0D0)/ARG1                                      
      CSQD=F3*F3                                                        
      IF(F3.LT.0.0D0.AND.CSQD.GT.U) F6=-1.0D0*F6                        
      F5=DSIN(THETA)                                                    
      DZCOM=DMIN1(DZCOM,1.0D0)                                          
      ARGZ=DSQRT(DXCOM*DXCOM+DYCOM*DYCOM)
      IF(ARGZ.EQ.0.0D0) THEN
       DCZ1=F6         
       DCX1=F9*F5                             
       DCY1=F8*F5 
       IF(NTMPFLG.EQ.1) THEN
C USE FREE KINEMATICS FOR IONISATION SECONDARY ANGLE
        F5S=F5*DSQRT(E1/ES(NCLTMP))
        IF(F5S.GT.1.0) F5S=1.0
        THSEC=DASIN(F5S)
        F5S=DSIN(THSEC)
        F6S=DCOS(THSEC)
        IF(F6.LT.0.0) F6S=-F6S
        PHIS=PHI0+API
        IF(PHIS.GT.F4) PHIS=PHI0-F4
        F8S=DSIN(PHIS)
        F9S=DCOS(PHIS)
        DCZ(NCLTMP)=F6S
        DCY(NCLTMP)=F8S*F5S
        DCX(NCLTMP)=F9S*F5S
        NTMPFLG=0
       ENDIF
       GO TO 190
      ENDIF                                            
      DCZ1=DZCOM*F6+ARGZ*F5*F8                                         
      DCY1=DYCOM*F6+(F5/ARGZ)*(DXCOM*F9-DYCOM*DZCOM*F8)                
      DCX1=DXCOM*F6-(F5/ARGZ)*(DYCOM*F9+DXCOM*DZCOM*F8) 
      IF(NTMPFLG.EQ.1) THEN
C USE FREE KINEMATICS FOR IONISATION SECONDARY ANGLE
       F5S=F5*DSQRT(E1/ES(NCLTMP))
       IF(F5S.GT.1.0) F5S=1.0
       THSEC=DASIN(F5S)
       F5S=DSIN(THSEC)
       F6S=DCOS(THSEC)
       IF(F6.LT.0.0) F6S=-F6S
       PHIS=PHI0+API
       IF(PHIS.GT.F4) PHIS=PHI0-F4
       F8S=DSIN(PHIS)
       F9S=DCOS(PHIS)
       DCZ(NCLTMP)=DZCOM*F6S+ARGZ*F5S*F8S
       DCY(NCLTMP)=DYCOM*F6S+(F5S/ARGZ)*(DXCOM*F9S-DYCOM*DZCOM*F8S)
       DCX(NCLTMP)=DXCOM*F6S-(F5S/ARGZ)*(DYCOM*F9S+DXCOM*DZCOM*F8S)
       NTMPFLG=0
      ENDIF
  190 CONTINUE
C TRANSFORM VELOCITY VECTORS TO LAB FRAME
      VTOT=CONST9*DSQRT(E1)
      CX1=DCX1*VTOT+VGX
      CY1=DCY1*VTOT+VGY
      CZ1=DCZ1*VTOT+VGZ
C CALCULATE ENERGY AND DIRECTION COSINES IN LAB FRAME
      E1=(CX1*CX1+CY1*CY1+CZ1*CZ1)/CONST10
      CONST11=1.0D0/(CONST9*DSQRT(E1))
      DCX1=CX1*CONST11
      DCY1=CY1*CONST11
      DCZ1=CZ1*CONST11
C STORE DIRECTION COSINES AND ENERGY AFTER N COLLISIONS
C FOR LATER REUSE IN PRIMARY GENERATION
      I100=I100+1
      IF(I100.EQ.200) THEN
       DCZ100=DCZ1
       DCX100=DCX1        
       DCY100=DCY1 
       E100=E1
       I100=0
      ENDIF  
      IF(IPRINT.GT.JPRINT) GO TO 200
      GO TO 1                                    
C    INTERMEDIATE PRINTOUT                       
 200  IPRINT=0 
      WZ=ZTOTS/TTOTS                          
      WZ=WZ*1.0D+09  
      WY=YTOTS/TTOTS   
      WY=WY*1.0D+09
      WX=XTOTS/TTOTS
      WX=WX*1.0D+09
      JCT=ID/100000
C      IF(J1.EQ.1) WRITE(6,201)                                         
C 201 FORMAT(/,7X,'INTERMEDIATE OUTPUT',/,'    VELZ     VELY     VELX    
C    /   TIME       COUNT    ')      
C ROTATE INTERMEDIATE OUTPUT INTO LAB FRAME 
      WZR=WZ*RCS-WX*RSN
      WYR=WY
      WXR=WZ*RSN+WX*RCS
C     WRITE(6,202) WZR,WYR,WXR,TTOTS,JCT   
C 202 FORMAT(3(1X,F8.3),1X,D10.3,4X,I6)                 
      J1=J1+1            
      GO TO 1
C  MAIN LOOP END                                                  
  700 XID=DFLOAT(ID)
      IF(NELEC.GT.IPRIM) THEN
       ANEION=DFLOAT(NEION)
       ANBT=DFLOAT(NELEC-IPRIM)
       ATTOINT=ANEION/ANBT
       ATTERT=DSQRT(ANEION)/ANEION
       AIOERT=DSQRT(ANBT)/ANBT
      ELSE
       ANEION=DFLOAT(NEION) 
       ATTOINT=-1.0D0
       ATTERT=DSQRT(ANEION)/ANEION
      ENDIF
      JCT=ID/100000  
      IF(J1.EQ.1) THEN
      WRITE(6,940) NCLUS,ITER
  940 FORMAT(2(/),' PROGRAM STOPPED  (TOO FEW COLLISIONS), DECREASE THE 
     /ESTIMATED ALPHA.  NCLUS = ',I7,'  ITER =',I9)      
      STOP
      ENDIF
      IF(JPRT.EQ.1) WRITE(6,878) NELEC,NEION,IPRIM
 878  FORMAT(/,' TOTAL NO OF ELECTRONS=',I8,/,' TOTAL NO OF NEG. IONS=',
     /I8,/,' TOTAL NO OF PRIMARIES=',I8)  
      EPRMBAR=0.0D0
      E2PRM=0.0D0
      IF(IPRIM.EQ.1) RETURN      
      DO 310 I=1,IPRIM
      E2PRM=E2PRM+EPRM(I)*EPRM(I)
 310  EPRMBAR=EPRMBAR+EPRM(I)
      EBAR=EPRMBAR/IPRIM
      EERR=DSQRT(E2PRM/IPRIM-EBAR**2)
      IF(JPRT.EQ.1) WRITE(6,836) EBAR,EERR
 836  FORMAT(/,2X,'AVERAGE ENERGY OF PRIMARY ELECTRON =',F10.3,' EV.',/,
     /'   ENERGY SPREAD OF PRIMARY ELECTRON =',F10.3,' EV.')
C     WRITE(6,835) (IESPECP(J),J=1,100)
C835  FORMAT(/,2X,'ENERGY SPECTRUM OF PRIMARY ELECTRONS IN 1 EV. BINS',/
C    /,10(2X,10I5,/))
      RETURN               
  315 IF(ITER.GT.NMAX) THEN
       WRITE(6,991) ITER,NMAX,NPONT,NELEC,IPRIM,NMXADD
 991   FORMAT(2(/),' PROGRAM STOPPED.  ITER =',I10,'    NMAX =',I10,/,
     /' NPONT=',I4,' NELEC=',I8,' IPRIM=',I4,' NMXADD=',I3)
       STOP
      ENDIF                                                             
      RETURN                                                            
      END
      SUBROUTINE TPLANEHT(T,E1,CX1,CY1,CZ1,EFLD,IPLANE)
      IMPLICIT REAL*8 (A-H,O-Z)  
      IMPLICIT INTEGER*8 (I-N)
      COMMON/CNSTS1/CONST1,CONST2,CONST3,CONST4,CONST5
      COMMON/BFLD/EOVB,WB,BTHETA,BMAG
      COMMON/ROTS/RCS,RSN,EFZ100,EFX100,F1,EOVBR
      COMMON/CION/ALPHAST,VDST,TSTEP,ZSTEP,TFINAL,ZFINAL,ITFINAL,IPRIM
      COMMON/OUTPTT/TIME(300),ICOLL(6,5),SPEC(4000),TMAX1,
     /AVE,DEN,XID,X,Y,Z,ST,NNULL,ICOLN(6,290),ICOLNN(6,10)
      COMMON/TPLOUTH/ETPL(8),XTPL(8),YTPL(8),ZTPL(8),TTPL(8),XXTPL(8),
     /YYTPL(8),ZZTPL(8),YZTPL(8),XZTPL(8),XYTPL(8),VZTPL(8),VYTPL(8),
     /VXTPL(8),NETPL(8),ATTOINT,ATTERT,AIOERT
C-----------------------------------------------------------------------
C STORES POSITION, TIME AND ENERGY AND SUMS REQUIRED
C TO CALCULATE DEVIATIONS AND MEANS AT PLANE =IPLANE 
C USED WITH BFIELD AT ANGLE BTHETA TO EFIELD      
C ROTATES STORED POSITIONS INTO LAB FRAME.
C-----------------------------------------------------------------------
      TIMESP=IPLANE*TSTEP
C CALC TIME LEFT TO ARRIVE AT PLANE
      TIMLFT=TIMESP-ST
      T2LFT=TIMLFT*TIMLFT
      WBT=WB*TIMLFT
      COSWT=DCOS(WBT)
      SINWT=DSIN(WBT)
      CX2=CX1+2.0D0*F1*TIMLFT
      CY2=(CY1-EOVBR)*COSWT+CZ1*SINWT+EOVBR
      CZ2=CZ1*COSWT-(CY1-EOVBR)*SINWT
      VTOT=DSQRT(CX2*CX2+CY2*CY2+CZ2*CZ2)
      DCZ2=CZ2/VTOT
      DCY2=CY2/VTOT
      DCX2=CX2/VTOT
      DX=CX1*TIMLFT+F1*TIMLFT*TIMLFT
      XPLANE=X+DX                          
      YPLANE=Y+EOVBR*TIMLFT+((CY1-EOVBR)*SINWT+CZ1*(1.0D0-COSWT))/WB
      DZ=(CZ1*SINWT+(EOVBR-CY1)*(1.0D0-COSWT))/WB
      ZPLANE=Z+DZ
C ROTATE POSITIONS     
      ZPLANER=ZPLANE*RCS-XPLANE*RSN
      YPLANER=YPLANE
      XPLANER=ZPLANE*RSN+XPLANE*RCS         
      EPLANE=E1+DZ*EFZ100+DX*EFX100
      VZPLANE=DCZ2*DSQRT(EPLANE)*CONST3*0.01D0
      VYPLANE=DCY2*DSQRT(EPLANE)*CONST3*0.01D0
      VXPLANE=DCX2*DSQRT(EPLANE)*CONST3*0.01D0
      XTPL(IPLANE)=XTPL(IPLANE)+XPLANER
      YTPL(IPLANE)=YTPL(IPLANE)+YPLANER
      ZTPL(IPLANE)=ZTPL(IPLANE)+ZPLANER
      XXTPL(IPLANE)=XXTPL(IPLANE)+XPLANER*XPLANER
      YYTPL(IPLANE)=YYTPL(IPLANE)+YPLANER*YPLANER
      ZZTPL(IPLANE)=ZZTPL(IPLANE)+ZPLANER*ZPLANER
      YZTPL(IPLANE)=YZTPL(IPLANE)+YPLANER*ZPLANER
      XZTPL(IPLANE)=XZTPL(IPLANE)+XPLANER*ZPLANER
      XYTPL(IPLANE)=XYTPL(IPLANE)+XPLANER*YPLANER
      ETPL(IPLANE)=ETPL(IPLANE)+EPLANE
      TTPL(IPLANE)=TTPL(IPLANE)+ST+TIMLFT
C ROTATE VELOCITIES 
      VZPLNER=VZPLANE*RCS-VXPLANE*RSN
      VYPLNER=VYPLANE
      VXPLNER=VZPLANE*RSN+VXPLANE*RCS     
      VZTPL(IPLANE)=VZTPL(IPLANE)+VZPLNER
      VYTPL(IPLANE)=VYTPL(IPLANE)+VYPLNER
      VXTPL(IPLANE)=VXTPL(IPLANE)+VXPLNER
      NETPL(IPLANE)=NETPL(IPLANE)+1 
      RETURN
      END 
      SUBROUTINE PTH(JPRT)
      IMPLICIT REAL*8 (A-H,O-Z) 
      IMPLICIT INTEGER*8 (I-N)
      COMMON/ROTS/RCS,RSN,EFZ100,EFX100,F1,EOVBR                 
      COMMON/CION/ALPHAST,VDST,TSTEP,ZSTEP,TFINAL,ZFINAL,ITFINAL,IPRIM
      COMMON/TPLOUTH/ETPL(8),XTPL(8),YTPL(8),ZTPL(8),TTPL(8),XXTPL(8),
     /YYTPL(8),ZZTPL(8),YZTPL(8),XZTPL(8),XYTPL(8),VZTPL(8),VYTPL(8),
     /VXTPL(8),NETPL(8),ATTOINT,ATTERT,AIOERT
      COMMON/PTTOFH/RI(8),EPT(8),VZPT(8),VYPT(8),VXPT(8),TTEST(8)
      COMMON/FAKE/FAKEI,IFAKE,IFAKET(8),IFAKED(9) 
      DIMENSION ANTPL(8)
C ------------------------------------------------
C CALCULATES PULSED TOWNSEND COEFFICIENTS
C-------------------------------------------------
      ANTPL(1)=DFLOAT(NETPL(1))
      RI(1)=(DLOG(ANTPL(1))-DLOG(DFLOAT(IPRIM)))/TSTEP
      RI(1)=RI(1)-FAKEI
      EPT(1)=ETPL(1)/ANTPL(1)
      TTEST(1)=TTPL(1)/ANTPL(1)
      VZPT(1)=1.0D+09*VZTPL(1)/ANTPL(1)
      VYPT(1)=1.0D+09*VYTPL(1)/ANTPL(1)
      VXPT(1)=1.0D+09*VXTPL(1)/ANTPL(1)
      DO 10 I=2,ITFINAL
      IF(NETPL(I).EQ.0) THEN
       ITFINAL=I-1
       GO TO 11
      ENDIF
      ANTPL(I)=DFLOAT(NETPL(I))      
      RI(I)=(DLOG(ANTPL(I))-DLOG(ANTPL(I-1)))/TSTEP
      RI(I)=RI(I)-FAKEI
      EPT(I)=ETPL(I)/ANTPL(I)
      TTEST(I)=TTPL(I)/ANTPL(I)
      VZPT(I)=1.0D+09*VZTPL(I)/ANTPL(I)
      VYPT(I)=1.0D+09*VYTPL(I)/ANTPL(I)
      VXPT(I)=1.0D+09*VXTPL(I)/ANTPL(I)
  10  CONTINUE
  11  IF(JPRT.EQ.1) WRITE(6,900) ITFINAL
 900  FORMAT(2(/),' PULSED TOWNSEND RESULTS AT',I2,' SEQUENTIAL TIME PLA
     /NES',/,'PLANE   (ION-ATT)FRQ.    ENERGY      WVZ       WVY       W
     /VX  NO.OF ELECTRNS',/)   
      DO 20 IPL=1,ITFINAL
      IF(JPRT.EQ.1) WRITE(6,910) IPL,RI(IPL),EPT(IPL),VZPT(IPL),
     /VYPT(IPL),VXPT(IPL),NETPL(IPL)
 910  FORMAT(1X,I2,4X,D12.4,4X,F7.2,4X,F6.1,4X,F6.1,4X,F6.1,4X,I8)
  20  CONTINUE 
      RETURN
      END
      SUBROUTINE TOFH(JPRT)
      IMPLICIT REAL*8 (A-H,O-Z)                                         
      IMPLICIT INTEGER*8 (I-N)
      COMMON/CION/ALPHAST,VDST,TSTEP,ZSTEP,TFINAL,ZFINAL,ITFINAL,IPRIM
      COMMON/TPLOUTH/ETPL(8),XTPL(8),YTPL(8),ZTPL(8),TTPL(8),XXTPL(8),
     /YYTPL(8),ZZTPL(8),YZTPL(8),XZTPL(8),XYTPL(8),VZTPL(8),VYTPL(8),
     /VXTPL(8),NETPL(8),ATTOINT,ATTERT,AIOERT 
      COMMON/TOFHOUT/RALPHA,RALPER,TOFENE,TOFENER,TOFWVZ,TOFWVZER,
     /TOFWVY,TOFWVYER,TOFWVX,TOFWVXER,TOFDZZ,TOFDZZER,TOFDXX,TOFDXXER,
     /TOFDYY,TOFDYYER,TOFDYZ,TOFDYZER,TOFDXY,TOFDXYER,TOFDXZ,TOFDXZER,
     /TOFWRZ,TOFWRZER,TOFWRY,TOFWRYER,TOFWRX,TOFWRXER,RATTOF,RATOFER 
      COMMON/PTTOFH/RI(8),EPT(8),VZPT(8),VYPT(8),VXPT(8),TTEST(8)
      COMMON/THRM/AMGAS(6),VTMB(6),TCFMX,TCFMXG(6),ITHRM
      DIMENSION DZTF(8),DXTF(8),DYTF(8),DYZTF(8),DXYTF(8),DXZTF(8)
      DIMENSION WRZ(8),WRY(8),WRX(8),ANTPL(8) 
C----------------------------------------------------------
C CALCULATES TIME OF FLIGHT COEFFICIENTS
C---------------------------------------------
      IF(ITHRM.EQ.1) CALL COLFT(DUM1,DUM2,FRION,FRATT,DUM3,IDUM4)
      IF(ITHRM.EQ.0) CALL COLF(DUM1,DUM2,FRION,FRATT,DUM3,IDUM4)
      ATTOINT=FRATT/FRION
      CORERR=DABS((FAKEI+FRION-FRATT)/(FRION-FRATT))
      ANTPL(1)=DFLOAT(NETPL(1))
      WRZ(1)=ZTPL(1)/(ANTPL(1)*TSTEP)
      WRY(1)=YTPL(1)/(ANTPL(1)*TSTEP)
      WRX(1)=XTPL(1)/(ANTPL(1)*TSTEP)
      DZTF(1)=((ZZTPL(1)/ANTPL(1))-(ZTPL(1)/ANTPL(1))**2)/(2.0D0*TSTEP)
      DXTF(1)=((XXTPL(1)/ANTPL(1))-(XTPL(1)/ANTPL(1))**2)/(2.0D0*TSTEP)
      DYTF(1)=((YYTPL(1)/ANTPL(1))-(YTPL(1)/ANTPL(1))**2)/(2.0D0*TSTEP)
      DYZTF(1)=((YZTPL(1)/ANTPL(1))-(YTPL(1)*ZTPL(1)/(ANTPL(1)**2)))/(2
     /.0D0*TSTEP) 
      DXZTF(1)=((XZTPL(1)/ANTPL(1))-(XTPL(1)*ZTPL(1)/(ANTPL(1)**2)))/(2
     /.0D0*TSTEP) 
      DXYTF(1)=((XYTPL(1)/ANTPL(1))-(XTPL(1)*YTPL(1)/(ANTPL(1)**2)))/(2
     /.0D0*TSTEP) 
      DO 10 I=2,ITFINAL
      ANTPL(I)=DFLOAT(NETPL(I)) 
      WRZ(I)=((ZTPL(I)/ANTPL(I))-(ZTPL(I-1)/ANTPL(I-1)))/TSTEP
      WRY(I)=((YTPL(I)/ANTPL(I))-(YTPL(I-1)/ANTPL(I-1)))/TSTEP
      WRX(I)=((XTPL(I)/ANTPL(I))-(XTPL(I-1)/ANTPL(I-1)))/TSTEP
      DZTF(I)=((ZZTPL(I)/ANTPL(I))-(ZTPL(I)/ANTPL(I))**2-(ZZTPL(I-1)/ANT
     /PL(I-1))+(ZTPL(I-1)/ANTPL(I-1))**2)/(2.0D0*TSTEP)
      DXTF(I)=((XXTPL(I)/ANTPL(I))-(XTPL(I)/ANTPL(I))**2-(XXTPL(I-1)/ANT
     /PL(I-1))+(XTPL(I-1)/ANTPL(I-1))**2)/(2.0D0*TSTEP)
      DYTF(I)=((YYTPL(I)/ANTPL(I))-(YTPL(I)/ANTPL(I))**2-(YYTPL(I-1)/ANT
     /PL(I-1))+(YTPL(I-1)/ANTPL(I-1))**2)/(2.0D0*TSTEP)
      DYZTF(I)=((YZTPL(I)/ANTPL(I))-(YTPL(I)*ZTPL(I)/(ANTPL(I)**2))-(YZT
     /PL(I-1)/ANTPL(I-1))+(YTPL(I-1)*ZTPL(I-1)/(ANTPL(I-1)**2)))/(2.0D0*
     /TSTEP)
      DXZTF(I)=((XZTPL(I)/ANTPL(I))-(XTPL(I)*ZTPL(I)/(ANTPL(I)**2))-(XZT
     /PL(I-1)/ANTPL(I-1))+(XTPL(I-1)*ZTPL(I-1)/(ANTPL(I-1)**2)))/(2.0D0*
     /TSTEP)
      DXYTF(I)=((XYTPL(I)/ANTPL(I))-(XTPL(I)*YTPL(I)/(ANTPL(I)**2))-(XYT
     /PL(I-1)/ANTPL(I-1))+(XTPL(I-1)*YTPL(I-1)/(ANTPL(I-1)**2)))/(2.0D0*
     /TSTEP)
  10  CONTINUE  
      DO 15 I=1,ITFINAL
      WRZ(I)=WRZ(I)*1.0D+09
      WRY(I)=WRY(I)*1.0D+09
      WRX(I)=WRX(I)*1.0D+09
      DZTF(I)=DZTF(I)*1.0D+16
      DXTF(I)=DXTF(I)*1.0D+16
      DYTF(I)=DYTF(I)*1.0D+16
      DYZTF(I)=DYZTF(I)*1.0D+16
      DXZTF(I)=DXZTF(I)*1.0D+16
      DXYTF(I)=DXYTF(I)*1.0D+16
  15  CONTINUE
      IF(JPRT.EQ.1) WRITE(6,900) ITFINAL
 900  FORMAT(2(/),' TIME OF FLIGHT RESULTS AT',I2,' SEQUENTIAL TIME PLAN
     /ES',/,'PLANE    DZZ     DXX     DYY     DYZ     DXZ     DXY      W
     /RZ     WRY     WRX',/)
      DO 20 IPL=1,ITFINAL   
      IF(JPRT.EQ.1) WRITE(6,910) IPL,DZTF(IPL),DXTF(IPL),DYTF(IPL),
     /DYZTF(IPL),DXZTF(IPL),DXYTF(IPL),WRZ(IPL),WRY(IPL),WRX(IPL)  
 910  FORMAT(1X,I2,2X,6F8.1,3F8.2)              
  20  CONTINUE    
      IF(NETPL(1).GT.NETPL(ITFINAL)) THEN
C        NET ATTACHMENT TAKE RESULTS FROM PLANE 2
       TOFENE=EPT(2)
       TOFENER=100.0D0*DABS((EPT(2)-EPT(3))/(2.0D0*EPT(2)))
       TOFWVZ=VZPT(2)
       TOFWVZER=100.0D0*DABS((VZPT(2)-VZPT(3))/(2.0D0*VZPT(2))) 
       TOFWVY=VYPT(2)
       TOFWVYER=100.0D0*DABS((VYPT(2)-VYPT(3))/(2.0D0*VYPT(2))) 
       TOFWVX=VXPT(2)
       TOFWVXER=100.0D0*DABS((VXPT(2)-VXPT(3))/(2.0D0*VXPT(2)))
       TOFDZZ=DZTF(2)
       TOFDZZER=100.0D0*DABS((DZTF(2)-DZTF(3))/(2.0D0*DZTF(2)))
       TOFDXX=DXTF(2)
       TOFDXXER=100.0D0*DABS((DXTF(2)-DXTF(3))/(2.0D0*DXTF(2)))
       TOFDYY=DYTF(2)
       TOFDYYER=100.0D0*DABS((DYTF(2)-DYTF(3))/(2.0D0*DYTF(2)))
       TOFDYZ=DYZTF(2)
       TOFDYZER=100.0D0*DABS((DYZTF(2)-DYZTF(3))/(2.0D0*DYZTF(2)))
       TOFDXZ=DXZTF(2)
       TOFDXZER=100.0D0*DABS((DXZTF(2)-DXZTF(3))/(2.0D0*DXZTF(2)))
       TOFDXY=DXYTF(2)
       TOFDXYER=100.0D0*DABS((DXYTF(2)-DXYTF(3))/(2.0D0*DXYTF(2)))
       TOFWRZ=WRZ(2)
       TOFWRY=WRY(2)
       TOFWRX=WRX(2)
       TOFWRZER=100.0D0*DABS((WRZ(2)-WRZ(3))/(2.0D0*WRZ(2)))
       TOFWRYER=100.0D0*DABS((WRY(2)-WRY(3))/(2.0D0*WRY(2))) 
       TOFWRXER=100.0D0*DABS((WRX(2)-WRX(3))/(2.0D0*WRX(2)))
        ANST2=DFLOAT(NETPL(2))
        ANST3=DFLOAT(NETPL(3))
        ANST4=ANST3-DSQRT(ANST3)
        ANST5=DLOG(ANST2/ANST3)
        ANST6=DLOG(ANST2/ANST4)
        ANST7=ANST6/ANST5
        ANST8=ANST7-1.0D0
       IF(ATTOINT.EQ.-1.0D0) THEN
C        NO IONISATION
        RALPHA=0.0D0
        RALPER=0.0D0
        RATTOF=-RI(2)
        RATOFER=100.0D0*DSQRT(ANST8**2+ATTERT**2)
       ELSE
        RALPHA=RI(2)/(1.0D0-ATTOINT)
        RALPER=100.0D0*DSQRT(ANST8**2+AIOERT**2)
        RATTOF=ATTOINT*RI(2)/(1.0D0-ATTOINT)
        RATOFER=100.0D0*DSQRT(ANST8**2+ATTERT**2)
       ENDIF 
      ELSE
C NET IONISATION TAKE RESULTS FROM PLANE ITFINAL
       I1=ITFINAL
       I2=ITFINAL-1  
       TOFENE=EPT(I1)
       TOFENER=100.0D0*DABS((EPT(I1)-EPT(I2))/EPT(I1))
       TOFWVZ=VZPT(I1)
       TOFWVZER=100.0D0*DABS((VZPT(I1)-VZPT(I2))/VZPT(I1))
       TOFWVY=VYPT(I1)
       TOFWVYER=100.0D0*DABS((VYPT(I1)-VYPT(I2))/VYPT(I1))
       TOFWVX=VXPT(I1)
       TOFWVXER=100.0D0*DABS((VXPT(I1)-VXPT(I2))/VXPT(I1))
       TOFDZZ=DZTF(I1)
       TOFDZZER=100.0D0*DABS((DZTF(I1)-DZTF(I2))/DZTF(I1))
       TOFDXX=DXTF(I1)
       TOFDXXER=100.0D0*DABS((DXTF(I1)-DXTF(I2))/DXTF(I1))
       TOFDYY=DYTF(I1)
       TOFDYYER=100.0D0*DABS((DYTF(I1)-DYTF(I2))/DYTF(I1))
       TOFDYZ=DYZTF(I1)
       TOFDYZER=100.0D0*DABS((DYZTF(I1)-DYZTF(I2))/DYZTF(I1))
       TOFDXZ=DXZTF(I1)
       TOFDXZER=100.0D0*DABS((DXZTF(I1)-DXZTF(I2))/DXZTF(I1))
       TOFDXY=DXYTF(I1)
       TOFDXYER=100.0D0*DABS((DXYTF(I1)-DXYTF(I2))/DXYTF(I1))
       TOFWRZ=WRZ(I1)
       TOFWRY=WRY(I1)
       TOFWRX=WRX(I1)
       TOFWRZER=100.0D0*DABS((WRZ(I1)-WRZ(I2))/WRZ(I1))
       TOFWRYER=100.0D0*DABS((WRY(I1)-WRY(I2))/WRY(I1))
       TOFWRXER=100.0D0*DABS((WRX(I1)-WRX(I2))/WRX(I1))
       ATER=DABS((RI(I1)-RI(I2))/RI(I1))
       RALPHA=RI(I1)/(1.0D0-ATTOINT)
       RALPER=100.0D0*DSQRT(ATER**2+AIOERT**2)
       RATTOF=ATTOINT*RI(I1)/(1.0D0-ATTOINT)
       IF(ATTOINT.NE.0.0D0) THEN
        RATOFER=100.0D0*DSQRT(ATER**2+ATTERT**2)
       ELSE
        RATOFER=0.0D0
       ENDIF  
      ENDIF
      RETURN
      END                
      SUBROUTINE ALPCLCAT 
      IMPLICIT REAL*8 (A-H,O-Z)
      IMPLICIT INTEGER*8 (I-N)
      COMMON/INPT/NGAS,NSTEP,NANISO,EFINAL,ESTEP,AKT,ARY,TEMPC,TORR,IPEN
      COMMON/SETP/TMAX,SMALL,API,ESTART,THETA,PHI,RSTART,EFIELD,NMAX 
      COMMON/TOFOUT/RALPHA,RALPER,TOFENE,TOFENER,TOFWV,TOFWVER,
     /TOFDL,TOFDLER,TOFDT,TOFDTER,TOFWR,TOFWRER,RATTOF,RATOFER 
      COMMON/VEL/WX,WY,WZ
      COMMON/CTOWNS/ALPHA,ATT
      COMMON/CTWNER/ALPER,ATTER
      COMMON/CION/ALPHAST,VDST,TSTEP,ZSTEP,TFINAL,ZFINAL,ITFINAL,IPRIM
      COMMON/FAKE/FAKEI,IFAKE,IFAKET(8),IFAKED(9) 
      COMMON/LARGET/CF(6,4000,290),EIN(6,290),TCF(6,4000),IARRY(6,290),
     /RGAS(6,290),IPN(6,290),WPL(6,290),IPLAST(6),ISIZE(6),
     /PENFRA(6,3,290),TCFMAX(6)
C ---------------------------------------------------------------------      
C  ESTIMATE TIME STEP FOR AVALANCHE SIMULATION IN TIME OF FLIGHT SIM.
C  USES ESTIMATED GAIN OF 3.0 BETWEEN PLANES. 
C  CALLS TOF AND PT SUBROUTINES AND UPDATES ALPHA AND ATT
C  VERSION WITH BFIELD PARALLEL TO EFIELD 
C ------------------------------------------------------------------
      IMAX=NMAX/10000000 
      IF(IMAX.LT.5) IMAX=5 
      NMAX=IMAX*10000000
      CORR=760.0*(TEMPC+273.15)/(TORR*293.15)
      ALPP=ALPHA/CORR
      ATTP=ATT/CORR
      ANETP=ALPP-ATTP
      ANET=ALPHA-ATT
      TCUTH=1.2D-10*CORR
      TCUTL=1.D-13*CORR
C   
      IF (ANETP.GT.30.0) THEN
       ALPHAD=0.0
       ALP1=ALPHA
       ATT1=ATT
C WEAK ATTACHMENT
       GO TO 6
      ENDIF 
C  SOME ATTACHMENT
      IF(DABS(ANETP).LT.100.) THEN
C SMALL NET ATTACHMENT
       ALPHAD=DABS(ATT)*0.8
      ELSE IF(DABS(ANETP).GE.100..AND.DABS(ANETP).LT.1000.) THEN
C LARGER NET ATTACHMENT
       ALPHAD=DABS(ANET)*0.65
      ELSE IF(DABS(ANETP).GT.1000..AND.DABS(ANETP).LT.10000.) THEN
       ALPHAD=DABS(ANET)*0.6
      ELSE IF(DABS(ANETP).GT.10000..AND.DABS(ANETP).LT.100000.) THEN
       ALPHAD=DABS(ANET)*0.5
      ELSE IF(DABS(ANETP).GT.100000..AND.DABS(ANETP).LT.2000000.) THEN
       ALPHAD=DABS(ANET)*0.4
      ELSE 
       WRITE(6,91) 
   91 FORMAT(' ATTACHMENT TOO LARGE PROGRAM STOPPED')
       STOP
      ENDIF
   3  VDST=WZ*1.D-5
      FAKEI=ALPHAD*WZ*1.D-12
      ALPHAST=0.85*DABS(ALPHAD+ANET)                        
      TSTEP=DLOG(3.0D0)/(ALPHAST*VDST*1.0D5)
      IF(TSTEP.GT.TCUTH) TSTEP=TCUTH
      IF(TSTEP.LT.TCUTL) TSTEP=TCUTL
C UPDATE NULL COLLISION FREQUENCY LIMIT
      DO 4 J=1,NGAS
    4 TCFMAX(J)=TCFMAX(J)+DABS(FAKEI)/DFLOAT(NGAS)
      WRITE(6,871) ALPHAST,ANET,ALPHAD,TSTEP
  871 FORMAT(' ALPHAST1=',D10.3,' ANET1=',D10.3,' ALPHAD1=',D10.3,/,
     /' TSTEP1=',D10.3)
C  CONVERT TO  PICOSECONDS     
      TSTEP=TSTEP*1.0D12
      TFINAL=7.0D0*TSTEP
      ITFINAL=7
C CALCULATE GOOD STARTING VALUES FOR ALPHA AND NETA
      JPRT=0
      CALL MONTEFTAT(JPRT)
      CALL PT(JPRT)
      CALL TOF(JPRT)
      ALP1=RALPHA/TOFWR*1.D7
      ALP1ER=RALPER*ALP1/100. 
      ATT1=RATTOF/TOFWR*1.D7
      ATT1ER=RATOFER*ATT1/100.
      WRITE(6,944) ALP1,ALP1ER,ATT1,ATT1ER
  944 FORMAT(' GOOD STARTING VALUES FOR CALC:',/,' ALPHA=',D12.3,' ERR='
     /,D12.3,/,' NETA =',D12.3,' ERR=',D12.3)
C
C CALCULATE USING GOOD STARTING VALUES
C  RESET TCFMAX
      DO 44 J=1,NGAS
   44 TCFMAX(J)=TCFMAX(J)-DABS(FAKEI)/DFLOAT(NGAS)
C CALCULATE FAKE IONISATION RATE SCALING BY 1.2 
      ALPHAD=ATT1*1.2
      IF((ALP1-ATT1).GT.30.*CORR) ALPHAD=DABS(ATT1)*0.4
      IF(DABS(ALP1-ATT1).LT.(ALP1/10.0).OR.DABS(ALP1-ATT1).LT.
     /(ATT1/10.0)) ALPHAD=DABS(ATT1)*0.3
      IF((ALP1-ATT1).GT.100.*CORR) ALPHAD=0.0
      WZ=TOFWR*1.D5
C
C
C NET IONISATION
C   5 IF(ANET.GT.(11000./CORR)) THEN
C LARGE IONISATION
C      ALPHAD=7000.-(ALPHA-ATT)
C     ELSE IF(DABS(ALPP).GT.(5.0*DABS(ANET)).OR.DABS(ATTP).GT.
C    /(5.0*DABS(ANET))) THEN
C LARGE CANCELLATION BETWEEN ATTACHMENT AND IONISATION
C      ALPHAD=-6.0*(ALPHA-ATT)
C     ELSE
C      ALPHAD=0.0
C     ENDIF 
    6 VDST=WZ*1.D-5 
      FAKEI=ALPHAD*WZ*1.D-12        
      ALPHAST=0.85*DABS(ALPHAD+ALP1-ATT1) 
      IF((ALP1+ALPHAD).GT.(10.0*ALPHAST).OR.ATT1.GT.(10.0*ALPHAST)) THEN
C LARGE CANCELLATION BETWEEN ALPHA AND NETA. REDUCE STEP SIZE BY 15.0
       IF((ALP1+ALPHAD).GT.100.*CORR) THEN
        ALPHAST=ALPHAST*15.0
       ELSE IF((ALP1+ALPHAD).GT.50.0*CORR) THEN
        ALPHAST=ALPHAST*12.0
       ELSE
        ALPHAST=ALPHAST*8.0
       ENDIF
      ENDIF                              
      TSTEP=DLOG(3.0D0)/(ALPHAST*VDST*1.0D5)
      IF(TSTEP.GT.TCUTH.AND.ALPHAD.NE.0.0) TSTEP=TCUTH
C     IF(TSTEP.LT.TCUTL) TSTEP=TCUTL
C UPDATE NULL COLLISION FREQUENCY
      DO 10 J=1,NGAS
   10 TCFMAX(J)=TCFMAX(J)+DABS(FAKEI)/DFLOAT(NGAS)
      ANET1=ALP1-ATT1
      WRITE(6,878) ALPHAST,ANET1,ALPHAD,TSTEP
  878 FORMAT(' ALPHAST=',D10.3,' ANET=',D10.3,' ALPHAD=',D10.3,/,
     /' TSTEP=',D10.3)
      TSTEP=TSTEP*1.0D12
      TFINAL=7.0D0*TSTEP
      ITFINAL=7
C CALC TIME OF FLIGHT AND PT 
      WRITE(6,25)
   25 FORMAT(/,2X,'SOLUTION FOR PULSED TOWNSEND AND TIME OF FLIGHT PARAM
     /ETERS',/,'  ------------------------------------------------------
     /--------') 
      WRITE(6,26) TSTEP
   26 FORMAT(1(/),'  TIME STEP BETWEEN SAMPLING PLANES =',D12.5,' PICOSE
     /CS.',/)       
      JPRT=1              
      CALL MONTEFTAT(JPRT)                            
      CALL FRIEDLANDT 
      CALL PT(JPRT) 
      CALL TOF(JPRT) 
      WRITE(6,27) RALPHA,RALPER,RATTOF,RATOFER
   27 FORMAT(/,'PT IONISATION AND ATTACHMENT RATES *10**12/SEC',/,'ALPHA
     /=',D10.3,' +- ',F6.2,' %      ATT=',D10.3,' +- ',F6.2,' %')    
      WRITE(6,28) 
   28 FORMAT(/,' TOF DIFFUSION')
      WRITE(6,29) TOFDL,TOFDLER,TOFDT,TOFDTER 
   29 FORMAT(/,'  DL=',F8.1,' +- ',F6.1,' %       DT=',F8.1,' +- ',F6.1,
     /' %')     
      WRITE(6,30) 
   30 FORMAT(/,' TOF DRIFT VELOCITY') 
      WRITE(6,31) TOFWR,TOFWRER   
   31 FORMAT(/,'    WR=',F8.2,' +- ',F6.2,' %') 
C      CALCULATE TOWNSEND SST COEFICIENTS FROM TOF RESULTS  
      WRN=TOFWR*1.0D05 
      FC1=WRN/(2.0D0*TOFDL) 
      FC2=((RALPHA-RATTOF)*1.0D12)/TOFDL   
      ALPZZ=FC1-DSQRT(FC1**2-FC2)     
      WRITE(6,32) ALPZZ
   32 FORMAT(/,'TOWNSEND COEFICIENT (ALPHA-ATT) CALCULATED FROM TOF RESU
     /LTS:',/,' ALPHA-ATT /CM.=',D11.4,' N.B. APPROXIMATE FORMULA NOT AC
     /CURATE',/)       
C---------------------------------------------------           
C LOAD NEW ALPHA AND ATTACHMENT INTO COMMON BLOCKS
C-----------------------------------------------
      ALPHA=RALPHA/TOFWR*1.D7
      ALPER=RALPER*ALPHA/100.
      ATT=RATTOF/TOFWR*1.D7
      ATTER=RATOFER*ATT/100.
C     WRITE(6,33) ALPHA,ALPER,ATT,ATTER 
C 33  FORMAT(' TOF TOWNSEND IN UNITS OF 1/CM:',/,'   GAIN =',D12.4,' +-'
C    /,D12.4,/,' ATTACH =',D12.4,' +-',D12.4,/)  
C-----------------------------------------------      
      RETURN                                                     
      END                                                               
      SUBROUTINE MONTEFTAT(JPRT)                                   
      IMPLICIT REAL*8 (A-H,O-Z)                                         
      IMPLICIT INTEGER*8 (I-N)
      COMMON/INPT/NGAS,NSTEP,NANISO,EFINAL,ESTEP,AKT,ARY,TEMPC,TORR,IPEN
      COMMON/CNSTS1/CONST1,CONST2,CONST3,CONST4,CONST5                  
      COMMON/SETP/TMAX,SMALL,API,ESTART,THETA,PHI,RSTART,EFIELD,NMAXOLD 
      COMMON/BFLD/EOVB,WB,BTHETA,BMAG                                   
      COMMON/LARGET/CF(6,4000,290),EIN(6,290),TCF(6,4000),IARRY(6,290),
     /RGAS(6,290),IPN(6,290),WPL(6,290),IPLAST(6),ISIZE(6),
     /PENFRA(6,3,290),TCFMAX(6)
      COMMON/LARGENT/CFN(6,4000,10),TCFN(6,4000),SCLENUL(6,10),NPLAST(6)
      COMMON/CION/ALPHAST,VDST,TSTEP,ZSTEP,TFINAL,ZFINAL,ITFINAL,IPRIM
      COMMON/OUTPTT/TIME(300),ICOLL(6,5),SPEC(4000),TMAX1,
     /AVE,DEN,XID,X,Y,Z,ST,NNULL,ICOLN(6,290),ICOLNN(6,10)
      COMMON/TTRM/ZTOT,TTOT,ZTOTS,TTOTS
      COMMON/THRM/AMGAS(6),VTMB(6),TCFMX,TCFMXG(6),ITHRM
      COMMON/RANM/RNMX(6)
      COMMON/ISPT/XS(2000),YS(2000),ZS(2000),TS(2000),ES(2000),
     /DCX(2000),DCY(2000),DCZ(2000),IPL(2000)
      COMMON/TPLOUT/ETPL(8),XTPL(8),YTPL(8),ZTPL(8),TTPL(8),XXTPL(8),
     /YYTPL(8),ZZTPL(8),VZTPL(8),NETPL(8),ATTOINT,ATTERT,AIOERT 
      COMMON/ANIST/PSCT(6,4000,290),ANGCT(6,4000,290),INDEX(6,290),NISO
      COMMON/IONFL/NC0(6,290),EC0(6,290),NG1(6,290),EG1(6,290),
     /NG2(6,290),EG2(6,290),WKLM(6,290),EFL(6,290)
      COMMON/FAKE/FAKEI,IFAKE,IFAKET(8),IFAKED(9) 
      DIMENSION EPRM(10000000),IESPECP(100),TEMP(6,4000)            
C -------------------------------------------------------------------   
C   CALCULATES COLLISION EVENTS AND UPDATES DIFFUSION AND VELOCITY.
C   THIS ROUTINE HANDLES TERMINATIONS AT FIXED DRIFT TIMES.
C   BFIELD PARALLEL TO EFIELD    
C ------------------------------------------------------------------- 
      IF(JPRT.EQ.0) THEN
C CALCULATION OF APPROXIMATE STARTING PARAMETERS
       NMAX=NMAXOLD
       IF(NMAXOLD.GT.80000000) NMAX=80000000
      ELSE
C ENTRY FOR NORMAL CALCULATION
       NMAX=NMAXOLD
      ENDIF
      S=0.0D0 
      ST=0.0D0 
      X=0.0D0
      Y=0.0D0
      Z=0.0D0 
      ZTOT=0.0D0
      ZTOTS=0.0D0
      TTOT=0.0D0
      TTOTS=0.0D0
      SMALL=1.0D-20                                                     
      TMAX1=0.0D0                                                       
      RDUM=RSTART                                                       
      E1=ESTART                                                         
      CONST9=CONST3*0.01D0
      CONST10=CONST9*CONST9
      DO 25 I=1,300
   25 TIME(I)=0.0D0
      DO 26 I=1,5
      DO 26 K=1,6
   26 ICOLL(K,I)=0
      DO 27 I=1,290
      DO 27 K=1,6
   27 ICOLN(K,I)=0
      DO 271 I=1,10
      DO 271 K=1,6
  271 ICOLNN(K,I)=0
      DO 28 I=1,4000
   28 SPEC(I)=0.0D0            
      DO 33 I=1,100
   33 IESPECP(I)=0 
      DO 34 I=1,8
      ETPL(I)=0.0D0
      XTPL(I)=0.0D0
      YTPL(I)=0.0D0
      ZTPL(I)=0.0D0
      TTPL(I)=0.0D0
      XXTPL(I)=0.0D0
      YYTPL(I)=0.0D0
      ZZTPL(I)=0.0D0
      VZTPL(I)=0.0D0
   34 NETPL(I)=0                                   
      ID=0
      N4000=4000
      N300=300
      N100=100
      I100=0                                                            
      NCOL=0                                                            
      NNULL=0
      NELEC=0
      NEION=0
      NMXADD=0
      NTMPFLG=0
      NPONT=0 
      NCLUS=0
      J1=1 
      ZSTRT=0.0D0
      TSSTRT=0.0D0 
C
      DO 111 K=1,6
      DO 111 J=1,N4000
  111 TEMP(K,J)=TCF(K,J)+TCFN(K,J)
      ABSFAKEI=DABS(FAKEI)
      IFAKE=0
      DO 112 J=1,8
  112 IFAKET(J)=0
C
C GENERATE INITIAL RANDOM NUMBERS FOR MAXWELL BOLTZMAN
      CALL GERJAN
      IMBPT=0
C
C     INITIAL DIRECTION COSINES                                         
      DCZ1=DCOS(THETA)                                                  
      DCX1=DSIN(THETA)*DCOS(PHI)                                        
      DCY1=DSIN(THETA)*DSIN(PHI) 
C INITIAL VELOCITY
      VTOT=CONST9*DSQRT(E1)
      CX1=DCX1*VTOT
      CY1=DCY1*VTOT
      CZ1=DCZ1*VTOT      
      E100=E1
      DCZ100=DCZ1
      DCX100=DCX1
      DCY100=DCY1                                       
      BP=EFIELD*EFIELD*CONST1                                           
      F1=EFIELD*CONST2                                                  
      F2=EFIELD*CONST3
      API=DACOS(-1.0D0)                                                 
      F4=2.0D0*API
      JPRINT=NMAX/10
      IPRINT=0
      ITER=0
      IPLANE=0
      IPRIM=0
C  LOOP FOR NEW STARTING ELECTRONS 
  544 IPRIM=IPRIM+1 
      IF(IPRIM.GT.1) THEN
C CHECK IF PROGRAM WILL EXCEED MAXIMUM NUMBER OF ITERATIONS 
C IN THIS CYCLE IF SO OUTPUT CURRENT RESULTS. 
       IF(ITER.GT.NMAX) THEN
        IPRIM=IPRIM-1
        GO TO 700      
       ENDIF 
       X=0.0D0
       Y=0.0D0
       Z=0.0D0
       DCZ1=DCZ100
       DCX1=DCX100
       DCY1=DCY100
       E1=E100
       VTOT=CONST9*DSQRT(E1)
       CX1=DCX1*VTOT
       CY1=DCY1*VTOT
       CZ1=DCZ1*VTOT
       NCLUS=NCLUS+1
       ST=0.0D0
       TSSTRT=0.0D0
       ZSTRT=0.0D0
       IPLANE=0
      ENDIF
      IF(IPRIM.GT.10000000) THEN
      WRITE(6,944) IPRIM
 944  FORMAT(/,2X,'PROGRAM STOPPED TOO MANY PRIMARIES IPRIM =',I7)
      GO TO 700
      ENDIF
      EPRM(IPRIM)=E1
      IDUM=DINT(E1)+1
      IDUM=DMIN0(IDUM,N100)
      IESPECP(IDUM)=IESPECP(IDUM)+1      
C START OF LOOP FOR NEWLY CREATED ELECTRONS
  555 TDASH=0.0D0
      NELEC=NELEC+1
      TSTOP=TSTEP+IPLANE*TSTEP       
C MAIN LOOP                                                             
    1 CONTINUE                                                          
C     IF(ITER.GT.NMAX)  GO TO 315
      R1=drand48(RDUM)
      T=-DLOG(R1)/TCFMX+TDASH
      TDASH=T
      AP=DCZ1*F2*DSQRT(E1)
  15  IF((T+ST).GE.TSTOP) THEN                     
       IPLANE=IPLANE+1
       TSTOP=TSTOP+TSTEP
C STORE POSITION AND ENERGY AT TIME PLANE =IPLANE. 
       CALL TPLANEAT(T,E1,CX1,CY1,DCZ1,AP,BP,EFIELD,IPLANE)
C CHECK IF PASSED THROUGH MORE THAN ONE PLANE IN THIS STEP       
       IF((T+ST).GE.TSTOP.AND.TSTOP.LE.TFINAL) GO TO 15
       IF((T+ST).GE.TFINAL) THEN
        ZTOT=ZTOT+Z     
        TTOT=TTOT+ST
        ZTOTS=ZTOTS+Z-ZSTRT      
        TTOTS=TTOTS+ST-TSSTRT    
        TSTOP=TSTEP
C NO MORE ELECTRONS IN CASCADE TRY NEW PRIMARY ELECTRON
        IF(NELEC.EQ.(NCLUS+1)) GO TO 544  
C TAKE ELECTRONS FROM STORE 
   20   X=XS(NPONT) 
        Y=YS(NPONT) 
        Z=ZS(NPONT) 
        ST=TS(NPONT) 
        E1=ES(NPONT) 
        DCX1=DCX(NPONT) 
        DCY1=DCY(NPONT) 
        DCZ1=DCZ(NPONT)
        VTOT=CONST9*DSQRT(E1)
        CX1=DCX1*VTOT
        CY1=DCY1*VTOT
        CZ1=DCZ1*VTOT 
        IPLANE=IPL(NPONT)   
        NPONT=NPONT-1 
        ZSTRT=Z 
        TSSTRT=ST
        GO TO 555
       ENDIF              
      ENDIF
      E=E1+(AP+BP*T)*T
      IF(E.LT.0.0D0) THEN
       WRITE(6,913)ITER,E,E1,AP,BP,DCZ1 
 913  FORMAT(3X,' AFTER STORE ITER=',I10,' E=',D12.3,' E1=',D12.3,' AP='
     /,D12.3,' BP=',D12.3,' DCZ1=',D12.3)       
       E=0.001D0
      ENDIF  
      WBT=WB*T
      COSWT=DCOS(WBT)
      SINWT=DSIN(WBT)
      CONST6=DSQRT(E1/E)
C FIND IDENTITY OF GAS FOR COLLISION
      KGAS=0
      IF(NGAS.EQ.1) THEN
       R2=drand48(RDUM)
       KGAS=1
       GO TO 4
      ENDIF
      R2=drand48(RDUM)
    3 KGAS=KGAS+1
      IF(TCFMXG(KGAS).LT.R2) GO TO 3
C CALCULATE ELECTRON VELOCITY VECTORS BEFORE COLLISION
    4 CX2=CX1*COSWT-CY1*SINWT
      CY2=CY1*COSWT+CX1*SINWT
      VTOT=CONST9*DSQRT(E)
      CZ2=VTOT*(DCZ1*CONST6+EFIELD*T*CONST5/DSQRT(E))
C CALCULATE GAS VELOCITY VECTORS VGX,VGY,VGZ
      IMBPT=IMBPT+1
      IF(IMBPT.GT.6) THEN
       CALL GERJAN
        IMBPT=1
      ENDIF
      VGX=VTMB(KGAS)*RNMX(IMBPT)
      IMBPT=IMBPT+1
      VGY=VTMB(KGAS)*RNMX(IMBPT)
      IMBPT=IMBPT+1
      VGZ=VTMB(KGAS)*RNMX(IMBPT)
C CALCULATE ENERGY WITH STATIONARY GAS TARGET, EOK.
      EOK=((CX2-VGX)**2+(CY2-VGY)**2+(CZ2-VGZ)**2)/CONST10
      IE=DINT(EOK/ESTEP)+1                                             
      IE=DMIN0(IE,N4000)                                               
C                                                                       
C     TEST FOR REAL OR NULL COLLISION 
C                                  
      R5=drand48(RDUM)                                         
      TEST1=TCF(KGAS,IE)/TCFMAX(KGAS)                                   
      IF(R5.LE.TEST1) GO TO 137
      NNULL=NNULL+1          
      TEST2=TEMP(KGAS,IE)/TCFMAX(KGAS)
      IF(R5.LT.TEST2) THEN
C TEST FOR NULL LEVELS
       IF(NPLAST(KGAS).EQ.0) GO TO 1  
       R2=drand48(RDUM)
       I=0
  888  I=I+1
       IF(CFN(KGAS,IE,I).LT.R2) GO TO 888
C INCREMENT NULL SCATTER SUM
       ICOLNN(KGAS,I)=ICOLNN(KGAS,I)+1
       GO TO 1
      ENDIF   
      TEST3=(TEMP(KGAS,IE)+ABSFAKEI)/TCFMAX(KGAS)
      IF(R5.LT.TEST3) THEN
C FAKE COLLISION INCREMENT COUNTERS
       IFAKE=IFAKE+1
       IFAKET(IPLANE+1)=IFAKET(IPLANE+1)+1
       IF(FAKEI.LT.0.0) THEN
        NEION=NEION+1
C FAKE ATTACHMENT START NEW ELECTRON
        IF(NELEC.EQ.(NCLUS+1)) GO TO 544
C ELECTRON CAPTURED TAKE ELECTRON FROM STORE
        GO TO 20
       ENDIF
C FAKE IONISATION ADD ELECTRON TO STORE
       NCLUS=NCLUS+1
       NPONT=NPONT+1
       NMXADD=MAX(NPONT,NMXADD)
       IF(NPONT.GT.2000) THEN 
        WRITE(6,546) NPONT,ITER
        STOP
       ENDIF     
       XS(NPONT)=X+(CX1*SINWT-CY1*(1.0D0-COSWT))/WB       
       YS(NPONT)=Y+(CY1*SINWT+CX1*(1.0D0-COSWT))/WB
       ZS(NPONT)=Z+DCZ1*T*CONST9*DSQRT(E1)+T*T*F1
       TS(NPONT)=ST+T
       ES(NPONT)=E
       IPL(NPONT)=IPLANE
       BOT=1.0D0/DSQRT(CX2*CX2+CY2*CY2+CZ2*CZ2)
       DCX(NPONT)=CX2*BOT
       DCY(NPONT)=CY2*BOT
       DCZ(NPONT)=CZ2*BOT
       GO TO 1
      ENDIF
      GO TO 1           
  137 NCOL=NCOL+1
C CALCULATE DIRECTION COSINES OF ELECTRON IN 0 KELVIN FRAME
      CONST11=1.0D0/(CONST9*DSQRT(EOK))
      DXCOM=(CX2-VGX)*CONST11
      DYCOM=(CY2-VGY)*CONST11
      DZCOM=(CZ2-VGZ)*CONST11                                       
C                                                                       
C  CALCULATE POSITIONS AT INSTANT BEFORE COLLISION
C    ALSO UPDATE DIFFUSION  AND ENERGY CALCULATIONS.                    
      T2=T*T
      IF(T.GE.TMAX1) TMAX1=T
      TDASH=0.0D0  
      CONST7=CONST9*DSQRT(E1)                                           
      A=T*CONST7                                                        
      DX=(CX1*SINWT-CY1*(1.0D0-COSWT))/WB                               
      X=X+DX           
      DY=(CY1*SINWT+CX1*(1.0D0-COSWT))/WB                               
      Y=Y+DY    
      Z=Z+DCZ1*A+T2*F1
      ST=ST+T
      IT=DINT(T+1.0D0)                                              
      IT=DMIN0(IT,N300)                                                
      TIME(IT)=TIME(IT)+1.0D0  
C ENERGY SPECTRUM IN 0 KELVIN FRAME                                 
      SPEC(IE)=SPEC(IE)+1.0D0
C --------------------------------------------------------------------- 
C     DETERMINATION OF REAL COLLISION TYPE                              
C --------------------------------------------------------------------- 
      R2=drand48(RDUM)
C FIND LOCATION WITHIN 4 UNITS IN COLLISION ARRAY
      CALL SORTT(KGAS,I,R2,IE)                                          
  140 I=I+1                                                             
      IF(CF(KGAS,IE,I).LT.R2) GO TO 140                                 
      S1=RGAS(KGAS,I)                                                   
      EI=EIN(KGAS,I)
      IF(EOK.LT.EI) THEN
C FIX ENERGY LOSS SMALLER THAN INCIDENT ENERGY IF A BINING ERROR OCCURS.
      EI=EOK-0.0001D0
      ENDIF                                                          
      IF(IPN(KGAS,I).EQ.0) GO TO 666
C ATTACHMENT       
      IF(IPN(KGAS,I).EQ.-1) THEN
       NEION=NEION+1
       IPT=IARRY(KGAS,I)
       ID=ID+1
       ITER=ITER+1
       IPRINT=IPRINT+1
       ICOLL(KGAS,IPT)=ICOLL(KGAS,IPT)+1
       ICOLN(KGAS,I)=ICOLN(KGAS,I)+1 
       IT=DINT(T+1.0D0)
       IT=DMIN0(IT,N300)
       TIME(IT)=TIME(IT)+1.0D0
       ZTOT=ZTOT+Z
       TTOT=TTOT+ST
       ZTOTS=ZTOTS+Z-ZSTRT
       TTOTS=TTOTS+ST-TSSTRT
C  ELECTRON CAPTURED START NEW PRIMARY          
       IF(NELEC.EQ.(NCLUS+1)) GO TO 544
C ELECTRON CAPTURED TAKE NEXT ELECTRON FROM STORE       
       GO TO 20
      ENDIF    
      EISTR=EI                               
      R9=drand48(RDUM)
C     ESEC=R9*(EOK-EI)      
C    USE OPAL PETERSON AND BEATY SPLITTING FACTOR.
      ESEC=WPL(KGAS,I)*TAN(R9*ATAN((EOK-EI)/(2.0D0*WPL(KGAS,I))))    
      ESEC=WPL(KGAS,I)*(ESEC/WPL(KGAS,I))**0.9524 
      EI=ESEC+EI 
C STORE POSITION ,ENERGY, DIRECTION COSINES AND TIME OF GENERATION
C OF IONISATION ELECTRON      
      NCLUS=NCLUS+1
      NPONT=NPONT+1
      NMXADD=MAX(NPONT,NMXADD)
      IF(NPONT.GT.2000) THEN 
      WRITE(6,546) NPONT,ITER
 546  FORMAT(2X,' PROGRAM STOPPED . NPONT=',I5,' ITER=',I10)
      STOP
      ENDIF     
      XS(NPONT)=X       
      YS(NPONT)=Y
      ZS(NPONT)=Z
      TS(NPONT)=ST
      ES(NPONT)=ESEC
      IPL(NPONT)=IPLANE
      NTMPFLG=1
      NCLTMP=NPONT
C RANDOMISE SECONDARY ELECTRON DIRECTION
C     R3=drand48(RDUM)
C     F3=1.0D0-2.0D0*R3
C     THETA0=DACOS(F3)
C     F6=DCOS(THETA0)
C     F5=DSIN(THETA0)
C     R4=drand48(rdum)
C     PHI0=F4*R4
C     F8=DSIN(PHI0)
C     F9=DCOS(PHI0)               
C     DCX(NPONT)=F9*F5
C     DCY(NPONT)=F8*F5
C     DCZ(NPONT)=F6
C STORE POSSIBLE SHELL EMISSIONS AUGER OR FLUORESCENCE
      IF(EISTR.GT.30.0) THEN
C AUGER EMISSION WITHOUT FLUORESCENCE
       NAUG=NC0(KGAS,I)
       EAVAUG=EC0(KGAS,I)/DFLOAT(NAUG)
       DO 701 JKL=1,NAUG
       NCLUS=NCLUS+1
       NPONT=NPONT+1
       XS(NPONT)=X
       YS(NPONT)=Y
       ZS(NPONT)=Z
       TS(NPONT)=ST
       ES(NPONT)=EAVAUG
       R3=drand48(RDUM)
       F3=1.0D0-2.0D0*R3
       THETA0=DACOS(F3)
       F6=DCOS(THETA0)
       F5=DSIN(THETA0)
       R4=drand48(RDUM)
       PHI0=F4*R4
       F8=DSIN(PHI0)
       F9=DCOS(PHI0)
       DCX(NPONT)=F9*F5
       DCY(NPONT)=F8*F5
       DCZ(NPONT)=F6
       IPL(NPONT)=IPLANE
  701  CONTINUE     
      ENDIF
C                                                                       
C  GENERATE SCATTERING ANGLES AND UPDATE  LABORATORY COSINES AFTER      
C   COLLISION ALSO UPDATE ENERGY OF ELECTRON.                           
C
  666 IPT=IARRY(KGAS,I)
      ID=ID+1    
      ITER=ITER+1
      IPRINT=IPRINT+1                                                   
      ICOLL(KGAS,IPT)=ICOLL(KGAS,IPT)+1 
      ICOLN(KGAS,I)=ICOLN(KGAS,I)+1   
C IF EXCITATION THEN ADD PROBABILITY ,PENFRA(KGAS,1,I),OF TRANSFER TO 
C GIVE IONISATION OF THE OTHER GASES IN THE MIXTURE
      IF(IPEN.EQ.0) GO TO 5
      IF(PENFRA(KGAS,1,I).NE.0.0) THEN
       RAN=drand48(RDUM)
       IF(RAN.GT.PENFRA(KGAS,1,I)) GO TO 5
       NCLUS=NCLUS+1
       NPONT=NPONT+1
       IF(NPONT.GT.2000) THEN
        WRITE(6,546) NPONT,ITER
        STOP
       ENDIF
C ENTER HERE POSSIBLE DELOCALISATION LENGTH FOR PENNING TRANSFER
       IF(PENFRA(KGAS,2,I).EQ.0.0) THEN
        XS(NPONT)=X
        YS(NPONT)=Y
        ZS(NPONT)=Z
        GO TO 667
       ENDIF    
       ASIGN=1.0
       RAN=drand48(RDUM)
       RAN1=drand48(RDUM)
       IF(RAN1.LT.0.5) ASIGN=-ASIGN
       XS(NPONT)=X-DLOG(RAN)*PENFRA(KGAS,2,I)*ASIGN
       RAN=drand48(RDUM)
       RAN1=drand48(RDUM)
       IF(RAN1.LT.0.5) ASIGN=-ASIGN
       YS(NPONT)=Y-DLOG(RAN)*PENFRA(KGAS,2,I)*ASIGN
       RAN=drand48(RDUM)
       RAN1=drand48(RDUM)
       IF(RAN1.LT.0.5) ASIGN=-ASIGN
       ZS(NPONT)=Z-DLOG(RAN)*PENFRA(KGAS,2,I)*ASIGN
C ENTER POSSIBLE PENNING TRANSFER TIME
  667  TPEN=ST
       IF(PENFRA(KGAS,3,I).EQ.0.0) GO TO 668
       RAN=drand48(RDUM)
       TPEN=ST-DLOG(RAN)*PENFRA(KGAS,3,I)
  668  TS(NPONT)=TPEN
       ES(NPONT)=1.0
       DCX(NPONT)=DCX1
       DCY(NPONT)=DCY1
       DCZ(NPONT)=DCZ1
C FIND LAST TIME PLANE BEFORE TPEN
       TSTOP1=0.0
       IPLANE1=0
       DO 669 KDUM=1,ITFINAL
       TSTOP1=TSTEP+TSTOP1
       IF(TPEN.LT.TSTOP1) GO TO 670
       IPLANE1=IPLANE1+1
  669  CONTINUE
C PENNING TRANSFER OCCURS AFTER FINAL TIME PLANE. CLEAR ENTRY
       NPONT=NPONT-1
       NCLUS=NCLUS-1
       GO TO 5
  670  IPL(NPONT)=IPLANE1         
      ENDIF                                   
    5 S2=(S1*S1)/(S1-1.0D0) 
C ANISOTROPIC SCATTERING
      R3=drand48(RDUM) 
      IF(INDEX(KGAS,I).EQ.1) THEN
       R31=drand48(RDUM)
       F3=1.0D0-R3*ANGCT(KGAS,IE,I)       
       IF(R31.GT.PSCT(KGAS,IE,I)) F3=-F3
      ELSE IF(INDEX(KGAS,I).EQ.2) THEN
       EPSI=PSCT(KGAS,IE,I)
       F3=1.0D0-(2.0D0*R3*(1.0D0-EPSI)/(1.0D0+EPSI*(1.0D0-2.0D0*R3)))
      ELSE
C ISOTROPIC SCATTERING                                                
       F3=1.0D0-2.0D0*R3  
      ENDIF
      THETA0=DACOS(F3)                                                  
      R4=drand48(RDUM)
      PHI0=F4*R4                                                        
      F8=DSIN(PHI0)                                                     
      F9=DCOS(PHI0)
      ARG1=1.0D0-S1*EI/EOK                                              
      ARG1=DMAX1(ARG1,SMALL)                                            
      D=1.0D0-F3*DSQRT(ARG1)                                            
      E1=EOK*(1.0D0-EI/(S1*EOK)-2.0D0*D/S2) 
      E1=DMAX1(E1,SMALL)                                                
      Q=DSQRT((EOK/E1)*ARG1)/S1                                         
      Q=DMIN1(Q,1.0D0)                                                  
      THETA=DASIN(Q*DSIN(THETA0))                                       
      F6=DCOS(THETA)                                                    
      U=(S1-1.0D0)*(S1-1.0D0)/ARG1                                      
      CSQD=F3*F3                                                        
      IF(F3.LT.0.0D0.AND.CSQD.GT.U) F6=-1.0D0*F6                        
      F5=DSIN(THETA)                                                    
      DZCOM=DMIN1(DZCOM,1.0D0)                                          
      ARGZ=DSQRT(DXCOM*DXCOM+DYCOM*DYCOM)
      IF(ARGZ.EQ.0.0D0) THEN
       DCZ1=F6         
       DCX1=F9*F5                             
       DCY1=F8*F5 
       IF(NTMPFLG.EQ.1) THEN
C USE FREE KINEMATICS FOR IONISATION SECONDARY ANGLE
        F5S=F5*DSQRT(E1/ES(NCLTMP))
        IF(F5S.GT.1.0) F5S=1.0
        THSEC=DASIN(F5S)
        F5S=DSIN(THSEC)
        F6S=DCOS(THSEC)
        IF(F6.LT.0.0) F6S=-F6S
        PHIS=PHI0+API
        IF(PHIS.GT.F4) PHIS=PHI0-F4
        F8S=DSIN(PHIS)
        F9S=DCOS(PHIS)
        DCZ(NCLTMP)=F6S
        DCY(NCLTMP)=F8S*F5S
        DCX(NCLTMP)=F9S*F5S
        NTMPFLG=0
       ENDIF
       GO TO 190
      ENDIF                                            
      DCZ1=DZCOM*F6+ARGZ*F5*F8                                         
      DCY1=DYCOM*F6+(F5/ARGZ)*(DXCOM*F9-DYCOM*DZCOM*F8)                 
      DCX1=DXCOM*F6-(F5/ARGZ)*(DYCOM*F9+DXCOM*DZCOM*F8) 
      IF(NTMPFLG.EQ.1) THEN
C USE FREE KINEMATICS FOR IONISATION SECONDARY ANGLE
       F5S=F5*DSQRT(E1/ES(NCLTMP))
       IF(F5S.GT.1.0) F5S=1.0
       THSEC=DASIN(F5S)
       F5S=DSIN(THSEC)
       F6S=DCOS(THSEC)
       IF(F6.LT.0.0) F6S=-F6S
       PHIS=PHI0+API
       IF(PHIS.GT.F4) PHIS=PHI0-F4
       F8S=DSIN(PHIS)
       F9S=DCOS(PHIS)
       DCZ(NCLTMP)=DZCOM*F6S+ARGZ*F5S*F8S
       DCY(NCLTMP)=DYCOM*F6S+(F5S/ARGZ)*(DXCOM*F9S-DYCOM*DZCOM*F8S)
       DCX(NCLTMP)=DXCOM*F6S-(F5S/ARGZ)*(DYCOM*F9S+DXCOM*DZCOM*F8S)
       NTMPFLG=0
      ENDIF
  190 CONTINUE  
C TRANSFORM VELOCITY VECTORS TO LAB FRAME
      VTOT=CONST9*DSQRT(E1)
      CX1=DCX1*VTOT+VGX
      CY1=DCY1*VTOT+VGY
      CZ1=DCZ1*VTOT+VGZ
C CALCULATE ENERGY AND DIRECTION IN LAB FRAME
      E1=(CX1*CX1+CY1*CY1+CZ1*CZ1)/CONST10
      CONST11=1.0D0/(CONST9*DSQRT(E1))
      DCX1=CX1*CONST11
      DCY1=CY1*CONST11
      DCZ1=CZ1*CONST11
C STORE DIRECTION COSINES AND ENERGY AFTER N COLLISIONS
C FOR LATER REUSE IN PRIMARY GENERATION
      I100=I100+1
      IF(I100.EQ.200) THEN
       DCZ100=DCZ1
       DCX100=DCX1        
       DCY100=DCY1 
       E100=E1
       I100=0
      ENDIF  
      IF(IPRINT.GT.JPRINT) GO TO 200
      GO TO 1                                    
C    INTERMEDIATE PRINTOUT                       
 200  IPRINT=0 
      W=ZTOTS/TTOTS                          
      W=W*1.0D+09                                                       
      JCT=ID/100000
C     IF(J1.EQ.1) WRITE(6,201)                                         
C 201 FORMAT(/,7X,'INTERMEDIATE OUTPUT',/,'    VEL      POS        TIME 
C    /       COUNT    ')                  
C     WRITE(6,202) W,ZTOTS,TTOTS,JCT   
C 202 FORMAT(1X,F8.3,2(1X,D10.3),4X,I6)                 
      J1=J1+1            
      GO TO 1
C  MAIN LOOP END                                                  
  700 XID=DFLOAT(ID)
      IF(NELEC.GT.IPRIM) THEN
       ANEION=DFLOAT(NEION)
       ANBT=DFLOAT(NELEC-IPRIM)
       ATTOINT=ANEION/ANBT
       ATTERT=DSQRT(ANEION)/ANEION
       AIOERT=DSQRT(ANBT)/ANBT
      ELSE
       ANEION=DFLOAT(NEION) 
       ATTOINT=-1.0D0
       ATTERT=DSQRT(ANEION)/ANEION
      ENDIF
      JCT=ID/100000  
      IF(J1.EQ.1) THEN
      WRITE(6,940) NCLUS,ITER
  940 FORMAT(2(/),' PROGRAM STOPPED  (TOO FEW COLLISIONS), DECREASE THE 
     /ESTIMATED ALPHA.  NCLUS = ',I7,'  ITER =',I9)      
      STOP
      ENDIF
      IF(JPRT.EQ.1) WRITE(6,878) NELEC,NEION,IPRIM
 878  FORMAT(/,' TOTAL NO OF ELECTRONS=',I8,/,' TOTAL NO OF NEG. IONS=',
     /I8,/,' TOTAL NO OF PRIMARIES=',I8)  
      EPRMBAR=0.0D0
      E2PRM=0.0D0
      IF(IPRIM.EQ.1) RETURN      
      DO 310 I=1,IPRIM
      E2PRM=E2PRM+EPRM(I)*EPRM(I)
 310  EPRMBAR=EPRMBAR+EPRM(I)
      EBAR=EPRMBAR/IPRIM
      EERR=DSQRT(E2PRM/IPRIM-EBAR**2)
      IF(JPRT.EQ.1) WRITE(6,836) EBAR,EERR
 836  FORMAT(/,2X,'AVERAGE ENERGY OF PRIMARY ELECTRON =',F10.3,' EV.',/,
     /'   ENERGY SPREAD OF PRIMARY ELECTRON =',F10.3,' EV.')
C     WRITE(6,835) (IESPECP(J),J=1,100)
C835  FORMAT(/,2X,'ENERGY SPECTRUM OF PRIMARY ELECTRONS IN 1 EV. BINS',/
C    /,10(2X,10I5,/))
      RETURN               
  315 IF(ITER.GT.NMAX) THEN
       WRITE(6,991) ITER,NMAX,NPONT,NELEC,IPRIM,NMXADD
 991   FORMAT(2(/),' PROGRAM STOPPED.  ITER =',I10,'    NMAX =',I10,/,
     /' NPONT=',I4,' NELEC=',I8,' IPRIM=',I4,' NMXADD=',I3)
       STOP
      ENDIF                                                             
      RETURN                                                            
      END
      SUBROUTINE TPLANEAT(T,E1,CX1,CY1,DCZ1,AP,BP,EFLD,IPLANE)
      IMPLICIT REAL*8 (A-H,O-Z)  
      IMPLICIT INTEGER*8 (I-N)
      COMMON/CNSTS1/CONST1,CONST2,CONST3,CONST4,CONST5
      COMMON/BFLD/EOVB,WB,BTHETA,BMAG
      COMMON/CION/ALPHAST,VDST,TSTEP,ZSTEP,TFINAL,ZFINAL,ITFINAL,IPRIM
      COMMON/OUTPTT/TIME(300),ICOLL(6,5),SPEC(4000),TMAX1,
     /AVE,DEN,XID,X,Y,Z,ST,NNULL,ICOLN(6,290),ICOLNN(6,10)
      COMMON/TPLOUT/ETPL(8),XTPL(8),YTPL(8),ZTPL(8),TTPL(8),XXTPL(8),
     /YYTPL(8),ZZTPL(8),VZTPL(8),NETPL(8),ATTOINT,ATTERT,AIOERT 
C-----------------------------------------------------------------------
C STORES POSITION, TIME AND ENERGY AND SUMS REQUIRED
C TO CALCULATE DEVIATIONS AND MEANS AT PLANE =IPLANE 
C-----------------------------------------------------------------------
      TIMESP=IPLANE*TSTEP
C CALC TIME LEFT TO ARRIVE AT PLANE
      TIMLFT=TIMESP-ST
      T2LFT=TIMLFT*TIMLFT
      A=AP*TIMLFT
      B=BP*T2LFT 
      EPLANE=E1+A+B
      WBT=WB*TIMLFT
      COSWT=DCOS(WBT)
      SINWT=DSIN(WBT)                      
      CONST6=DSQRT(E1/EPLANE)         
      DCZ2=DCZ1*CONST6+EFLD*TIMLFT*CONST5/DSQRT(EPLANE)
      XPLANE=X+(CX1*SINWT-CY1*(1.0D0-COSWT))/WB   
      YPLANE=Y+(CY1*SINWT+CX1*(1.0D0-COSWT))/WB      
      ZPLANE=Z+DCZ1*TIMLFT*DSQRT(E1)*CONST3*0.01D0+T2LFT*EFLD*CONST2
      VZPLANE=DCZ2*DSQRT(EPLANE)*CONST3*0.01D0
      XTPL(IPLANE)=XTPL(IPLANE)+XPLANE
      YTPL(IPLANE)=YTPL(IPLANE)+YPLANE
      ZTPL(IPLANE)=ZTPL(IPLANE)+ZPLANE
      XXTPL(IPLANE)=XXTPL(IPLANE)+XPLANE*XPLANE
      YYTPL(IPLANE)=YYTPL(IPLANE)+YPLANE*YPLANE
      ZZTPL(IPLANE)=ZZTPL(IPLANE)+ZPLANE*ZPLANE
      ETPL(IPLANE)=ETPL(IPLANE)+EPLANE
      TTPL(IPLANE)=TTPL(IPLANE)+ST+TIMLFT
      VZTPL(IPLANE)=VZTPL(IPLANE)+VZPLANE
      NETPL(IPLANE)=NETPL(IPLANE)+1 
      RETURN
      END

