      NC01(J)=0
      NC02(J)=0
      NC03(J)=0
      NC04(J)=0
      NC05(J)=0
      NC06(J)=0
      NG11(J)=0
      NG12(J)=0
      NG13(J)=0
      NG14(J)=0
      NG15(J)=0
      NG16(J)=0
      NG21(J)=0
      NG22(J)=0
      NG23(J)=0
      NG24(J)=0
      NG25(J)=0
      NG26(J)=0
      DO 222 I=1,N4000
      QION1(J,I)=0.0D0
      QION2(J,I)=0.0D0
      QION3(J,I)=0.0D0
      QION4(J,I)=0.0D0
      QION5(J,I)=0.0D0
      QION6(J,I)=0.0D0
  222 CONTINUE
