      QIN(27,I)=0.0628/(EIN(27)*BETA2)*(DLOG(BETA2*GAMMA2*EMASS2/(4.0
     /*EIN(27)))-BETA2-DEN(I)/2.0)*BBCONST*EN/(EN+EIN(27)+E(3))*1.017
      IF(QIN(27,I).LT.0.0) QIN(27,I)=0.0       
      IF(EN.LE.(3.0*EIN(27))) GO TO 737
      PEQIN(27,I)=PEQEL(2,(I-IOFFN(27)))
