      IF(EN.LE.1000.) GO TO 800
      DO 780 J=2,NBRM
      IF(EN.LE.EBRM(J)) GO TO 790
  780 CONTINUE
      J=NBREM
  790 A=(DLOG(Z6T(J))-DLOG(Z6T(J-1)))/(EBRM(J)-EBRM(J-1))
      B=(DLOG(Z6T(J))*EBRM(J-1)-DLOG(Z6T(J-1))*EBRM(J))/
     /(EBRM(J-1)-EBRM(J))
      A1=(DLOG(Z1T(J))-DLOG(Z1T(J-1)))/(EBRM(J)-EBRM(J-1))
      B1=(DLOG(Z1T(J))*EBRM(J-1)-DLOG(Z1T(J-1))*EBRM(J))/
     /(EBRM(J-1)-EBRM(J))
      QIN(56,I)=DEXP(A*EN+B)*2.D-24
      QIN(57,I)=DEXP(A1*EN+B1)*6.D-24
  800 CONTINUE
C
C  LOAD NULL COLLISIONS
C
C WINTERS DISSOCIATION X-SECTION       REDUCED BY * 0.9
c WINTERS  CHEM.PHYS. 36(1979)353
      QNULL(1,I)=0.0
      IF(EN.LE.XNUL1(1)) GO TO 805
      IF(EN.GT.XNUL1(NUL1)) GO TO 803
      DO 801 J=2,NUL1
      IF(EN.LE.XNUL1(J)) GO TO 802
  801 CONTINUE
      J=NUL1
  802 A=(YNUL1(J)-YNUL1(J-1))/(XNUL1(J)-XNUL1(J-1))
      B=(XNUL1(J-1)*YNUL1(J)-XNUL1(J)*YNUL1(J-1))/(XNUL1(J-1)-XNUL1(J))
      QNULL(1,I)=(A*EN+B)*1.D-16*0.9*SCLN(1)    
      GO TO 805
C SCALE BY 1/E ABOVE XNUL1(NUL1)) EV
  803 QNULL(1,I)=YNUL1(NUL1)*(XNUL1(NUL1)/EN)*1.D-16*0.9*SCLN(1)    
C
C LIGHT EMISSION FROM H ALPHA   
C  MOHLMANN AND DE HEER  CHEM.PHYS.19(1979)233 
C  
  805 QNULL(2,I)=0.0
      IF(EN.LE.XNUL2(1)) GO TO 810
      IF(EN.GT.XNUL2(NUL2)) GO TO 808
      DO 806 J=2,NUL2
      IF(EN.LE.XNUL2(J)) GO TO 807
  806 CONTINUE
      J=NUL2
  807 A=(YNUL2(J)-YNUL2(J-1))/(XNUL2(J)-XNUL2(J-1))
      B=(XNUL2(J-1)*YNUL2(J)-XNUL2(J)*YNUL2(J-1))/(XNUL2(J-1)-XNUL2(J))
      QNULL(2,I)=(A*EN+B)*1.D-16*SCLN(2)
      GO TO 810
C SCALE BY 1/E ABOVE XNUL2(NUL2)) EV
  808 QNULL(2,I)=YNUL2(NUL2)*(XNUL2(NUL2)/EN)*1.D-16*SCLN(2)  
C  
C LIGHT EMISSION FROM CH2(A2DELTA - X2PI)
C  MOHLMANN AND DE HEER  CHEM.PHYS.19(1979)233 
C  
  810 QNULL(3,I)=0.0
      IF(EN.LE.XNUL3(1)) GO TO 815
      IF(EN.GT.XNUL3(NUL3)) GO TO 813
      DO 811 J=2,NUL3
      IF(EN.LE.XNUL3(J)) GO TO 812
  811 CONTINUE
      J=NUL3
  812 A=(YNUL3(J)-YNUL3(J-1))/(XNUL3(J)-XNUL3(J-1))
      B=(XNUL3(J-1)*YNUL3(J)-XNUL3(J)*YNUL3(J-1))/(XNUL3(J-1)-XNUL3(J))
      QNULL(3,I)=(A*EN+B)*1.D-16*SCLN(3)
      GO TO 815
C SCALE BY 1/E ABOVE XNUL3(NUL3)) EV
  813 QNULL(3,I)=YNUL3(NUL3)*(XNUL3(NUL3)/EN)*1.D-16*SCLN(3)    
C
  815 CONTINUE
      SUMEXC=0.0
      DO 850 J=1,NIN
  850 SUMEXC=SUMEXC+QIN(J,I)
      SUMION=0.0
      DO 851 J=1,NION
  851 SUMION=SUMION+QION(J,I)
      SUMATT=0.0
      DO 852 J=1,NATT
  852 SUMATT=SUMATT+QATT(J,I)
      SUMINEL=SUMEXC+SUMION+SUMATT
C     WRITE(6,888) EN,SUMEXC,SUMION,SUMINEL
C 888 FORMAT(' EN=',D12.3,' SUMEXC=',D12.3,' SUMION=',D12.3,'SUMINEL=',
C    /D12.3)
      SUMEXCH=0.0
      DO 854 J=11,55
  854 SUMEXCH=SUMEXCH+QIN(J,I)
      SUMTRP=QIN(11,I)+QIN(13,I)+QIN(25,I)
      SUMSNG=SUMEXCH-SUMTRP
C     WRITE(6,889) EN,SUMTRP,SUMSNG,SUMEXCH
C 889 FORMAT(' EN=',D12.4,' SUMTRIPLETS=',D12.3,' SUMSINGLETS=',D12.3,'
C    / TOTAL=',D12.3)
 1000 CONTINUE                                                          
C  SAVE COMPUTE TIME 
      DO 1010 K=1,NIN
      IF(EFINAL.LE.EIN(K)) THEN
       NIN=K-1
       GO TO 1011
      ENDIF
 1010 CONTINUE
 1011 CONTINUE
C     IF(EFINAL.GT.1000.) NIN=57
C                                      
      RETURN  
      END
