C     SAVE ON COMPUTING TIME                                            
C
      DO 2000 K=1,74                                                    
      J=145-K   
      IF(EFINAL.LE.EIN(J)) NIN=J-1
 2000 CONTINUE
C     IF(EFINAL.GT.1000.) NIN=146
C                                                                
      RETURN                                                            
