      IF(EN.LT.XATT(1)) GO TO 40                                        
      IF(EN.GT.XATT(NATT1)) GO TO 40                                    
      DO 31 J=2,NATT1                                                   
      IF(EN.LE.XATT(J)) GO TO 32                                        
   31 CONTINUE                                                          
      J=NATT1                                                           
   32 A=(YATT(J)-YATT(J-1))/(XATT(J)-XATT(J-1))                         
