      SCRPT(259)=' DIPOLE NEUTRAL DISS  F=0.097947   ELOSS= 14.117  '
      SCRPT(260)=' DIPOLE NEUTRAL DISS  F=0.039540   ELOSS= 15.117  '
      SCRPT(261)=' DIPOLE NEUTRAL DISS  F=0.042191   ELOSS= 16.117  '
      SCRPT(262)=' DIPOLE NEUTRAL DISS  F=0.059428   ELOSS= 17.117  '
      SCRPT(263)=' DIPOLE NEUTRAL DISS  F=0.052795   ELOSS= 18.117  '
      SCRPT(264)=' DIPOLE NEUTRAL DISS  F=0.024912   ELOSS= 19.117  '
      SCRPT(265)=' DIPOLE NEUTRAL DISS  F=0.010524   ELOSS= 20.117  '
      SCRPT(266)=' DIPOLE NEUTRAL DISS  F=0.002614   ELOSS= 21.117  '
C  NULL COLLISIONS  
      SCRPTN(1)=' H2O: OH(X)  GROUND STATE DISSOCIATION             '
      SCRPTN(2)=' H2O: OH(A-X) 306-350 NM EMISSION                  ' 
      SCRPTN(3)=' H2O: H ALPHA 3-2 (BALMER)  656.3 NM.  EMISSION    '  
      SCRPTN(4)=' H2O: H ALPHA 2-1 (LYMAN)   121.6 NM.  EMISSION    '
C CALC POPULATION OF LOW ENERGY VIBRATIONAL STATE
      APOPV1=DEXP(EIN(211)/AKT)
      APOPGS=1.0
      APOPSUM=APOPGS+APOPV1
      APOPV1=APOPV1/APOPSUM
C KEEP APOPGS=1 TO ALLOW FOR EXCITATIONS FROM UPPER STATE   
C                                          
      DO 900 I=1,NSTEP                                               
      EN=EG(I)
      GAMMA1=(EMASS2+2.0D0*EN)/EMASS2
      GAMMA2=GAMMA1*GAMMA1
      BETA=DSQRT(1.0D0-1.0D0/GAMMA2)
      BETA2=BETA*BETA
C
C ELASTIC
      IF(EN.LE.XEL(1)) THEN
       QELA=YEL(1)*1.D-16
       GO TO 32
      ENDIF                                             
      DO 30 J=2,NELA                                                    
      IF(EN.LE.XEL(J)) GO TO 31                                         
   30 CONTINUE                                                          
      J=NELA    
   31 Y1=DLOG(YEL(J))
      Y2=DLOG(YEL(J-1)) 
      X1=DLOG(XEL(J))
      X2=DLOG(XEL(J-1))                                                 
      A=(Y1-Y2)/(X1-X2)                                                 
      B=(X2*Y1-X1*Y2)/(X2-X1)                                           
      QELA=DEXP(A*DLOG(EN)+B)*1.0D-16    
C MOMENTUM TRANSFER ELASTIC
   32 IF(EN.LE.XMT(1)) THEN
       QMMT=YMT(1)*1.D-16
       GO TO 35
      ENDIF
      DO 33 J=2,NMMT
      IF(EN.LE.XMT(J)) GO TO 34
   33 CONTINUE
      J=NMMT
   34 Y1=DLOG(YMT(J))
      Y2=DLOG(YMT(J-1))
      X1=DLOG(XMT(J))
      X2=DLOG(XMT(J-1))  
      A=(Y1-Y2)/(X1-X2)
      B=(X2*Y1-X1*Y2)/(X2-X1)
      QMMT=DEXP(A*DLOG(EN)+B)*1.D-16
C ANGULAR DISTRIBUTION FUNCTION OKHRIMOVSKY
   35 IF(EN.LE.XEPS(1)) THEN
       EPS=YEPS(1)
       GO TO 38
      ENDIF
      DO 36 J=2,NEPS
      IF(EN.LE.XEPS(J)) GO TO 37
   36 CONTINUE
      J=NEPS
   37 A=(YEPS(J)-YEPS(J-1))/(XEPS(J)-XEPS(J-1))
      B=(XEPS(J-1)*YEPS(J)-XEPS(J)*YEPS(J-1))/(XEPS(J-1)-XEPS(J))
      EPS=A*EN+B
C CONVERT TO  (1.0-EPS)
   38 EPS=1.0-EPS
C USE ELASTIC                  
      Q(2,I)=QELA         
      PEQEL(2,I)=EPS
C CAN USE ISOTROPIC ELASTIC WITH FOLLOWING COMMENTED LINES
C     Q(2,I)=QMMT
