      IF(EN.LE.XION2(J)) GO TO 55
   54 CONTINUE
      J=NION2
   55 A=(YION2(J)-YION2(J-1))/(XION2(J)-XION2(J-1))
      B=(XION2(J-1)*YION2(J)-XION2(J)*YION2(J-1))/(XION2(J-1)-XION2(J))
      QION(2,I)=(A*EN+B)*1.D-16
      GO TO 57
