   57 IF(EN.GT.XION3(NION3)) GO TO 60
      IF(EN.LE.XION3(1)) GO TO 90
      DO 58 J=2,NION3
      IF(EN.LE.XION3(J)) GO TO 59
   58 CONTINUE
      J=NION3
   59 A=(YION3(J)-YION3(J-1))/(XION3(J)-XION3(J-1))
      B=(XION3(J-1)*YION3(J)-XION3(J)*YION3(J-1))/(XION3(J-1)-XION3(J))
      QION(3,I)=(A*EN+B)*1.D-16
      GO TO 61
   60 QION(3,I)=QCOUNT*0.13275
C IONISATION TO  O+
   61 IF(EN.GT.XION4(NION4)) GO TO 64
      IF(EN.LE.XION4(1)) GO TO 90
      DO 62 J=2,NION4
      IF(EN.LE.XION4(J)) GO TO 63
   62 CONTINUE
      J=NION4
   63 A=(YION4(J)-YION4(J-1))/(XION4(J)-XION4(J-1))
      B=(XION4(J-1)*YION4(J)-XION4(J)*YION4(J-1))/(XION4(J-1)-XION4(J))
      QION(4,I)=(A*EN+B)*1.D-16
      GO TO 65
   64 QION(4,I)=QCOUNT*0.02129
C IONISATION TO  H2+
   65 IF(EN.GT.XION5(NION5)) GO TO 68
      IF(EN.LE.XION5(1)) GO TO 90
      DO 66 J=2,NION5
      IF(EN.LE.XION5(J)) GO TO 67
   66 CONTINUE
      J=NION5
   67 A=(YION5(J)-YION5(J-1))/(XION5(J)-XION5(J-1))
      B=(XION5(J-1)*YION5(J)-XION5(J)*YION5(J-1))/(XION5(J-1)-XION5(J))
      QION(5,I)=(A*EN+B)*1.D-16
      GO TO 73
   68 QION(5,I)=QCOUNT*0.00035
C IONISATION TO  H+ + OH+
   73 IF(EN.GT.XION6(NION6)) GO TO 76
      IF(EN.LE.XION6(1)) GO TO 90
      DO 74 J=2,NION6
      IF(EN.LE.XION6(J)) GO TO 75
   74 CONTINUE
      J=NION6
   75 A=(YION6(J)-YION6(J-1))/(XION6(J)-XION6(J-1))
      B=(XION6(J-1)*YION6(J)-XION6(J)*YION6(J-1))/(XION6(J-1)-XION6(J))
      QION(6,I)=(A*EN+B)*1.D-16
      GO TO 77
   76 QION(6,I)=QCOUNT*0.01395
C IONISATION TO  H+ + O+
   77 IF(EN.GT.XION7(NION7)) GO TO 80
      IF(EN.LE.XION7(1)) GO TO 90
      DO 78 J=2,NION7
      IF(EN.LE.XION7(J)) GO TO 79
   78 CONTINUE
      J=NION7
   79 A=(YION7(J)-YION7(J-1))/(XION7(J)-XION7(J-1))
      B=(XION7(J-1)*YION7(J)-XION7(J)*YION7(J-1))/(XION7(J-1)-XION7(J))
      QION(7,I)=(A*EN+B)*1.D-16
      GO TO 81
   80 QION(7,I)=QCOUNT*0.00705
C IONISATION TO  O++
   81 IF(EN.GT.XION8(NION8)) GO TO 84
      IF(EN.LE.XION8(1)) GO TO 90
      DO 82 J=2,NION8
      IF(EN.LE.XION8(J)) GO TO 83
   82 CONTINUE
      J=NION8
   83 A=(YION8(J)-YION8(J-1))/(XION8(J)-XION8(J-1))
      B=(XION8(J-1)*YION8(J)-XION8(J)*YION8(J-1))/(XION8(J-1)-XION8(J))
      QION(8,I)=(A*EN+B)*1.D-16
      GO TO 85
   84 QION(8,I)=QCOUNT*0.00085
C IONISATION TO OXYGEN K-SHELL
   85 IF(EN.LE.XKSH(1)) GO TO 90
      DO 86 J=2,NKSH 
      IF(EN.LE.XKSH(J)) GO TO 87
   86 CONTINUE
      J=NKSH
   87 A=(YKSH(J)-YKSH(J-1))/(XKSH(J)-XKSH(J-1))
      B=(XKSH(J-1)*YKSH(J)-XKSH(J)*YKSH(J-1))/(XKSH(J-1)-XKSH(J))
      QION(9,I)=(A*EN+B)*1.D-16
C
   90 DO 98 J=1,NION     
      IF(EN.LE.(2.0*EION(J))) GO TO 98
      PEQION(J,I)=PEQEL(2,(I-IOFFION(J)))
   98 CONTINUE
C
C  ATTACHMENT    H-
      Q(4,I)=0.0                 
      QATT(1,I)=0.0                                       
      IF(EN.LE.XATT1(1).OR.EN.GE.XATT1(NATT1)) GO TO 103               
      DO 101  J=2,NATT1                                                
      IF(EN.LE.XATT1(J)) GO TO 102                                      
  101 CONTINUE                                                          
      J=NATT1     
  102 YJ=DLOG(YATT1(J))
      YJ1=DLOG(YATT1(J-1))
      XJ=DLOG(XATT1(J))
      XJ1=DLOG(XATT1(J-1))                                             
      A=(YJ-YJ1)/(XJ-XJ1)                     
      B=(XJ1*YJ-XJ*YJ1)/(XJ1-XJ) 
