      IF(EN.LE.XATT2(J)) GO TO 105                                      
  104 CONTINUE                                                          
      J=NATT2     
  105 YJ=DLOG(YATT2(J))
      YJ1=DLOG(YATT2(J-1))
      XJ=DLOG(XATT2(J))
      XJ1=DLOG(XATT2(J-1))                                             
      A=(YJ-YJ1)/(XJ-XJ1)                     
      B=(XJ1*YJ-XJ*YJ1)/(XJ1-XJ) 
      QATT(2,I)=DEXP(A*DLOG(EN)+B)*1.D-21  
