  106 QATT(3,I)=0.0
      IF(EN.LE.XATT3(1).OR.EN.GE.XATT3(NATT3)) GO TO 109               
      DO 107  J=2,NATT3                                                
      IF(EN.LE.XATT3(J)) GO TO 108                                      
  107 CONTINUE                                                          
      J=NATT3     
  108 YJ=DLOG(YATT3(J))
      YJ1=DLOG(YATT3(J-1))
      XJ=DLOG(XATT3(J))
      XJ1=DLOG(XATT3(J-1))                                             
      A=(YJ-YJ1)/(XJ-XJ1)                     
      B=(XJ1*YJ-XJ*YJ1)/(XJ1-XJ) 
      QATT(3,I)=DEXP(A*DLOG(EN)+B)*1.D-21  
C   
  109 Q(4,I)=0.0                                             
      Q(5,I)=0.0                                                        
      Q(6,I)=0.0 
C
C  ZERO INELASTIC ARRAYS
      DO 130 L=1,NIN
      QIN(L,I)=0.0D0
      PEQIN(L,I)=0.0D0
  130 CONTINUE
C ----------------------------------------------------------------      
C  DIPOLE BORN ROTATIONAL STATES                                 
C----------------------------------------------------------------
      ENRT=DSQRT(EN)
C      
C  SUPER ELASTIC ROTATIONAL COLLISIONS
      DO 140 L=2,210,2
      AL=AJIN(L)
      L2=L/2
      QIN(L,I)=DBK*SALPHA(L2)*PJ(IMAP(L))*DLOG((ENRT+DSQRT(EN-EIN(L)))/
     /(DSQRT(EN-EIN(L))-ENRT))/((2.0*AL+1.0)*EN)*AMPROT       
C IF ENERGY GT 200OEV SET ROTATION TO 0.0 AND USE AS MOD ELASTIC
      IF(EN.GT.2000.0) THEN
       QIN(L,I)=0.0
       GO TO 140
      ENDIF
C CALCULATE DIPOLE ANGULAR DISTRIBUTION FACTOR FOR TRANSITION
      EPOINT=EN/DABS(EIN(L))
      IF(EPOINT.GT.ENRTS(NRTANG)) THEN
       WRITE(6,922) EPOINT,ENRTS(NRTANG),EN
  922  FORMAT(' ENERGY ERROR IN ROT SUPER ANG CALC  EPOINT=',D12.4,' ENR
     /TS=',D12.4,' EN=',D12.4)
      ENDIF
      DO 131 M=1,NRTANG
      IF(EPOINT.LE.ENRTS(M)) GO TO 132 
  131 CONTINUE
      M=NRTANG
  132 A=(YEPSR(M)-YEPSR(M-1))/(ENRTS(M)-ENRTS(M-1))
      B=(ENRTS(M-1)*YEPSR(M)-ENRTS(M)*YEPSR(M-1))/(ENRTS(M-1)-ENRTS(M))
      EPSIL=A*EPOINT+B
      PEQIN(L,I)=1.0-EPSIL
C CALCULATE USED MT XSEC
      A=(YMTRT(M)-YMTRT(M-1))/(ENRTS(M)-ENRTS(M-1))
      B=(ENRTS(M-1)*YMTRT(M)-ENRTS(M)*YMTRT(M-1))/(ENRTS(M-1)-ENRTS(M))
      XSECDUM(L)=A*EPOINT+B
      XSECDUM(L)=XSECDUM(L)*QIN(L,I)
  140 CONTINUE
C 
C ROTATIONAL COLLISIONS
      DO 150 L=1,209,2
      QIN(L,I)=0.0
      IF(EN.LE.EIN(L)) GO TO 150
      AL=AJIN(L)
      L2=(L+1)/2
      QIN(L,I)=DBK*SALPHA(L2)*PJ(IMAP(L))*DLOG((ENRT+DSQRT(EN-EIN(L)))/
     /(ENRT-DSQRT(EN-EIN(L))))/((2.0*AL+1.0)*EN)*AMPROT       
C IF ENERGY GT 2000EV SET TO 0.0 AND USE MOD ELASTIC
      IF(EN.GT.2000.0) THEN
       QIN(L,I)=0.0
       GO TO 150
      ENDIF
C CALCULATE DIPOLE ANGULAR DISTRIBUTION FACTOR FOR TRANSITION
      EPOINT=EN/DABS(EIN(L))
      IF(EPOINT.GT.ENROT(NRTANG)) THEN
       WRITE(6,923) EPOINT,ENROT(NRTANG),EN
  923  FORMAT(' ENERGY ERROR IN ROT ANG CALC  EPOINT=',D12.4,' ENROT=',
     /D12.4,' EN=',D12.4)
      ENDIF
      DO 141 M=1,NRTANG
      IF(EPOINT.LE.ENROT(M)) GO TO 142 
  141 CONTINUE
      M=NRTANG
  142 A=(YEPSR(M)-YEPSR(M-1))/(ENROT(M)-ENROT(M-1))
      B=(ENROT(M-1)*YEPSR(M)-ENROT(M)*YEPSR(M-1))/(ENROT(M-1)-ENROT(M))
      EPSIL=A*EPOINT+B
      PEQIN(L,I)=1.0-EPSIL
C CALCULATE USED MT XSEC
      A=(YMTRT(M)-YMTRT(M-1))/(ENROT(M)-ENROT(M-1))
      B=(ENROT(M-1)*YMTRT(M)-ENROT(M)*YMTRT(M-1))/(ENROT(M-1)-ENROT(M))
      XSECDUM(L)=A*EPOINT+B
      XSECDUM(L)=XSECDUM(L)*QIN(L,I)
  150 CONTINUE  
C CALCULATE TOTAL AND MT SUMS OVER ROTATIONS
C     QSUPTT=0.0
C     QSUPMT=0.0
C     DO 160 L=2,210,2
C     QSUPTT=QSUPTT+QIN(L,I)
C     QSUPMT=QSUPMT+XSECDUM(L)
C 160 CONTINUE
C     QTOTTT=0.0
C     QTOTMT=0.0
C     DO 161 L=1,209,2
C     QTOTTT=QTOTTT+QIN(L,I)
C     QTOTMT=QTOTMT+XSECDUM(L)
C     QTSMT=QSUPMT+QTOTMT
C     QTSEL=QSUPTT+QTOTTT
C 161 CONTINUE
C     WRITE(6,944) EN,QSUPTT,QSUPMT,QTOTTT,QTOTMT,QTSEL,QTSMT
C 944 FORMAT(' CROSS SECTION ROTATIONAL SUMS AT ENERGY =',D12.4,/,
C    /' TOTAL SUPER =',D12.4,'  MT SUPER =',D12.4,/,' TOTAL ROTAT =',
C    /D12.4,'  MT ROTAT =',D12.4,/,' TOT ROT=',D12.4,' TOT ROT MT=',
C    /D12.4)
C 
C VIBRATION BEND V2 SUPERELASTIC (DIPOLE 1/E FALL OFF ABOVE ENERGY OF
C XVIB1(NVIB1) EV ) 
      QIN(211,I)=0.0
      IF(EN.LE.0.0) GO TO 180
      IF((EN+EIN(212)).GT.XVIB1(NVIB1)) GO TO 173
      DO 171 J=2,NVIB1
      IF((EN+EIN(212)).LE.XVIB1(J)) GO TO 172
  171 CONTINUE
      J=NVIB1
  172 A=(YVIB1(J)-YVIB1(J-1))/(XVIB1(J)-XVIB1(J-1))
      B=(XVIB1(J-1)*YVIB1(J)-XVIB1(J)*YVIB1(J-1))/(XVIB1(J-1)-XVIB1(J))
      QIN(211,I)=(EN+EIN(212))*(A*(EN+EIN(212))+B)/EN
      GOTO 174
  173 QIN(211,I)=YVIB1(NVIB1)*(XVIB1(NVIB1)/(EN+EIN(212)))/EN
  174 QIN(211,I)=QIN(211,I)*APOPV1*1.D-16        
      PEQIN(211,I)=PEQEL(2,(I-IOFFN(211)))
  180 CONTINUE
C  VIBRATION   BEND  V2 (DIPOLE 1/E FALL OFF ABOVE ENERGY OF
C  XVIB1(NVIB1) EV )
      QIN(212,I)=0.0                                                 
      IF(EN.LE.EIN(212)) GO TO 210   
      IF(EN.GT.XVIB1(NVIB1)) GO TO 203                                
      DO 201 J=2,NVIB1                                                  
      IF(EN.LE.XVIB1(J)) GO TO 202                                      
  201 CONTINUE                                                          
      J=NVIB1                                                           
  202 A=(YVIB1(J)-YVIB1(J-1))/(XVIB1(J)-XVIB1(J-1))                     
      B=(XVIB1(J-1)*YVIB1(J)-XVIB1(J)*YVIB1(J-1))/(XVIB1(J-1)-XVIB1(J)) 
      QIN(212,I)=(A*EN+B)       
      GO TO 204
  203 QIN(212,I)=YVIB1(NVIB1)*(XVIB1(NVIB1)/EN)  
  204 QIN(212,I)=QIN(212,I)*APOPGS*1.D-16 
C CALCULATE DIPOLE ANGULAR DISTRIBUTION FACTOR FOR TRANSITION
      EPOINT=EN/DABS(EIN(212))
      IF(EPOINT.GT.500.) GO TO 2043
      DO 2041 M=1,NRTANG
      IF(EPOINT.LE.ENROT(M)) GO TO 2042
 2041 CONTINUE
      M=NRTANG
 2042 A=(YEPSR(M)-YEPSR(M-1))/(ENROT(M)-ENROT(M-1))
      B=(ENROT(M-1)*YEPSR(M)-ENROT(M)*YEPSR(M-1))/(ENROT(M-1)-ENROT(M))
      EPSIL=A*EPOINT+B
      PEQIN(212,I)=1.0-EPSIL
      GO TO 210
 2043 PEQIN(212,I)=PEQEL(2,(I-IOFFN(212)))
  210 CONTINUE      
C  VIBRATION STRETCH V1+V3                                           
      QIN(213,I)=0.0                                                    
      IF(EN.LE.EIN(213)) GO TO 220                    
      IF(EN.GT.XVIB2(NVIB2)) GO TO 213                  
      DO 211 J=2,NVIB2                                                  
      IF(EN.LE.XVIB2(J)) GO TO 212                                      
  211 CONTINUE                                                          
      J=NVIB2                                                           
