      IF(EN.LE.XNUL3(1)) GO TO 815
      IF(EN.GT.XNUL3(NUL3)) GO TO 813
      DO 811 J=2,NUL3
      IF(EN.LE.XNUL3(J)) GO TO 812
  811 CONTINUE
      J=NUL3
  812 A=(YNUL3(J)-YNUL3(J-1))/(XNUL3(J)-XNUL3(J-1))
      B=(XNUL3(J-1)*YNUL3(J)-XNUL3(J)*YNUL3(J-1))/(XNUL3(J-1)-XNUL3(J))
      QNULL(3,I)=(A*EN+B)*1.D-16*SCLN(3)
      GO TO 815
C SCALE BY 1/E ABOVE XNUL3(NUL3) EV
  813 QNULL(3,I)=YNUL3(NUL3)*(XNUL3(NUL3)/EN)*1.D-16*SCLN(3)
C LIGHT EMISSION FROM H(2-1) , MOHLMANN AND DEHEER CHEM.PHYS.19(1979)233
  815 QNULL(4,I)=0.0
      IF(EN.LE.XNUL4(1))  GO TO 820
      IF(EN.GT.XNUL4(NUL4)) GO TO 818
      DO 816 J=2,NUL4
      IF(EN.LE.XNUL4(J)) GO TO 817
  816 CONTINUE
      J=NUL4
  817 A=(YNUL4(J)-YNUL4(J-1))/(XNUL4(J)-XNUL4(J-1))
      B=(XNUL4(J-1)*YNUL4(J)-XNUL4(J)*YNUL4(J-1))/(XNUL4(J-1)-XNUL4(J))
      QNULL(4,I)=(A*EN+B)*1.D-16*SCLN(4)
      GO TO 820
