C SCALE ROTATION X-SECTIONS BY ROTSCALE
      ROTSCALE=0.75
C CALC FRACTIONAL POPULATION DENSITY OF ROTATIONAL STATES
      B0=1.783D-4
C ROTATIONAL QUADRUPOLE MOMENT
      QBQA=0.90
      QBK=1.67552*(QBQA*A0)**2
      DO 103 K=2,48,2
      AK=DFLOAT(K)
  103 PJ(K)=0.0
      DO 104 K=1,49,2
      AK=DFLOAT(K)
  104 PJ(K)=3.0*(2.0*AK+1.0)*DEXP(-AK*(AK+1.0)*B0/AKT)
      ASUM=0.0
      DO 105 K=1,49
  105 ASUM=ASUM+PJ(K)
