      QIN(84,I)=0.0                                                     
      IF(EN.LE.EIN(84)) GO TO 330                              
      IF(EN.GT.XVB7(NVIB7)) GO TO 321         
      DO 310 J=2,NVIB7                                                  
      IF(EN.LE.XVB7(J)) GO TO 320                                      
  310 CONTINUE                                                          
      J=NVIB7                                                           
