      QIN(88,I)=0.0                                                     
      IF(EN.LE.EIN(88)) GO TO 2130                        
      IF(EN.GT.XVB11(NVIB11)) GO TO 2121              
      DO 2110 J=2,NVIB11                                              
      IF(EN.LE.XVB11(J)) GO TO 2120                                     
 2110 CONTINUE                                                          
      J=NVIB11                                                          
 2120 A=(YVB11(J)-YVB11(J-1))/(XVB11(J)-XVB11(J-1))                     
      B=(XVB11(J-1)*YVB11(J)-XVB11(J)*YVB11(J-1))/(XVB11(J-1)-XVB11(J)) 
