      IF(EN.LE.XEN(1)) THEN
       QELA=YELTT(1)
       QELM=YELMT(1)
       GO TO 35
      ENDIF                                        
      DO 25 J=2,NDATA                                                 
      IF(EN.LE.XEN(J)) GO TO 26                                         
   25 CONTINUE                                                          
      J=NDATA                                                           
   26 Y1=DLOG(YELTT(J))
      Y2=DLOG(YELTT(J-1))
      X1=DLOG(XEN(J))
      X2=DLOG(XEN(J-1))
      A=(Y1-Y2)/(X1-X2)                         
      B=(X2*Y1-X1*Y2)/(X2-X1)  
      QELA=DEXP(A*DLOG(EN)+B)*1.0D-16
C MOMENTUM TRANSFER ELASTIC
      DO 30 J=2,NDATA
      IF(EN.LE.XEN(J)) GO TO 31
   30 CONTINUE
      J=NDATA
   31 Y1=DLOG(YELMT(J))
      Y2=DLOG(YELMT(J-1))
      X1=DLOG(XEN(J))
      X2=DLOG(XEN(J-1))
