      Q(2,I)=QELA
      PEQEL(2,I)=EPS       
C USE MT IF NO ANGULAR SCATTERING DISTRIBUTION
      IF(NANISO.EQ.0) Q(2,I)=QELMT
      IF(NANISO.EQ.0) PEQEL(2,I)=0.0
C-----------------------------------
C      
C IONISATION CALCULATION 
   40 DO 41 J=1,NION
