      IF(NANISO.EQ.2) PEQION(J,I)=0.0
   41 QION(J,I)=0.0
C IF ENERGY LESS THAN 1KEV CALCULATE COUNTING AND GROSS IONISATION
      IF(EN.GT.1000.) GO TO 44
      IF(EN.LE.EION(1)) GO TO 91
      DO 42 J=2,NIONC
      IF(EN.LE.XION(J)) GO TO 43
   42 CONTINUE      
      J=NIONC
   43 A=(YIONC(J)-YIONC(J-1))/(XION(J)-XION(J-1))
      B=(XION(J-1)*YIONC(J)-XION(J)*YIONC(J-1))/(XION(J-1)-XION(J))
