      GO TO 45
C CALCULATE COUNTING AND GROSS IONISATION FROM BORN BETEHE FIT
   44 X2=1.0D0/BETA2
      X1=X2*DLOG(BETA2/(1.0D0-BETA2))-1.0D0
      QCOUNT=CONST*(AM2*(X1-DEN(I)/2.0)+C*X2)
      QGROSS=QCOUNT*1.218
   45 CONTINUE  
C
C IONISATION TO N2O+
      IF(EN.GT.XION1(NION1)) GO TO 52
      IF(EN.LE.XION1(1)) GO TO 91     
