      IF(EN.LE.XION1(J)) GO TO 51
   50 CONTINUE
      J=NION1
   51 A=(YION1(J)-YION1(J-1))/(XION1(J)-XION1(J-1))
      B=(XION1(J-1)*YION1(J)-XION1(J)*YION1(J-1))/(XION1(J-1)-XION1(J))
