C IONISATION TO (NO+ , N)
   53 IF(EN.GT.XION2(NION2)) GO TO 56
      IF(EN.LE.XION2(1)) GO TO 91     
      DO 54 J=2,NION2
      IF(EN.LE.XION2(J)) GO TO 55
   54 CONTINUE
      J=NION2
   55 A=(YION2(J)-YION2(J-1))/(XION2(J)-XION2(J-1))
      B=(XION2(J-1)*YION2(J)-XION2(J)*YION2(J-1))/(XION2(J-1)-XION2(J))
      QION(2,I)=(A*EN+B)*1.D-16
      GO TO 57
   56 QION(2,I)=QCOUNT*0.2088
C IONISATION TO (N2+ , O)
   57 IF(EN.GT.XION3(NION3)) GO TO 60
      IF(EN.LE.XION3(1)) GO TO 91  
      DO 58 J=2,NION3
      IF(EN.LE.XION3(J)) GO TO 59
   58 CONTINUE
      J=NION3
   59 A=(YION3(J)-YION3(J-1))/(XION3(J)-XION3(J-1))
      B=(XION3(J-1)*YION3(J)-XION3(J)*YION3(J-1))/(XION3(J-1)-XION3(J))
      QION(3,I)=(A*EN+B)*1.D-16
      GO TO 61
   60 QION(3,I)=QCOUNT*0.0623
C IONISATION TO (O+ , N2)
   61 IF(EN.GT.XION4(NION4)) GO TO 64
      IF(EN.LE.XION4(1)) GO TO 91      
      DO 62 J=2,NION4
      IF(EN.LE.XION4(J)) GO TO 63
   62 CONTINUE
      J=NION4
   63 A=(YION4(J)-YION4(J-1))/(XION4(J)-XION4(J-1))
      B=(XION4(J-1)*YION4(J)-XION4(J)*YION4(J-1))/(XION4(J-1)-XION4(J))
      QION(4,I)=(A*EN+B)*1.D-16
      GO TO 65
   64 QION(4,I)=QCOUNT*0.0135 
C IONISATION TO (N+ , NO)
   65 IF(EN.GT.XION5(NION5)) GO TO 68
      IF(EN.LE.XION5(1)) GO TO 91     
      DO 66 J=2,NION5
      IF(EN.LE.XION5(J)) GO TO 67
   66 CONTINUE
      J=NION5
   67 A=(YION5(J)-YION5(J-1))/(XION5(J)-XION5(J-1))
      B=(XION5(J-1)*YION5(J)-XION5(J)*YION5(J-1))/(XION5(J-1)-XION5(J))
      QION(5,I)=(A*EN+B)*1.D-16
