C DOUBLE IONISATION  TO (N2+ , O+)
   69 IF(EN.GT.XION6(NION6)) GO TO 72
      IF(EN.LE.XION6(1)) GO TO 91   
      DO 70 J=2,NION6
      IF(EN.LE.XION6(J)) GO TO 71
   70 CONTINUE
      J=NION6
