      B=(XION7(J-1)*YION7(J)-XION7(J)*YION7(J-1))/(XION7(J-1)-XION7(J))
      QION(7,I)=(A*EN+B)*1.D-16
      GO TO 77
   76 QION(7,I)=QCOUNT*0.0399 
C DOUBLE IONISATION  TO (N+ , N+  ,O)
   77 IF(EN.GT.XION8(NION8)) GO TO 80
      IF(EN.LE.XION8(1)) GO TO 91  
      DO 78 J=2,NION8
      IF(EN.LE.XION8(J)) GO TO 79
   78 CONTINUE
      J=NION8
   79 A=(YION8(J)-YION8(J-1))/(XION8(J)-XION8(J-1))
      B=(XION8(J-1)*YION8(J)-XION8(J)*YION8(J-1))/(XION8(J-1)-XION8(J))
      QION(8,I)=(A*EN+B)*1.D-16
      GO TO 81
   80 QION(8,I)=QCOUNT*0.0932 
C DOUBLE IONISATION  TO (O+ , N+  ,N)
   81 IF(EN.GT.XION9(NION9)) GO TO 84
      IF(EN.LE.XION9(1)) GO TO 91   
      DO 82 J=2,NION9
      IF(EN.LE.XION9(J)) GO TO 83
   82 CONTINUE
      J=NION9
   83 A=(YION9(J)-YION9(J-1))/(XION9(J)-XION9(J-1))
      B=(XION9(J-1)*YION9(J)-XION9(J)*YION9(J-1))/(XION9(J-1)-XION9(J))
      QION(9,I)=(A*EN+B)*1.D-16
      GO TO 85
   84 QION(9,I)=QCOUNT*0.0432 
C K SHELL IONISATION OF NITROGEN *2        
   85 IF(EN.GT.XION10(NION10)) GO TO 88
      IF(EN.LE.XION10(1)) GO TO 91   
      DO 86 J=2,NION10
      IF(EN.LE.XION10(J)) GO TO 87
   86 CONTINUE
      J=NION10
   87 A=(YION10(J)-YION10(J-1))/(XION10(J)-XION10(J-1))
      B=(XION10(J-1)*YION10(J)-XION10(J)*YION10(J-1))/
     /(XION10(J-1)-XION10(J))
      QION(10,I)=(A*EN+B)*2.D-16
C K SHELL IONISATION OF OXYGEN         
   88 IF(EN.GT.XION11(NION11)) GO TO 91
      IF(EN.LE.XION11(1)) GO TO 91  
      DO 89 J=2,NION11
      IF(EN.LE.XION11(J)) GO TO 90
   89 CONTINUE
      J=NION11
   90 A=(YION11(J)-YION11(J-1))/(XION11(J)-XION11(J-1))
      B=(XION11(J-1)*YION11(J)-XION11(J)*YION11(J-1))/
     /(XION11(J-1)-XION11(J))
      QION(11,I)=(A*EN+B)*1.D-16
C
   91 DO 92 J=1,NION
      IF(EN.LE.(2.0*EION(J))) GO TO 92
      PEQION(J,I)=PEQEL(2,(I-IOFFION(J)))
   92 CONTINUE
C-----------------------------------------------------------------------
C DISSOCIATIVE ATTACHMENT    N2O  -->  N2  + O-                                 
      Q(4,I)=0.0
      QATT(1,I)=0.0
      IF(EN.LE.XATT(1).OR.EN.GT.XATT(NATT1)) GO TO 110        
      DO 101 J=2,NATT1                                                  
      IF(EN.LE.XATT(J)) GO TO 102                                      
  101 CONTINUE                                                          
      J=NATT1                                                           
  102 A=(YATT(J)-YATT(J-1))/(XATT(J)-XATT(J-1))                         
      B=(XATT(J-1)*YATT(J)-XATT(J)*YATT(J-1))/(XATT(J-1)-XATT(J))       
      Q(4,I)=(A*EN+B)*1.D-18*SCALEAT*AMPATT
      QATT(1,I)=Q(4,I)  
C                                                
  110 Q(5,I)=0.0                                                        
      Q(6,I)=0.0 
C
C ZERO INELASTIC ARRAYS
      DO 120 L=1,NIN
      QIN(L,I)=0.0D0
      PEQIN(L,I)=0.0D0
  120 CONTINUE
C
C----------------------------------------------------------------------
C DIPOLE BORN ROTATIONAL STATES
C--------------------------------------------------------------------- 
      ENRT=DSQRT(EN)
C
C SUPERELASTIC ROTATIONAL COLLISIONS
      DO 130 L=1,60
      AL=DFLOAT(L)
      QIN(L,I)=PJ(L)*DBK*DLOG((ENRT+DSQRT(EN-EIN(L)))/(DSQRT(EN-EIN(L))-
     /ENRT))*AL/((2.0*AL+1.0)*EN)         
      IF(EN.GT.500.0) THEN
       QIN(L,I)=0.0
       GO TO 130
      ENDIF
C CALCULATE DIPOLE ANGULAR DSTRIBUTION FACTOR FOR TRANSITION
       EPOINT=EN/DABS(EIN(L))
      IF(EPOINT.GT.ENRTS(NRTANG)) THEN
       WRITE(6,9922) EPOINT,ENRTS(NRTANG),EN
 9922  FORMAT(' ENERGY ERROR IN ROT SUPER ANG CALC  EPOINT=',D12.4,' ENR
     /TS=',D12.4,' EN=',D12.4)
      ENDIF
      DO 121 M=2,NRTANG
      IF(EPOINT.LE.ENRTS(M)) GO TO 122
  121 CONTINUE
      M=NRTANG
  122 A=(YEPSR(M)-YEPSR(M-1))/(ENRTS(M)-ENRTS(M-1))
      B=(ENRTS(M-1)*YEPSR(M)-ENRTS(M)*YEPSR(M-1))/(ENRTS(M-1)-ENRTS(M))
      EPSIL=A*EPOINT+B
      PEQIN(L,I)=1.0-EPSIL
C CALCULATE USED MT XSEC
      A=(YMTRT(M)-YMTRT(M-1))/(ENRTS(M)-ENRTS(M-1))
      B=(ENRTS(M-1)*YMTRT(M)-ENRTS(M)*YMTRT(M-1))/(ENRTS(M-1)-ENRTS(M))
      XSECDUM(L)=A*EPOINT+B
      XSECDUM(L)=XSECDUM(L)*QIN(L,I)
  130 CONTINUE
C 
C ROTATIONAL COLLISIONS
C    ROT 0-1
      IF(EN.LE.EIN(61)) THEN
       QIN(61,I)=0.0
       GO TO 151
      ENDIF
      QIN(61,I)=FROT0*DBK*DLOG((ENRT+DSQRT(EN-EIN(61)))/(ENRT-DSQRT(EN-
     /EIN(61))))/EN              
      IF(EN.GT.500.0) QIN(61,I)=0.0
C ROT 1-2 AND HIGHER
      DO 140 L=62,120
      IF(EN.LE.EIN(L)) THEN
       QIN(L,I)=0.0
       GO TO 140
      ENDIF      
      AL=DFLOAT(L-61)
      QIN(L,I)=PJ(L-61)*DBK*DLOG((ENRT+DSQRT(EN-EIN(L)))/(ENRT-DSQRT(EN-
     /EIN(L))))*(AL+1.0)/((2.0*AL+1.0)*EN)       
      IF(EN.GT.500.0) THEN
       QIN(L,I)=0.0
       GO TO 140
      ENDIF
  140 CONTINUE
C CALCULATE DIPOLE ANGULAR DISTRIBUTION FACTOR FOR TRANSITION
      DO 150 L=61,120
      IF(EN.GT.500.0) GO TO 150
      EPOINT=EN/DABS(EIN(L)) 
      IF(EPOINT.GT.ENROT(NRTANG)) THEN
       WRITE(6,9923) EPOINT,ENROT(NRTANG),EN
 9923  FORMAT(' ENERGY ERROR IN ROT ANG CALC  EPOINT=',D12.4,' ENROT=',
     /D12.4,' EN=',D12.4)
      ENDIF
      DO 141 M=2,NRTANG
      IF(EPOINT.LE.ENROT(M)) GO TO 142
  141 CONTINUE
      M=NTRANG
  142 A=(YEPSR(M)-YEPSR(M-1))/(ENROT(M)-ENROT(M-1))
      B=(ENROT(M-1)*YEPSR(M)-ENROT(M)*YEPSR(M-1))/(ENROT(M-1)-ENROT(M))
      EPSIL=A*EPOINT+B
      PEQIN(L,I)=1.0-EPSIL
C CALCULATE USED MT XSEC
      A=(YMTRT(M)-YMTRT(M-1))/(ENROT(M)-ENROT(M-1))
      B=(ENROT(M-1)*YMTRT(M)-ENROT(M)*YMTRT(M-1))/(ENROT(M-1)-ENROT(M))
      XSECDUM(L)=A*EPOINT+B
      XSECDUM(L)=XSECDUM(L)*QIN(L,I)
  150 CONTINUE
C 
C CALCULATE TOTAL AND MT SUMS OVER ROTATIONS
  151 QSUPTT=0.0
      QSUPMT=0.0
      DO 160 L=1,60
      QSUPTT=QSUPTT+QIN(L,I)
      QSUPMT=QSUPMT+XSECDUM(L)
  160 CONTINUE
      QTOTTT=0.0
      QTOTMT=0.0
      DO 161 L=61,120
      QTOTTT=QTOTTT+QIN(L,I)
      QTOTMT=QTOTMT+XSECDUM(L)
  161 CONTINUE
      QTSMT=QSUPMT+QTOTMT
      QTSEL=QSUPTT+QTOTTT
C     WRITE(6,944) EN,QSUPTT,QSUPMT,QTOTTT,QTOTMT,QTSEL,QTSMT
C 944 FORMAT(' CROSS SECTION ROTATIONAL SUMS AT ENERGY =',D12.4,/,
C    /' TOTAL SUPER =',D12.4,'  MT SUPER =',D12.4,/,' TOTAL ROTAT =',
C    /D12.4,'  MT ROTAT =',D12.4,/,' TOT ROT=',D12.4,' TOT ROT MT=',
C    /D12.4)
C***********************************************************************
C SUPERELASTIC V(010)  BEND MODE
  200 QIN(121,I)=0.0   
      IF(EN.LE.0.0) GO TO 210
      EFAC=DSQRT(1.0-(EIN(121)/EN))
      QIN(121,I)=AMPV2*DLOG((EFAC+1.0)/(EFAC-1.0))/EN       
C
      QRES=0.0
      IF((EN+EIN(122)).GT.XVIBH(NVIBH).OR.(EN+EIN(122)).LT.XVIBH(1)) 
     /GO TO 203
      DO 201 J=2,NVIBH
      IF((EN+EIN(122)).LE.XVIBH(J)) GO TO 202
  201 CONTINUE
      J=NVIBH
  202 A=(YVIBH(J)-YVIBH(J-1))/(XVIBH(J)-XVIBH(J-1))
      B=(XVIBH(J-1)*YVIBH(J)-XVIBH(J)*YVIBH(J-1))/(XVIBH(J-1)-XVIBH(J))
      QRES=ARESH2*(EN+EIN(122))*(A*(EN+EIN(122))+B)/EN 
  203 QIN(121,I)=QIN(121,I)+QRES
