      IF(EN.LE.0.0) GO TO 250                                       
      IF(EN.GE.XATT(NATT1)) GO TO 250                                   
      DO 210 J=2,NATT1                                                  
      IF(EN.LE.XATT(J)) GO TO 220                                       
  210 CONTINUE                                                          
      J=NATT1 
  220 XNJ=DLOG(XATT(J))
      XNJ1=DLOG(XATT(J-1))
      XNL=XATT(J)
      XNL1=XATT(J-1)
C LOG
      IF(YAT1(J).EQ.0.0.OR.YAT1(J-1).EQ.0.0) THEN
       QAT1=0.0
       GO TO 221
      ENDIF
      Y1J=DLOG(YAT1(J))
      Y1J1=DLOG(YAT1(J-1))
      A1=(Y1J-Y1J1)/(XNJ-XNJ1)
      B1=(XNJ1*Y1J-XNJ*Y1J1)/(XNJ1-XNJ)
      QAT1=DEXP(A1*DLOG(EN)+B1)*1.D-16
