C----------------------------------------------------------------
C      
C  SUPER ELASTIC ROTATIONAL COLLISIONS
      DO 140 L=1,27,2
C CALCULATE DIPOLE ANGULAR DISTRIBUTION FACTOR FOR TRANSITION
      EPOINT=EN/DABS(EIN(L))
      IF(EPOINT.GT.ENRTS(NRTANG)) THEN
       WRITE(6,922) EPOINT,ENRTS(NRTANG),EN
  922  FORMAT(' ENERGY ERROR IN ROT SUPER ANG CALC  EPOINT=',D12.4,' ENR
     /TS=',D12.4,' EN=',D12.4)
      ENDIF
      DO 131 M=1,NRTANG
      IF(EPOINT.LE.ENRTS(M)) GO TO 132 
  131 CONTINUE
      M=NRTANG
  132 A=(YEPSR(M)-YEPSR(M-1))/(ENRTS(M)-ENRTS(M-1))
      B=(ENRTS(M-1)*YEPSR(M)-ENRTS(M)*YEPSR(M-1))/(ENRTS(M-1)-ENRTS(M))
      EPSIL=A*EPOINT+B
      PEQIN(L,I)=1.0-EPSIL
C CALCULATE USED MT XSEC
      A=(YMTRT(M)-YMTRT(M-1))/(ENRTS(M)-ENRTS(M-1))
      B=(ENRTS(M-1)*YMTRT(M)-ENRTS(M)*YMTRT(M-1))/(ENRTS(M-1)-ENRTS(M))
      XSECDUM(L)=A*EPOINT+B
      XSECDUM(L)=XSECDUM(L)*QIN(L,I)
  140 CONTINUE
C 
C ROTATIONAL COLLISIONS
      DO 150 L=2,28,2
C CALCULATE DIPOLE ANGULAR DISTRIBUTION FACTOR FOR TRANSITION
      EPOINT=EN/DABS(EIN(L))
      IF(EPOINT.GT.ENROT(NRTANG)) THEN
       WRITE(6,923) EPOINT,ENROT(NRTANG),EN
  923  FORMAT(' ENERGY ERROR IN ROT ANG CALC  EPOINT=',D12.4,' ENROT=',
     /D12.4,' EN=',D12.4)
      ENDIF
      IF(EN.LE.EIN(L)) THEN
       PEQIN(L,I)=0.0
       XSECDUM(L)=0.0
