      QATT(1,I)=Q(4,I)                                           
C                                                                       
  300 Q(5,I)=0.0                                                        
      Q(6,I)=0.0 
C                                                             
C V7 SUPERELASTIC                                                       
      QIN(1,I)=0.0                                                      
      IF(EN.LE.0.0) GO TO 350
      EFAC=DSQRT(1.0-(EIN(1)/EN))
