  600 CONTINUE                                                          
C                                                                       
C                                                                       
      QIN(8,I)=0.0                                                      
      IF(EN.LE.EIN(8)) GO TO 850                                        
      DO 810 J=2,NEXC1                                                  
      IF(EN.LE.XEXC1(J)) GO TO 820                                      
  810 CONTINUE                                                          
